          pagado_total,
          por_cobrar,
          gastos_total,
          costo_directo,
          utilidad_bruta,
          utilidad_vs_presupuesto,
          utilidad_pct_real,
          avance_financiero_pct,
          margen_presupuestado_pct,
          empresa_nombre,
          dependencia_nombre
