export type AppError = { code?: string; error: string };

export function adaptSupabaseError(e: any): AppError {
  const msg = e?.message || String(e || 'Unknown');
  const code = e?.code || '';

  if (/Failed to fetch|Network|ERR_NETWORK|TypeError: fetch/i.test(msg))
    return { code: 'NETWORK', error: 'Sin conexi√≥n con el servicio. Int√©ntalo de nuevo.' };

  // permisos / RLS
  if (/permission denied|401|403|RLS/i.test(msg))
    return { code: 'FORBIDDEN', error: 'No tienes permisos para esa operaci√≥n.' };

  // recurso inexistente
  if (/PGRST116|No rows found/i.test(msg))
    return { code: 'NOT_FOUND', error: 'No se encontr√≥ la informaci√≥n solicitada.' };

  // validaci√≥n
  if (/invalid input|violates|constraint/i.test(msg))
    return { code: 'VALIDATION', error: 'Datos inv√°lidos o incompletos.' };

  // config supabase
  if (/Invalid API key|Project not found/i.test(msg))
    return { code: 'CONFIG', error: 'Configuraci√≥n de base de datos inv√°lida.' };

  return { code: code || 'UNKNOWN', error: 'Ocurri√≥ un error. Intenta de nuevo.' };
}

