import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import RoleBasedSidebar from '../../components/ui/RoleBasedSidebar';
import NavigationHeader from '../../components/ui/NavigationHeader';
import UserContextHeader from '../../components/ui/UserContextHeader';
import NotificationCenter from '../../components/ui/NotificationCenter';
import Icon from '../../components/AppIcon';
import Button from '../../components/ui/Button';
import Input from '../../components/ui/Input';
import EmployeeFilters from './components/EmployeeFilters';
import EmployeeTable from './components/EmployeeTable';
import EmployeeDetailPanel from './components/EmployeeDetailPanel';
import BulkActionsToolbar from './components/BulkActionsToolbar';
import EmployeeCreationModal from './components/EmployeeCreationModal';
import { useAuth } from '../../contexts/AuthContext';
import enhancedEmployeeService from '../../services/enhancedEmployeeService';

const AdministratorEmployeeManagementConsole = () => {
  const navigate = useNavigate();
  const { getCurrentUserContext, loading: authLoading } = useAuth();

  // State management
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);
  const [selectedEmployees, setSelectedEmployees] = useState([]);
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [isDetailPanelOpen, setIsDetailPanelOpen] = useState(false);
  const [isEditingEmployee, setIsEditingEmployee] = useState(false);
  const [isCreationModalOpen, setIsCreationModalOpen] = useState(false);
  const [globalSearch, setGlobalSearch] = useState('');

  // Data state
  const [employees, setEmployees] = useState([]);
  const [filteredEmployees, setFilteredEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [stats, setStats] = useState(null);

  // Filter state
  const [filters, setFilters] = useState({
    search: '',
    site: 'all',
    supervisor: 'all',
    status: [],
    position: 'all',
    hireDateFrom: '',
    hireDateTo: ''
  });

  const [sortConfig, setSortConfig] = useState({
    column: 'name',
    direction: 'asc'
  });

  const [savedFilters, setSavedFilters] = useState([]);

  const currentUser = getCurrentUserContext();

  // Load employees data
  const loadEmployees = async () => {
    try {
      setLoading(true);
      setError('');
      const employeesData = await enhancedEmployeeService?.getEmployees();
      setEmployees(employeesData);
    } catch (err) {
      console.error('Error loading employees:', err);
      setError(err?.message || 'Failed to load employees');
    } finally {
      setLoading(false);
    }
  };

  // Load statistics
  const loadStats = async () => {
    try {
      const statsData = await enhancedEmployeeService?.getEmployeeStats();
      setStats(statsData);
    } catch (err) {
      console.error('Error loading stats:', err);
    }
  };

  // Initial data load
  useEffect(() => {
    if (!authLoading && currentUser) {
      loadEmployees();
      loadStats();
    }
  }, [authLoading, currentUser]);

  // Filter and sort employees
  useEffect(() => {
    let filtered = [...employees];

    // Apply filters
    if (filters?.search) {
      const searchTerm = filters?.search?.toLowerCase();
      filtered = filtered?.filter(employee =>
        employee?.name?.toLowerCase()?.includes(searchTerm) ||
        employee?.email?.toLowerCase()?.includes(searchTerm) ||
        employee?.employeeId?.toLowerCase()?.includes(searchTerm) ||
        employee?.phone?.toLowerCase()?.includes(searchTerm)
      );
    }

    if (filters?.site !== 'all') {
      filtered = filtered?.filter(employee => employee?.site === filters?.site);
    }

    if (filters?.supervisor !== 'all') {
      filtered = filtered?.filter(employee => employee?.supervisor === filters?.supervisor);
    }

    if (filters?.status?.length > 0) {
      filtered = filtered?.filter(employee => filters?.status?.includes(employee?.status));
    }

    if (filters?.position !== 'all') {
      filtered = filtered?.filter(employee => employee?.position === filters?.position);
    }

    if (filters?.hireDateFrom) {
      filtered = filtered?.filter(employee => 
        new Date(employee?.hireDate) >= new Date(filters?.hireDateFrom)
      );
    }

    if (filters?.hireDateTo) {
      filtered = filtered?.filter(employee => 
        new Date(employee?.hireDate) <= new Date(filters?.hireDateTo)
      );
    }

    // Apply sorting
    filtered?.sort((a, b) => {
      const aValue = a?.[sortConfig?.column];
      const bValue = b?.[sortConfig?.column];

      if (sortConfig?.direction === 'asc') {
        return aValue < bValue ? -1 : aValue > bValue ? 1 : 0;
      } else {
        return aValue > bValue ? -1 : aValue < bValue ? 1 : 0;
      }
    });

    setFilteredEmployees(filtered);
  }, [employees, filters, sortConfig]);

  // Update global search in filters
  useEffect(() => {
    setFilters(prev => ({ ...prev, search: globalSearch }));
  }, [globalSearch]);

  // Event handlers
  const handleEmployeeSelect = (employeeId, selected) => {
    if (selected) {
      setSelectedEmployees(prev => [...prev, employeeId]);
    } else {
      setSelectedEmployees(prev => prev?.filter(id => id !== employeeId));
    }
  };

  const handleSelectAll = (selected) => {
    if (selected) {
      setSelectedEmployees(filteredEmployees?.map(emp => emp?.id));
    } else {
      setSelectedEmployees([]);
    }
  };

  const handleEmployeeClick = async (employee) => {
    try {
      // Fetch complete employee details
      const fullEmployee = await enhancedEmployeeService?.getEmployeeById(employee?.id);
      setSelectedEmployee(fullEmployee);
      setIsDetailPanelOpen(true);
      setIsEditingEmployee(false);
    } catch (err) {
      console.error('Error loading employee details:', err);
      setError('Failed to load employee details');
    }
  };

  const handleCloseDetailPanel = () => {
    setIsDetailPanelOpen(false);
    setSelectedEmployee(null);
    setIsEditingEmployee(false);
  };

  const handleSaveEmployee = async (updatedEmployee) => {
    try {
      await enhancedEmployeeService?.updateEmployee(updatedEmployee?.id, updatedEmployee);
      setSelectedEmployee(updatedEmployee);
      // Reload employees list
      await loadEmployees();
      await loadStats();
    } catch (err) {
      console.error('Error updating employee:', err);
      setError('Failed to update employee');
    }
  };

  const handleCreateEmployee = async (newEmployeeData) => {
    try {
      await enhancedEmployeeService?.createEmployee(newEmployeeData);
      // Reload employees list
      await loadEmployees();
      await loadStats();
      setIsCreationModalOpen(false);
    } catch (err) {
      console.error('Error creating employee:', err);
      setError('Failed to create employee');
    }
  };

  const handleBulkAction = async (action) => {
    try {
      if (action === 'delete' && selectedEmployees?.length > 0) {
        // Implement bulk delete
        for (const employeeId of selectedEmployees) {
          await enhancedEmployeeService?.deleteEmployee(employeeId, currentUser?.id);
        }
        await loadEmployees();
        await loadStats();
        setSelectedEmployees([]);
      } else if (action === 'activate' && selectedEmployees?.length > 0) {
        await enhancedEmployeeService?.bulkUpdateEmployees(selectedEmployees, { status: 'active' });
        await loadEmployees();
        setSelectedEmployees([]);
      } else if (action === 'deactivate' && selectedEmployees?.length > 0) {
        await enhancedEmployeeService?.bulkUpdateEmployees(selectedEmployees, { status: 'inactive' });
        await loadEmployees();
        setSelectedEmployees([]);
      }
    } catch (err) {
      console.error('Error in bulk action:', err);
      setError(`Failed to ${action} employees`);
    }
  };

  const handleSaveFilter = (filterData) => {
    setSavedFilters(prev => [...prev, filterData]);
  };

  const handleLoadFilter = (savedFilter) => {
    setFilters(savedFilter?.filters);
  };

  const handleClearFilters = () => {
    setFilters({
      search: '',
      site: 'all',
      supervisor: 'all',
      status: [],
      position: 'all',
      hireDateFrom: '',
      hireDateTo: ''
    });
    setGlobalSearch('');
  };

  const handleViewAttendance = (employee) => {
    navigate('/attendance-history-and-analytics-dashboard', { 
      state: { employeeId: employee?.id } 
    });
  };

  const handleViewPayroll = (employee) => {
    navigate('/payroll-calculation-and-management-interface', { 
      state: { employeeId: employee?.id } 
    });
  };

  const handleViewIncidents = (employee) => {
    navigate('/incident-registration-and-management-system', { 
      state: { employeeId: employee?.id } 
    });
  };

  const handleExportData = () => {
    const dataToExport = selectedEmployees?.length > 0 
      ? employees?.filter(emp => selectedEmployees?.includes(emp?.id))
      : filteredEmployees;

    // Create CSV content
    const csvContent = [
      // Headers
      ['ID Empleado', 'Nombre', 'Email', 'Tel√©fono', 'Obra', 'Supervisor', 'Puesto', 'Estado', 'Salario Diario']?.join(','),
      // Data rows
      ...dataToExport?.map(emp => [
        emp?.employeeId || '',
        `"${emp?.name || ''}"`,
        emp?.email || '',
        emp?.phone || '',
        `"${emp?.site || ''}"`,
        `"${emp?.supervisor || ''}"`,
        emp?.position || '',
        emp?.status || '',
        emp?.dailySalary || 0
      ]?.join(','))
    ]?.join('\n');

    // Download file
    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const link = document.createElement('a');
    const url = URL.createObjectURL(blob);
    link?.setAttribute('href', url);
    link?.setAttribute('download', `empleados_${new Date()?.toISOString()?.split('T')?.[0]}.csv`);
    link.style.visibility = 'hidden';
    document.body?.appendChild(link);
    link?.click();
    document.body?.removeChild(link);
  };

  // Show loading state
  if (authLoading || loading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="flex flex-col items-center space-y-4">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
          <span className="text-gray-600">Cargando empleados...</span>
        </div>
      </div>
    );
  }

  // Show error state
  if (error && !loading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <Icon name="AlertCircle" size={48} className="text-red-500 mx-auto mb-4" />
          <h2 className="text-lg font-semibold text-gray-900 mb-2">Error al cargar datos</h2>
          <p className="text-gray-600 mb-4">{error}</p>
          <Button onClick={loadEmployees} variant="outline">
            Reintentar
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      {/* Sidebar */}
      <RoleBasedSidebar 
        isCollapsed={sidebarCollapsed}
        onToggleCollapse={() => setSidebarCollapsed(!sidebarCollapsed)}
        userRole={currentUser?.role?.toLowerCase()}
      />

      {/* Main Content */}
      <div className={`transition-all duration-300 ease-out-cubic ${sidebarCollapsed ? 'ml-16' : 'ml-60'} pb-16 md:pb-0`}>
        {/* Header */}
        <header className="bg-card border-b border-border px-6 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <Button
                variant="ghost"
                size="icon"
                onClick={() => setSidebarCollapsed(!sidebarCollapsed)}
                className="hidden md:flex"
                iconName={sidebarCollapsed ? 'PanelLeftOpen' : 'PanelLeftClose'}
                iconSize={20}
              />

              <div>
                <h1 className="text-2xl font-bold text-foreground">Consola de Empleados</h1>
                <p className="text-muted-foreground">Gesti√≥n integral de empleados y estructura organizacional</p>
              </div>
            </div>

            <div className="flex items-center space-x-4">
              {/* Global Search */}
              <div className="hidden md:block w-80">
                <Input
                  type="search"
                  placeholder="Buscar empleados..."
                  value={globalSearch}
                  onChange={(e) => setGlobalSearch(e?.target?.value)}
                  className="w-full"
                />
              </div>

              <NotificationCenter />
              <UserContextHeader 
                onLogout={() => navigate('/employee-login-portal')}
              />
            </div>
          </div>
        </header>

        {/* Content */}
        <main className="p-6">
          {/* Navigation Header */}
          <NavigationHeader 
            title="Consola de Empleados"
            subtitle="Gesti√≥n integral de empleados y estructura organizacional"
            showBackButton={false}
            showHomeButton={false}
          />

          {/* Stats Cards */}
          {stats && (
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
              <div className="bg-card rounded-lg p-4 border border-border">
                <div className="flex items-center space-x-3">
                  <div className="p-2 bg-blue-100 rounded-lg">
                    <Icon name="Users" size={20} className="text-blue-600" />
                  </div>
                  <div>
                    <div className="text-2xl font-bold text-foreground">{stats?.total}</div>
                    <div className="text-sm text-muted-foreground">Total Empleados</div>
                  </div>
                </div>
              </div>

              <div className="bg-card rounded-lg p-4 border border-border">
                <div className="flex items-center space-x-3">
                  <div className="p-2 bg-green-100 rounded-lg">
                    <Icon name="CheckCircle" size={20} className="text-green-600" />
                  </div>
                  <div>
                    <div className="text-2xl font-bold text-foreground">{stats?.active}</div>
                    <div className="text-sm text-muted-foreground">Activos</div>
                  </div>
                </div>
              </div>

              <div className="bg-card rounded-lg p-4 border border-border">
                <div className="flex items-center space-x-3">
                  <div className="p-2 bg-gray-100 rounded-lg">
                    <Icon name="Pause" size={20} className="text-gray-600" />
                  </div>
                  <div>
                    <div className="text-2xl font-bold text-foreground">{stats?.inactive}</div>
                    <div className="text-sm text-muted-foreground">Inactivos</div>
                  </div>
                </div>
              </div>

              <div className="bg-card rounded-lg p-4 border border-border">
                <div className="flex items-center space-x-3">
                  <div className="p-2 bg-red-100 rounded-lg">
                    <Icon name="AlertCircle" size={20} className="text-red-600" />
                  </div>
                  <div>
                    <div className="text-2xl font-bold text-foreground">{stats?.suspended}</div>
                    <div className="text-sm text-muted-foreground">Suspendidos</div>
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Action Bar */}
          <div className="flex items-center justify-between mb-6">
            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-2 text-muted-foreground">
                <Icon name="Users" size={20} />
                <span className="text-sm">
                  {filteredEmployees?.length} empleado{filteredEmployees?.length !== 1 ? 's' : ''} 
                  {filteredEmployees?.length !== employees?.length && ` de ${employees?.length} total`}
                </span>
              </div>
            </div>

            <div className="flex items-center space-x-2">
              <Button
                variant="outline"
                onClick={handleExportData}
                iconName="Download"
                iconSize={16}
              >
                Exportar
              </Button>

              <Button
                onClick={() => setIsCreationModalOpen(true)}
                iconName="Plus"
                iconSize={16}
              >
                Nuevo Empleado
              </Button>
            </div>
          </div>

          {/* Bulk Actions */}
          <BulkActionsToolbar
            selectedCount={selectedEmployees?.length}
            onBulkAction={handleBulkAction}
            onClearSelection={() => setSelectedEmployees([])}
            userRole={currentUser?.role?.toLowerCase()}
          />

          {/* Main Layout */}
          <div className="grid grid-cols-1 xl:grid-cols-4 gap-6">
            {/* Filters Sidebar */}
            <div className="xl:col-span-1">
              <EmployeeFilters
                filters={filters}
                onFiltersChange={setFilters}
                onClearFilters={handleClearFilters}
                savedFilters={savedFilters}
                onSaveFilter={handleSaveFilter}
                onLoadFilter={handleLoadFilter}
              />
            </div>

            {/* Employee Table */}
            <div className={`${isDetailPanelOpen ? 'xl:col-span-2' : 'xl:col-span-3'}`}>
              <EmployeeTable
                employees={filteredEmployees}
                selectedEmployees={selectedEmployees}
                onEmployeeSelect={handleEmployeeSelect}
                onSelectAll={handleSelectAll}
                onEmployeeClick={handleEmployeeClick}
                onSort={setSortConfig}
                sortConfig={sortConfig}
                onDelete={async (employeeId) => {
                  try {
                    await enhancedEmployeeService?.deleteEmployee(employeeId, currentUser?.id);
                    await loadEmployees();
                    await loadStats();
                  } catch (err) {
                    console.error('Error deleting employee:', err);
                    setError('Failed to delete employee');
                  }
                }}
                onRestore={async (employeeId) => {
                  try {
                    await enhancedEmployeeService?.updateEmployee(employeeId, { status: 'active' });
                    await loadEmployees();
                    await loadStats();
                  } catch (err) {
                    console.error('Error restoring employee:', err);
                    setError('Failed to restore employee');
                  }
                }}
                userRole={currentUser?.role?.toLowerCase()}
                loading={loading}
              />
            </div>

            {/* Detail Panel */}
            {isDetailPanelOpen && (
              <div className="xl:col-span-1">
                <EmployeeDetailPanel
                  employee={selectedEmployee}
                  onClose={handleCloseDetailPanel}
                  onSave={handleSaveEmployee}
                  onViewAttendance={handleViewAttendance}
                  onViewPayroll={handleViewPayroll}
                  onViewIncidents={handleViewIncidents}
                  userRole={currentUser?.role?.toLowerCase()}
                  isEditing={isEditingEmployee}
                  onToggleEdit={() => setIsEditingEmployee(!isEditingEmployee)}
                />
              </div>
            )}
          </div>
        </main>
      </div>

      {/* Employee Creation Modal */}
      <EmployeeCreationModal
        isOpen={isCreationModalOpen}
        onClose={() => setIsCreationModalOpen(false)}
        onSave={handleCreateEmployee}
        userRole={currentUser?.role?.toLowerCase()}
      />
    </div>
  );
};

export default AdministratorEmployeeManagementConsole;

src/pages/advanced-payroll-calculation-e.content:
import React, { useState, useEffect } from 'react';
import { Calculator, DollarSign, Clock, TrendingUp, Users, FileText, AlertCircle, CheckCircle2 } from 'lucide-react';
import { useAuth } from '../../contexts/AuthContext';
import { payrollService } from '../../services/payrollService';
import { employeeService } from '../../services/employeeService';
import BrandedHeader from '../../components/ui/BrandedHeader';
import PayrollCalculationDashboard from './components/PayrollCalculationDashboard';
import EmployeePayrollGrid from './components/EmployeePayrollGrid';
import BulkProcessingTools from './components/BulkProcessingTools';
import PayrollAuditTrail from './components/PayrollAuditTrail';
import CurrencyDisplay from '../../components/ui/CurrencyDisplay';

export default function AdvancedPayrollCalculationEngineWithComprehensiveWageManagement() {
  const { user, userProfile } = useAuth();
  const [employees, setEmployees] = useState([]);
  const [selectedEmployees, setSelectedEmployees] = useState([]);
  const [payrollData, setPayrollData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [processing, setProcessing] = useState(false);
  const [activeView, setActiveView] = useState('dashboard');
  const [auditEntries, setAuditEntries] = useState([]);
  const [weekRange, setWeekRange] = useState({
    start: new Date(),
    end: new Date()
  });

  // Initialize week range to current week
  useEffect(() => {
    const now = new Date();
    const startOfWeek = new Date(now);
    startOfWeek?.setDate(now?.getDate() - now?.getDay());
    const endOfWeek = new Date(startOfWeek);
    endOfWeek?.setDate(startOfWeek?.getDate() + 6);

    setWeekRange({
      start: startOfWeek,
      end: endOfWeek
    });
  }, []);

  // Load employees with payroll data
  useEffect(() => {
    if (!user) return;

    const loadEmployeesAndPayroll = async () => {
      try {
        setLoading(true);
        const employeeData = await employeeService?.getAllEmployees();
        setEmployees(employeeData || []);

        // Load existing payroll data for current week
        const startDate = weekRange?.start?.toISOString()?.split('T')?.[0];
        const payrollSummary = await payrollService?.getPayrollSummary(startDate, startDate);
        setPayrollData(payrollSummary || []);
      } catch (error) {
        console.error('Error loading data:', error);
      } finally {
        setLoading(false);
      }
    };

    loadEmployeesAndPayroll();
  }, [user, weekRange]);

  // Calculate payroll for single employee
  const calculateSinglePayroll = async (employeeId) => {
    try {
      setProcessing(true);
      const startDate = weekRange?.start?.toISOString()?.split('T')?.[0];

      const calculation = await payrollService?.calculateWeeklyPayroll(
        employeeId, 
        startDate, 
        weekRange?.end?.toISOString()?.split('T')?.[0]
      );

      // Update payroll data
      setPayrollData(prev => {
        const updated = prev?.filter(p => p?.employeeId !== employeeId);
        return [...updated, calculation];
      });

      // Add audit entry
      const employee = employees?.find(e => e?.id === employeeId);
      setAuditEntries(prev => [...prev, {
        id: Date.now(),
        action: 'calculate_payroll',
        employee: employee?.full_name || 'Unknown',
        amount: calculation?.grossPay || 0,
        timestamp: new Date(),
        user: userProfile?.full_name || 'System'
      }]);

    } catch (error) {
      console.error('Error calculating payroll:', error);
    } finally {
      setProcessing(false);
    }
  };

  // Bulk calculate payroll for selected employees
  const calculateBulkPayroll = async () => {
    if (selectedEmployees?.length === 0) return;

    try {
      setProcessing(true);
      const calculations = [];

      for (const employeeId of selectedEmployees) {
        const startDate = weekRange?.start?.toISOString()?.split('T')?.[0];
        const calculation = await payrollService?.calculateWeeklyPayroll(
          employeeId,
          startDate,
          weekRange?.end?.toISOString()?.split('T')?.[0]
        );
        calculations?.push(calculation);
      }

      // Update payroll data
      setPayrollData(prev => {
        const filtered = prev?.filter(p => !selectedEmployees?.includes(p?.employeeId));
        return [...filtered, ...calculations];
      });

      // Add bulk audit entry
      setAuditEntries(prev => [...prev, {
        id: Date.now(),
        action: 'bulk_calculate',
        employee: `${selectedEmployees?.length} employees`,
        amount: calculations?.reduce((sum, calc) => sum + (calc?.grossPay || 0), 0),
        timestamp: new Date(),
        user: userProfile?.full_name || 'System'
      }]);

    } catch (error) {
      console.error('Error in bulk calculation:', error);
    } finally {
      setProcessing(false);
    }
  };

  // Calculate Christmas bonus (Aguinaldo)
  const calculateAguinaldo = async (employeeId, daysWorked = 365) => {
    try {
      const employee = employees?.find(e => e?.id === employeeId);
      if (!employee) return;

      // 15-day minimum requirement based on daily salary
      const dailySalary = employee?.daily_salary || 0;
      const aguinaldo = (dailySalary * 15 * daysWorked) / 365;

      setAuditEntries(prev => [...prev, {
        id: Date.now(),
        action: 'calculate_aguinaldo',
        employee: employee?.full_name,
        amount: aguinaldo,
        timestamp: new Date(),
        user: userProfile?.full_name || 'System',
        details: `${daysWorked} days worked`
      }]);

      return aguinaldo;
    } catch (error) {
      console.error('Error calculating aguinaldo:', error);
    }
  };

  // Calculate severance payment (Finiquito)
  const calculateSeverance = async (employeeId, terminationReason = 'voluntary') => {
    try {
      const employee = employees?.find(e => e?.id === employeeId);
      if (!employee) return;

      const dailySalary = employee?.daily_salary || 0;
      let severanceAmount = 0;

      // Different calculations based on termination type
      switch (terminationReason) {
        case 'without_cause':
          severanceAmount = dailySalary * 90; // 90-day salary
          break;
        case 'voluntary':
          severanceAmount = dailySalary * 20; // 20-day vacation premium
          break;
        default:
          severanceAmount = dailySalary * 30;
      }

      setAuditEntries(prev => [...prev, {
        id: Date.now(),
        action: 'calculate_severance',
        employee: employee?.full_name,
        amount: severanceAmount,
        timestamp: new Date(),
        user: userProfile?.full_name || 'System',
        details: `Reason: ${terminationReason}`
      }]);

      return severanceAmount;
    } catch (error) {
      console.error('Error calculating severance:', error);
    }
  };

  // Process payroll (mark as approved)
  const processPayroll = async (payrollId) => {
    try {
      await payrollService?.processPayroll(payrollId, user?.id);

      // Update local state
      setPayrollData(prev => 
        prev?.map(p => 
          p?.id === payrollId 
            ? { ...p, processed: true, processedBy: user?.id, processedAt: new Date() }
            : p
        )
      );
    } catch (error) {
      console.error('Error processing payroll:', error);
    }
  };

  const stats = {
    totalEmployees: employees?.length,
    processedPayroll: payrollData?.filter(p => p?.processed)?.length,
    totalGrossPay: payrollData?.reduce((sum, p) => sum + (p?.grossPay || 0), 0),
    totalNetPay: payrollData?.reduce((sum, p) => sum + (p?.netPay || 0), 0)
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="flex flex-col items-center space-y-4">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
          <span className="text-gray-600">Cargando motor de n√≥minas...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <BrandedHeader 
        title="Motor Avanzado de C√°lculo de N√≥minas"
        subtitle="Gesti√≥n integral de salarios con c√°lculos automatizados"
        icon={Calculator}
        user={userProfile}
      />
      {/* Stats Cards */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
          <div className="bg-white p-6 rounded-lg shadow">
            <div className="flex items-center">
              <Users className="h-8 w-8 text-blue-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Total Empleados</p>
                <p className="text-2xl font-bold text-gray-900">{stats?.totalEmployees}</p>
              </div>
            </div>
          </div>

          <div className="bg-white p-6 rounded-lg shadow">
            <div className="flex items-center">
              <CheckCircle2 className="h-8 w-8 text-green-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">N√≥minas Procesadas</p>
                <p className="text-2xl font-bold text-gray-900">{stats?.processedPayroll}</p>
              </div>
            </div>
          </div>

          <div className="bg-white p-6 rounded-lg shadow">
            <div className="flex items-center">
              <DollarSign className="h-8 w-8 text-emerald-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Salario Bruto</p>
                <p className="text-2xl font-bold text-gray-900">
                  <CurrencyDisplay amount={stats?.totalGrossPay} />
                </p>
              </div>
            </div>
          </div>

          <div className="bg-white p-6 rounded-lg shadow">
            <div className="flex items-center">
              <TrendingUp className="h-8 w-8 text-purple-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Salario Neto</p>
                <p className="text-2xl font-bold text-gray-900">
                  <CurrencyDisplay amount={stats?.totalNetPay} />
                </p>
              </div>
            </div>
          </div>
        </div>

        {/* Navigation Tabs */}
        <div className="bg-white rounded-lg shadow mb-8">
          <div className="border-b border-gray-200">
            <nav className="-mb-px flex">
              <button
                onClick={() => setActiveView('dashboard')}
                className={`px-6 py-3 font-medium text-sm border-b-2 ${
                  activeView === 'dashboard' ?'border-blue-500 text-blue-600' :'border-transparent text-gray-500 hover:text-gray-700'
                }`}
              >
                <Calculator className="h-4 w-4 inline mr-2" />
                Panel de C√°lculo
              </button>

              <button
                onClick={() => setActiveView('employees')}
                className={`px-6 py-3 font-medium text-sm border-b-2 ${
                  activeView === 'employees' ?'border-blue-500 text-blue-600' :'border-transparent text-gray-500 hover:text-gray-700'
                }`}
              >
                <Users className="h-4 w-4 inline mr-2" />
                Lista de Empleados
              </button>

              <button
                onClick={() => setActiveView('bulk')}
                className={`px-6 py-3 font-medium text-sm border-b-2 ${
                  activeView === 'bulk' ?'border-blue-500 text-blue-600' :'border-transparent text-gray-500 hover:text-gray-700'
                }`}
              >
                <FileText className="h-4 w-4 inline mr-2" />
                Procesamiento Masivo
              </button>

              <button
                onClick={() => setActiveView('audit')}
                className={`px-6 py-3 font-medium text-sm border-b-2 ${
                  activeView === 'audit' ?'border-blue-500 text-blue-600' :'border-transparent text-gray-500 hover:text-gray-700'
                }`}
              >
                <AlertCircle className="h-4 w-4 inline mr-2" />
                Auditor√≠a
              </button>
            </nav>
          </div>
        </div>

        {/* Content Area */}
        <div className="grid grid-cols-12 gap-8">
          {/* Main Content (60%) */}
          <div className="col-span-12 lg:col-span-7">
            {activeView === 'dashboard' && (
              <PayrollCalculationDashboard
                weekRange={weekRange}
                onWeekRangeChange={setWeekRange}
                payrollData={payrollData}
                onCalculatePayroll={calculateSinglePayroll}
                onCalculateAguinaldo={calculateAguinaldo}
                onCalculateSeverance={calculateSeverance}
                processing={processing}
              />
            )}

            {activeView === 'employees' && (
              <EmployeePayrollGrid
                employees={employees}
                payrollData={payrollData}
                selectedEmployees={selectedEmployees}
                onSelectionChange={setSelectedEmployees}
                onCalculatePayroll={calculateSinglePayroll}
                onProcessPayroll={processPayroll}
                processing={processing}
              />
            )}

            {activeView === 'bulk' && (
              <BulkProcessingTools
                selectedEmployees={selectedEmployees}
                employees={employees}
                onBulkCalculate={calculateBulkPayroll}
                processing={processing}
                weekRange={weekRange}
              />
            )}

            {activeView === 'audit' && (
              <PayrollAuditTrail
                auditEntries={auditEntries}
                onClearAudit={() => setAuditEntries([])}
              />
            )}
          </div>

          {/* Sidebar (40%) */}
          <div className="col-span-12 lg:col-span-5">
            <div className="bg-white rounded-lg shadow">
              <div className="p-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">
                  <Clock className="h-5 w-5 inline mr-2" />
                  Actualizaciones en Tiempo Real
                </h3>

                <div className="space-y-4">
                  {payrollData?.slice(0, 5)?.map((payroll, index) => (
                    <div key={index} className="flex items-center justify-between p-3 bg-gray-50 rounded">
                      <div>
                        <p className="font-medium text-sm">{payroll?.employeeName || 'Empleado'}</p>
                        <p className="text-xs text-gray-500">
                          {payroll?.regularHours || 0}h normales, {payroll?.overtimeHours || 0}h extra
                        </p>
                      </div>
                      <div className="text-right">
                        <p className="font-bold text-sm">
                          <CurrencyDisplay amount={payroll?.grossPay || 0} />
                        </p>
                        <span className={`inline-flex px-2 py-1 rounded-full text-xs font-medium ${
                          payroll?.processed 
                            ? 'bg-green-100 text-green-800' :'bg-yellow-100 text-yellow-800'
                        }`}>
                          {payroll?.processed ? 'Procesada' : 'Pendiente'}
                        </span>
                      </div>
                    </div>
                  ))}
                </div>

                {payrollData?.length === 0 && (
                  <div className="text-center py-8 text-gray-500">
                    <Calculator className="h-12 w-12 mx-auto mb-4 opacity-50" />
                    <p>No hay c√°lculos de n√≥mina disponibles</p>
                    <p className="text-sm">Selecciona empleados para comenzar</p>
                  </div>
                )}
              </div>
            </div>

            {/* Quick Actions */}
            <div className="bg-white rounded-lg shadow mt-6">
              <div className="p-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">Acciones R√°pidas</h3>

                <div className="space-y-3">
                  <button
                    onClick={calculateBulkPayroll}
                    disabled={selectedEmployees?.length === 0 || processing}
                    className="w-full px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    Calcular N√≥mina Semanal
                  </button>

                  <button
                    onClick={() => calculateBulkPayroll()}
                    disabled={processing}
                    className="w-full px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 disabled:opacity-50"
                  >
                    Generar Aguinaldos
                  </button>

                  <button
                    onClick={() => calculateBulkPayroll()}
                    disabled={processing}
                    className="w-full px-4 py-2 bg-orange-600 text-white rounded-lg hover:bg-orange-700 disabled:opacity-50"
                  >
                    Calcular Finiquitos
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

