// src/modules/attendance-history-and-analytics-dashboard/index.jsx
import React, { useState, useEffect, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import RoleBasedSidebar from '../../components/ui/RoleBasedSidebar';
import NavigationBreadcrumb from '../../components/ui/NavigationBreadcrumb';
import UserContextHeader from '../../components/ui/UserContextHeader';
import NotificationCenter from '../../components/ui/NotificationCenter';
import Icon from '../../components/AppIcon';
import Button from '../../components/ui/Button';

// Page components
import KPICard from './components/KPICard';
import FilterPanel from './components/FilterPanel';
import AttendanceGrid from './components/AttendanceGrid';
import AttendanceChart from './components/AttendanceChart';
import ExportPanel from './components/ExportPanel';

// Service (con listAttendancePaginated)
import attendanceService from '@/services/attendanceService';

// ---------- Helpers ----------
const sortKeyMap = {
  // mapeo de claves del grid -> service
  employee: 'employee',
  date: 'date',
  clockIn: 'clockIn',
  clockOut: 'clockOut',
  totalHours: 'totalHours',
  overtime: 'overtime',
  status: 'status',
};

// Mapea filtros de UI -> par√°metros del servicio
const mapUiFiltersToService = (f) => {
  const norm = (s) => (s && String(s).trim().length ? s : null);
  return {
    startDate: norm(f?.dateFrom) || null,
    endDate: norm(f?.dateTo) || null,
    siteId: f?.site && f?.site !== 'all' ? f?.site : null,
    // Si en tu UI tienes un selector de empleado por id, √∫salo aqu√≠.
    employeeId: f?.employeeId || null,
    status: f?.status && f?.status !== 'all' ? f?.status : null,
    search: norm(f?.employee) || null, // b√∫squeda por nombre de empleado (string libre)
  };
};

// ---------- Dashboard ----------
const AttendanceHistoryAndAnalyticsDashboard = () => {
  const navigate = useNavigate();

  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);
  const [filterPanelCollapsed, setFilterPanelCollapsed] = useState(false);

  // Chart selector (el componente usa datos mock internos, as√≠ que esto es visual)
  const [chartType, setChartType] = useState('bar');

  // Grid selection + sort
  const [selectedRecords, setSelectedRecords] = useState([]);
  const [sortConfig, setSortConfig] = useState({ key: 'date', direction: 'desc' });

  // Export
  const [isExporting, setIsExporting] = useState(false);

  // Usuario actual (mock)
  const currentUser = {
    name: 'Ana Rodr√≠guez',
    role: 'Supervisor',
    site: 'Obra Central',
    avatar: null,
  };

  // Filtros UI
  const [filters, setFilters] = useState({
    dateFrom: '2025-01-01',
    dateTo: '2025-01-04',
    site: 'all',
    supervisor: 'all',
    status: 'all',
    employee: '',
    includeIncidents: false,
    includeOvertime: true,
    savedView: '',
  });

  // Datos del grid (desde servicio)
  const [records, setRecords] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(1);
  const pageSize = 20;
  const [totalPages, setTotalPages] = useState(1);
  const [totalCount, setTotalCount] = useState(0);

  // KPI data (mock visual)
  const kpiData = [
    {
      title: 'Asistencia General',
      value: '94.2',
      unit: '%',
      trend: 'up',
      trendValue: '+2.1%',
      icon: 'Users',
      color: 'success',
      description: 'vs. mes anterior',
    },
    {
      title: 'Tardanzas',
      value: '6.8',
      unit: '%',
      trend: 'down',
      trendValue: '-1.2%',
      icon: 'Clock',
      color: 'warning',
      description: 'Reducci√≥n significativa',
    },
    {
      title: 'Horas Extra',
      value: '142.5',
      unit: 'hrs',
      trend: 'up',
      trendValue: '+8.3%',
      icon: 'Zap',
      color: 'primary',
      description: 'Esta semana',
    },
    {
      title: 'D√≠as Incompletos',
      value: '12',
      unit: '',
      trend: 'down',
      trendValue: '-3',
      icon: 'AlertTriangle',
      color: 'error',
      description: 'Pendientes de validaci√≥n',
    },
  ];

  // --------- Handlers de filtros / orden ----------
  const handleFiltersChange = (newFilters) => {
    setFilters(newFilters);
  };

  const handleApplyFilters = (appliedFilters) => {
    setFilters(appliedFilters);
    setPage(1);
  };

  const handleResetFilters = () => {
    const resetFilters = {
      dateFrom: '',
      dateTo: '',
      site: 'all',
      supervisor: 'all',
      status: 'all',
      employee: '',
      includeIncidents: false,
      includeOvertime: true,
      savedView: '',
    };
    setFilters(resetFilters);
    setPage(1);
  };

  const handleSort = (key) => {
    setSortConfig((prev) => ({
      key,
      direction: prev?.key === key && prev?.direction === 'asc' ? 'desc' : 'asc',
    }));
    setPage(1);
  };

  // --------- Bulk / Edici√≥n / Export ----------
  const handleBulkAction = (action) => {
    // Aqu√≠ puedes enviar a un endpoint o abrir modal de confirmaci√≥n
    console.log(`Bulk action: ${action} on records:`, selectedRecords);
  };

  const handleRecordEdit = (record) => {
    console.log('Editing record:', record);
    // Aqu√≠ puedes navegar a un formulario o abrir modal
  };

  const handleExport = async (exportConfig) => {
    setIsExporting(true);
    console.log('Exporting with config:', exportConfig);
    setTimeout(() => {
      setIsExporting(false);
      // Notificaci√≥n de √©xito
    }, 2000);
  };

  // --------- Sesi√≥n / sitio ----------
  const handleLogout = () => {
    navigate('/employee-login-portal');
  };

  const handleSiteChange = (site) => {
    console.log('Site changed to:', site);
  };

  // --------- Carga de datos ----------
  const serviceParams = useMemo(() => {
    const base = mapUiFiltersToService(filters);
    return {
      ...base,
      page,
      pageSize,
      sortKey: sortKeyMap[sortConfig?.key] || 'date',
      sortDir: sortConfig?.direction || 'desc',
    };
  }, [filters, page, pageSize, sortConfig]);

  useEffect(() => {
    let cancelled = false;
    (async () => {
      setLoading(true);
      const { ok, data, error } = await attendanceService.listAttendancePaginated(serviceParams);
      if (cancelled) return;
      setLoading(false);

      if (!ok) {
        console.error('Error loading attendance:', error);
        setRecords([]);
        setTotalCount(0);
        setTotalPages(1);
        return;
      }

      setRecords(data?.data || []);
      setTotalCount(data?.totalCount || 0);
      setTotalPages(data?.totalPages || 1);
    })();
    return () => {
      cancelled = true;
    };
  }, [serviceParams]);

  // --------- Atajos de teclado ----------
  useEffect(() => {
    const handleKeyPress = (e) => {
      if (e?.ctrlKey || e?.metaKey) {
        switch (e?.key) {
          case 'f':
            e?.preventDefault();
            setFilterPanelCollapsed((v) => !v);
            break;
          case 'e':
            e?.preventDefault();
            handleExport({ format: 'excel', dateRange: 'current' });
            break;
          default:
            break;
        }
      }
    };
    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [filterPanelCollapsed]);

  return (
    <div className="min-h-screen bg-background">
      {/* Sidebar */}
      <RoleBasedSidebar
        isCollapsed={sidebarCollapsed}
        userRole={currentUser?.role?.toLowerCase()}
      />

      {/* Main Content */}
      <div className={`transition-all duration-300 ${sidebarCollapsed ? 'ml-16' : 'ml-60'} pb-16 md:pb-0`}>
        {/* Header */}
        <header className="bg-card border-b border-border px-6 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <Button
                variant="ghost"
                size="icon"
                onClick={() => setSidebarCollapsed(!sidebarCollapsed)}
                className="hidden md:flex"
              >
                <Icon name={sidebarCollapsed ? 'ChevronRight' : 'ChevronLeft'} size={20} />
              </Button>

              <div>
                <h1 className="text-xl font-semibold text-foreground">Historial y An√°lisis de Asistencia</h1>
                <p className="text-sm text-muted-foreground">
                  An√°lisis detallado y tendencias de asistencia laboral
                </p>
              </div>
            </div>

            <div className="flex items-center space-x-4">
              <NotificationCenter />
              <UserContextHeader
                user={currentUser}
                onLogout={handleLogout}
                onSiteChange={handleSiteChange}
              />
            </div>
          </div>
        </header>

        {/* Content */}
        <main className="p-6">
          <NavigationBreadcrumb />

          {/* KPI Cards */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
            {kpiData?.map((kpi, index) => (
              <KPICard
                key={index}
                title={kpi?.title}
                value={kpi?.value}
                unit={kpi?.unit}
                trend={kpi?.trend}
                trendValue={kpi?.trendValue}
                icon={kpi?.icon}
                color={kpi?.color}
                description={kpi?.description}
              />
            ))}
          </div>

          {/* Main Dashboard Layout */}
          <div className="flex gap-6">
            {/* Filter Panel */}
            <FilterPanel
              isCollapsed={filterPanelCollapsed}
              onToggleCollapse={() => setFilterPanelCollapsed(!filterPanelCollapsed)}
              filters={filters}
              onFiltersChange={handleFiltersChange}
              onApplyFilters={handleApplyFilters}
              onResetFilters={handleResetFilters}
            />

            {/* Analytics Area */}
            <div className="flex-1 space-y-6">
              {/* Charts Section (visual; usa datos internos del componente) */}
              <AttendanceChart
                data={records} // el componente usa mocks internos; mantenemos la prop para futura integraci√≥n
                chartType={chartType}
                onChartTypeChange={setChartType}
              />

              {/* Data Grid */}
              <AttendanceGrid
                data={records}
                onSort={handleSort}
                sortConfig={sortConfig}
                onBulkAction={handleBulkAction}
                selectedRecords={selectedRecords}
                onRecordSelect={setSelectedRecords}
                onRecordEdit={handleRecordEdit}
              />

              {/* Paginaci√≥n (nivel dashboard, porque el grid actual muestra una barra est√°tica) */}
              <div className="flex items-center justify-between p-4 border border-border rounded-lg bg-card">
                <div className="text-sm text-muted-foreground">
                  Mostrando {records?.length} de {totalCount} registros
                </div>
                <div className="flex items-center space-x-2">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => setPage((p) => Math.max(1, p - 1))}
                    disabled={page === 1 || loading}
                  >
                    <Icon name="ChevronLeft" size={16} />
                  </Button>
                  <span className="px-3 py-1 text-sm bg-primary text-primary-foreground rounded">
                    {page}
                  </span>
                  <span className="px-3 py-1 text-sm text-muted-foreground">
                    / {totalPages}
                  </span>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => setPage((p) => (p < totalPages ? p + 1 : p))}
                    disabled={page >= totalPages || loading}
                  >
                    <Icon name="ChevronRight" size={16} />
                  </Button>
                </div>
              </div>
            </div>

            {/* Export Panel - Desktop Only */}
            <div className="hidden xl:block w-80">
              <ExportPanel
                onExport={handleExport}
                isExporting={isExporting}
              />
            </div>
          </div>

          {/* Mobile Export Button */}
          <div className="xl:hidden mt-6">
            <Button
              variant="default"
              fullWidth
              iconName="Download"
              iconPosition="left"
              onClick={() => handleExport({ format: 'excel', dateRange: 'current' })}
              loading={isExporting}
            >
              Exportar Datos
            </Button>
          </div>

          {/* Keyboard Shortcuts Help */}
          <div className="hidden md:block fixed bottom-4 right-4">
            <div className="bg-card border border-border rounded-lg p-3 shadow-lg">
              <div className="text-xs text-muted-foreground space-y-1">
                <div><kbd className="px-1 py-0.5 bg-muted rounded text-xs">Ctrl+F</kbd> Filtros</div>
                <div><kbd className="px-1 py-0.5 bg-muted rounded text-xs">Ctrl+E</kbd> Exportar</div>
              </div>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
};

export default AttendanceHistoryAndAnalyticsDashboard;

src/pages/comprehensive-employee-registr.content:
import React, { useEffect, useMemo, useState } from 'react';
import { Users, UserPlus, Search, Download, RefreshCw, Building2, UserCheck, AlertTriangle } from 'lucide-react';

import { useAuth } from '@/contexts/AuthContext';
import { useBranding } from '@/components/BrandingProvider';

import BrandedHeader from '@/components/ui/BrandedHeader';
import BrandedFooter from '@/components/ui/BrandedFooter';
import Button from '@/components/ui/Button';
import Input from '@/components/ui/Input';
import Select from '@/components/ui/Select';
import NavigationBreadcrumb from '@/components/ui/NavigationBreadcrumb';
import UserContextHeader from '@/components/ui/UserContextHeader';
import NotificationCenter from '@/components/ui/NotificationCenter';
import CurrencyDisplay from '@/components/ui/CurrencyDisplay';
import Loading from '../../components/ui/Loading';
import ErrorState from '../../components/ui/ErrorState';
import { showToast } from '../../components/ui/ToastHub';

import { EmployeeRegistrationWizard } from './components/EmployeeRegistrationWizard';
import { EmployeeProfileEditor } from './components/EmployeeProfileEditor';
import { EmployeeListGrid } from './components/EmployeeListGrid';

import { supabase } from '@/lib/supabase';
import enhancedEmployeeService from '@/services/enhancedEmployeeService';
import { useQuery } from '../../hooks/useQuery';

export default function ComprehensiveEmployeeRegistrationAndProfileManagement() {
  const { isAdmin, isSuperAdmin } = useAuth();
  const { branding, formatCurrency } = useBranding();

  // UI State
  const [showRegistrationWizard, setShowRegistrationWizard] = useState(false);
  const [showProfileEditor, setShowProfileEditor] = useState(false);
  const [selectedEmployee, setSelectedEmployee] = useState(null);

  // Filtros y b√∫squeda
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({
    site: 'all',
    position: 'all',
    status: 'active',
    role: 'all',
    supervisor: 'all'
  });

  const breadcrumbItems = useMemo(() => ([
    { label: 'Gesti√≥n', href: '/admin' },
    { label: 'Empleados', href: '/comprehensive-employee-registration-and-profile-management' }
  ]), []);

  // ------------ QUERIES ------------
  // Empleados
  const {
    data: employeesData,
    isLoading: employeesLoading,
    error: employeesError,
    refetch: refetchEmployees
  } = useQuery(
    enhancedEmployeeService.getEmployees.bind(enhancedEmployeeService),
    {
      params: {
        search: searchTerm || undefined,
        site: filters.site,
        position: filters.position,
        status: filters.status === 'all' ? [] : [filters.status],
        supervisor: filters.supervisor
      },
      deps: [searchTerm, JSON.stringify(filters)],
      retry: 1,
      keepPreviousData: true,
      onError: (e) => showToast({ title: 'Error al cargar empleados', message: e.message, type: 'error' })
    }
  );

  // Sitios
  const {
    data: sitesData,
    isLoading: sitesLoading,
    error: sitesError,
    refetch: refetchSites
  } = useQuery(
    enhancedEmployeeService.getSites.bind(enhancedEmployeeService),
    {
      deps: [],
      retry: 1,
      keepPreviousData: true,
      onError: (e) => showToast({ title: 'Error al cargar sitios', message: e.message, type: 'error' })
    }
  );

  // Supervisores
  const {
    data: supervisorsData,
    isLoading: supervisorsLoading,
    error: supervisorsError,
    refetch: refetchSupervisors
  } = useQuery(
    enhancedEmployeeService.getSupervisors.bind(enhancedEmployeeService),
    {
      deps: [],
      retry: 1,
      keepPreviousData: true,
      onError: (e) => showToast({ title: 'Error al cargar supervisores', message: e.message, type: 'error' })
    }
  );

  // Estad√≠sticas
  const {
    data: statsData,
    isLoading: statsLoading,
    error: statsError,
    refetch: refetchStats
  } = useQuery(
    enhancedEmployeeService.getEmployeeStats.bind(enhancedEmployeeService),
    {
      deps: [],
      retry: 1,
      keepPreviousData: true,
      onError: (e) => showToast({ title: 'Error al calcular estad√≠sticas', message: e.message, type: 'error' })
    }
  );

  const loading = employeesLoading || sitesLoading || supervisorsLoading || statsLoading;
  const anyError = employeesError || sitesError || supervisorsError || statsError;

  // Derivados
  const employees = employeesData ?? [];
  const constructionSites = sitesData ?? [];
  const supervisors = supervisorsData ?? [];

  const statistics = useMemo(() => ({
    totalEmployees: statsData?.total ?? 0,
    activeEmployees: statsData?.active ?? 0,
    totalSites: constructionSites?.length ?? 0,
    averageSalary: (() => {
      if (!employees?.length) return 0;
      let total = 0;
      let count = 0;
      employees.forEach(emp => {
        if (emp?.salaryType === 'hourly' && emp?.hourlyRate > 0) {
          total += Number(emp?.hourlyRate);
          count++;
        } else if (emp?.salaryType === 'daily' && emp?.dailySalary > 0) {
          total += Number(emp?.dailySalary) / 8;
          count++;
        }
      });
      return count ? total / count : 0;
    })()
  }), [statsData, constructionSites, employees]);

  // Re-fetch agrupado
  const refreshAll = async () => {
    await Promise.all([refetchEmployees(), refetchSites(), refetchSupervisors(), refetchStats()]);
  };

  // ------------ ACCIONES ------------
  const handleEmployeeRegistration = async (employeeData) => {
    try {
      // 1) Crear usuario (opcional)
      let userId = null;
      if (employeeData?.email) {
        const { data: authData, error: authError } = await supabase?.auth?.signUp({
          email: employeeData?.email,
          password: employeeData?.tempPassword || 'AsistenciaPro2024',
          options: { data: { full_name: employeeData?.fullName, role: employeeData?.role || 'user' } }
        });
        if (authError && !authError?.message?.includes('already registered')) throw authError;
        userId = authData?.user?.id ?? null;
      }

      // 2) Crear perfil
      const payload = {
        userId,
        name: employeeData?.fullName,
        employeeId: employeeData?.employeeId || `EMP-${Date.now()}`,
        phone: employeeData?.phone,
        address: employeeData?.address,
        birthDate: employeeData?.birthDate,
        hireDate: employeeData?.hireDate || new Date().toISOString().split('T')[0],
        position: employeeData?.position || 'alba√±il',
        salaryType: employeeData?.salaryType || 'daily',
        hourlyRate: employeeData?.salaryType === 'hourly' ? Number(employeeData?.hourlyRate || 0) : 0,
        dailySalary: employeeData?.salaryType === 'daily' ? Number(employeeData?.dailySalary || 0) : 0,
        siteId: employeeData?.siteId || null,
        supervisorId: employeeData?.supervisorId || null,
        emergencyContact: employeeData?.emergencyContact,
        idNumber: employeeData?.idNumber,
        avatar: employeeData?.profilePicture || null
      };

      await enhancedEmployeeService.createEmployee(payload);

      showToast({ title: 'Listo', message: 'Empleado registrado exitosamente', type: 'success' });
      setShowRegistrationWizard(false);
      await Promise.all([refetchEmployees(), refetchStats()]);
      return { success: true };
    } catch (error) {
      showToast({ title: 'Error al registrar', message: error?.message, type: 'error' });
      return { success: false, error: error?.message };
    }
  };

  const handleEmployeeUpdate = async (employeeId, updateData) => {
    try {
      const payload = {
        name: updateData?.fullName,
        phone: updateData?.phone,
        address: updateData?.address,
        birthDate: updateData?.birthDate,
        position: updateData?.position,
        salaryType: updateData?.salaryType,
        hourlyRate: updateData?.salaryType === 'hourly' ? Number(updateData?.hourlyRate || 0) : 0,
        dailySalary: updateData?.salaryType === 'daily' ? Number(updateData?.dailySalary || 0) : 0,
        siteId: updateData?.siteId,
        supervisorId: updateData?.supervisorId,
        emergencyContact: updateData?.emergencyContact,
        idNumber: updateData?.idNumber,
        avatar: updateData?.profilePicture
      };

      await enhancedEmployeeService.updateEmployee(employeeId, payload);

      showToast({ title: 'Actualizado', message: 'Perfil del empleado actualizado', type: 'success' });
      setShowProfileEditor(false);
      setSelectedEmployee(null);
      await refetchEmployees();
      return { success: true };
    } catch (error) {
      showToast({ title: 'Error al actualizar', message: error?.message, type: 'error' });
      return { success: false, error: error?.message };
    }
  };

  const handleEmployeeAction = async (employeeId, action) => {
    try {
      let status = null;
      if (action === 'activate') status = 'active';
      if (action === 'suspend') status = 'suspended';
      if (action === 'deactivate') status = 'inactive';
      if (!status) return;

      await enhancedEmployeeService.updateEmployee(employeeId, { status });
      showToast({ title: 'Hecho', message: 'Estado actualizado', type: 'success' });
      await Promise.all([refetchEmployees(), refetchStats()]);
    } catch (error) {
      showToast({ title: 'Error', message: error?.message, type: 'error' });
    }
  };

  const exportEmployeeData = async () => {
    try {
      if (!employees?.length) return;
      const csvRows = employees.map(emp => ({
        'ID Empleado': emp?.employeeId || '',
        'Nombre Completo': emp?.name || '',
        'Email': emp?.email || 'N/A',
        'Tel√©fono': emp?.phone || 'N/A',
        'Puesto': emp?.position || '',
        'Sitio': emp?.site || 'Sin asignar',
        'Supervisor': emp?.supervisor || 'Sin asignar',
        'Salario por Hora': formatCurrency(emp?.hourlyRate || 0),
        'Salario Diario': formatCurrency(emp?.dailySalary || 0),
        'Estado': emp?.status || '',
        'Fecha de Contrataci√≥n': emp?.hireDate || ''
      }));

      const header = Object.keys(csvRows[0] || {}).join(',');
      const body = csvRows
        .map(r =>
          Object.values(r)
            .map(v => `"${String(v).replace(/"/g, '""')}"`)
            .join(',')
        )
        .join('\n');
      const csvContent = `${header}\n${body}`;

      const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
      const url = URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `empleados_${new Date().toISOString().split('T')[0]}.csv`;
      link.click();
      URL.revokeObjectURL(url);

      showToast({ title: 'Listo', message: 'Datos exportados', type: 'success' });
    } catch (_) {
      showToast({ title: 'Error', message: 'No se pudo exportar', type: 'error' });
    }
  };

  // ------------ GUARD DE ACCESO ------------
  if (!isAdmin() && !isSuperAdmin()) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <AlertTriangle className="mx-auto h-12 w-12 text-red-500 mb-4" />
          <h1 className="text-2xl font-bold text-gray-900 mb-2">Acceso Denegado</h1>
          <p className="text-gray-600">No tienes permisos para acceder a esta secci√≥n.</p>
        </div>
      </div>
    );
  }

  // ------------ LOADING / ERROR ------------
  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <Loading label="Cargando empleados y cat√°logos‚Ä¶" />
      </div>
    );
  }

  if (anyError) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <ErrorState message="No se pudieron cargar los datos." onRetry={refreshAll} />
      </div>
    );
  }

  // ------------ RENDER ------------
  return (
    <div className="min-h-screen bg-gray-50">
      <BrandedHeader />
      <UserContextHeader />
      <NavigationBreadcrumb items={breadcrumbItems} />

      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Page Header */}
        <div className="mb-8">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold text-gray-900 flex items-center gap-3">
                <Users className="h-8 w-8" style={{ color: branding?.color_primario }} />
                Gesti√≥n Integral de Empleados
              </h1>
              <p className="text-gray-600 mt-2">
                Registro, edici√≥n y administraci√≥n completa de perfiles de empleados
              </p>
            </div>
            <div className="flex gap-3">
              <Button
                onClick={() => setShowRegistrationWizard(true)}
                className="flex items-center gap-2"
                style={{ backgroundColor: branding?.color_primario }}
              >
                <UserPlus className="h-4 w-4" />
                Registrar Empleado
              </Button>

              <Button
                variant="outline"
                onClick={exportEmployeeData}
                className="flex items-center gap-2"
                disabled={employees?.length === 0}
              >
                <Download className="h-4 w-4" />
                Exportar
              </Button>

              <Button
                variant="outline"
                onClick={refreshAll}
                className="flex items-center gap-2"
              >
                <RefreshCw className="h-4 w-4" />
                Actualizar
              </Button>
            </div>
          </div>
        </div>

        {/* Statistics Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Total Empleados</p>
                <p className="text-2xl font-bold text-gray-900">{statistics?.totalEmployees}</p>
              </div>
              <div className="bg-blue-100 p-3 rounded-full">
                <Users className="h-6 w-6 text-blue-600" />
              </div>
            </div>
            <p className="text-xs text-gray-500 mt-2">Empleados registrados en el sistema</p>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Empleados Activos</p>
                <p className="text-2xl font-bold text-gray-900">{statistics?.activeEmployees}</p>
              </div>
              <div className="bg-green-100 p-3 rounded-full">
                <UserCheck className="h-6 w-6 text-green-600" />
              </div>
            </div>
            <p className="text-xs text-gray-500 mt-2">Con estado activo</p>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Sitios de Construcci√≥n</p>
                <p className="text-2xl font-bold text-gray-900">{statistics?.totalSites}</p>
              </div>
              <div className="bg-orange-100 p-3 rounded-full">
                <Building2 className="h-6 w-6 text-orange-600" />
              </div>
            </div>
            <p className="text-xs text-gray-500 mt-2">Obras activas</p>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Salario Promedio</p>
                <p className="text-2xl font-bold text-gray-900">
                  <CurrencyDisplay amount={statistics?.averageSalary} />
                </p>
              </div>
              <div className="bg-purple-100 p-3 rounded-full">
                <div className="h-6 w-6 text-purple-600 flex items-center justify-center text-sm font-bold">
                  {branding?.simbolo_moneda}
                </div>
              </div>
            </div>
            <p className="text-xs text-gray-500 mt-2">Por hora promedio</p>
          </div>
        </div>

        {/* Search and Filter Bar */}
        <div className="bg-white rounded-lg shadow mb-8">
          <div className="p-6">
            <div className="flex flex-col lg:flex-row gap-4">
              <div className="flex-1">
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                  <Input
                    type="text"
                    placeholder="Buscar por nombre, ID, correo o tel√©fono..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e?.target?.value)}
                    className="pl-10"
                  />
                </div>
              </div>

              <div className="flex gap-3 flex-wrap">
                <Select
                  value={filters?.site}
                  onChange={(e) => setFilters((f) => ({ ...f, site: e?.target?.value }))}
                  options={[
                    { value: 'all', label: 'Todos los sitios' },
                    ...constructionSites?.map(site => ({ value: site?.id, label: site?.name }))
                  ]}
                  className="w-48"
                />

                <Select
                  value={filters?.position}
                  onChange={(e) => setFilters((f) => ({ ...f, position: e?.target?.value }))}
                  options={[
                    { value: 'all', label: 'Todos los puestos' },
                    { value: 'alba√±il', label: 'Alba√±il' },
                    { value: 'ayudante', label: 'Ayudante' },
                    { value: 'supervisor', label: 'Supervisor' },
                    { value: 'administrativo', label: 'Administrativo' },
                    { value: 'electricista', label: 'Electricista' },
                    { value: 'plomero', label: 'Plomero' },
                    { value: 'pintor', label: 'Pintor' },
                    { value: 'carpintero', label: 'Carpintero' },
                    { value: 'soldador', label: 'Soldador' },
                    { value: 'operador_maquinaria', label: 'Operador de Maquinaria' }
                  ]}
                  className="w-48"
                />

                <Select
                  value={filters?.status}
                  onChange={(e) => setFilters((f) => ({ ...f, status: e?.target?.value }))}
                  options={[
                    { value: 'all', label: 'Todos los estados' },
                    { value: 'active', label: 'Activo' },
                    { value: 'inactive', label: 'Inactivo' },
                    { value: 'suspended', label: 'Suspendido' }
                  ]}
                  className="w-48"
                />
              </div>
            </div>
          </div>
        </div>

        {/* Employee List Grid */}
        <EmployeeListGrid
          employees={employees}
          constructionSites={constructionSites}
          supervisors={supervisors}
          onEditEmployee={(employee) => {
            setSelectedEmployee(employee);
            setShowProfileEditor(true);
          }}
          onEmployeeAction={handleEmployeeAction}
          loading={loading}
          formatCurrency={formatCurrency}
        />
      </main>

      {/* Modales */}
      {showRegistrationWizard && (
        <EmployeeRegistrationWizard
          constructionSites={constructionSites}
          supervisors={supervisors}
          onSubmit={handleEmployeeRegistration}
          onClose={() => setShowRegistrationWizard(false)}
          branding={branding}
        />
      )}

      {showProfileEditor && selectedEmployee && (
        <EmployeeProfileEditor
          employee={selectedEmployee}
          constructionSites={constructionSites}
          supervisors={supervisors}
          onSubmit={(updateData) => handleEmployeeUpdate(selectedEmployee?.id, updateData)}
          onClose={() => {
            setShowProfileEditor(false);
            setSelectedEmployee(null);
          }}
          branding={branding}
        />
      )}

      {/* Notificaciones globales */}
      <NotificationCenter />

      <BrandedFooter />
    </div>
  );
}

src/pages/comprehensive-reporting-and-ex.content:
// src/pages/comprehensive-reporting-and-export-center/index.jsx
import React, { useMemo, useState } from 'react';
import { useNavigate } from 'react-router-dom';

import RoleBasedSidebar from '@/components/ui/RoleBasedSidebar';
import NavigationBreadcrumb from '@/components/ui/NavigationBreadcrumb';
import UserContextHeader from '@/components/ui/UserContextHeader';
import NotificationCenter from '@/components/ui/NotificationCenter';
import Icon from '@/components/AppIcon';
import Button from '@/components/ui/Button';
import Loading from '@/components/ui/Loading';
import ErrorState from '@/components/ui/ErrorState';
import { showToast } from '@/components/ui/ToastHub';

import ReportTemplateLibrary from './components/ReportTemplateLibrary';
import ReportBuilder from './components/ReportBuilder';
import ReportPreview from './components/ReportPreview';
import ScheduledReports from './components/ScheduledReports';
import ReportHistory from './components/ReportHistory';

import { useAuth } from '@/contexts/AuthContext';
import { useQuery } from '@/hooks/useQuery';
import reportingService from '../../services/reportingService';

const TABS = [
  { id: 'builder', name: 'Constructor', icon: 'Settings', description: 'Crear y personalizar reportes' },
  { id: 'templates', name: 'Plantillas', icon: 'FileText', description: 'Plantillas predefinidas' },
  { id: 'scheduled', name: 'Programados', icon: 'Calendar', description: 'Reportes autom√°ticos' },
  { id: 'history', name: 'Historial', icon: 'Clock', description: 'Reportes generados' },
];

const ComprehensiveReportingAndExportCenter = () => {
  const navigate = useNavigate();
  const { getCurrentUserContext } = useAuth();
  const currentUser = getCurrentUserContext();

  // UI state
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);
  const [activeTab, setActiveTab] = useState('builder');
  const [selectedTemplate, setSelectedTemplate] = useState(null);
  const [reportConfig, setReportConfig] = useState(null);

  // Query: Stats (para los ‚ÄúQuick Stats‚Äù)
  const {
    data: stats,
    isLoading: statsLoading,
    error: statsError,
    refetch: refetchStats,
  } = useQuery(reportingService.getReportingStats, {
    deps: [], // se carga al entrar
    retry: 1,
    onError: (e) => showToast({ title: 'Error al cargar estad√≠sticas', message: e.message, type: 'error' }),
  });

  // Query: Plantillas
  const {
    data: templates,
    isLoading: templatesLoading,
    error: templatesError,
    refetch: refetchTemplates,
  } = useQuery(reportingService.getTemplates, {
    deps: [],
    retry: 1,
    onError: (e) => showToast({ title: 'Error al cargar plantillas', message: e.message, type: 'error' }),
  });

  // Query: Programaciones
  const {
    data: scheduled,
    isLoading: scheduledLoading,
    error: scheduledError,
    refetch: refetchScheduled,
  } = useQuery(reportingService.getScheduledReports, {
    deps: [],
    retry: 1,
    onError: (e) => showToast({ title: 'Error al cargar programaciones', message: e.message, type: 'error' }),
  });

  // Query: Historial (paginado)
  const [historyPage, setHistoryPage] = useState(0);
  const {
    data: historyData,
    isLoading: historyLoading,
    error: historyError,
    refetch: refetchHistory,
  } = useQuery(reportingService.getReportHistory, {
    params: { page: historyPage, pageSize: 25 },
    deps: [historyPage],
    keepPreviousData: true,
    retry: 1,
    onError: (e) => showToast({ title: 'Error al cargar historial', message: e.message, type: 'error' }),
  });

  const historyRows = historyData?.rows ?? [];
  const historyCount = historyData?.count ?? 0;
  const historyPageSize = historyData?.pageSize ?? 25;
  const historyTotalPages = useMemo(
    () => Math.max(1, Math.ceil(historyCount / historyPageSize)),
    [historyCount, historyPageSize]
  );

  const anyError = statsError || templatesError || scheduledError || historyError;
  const loading =
    statsLoading || templatesLoading || scheduledLoading || historyLoading;

  const refreshAll = async () => {
    await Promise.all([refetchStats(), refetchTemplates(), refetchScheduled(), refetchHistory()]);
    showToast({ title: 'Actualizado', message: 'Datos de reportes actualizados', type: 'success' });
  };

  const handleTemplateSelect = (template) => {
    setSelectedTemplate(template);
    setActiveTab('builder');
  };

  const handleReportConfigChange = (config) => setReportConfig(config);

  const handleLogout = () => navigate('/employee-login-portal');
  const handleProfileClick = () => navigate('/profile');
  const handleSiteChange = (site) => {
    // Si tu header permite cambiar sitio, podr√≠as re-filtrar plantillas o programaciones por site.id
    // Por ahora, s√≥lo mostramos un toast.
    showToast({ title: 'Sitio cambiado', message: site?.name || '‚Äî', type: 'info' });
  };

  // Quick stats derivados
  const quickStats = {
    templates: stats?.templates ?? 0,
    scheduled: stats?.scheduled ?? 0,
    generatedThisMonth: stats?.generatedThisMonth ?? 0,
    successRate: stats?.successRate ?? 0, // porcentaje ya calculado en el service
  };

  // Loading / Error global (UI simple)
  if (loading && !historyRows.length && !templates?.length) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <Loading label="Cargando centro de reportes‚Ä¶" />
      </div>
    );
  }

  if (anyError && !historyRows.length && !templates?.length) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <ErrorState message="No se pudieron cargar los datos del centro de reportes." onRetry={refreshAll} />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      {/* Sidebar */}
      <RoleBasedSidebar
        isCollapsed={sidebarCollapsed}
        userRole={currentUser?.role?.toLowerCase()}
      />

      {/* Main Content */}
      <div className={`transition-all duration-300 ease-out-cubic ${sidebarCollapsed ? 'ml-16' : 'ml-60'} pb-16 md:pb-0`}>
        {/* Header */}
        <header className="bg-card border-b border-border px-6 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <Button
                variant="ghost"
                size="sm"
                iconName={sidebarCollapsed ? 'ChevronRight' : 'ChevronLeft'}
                onClick={() => setSidebarCollapsed(!sidebarCollapsed)}
                className="hidden md:flex"
              />
              <div>
                <h1 className="text-xl font-semibold text-foreground">Centro de Reportes</h1>
                <p className="text-sm text-muted-foreground">Generaci√≥n y exportaci√≥n de reportes empresariales</p>
              </div>
            </div>

            <div className="flex items-center space-x-4">
              <Button variant="outline" iconName="RefreshCw" onClick={refreshAll}>
                Actualizar
              </Button>
              <NotificationCenter />
              <UserContextHeader
                user={currentUser}
                onLogout={handleLogout}
                onProfileClick={handleProfileClick}
                onSiteChange={handleSiteChange}
              />
            </div>
          </div>
        </header>

        {/* Content */}
        <main className="p-6">
          <NavigationBreadcrumb />

          {/* Quick Stats */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
            <div className="bg-card rounded-lg border border-border p-6">
              <div className="flex items-center space-x-3">
                <div className="p-2 bg-primary/10 rounded-lg">
                  <Icon name="FileText" size={24} className="text-primary" />
                </div>
                <div>
                  <p className="text-2xl font-bold text-foreground">{quickStats.templates}</p>
                  <p className="text-sm text-muted-foreground">Plantillas Disponibles</p>
                </div>
              </div>
            </div>

            <div className="bg-card rounded-lg border border-border p-6">
              <div className="flex items-center space-x-3">
                <div className="p-2 bg-success/10 rounded-lg">
                  <Icon name="Calendar" size={24} className="text-success" />
                </div>
                <div>
                  <p className="text-2xl font-bold text-foreground">{quickStats.scheduled}</p>
                  <p className="text-sm text-muted-foreground">Reportes Programados</p>
                </div>
              </div>
            </div>

            <div className="bg-card rounded-lg border border-border p-6">
              <div className="flex items-center space-x-3">
                <div className="p-2 bg-warning/10 rounded-lg">
                  <Icon name="Download" size={24} className="text-warning" />
                </div>
                <div>
                  <p className="text-2xl font-bold text-foreground">{quickStats.generatedThisMonth}</p>
                  <p className="text-sm text-muted-foreground">Reportes Este Mes</p>
                </div>
              </div>
            </div>

            <div className="bg-card rounded-lg border border-border p-6">
              <div className="flex items-center space-x-3">
                <div className="p-2 bg-accent/10 rounded-lg">
                  <Icon name="TrendingUp" size={24} className="text-accent" />
                </div>
                <div>
                  <p className="text-2xl font-bold text-foreground">
                    {Number(quickStats.successRate).toFixed(1)}%
                  </p>
                  <p className="text-sm text-muted-foreground">Tasa de √âxito</p>
                </div>
              </div>
            </div>
          </div>

          {/* Navigation Tabs */}
          <div className="bg-card rounded-lg border border-border mb-6">
            <div className="border-b border-border">
              <nav className="flex space-x-8 px-6">
                {TABS.map((tab) => (
                  <button
                    key={tab.id}
                    onClick={() => setActiveTab(tab.id)}
                    className={`flex items-center space-x-2 py-4 px-1 border-b-2 font-medium text-sm transition-colors duration-150 ease-out-cubic ${
                      activeTab === tab.id
                        ? 'border-primary text-primary'
                        : 'border-transparent text-muted-foreground hover:text-foreground hover:border-muted-foreground'
                    }`}
                  >
                    <Icon name={tab.icon} size={16} />
                    <span>{tab.name}</span>
                  </button>
                ))}
              </nav>
            </div>

            <div className="p-6">
              <div className="mb-2">
                <h2 className="text-lg font-semibold text-foreground">
                  {TABS.find((t) => t.id === activeTab)?.name}
                </h2>
                <p className="text-sm text-muted-foreground">
                  {TABS.find((t) => t.id === activeTab)?.description}
                </p>
              </div>
            </div>
          </div>

          {/* Tab Content */}
          <div className="space-y-6">
            {activeTab === 'builder' && (
              <div className="grid grid-cols-1 lg:grid-cols-5 gap-6">
                <div className="lg:col-span-2">
                  <ReportBuilder
                    selectedTemplate={selectedTemplate}
                    onReportChange={setReportConfig}
                    onGenerate={async (config) => {
                      try {
                        const res = await reportingService.previewReport(config);
                        showToast({ title: 'Vista previa', message: 'Reporte generado en vista previa', type: 'success' });
                        setReportConfig({ ...(config || {}), previewUrl: res?.previewUrl });
                      } catch (e) {
                        showToast({ title: 'Error', message: e.message, type: 'error' });
                      }
                    }}
                  />
                </div>
                <div className="lg:col-span-3">
                  <ReportPreview reportConfig={reportConfig} />
                </div>
              </div>
            )}

            {activeTab === 'templates' && (
              <ReportTemplateLibrary
                templates={templates || []}
                loading={templatesLoading}
                onSelectTemplate={handleTemplateSelect}
                selectedTemplate={selectedTemplate}
                onRefresh={refetchTemplates}
              />
            )}

            {activeTab === 'scheduled' && (
              <ScheduledReports
                items={scheduled || []}
                loading={scheduledLoading}
                onRefresh={refetchScheduled}
                onCreate={async (payload) => {
                  try {
                    await reportingService.createScheduledReport(payload);
                    showToast({ title: 'Programado', message: 'Reporte programado con √©xito', type: 'success' });
                    await refetchScheduled();
                    await refetchStats();
                  } catch (e) {
                    showToast({ title: 'Error', message: e.message, type: 'error' });
                  }
                }}
                onToggleActive={async (id, nextActive) => {
                  try {
                    await reportingService.updateScheduledReport(id, { active: nextActive });
                    await refetchScheduled();
                  } catch (e) {
                    showToast({ title: 'Error', message: e.message, type: 'error' });
                  }
                }}
                onDelete={async (id) => {
                  try {
                    await reportingService.deleteScheduledReport(id);
                    await refetchScheduled();
                    await refetchStats();
                  } catch (e) {
                    showToast({ title: 'Error', message: e.message, type: 'error' });
                  }
                }}
              />
            )}

            {activeTab === 'history' && (
              <ReportHistory
                rows={historyRows}
                loading={historyLoading}
                page={historyPage}
                totalPages={historyTotalPages}
                onPrev={() => setHistoryPage((p) => Math.max(0, p - 1))}
                onNext={() => setHistoryPage((p) => (p + 1 < historyTotalPages ? p + 1 : p))}
                onRefresh={refetchHistory}
                onDownload={async (row) => {
                  try {
                    const url = await reportingService.getReportDownloadUrl(row.id);
                    if (url) window.open(url, '_blank', 'noopener,noreferrer');
                  } catch (e) {
                    showToast({ title: 'Error', message: e.message, type: 'error' });
                  }
                }}
              />
            )}
          </div>
        </main>
      </div>
    </div>
  );
};

export default ComprehensiveReportingAndExportCenter;

src/pages/construction-site-and-supervis.content:
// src/pages/construction-site-and-supervisor-management-hub/index.jsx
import React, { useEffect, useMemo, useState } from 'react';
import { useNavigate } from 'react-router-dom';

import RoleBasedSidebar from '@/components/ui/RoleBasedSidebar';
import NavigationBreadcrumb from '@/components/ui/NavigationBreadcrumb';
import UserContextHeader from '@/components/ui/UserContextHeader';
import NotificationCenter from '@/components/ui/NotificationCenter';
import Icon from '@/components/AppIcon';
import Button from '@/components/ui/Button';
import Input from '@/components/ui/Input';
import Select from '@/components/ui/Select';
import Loading from '@/components/ui/Loading';
import ErrorState from '@/components/ui/ErrorState';
import { showToast } from '@/components/ui/ToastHub';

import SiteCard from './components/SiteCard';
import SupervisorCard from './components/SupervisorCard';
import OrganizationalTree from './components/OrganizationalTree';
import BulkAssignmentPanel from './components/BulkAssignmentPanel';
import QuickStatsPanel from './components/QuickStatsPanel';

import { useQuery } from '@/hooks/useQuery';
import { getAllSites } from '@/services/constructionSiteService';
import enhancedEmployeeService from '@/services/enhancedEmployeeService';
import { useAuth } from '@/contexts/AuthContext';

const DEBOUNCE_MS = 350;

const ConstructionSiteAndSupervisorManagementHub = () => {
  const navigate = useNavigate();
  const { getCurrentUserContext } = useAuth();
  const currentUser = getCurrentUserContext();
  const userRole = currentUser?.role?.toLowerCase?.() || 'user';

  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);
  const [activeTab, setActiveTab] = useState('sites'); // 'sites' | 'supervisors'
  const [searchTerm, setSearchTerm] = useState('');
  const [debouncedSearch, setDebouncedSearch] = useState('');
  const [filterStatus, setFilterStatus] = useState('all');
  const [selectedNode, setSelectedNode] = useState(null);
  const [showBulkAssignment, setShowBulkAssignment] = useState(false);

  // ------------------------------
  // Debounce b√∫squeda
  // ------------------------------
  useEffect(() => {
    const t = setTimeout(() => setDebouncedSearch(searchTerm?.trim()), DEBOUNCE_MS);
    return () => clearTimeout(t);
  }, [searchTerm]);

  // ------------------------------
  // SITIOS (paginaci√≥n + b√∫squeda)
  // ------------------------------
  const [page, setPage] = useState(0);

  const {
    data: sitesData,
    isLoading: sitesLoading,
    isFetching: sitesFetching,
    error: sitesError,
    refetch: refetchSites,
  } = useQuery(getAllSites, {
    params: { page, search: debouncedSearch || undefined },
    deps: [page, debouncedSearch],
    keepPreviousData: true,
    retry: 1,
    onError: (e) =>
      showToast({
        title: 'Error al cargar sitios',
        message: e?.message || 'Intenta nuevamente',
        type: 'error',
      }),
  });

  const sitesRows = sitesData?.rows ?? [];
  const sitesCount = sitesData?.count ?? 0;
  const pageSize = sitesData?.pageSize ?? 50;
  const totalPages = Math.max(1, Math.ceil(sitesCount / pageSize));

  // Filtro de estado (cliente): 'active' -> is_active true
  const filteredSites = useMemo(() => {
    const term = (debouncedSearch || '').toLowerCase();
    return (sitesRows || []).filter((site) => {
      const matchesSearch =
        !term ||
        site?.name?.toLowerCase()?.includes(term) ||
        site?.location?.toLowerCase()?.includes(term) ||
        (site?.code ? site.code.toLowerCase().includes(term) : false);

      const matchesStatus =
        filterStatus === 'all'
          ? true
          : filterStatus === 'active'
          ? !!site?.is_active
          : true; // otros estados a√∫n no existen en DB

      return matchesSearch && matchesStatus;
    });
  }, [sitesRows, debouncedSearch, filterStatus]);

  // ------------------------------
  // SUPERVISORES (reales)
  // ------------------------------
  const {
    data: supervisorsData,
    isLoading: supervisorsLoading,
    error: supervisorsError,
    refetch: refetchSupervisors,
  } = useQuery(enhancedEmployeeService.getSupervisors, {
    deps: [],
    retry: 1,
    onError: (e) =>
      showToast({
        title: 'Error al cargar supervisores',
        message: e?.message || 'Intenta nuevamente',
        type: 'error',
      }),
  });

  const supervisors = supervisorsData ?? [];

  const filteredSupervisors = useMemo(() => {
    const term = (debouncedSearch || '').toLowerCase();

    return (supervisors || []).filter((supervisor) => {
      const matchesSearch =
        !term ||
        supervisor?.full_name?.toLowerCase()?.includes(term) ||
        supervisor?.email?.toLowerCase()?.includes(term);

      // Si tu tabla de usuarios/perfiles tiene status, map√©alo aqu√≠.
      // Por ahora, no filtramos por estado (s√≥lo 'all').
      const matchesStatus = filterStatus === 'all' ? true : true;

      return matchesSearch && matchesStatus;
    });
  }, [supervisors, debouncedSearch, filterStatus]);

  // ------------------------------
  // Handlers (sin mocks)
  // ------------------------------
  const handleSiteEdit = (site) => {
    // navigate(`/construction-sites/${site.id}/edit`);
    showToast({ title: 'Acci√≥n', message: `Editar sitio ${site?.name}`, type: 'info' });
  };

  const handleSiteDelete = (site) => {
    showToast({ title: 'Pendiente', message: 'Eliminar sitio (conectar servicio)', type: 'warning' });
  };

  const handleSiteViewDetails = (site) => {
    // navigate(`/construction-sites/${site.id}`);
    showToast({ title: 'Detalle', message: `Sitio: ${site?.name}`, type: 'info' });
  };

  const handleAssignSupervisor = (site) => {
    showToast({ title: 'Asignaci√≥n', message: `Asignar supervisor a ${site?.name}`, type: 'info' });
  };

  const handleSupervisorEdit = (supervisor) => {
    showToast({ title: 'Acci√≥n', message: `Editar supervisor ${supervisor?.full_name}`, type: 'info' });
  };

  const handleSupervisorDelete = (supervisor) => {
    showToast({ title: 'Pendiente', message: 'Eliminar supervisor (conectar servicio)', type: 'warning' });
  };

  const handleSupervisorViewDetails = (supervisor) => {
    showToast({ title: 'Detalle', message: `Supervisor: ${supervisor?.full_name}`, type: 'info' });
  };

  const handleAssignSites = (supervisor) => {
    setShowBulkAssignment(true);
    setSelectedNode({ type: 'supervisor', id: supervisor?.id, label: supervisor?.full_name });
  };

  const handleNodeSelect = (node) => setSelectedNode(node);

  const handleDragDrop = (draggedItem, targetItem) => {
    showToast({ title: 'Reordenar', message: 'Arrastrar y soltar pendiente de servicio', type: 'info' });
  };

  const handleBulkAssign = async (assignmentData) => {
    showToast({ title: 'Asignando‚Ä¶', message: 'Procesando asignaci√≥n masiva', type: 'info' });
    await new Promise((r) => setTimeout(r, 800));
    showToast({ title: 'Listo', message: 'Asignaci√≥n completada', type: 'success' });
  };

  const handleViewReports = (node) => {
    navigate('/comprehensive-reporting-and-export-center');
  };

  const handleExportData = () => {
    showToast({ title: 'Exportar', message: 'Exportaci√≥n pendiente de implementar', type: 'info' });
  };

  const handleLogout = () => navigate('/employee-login-portal');

  useEffect(() => {
    document.title = 'Gesti√≥n de Sitios y Supervisores - AsistenciaPro';
  }, []);

  const anyError = sitesError || supervisorsError;

  return (
    <div className="min-h-screen bg-background">
      <RoleBasedSidebar isCollapsed={sidebarCollapsed} userRole={userRole} />

      <div className={`transition-all duration-300 ease-out-cubic ${sidebarCollapsed ? 'md:ml-16' : 'md:ml-60'}`}>
        {/* Header */}
        <header className="bg-card border-b border-border px-6 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <Button
                variant="ghost"
                size="icon"
                onClick={() => setSidebarCollapsed(!sidebarCollapsed)}
                className="hidden md:flex"
                aria-label="Alternar barra lateral"
              >
                <Icon name={sidebarCollapsed ? 'ChevronRight' : 'ChevronLeft'} size={20} />
              </Button>

              <div>
                <h1 className="text-2xl font-bold text-foreground">Gesti√≥n de Sitios y Supervisores</h1>
                <p className="text-sm text-muted-foreground">Administra la estructura organizacional y asignaciones</p>
              </div>
            </div>

            <div className="flex items-center space-x-4">
              <Button
                variant="outline"
                iconName="RefreshCw"
                onClick={() => {
                  refetchSites();
                  refetchSupervisors();
                }}
              >
                Actualizar
              </Button>
              <NotificationCenter />
              <UserContextHeader onLogout={handleLogout} />
            </div>
          </div>
        </header>

        {/* Main Content */}
        <main className="p-6">
          <NavigationBreadcrumb />

          {anyError && (
            <div className="mb-4">
              <ErrorState
                message="No se pudieron cargar algunos datos."
                onRetry={() => {
                  refetchSites();
                  refetchSupervisors();
                }}
              />
            </div>
          )}

          <div className="grid grid-cols-12 gap-6 h-[calc(100vh-200px)]">
            {/* Left Panel - Organizational Tree */}
            <div className="col-span-12 lg:col-span-4 xl:col-span-3">
              <OrganizationalTree
                sites={sitesRows}
                supervisors={supervisors}
                onNodeSelect={handleNodeSelect}
                selectedNode={selectedNode}
                onDragDrop={handleDragDrop}
                userRole={userRole}
                isLoading={sitesLoading || supervisorsLoading}
              />
            </div>

            {/* Center Panel */}
            <div className="col-span-12 lg:col-span-5 xl:col-span-6">
              <div className="bg-card border border-border rounded-lg h-full flex flex-col">
                {/* Tabs */}
                <div className="flex items-center justify-between p-4 border-b border-border">
                  <div className="flex items-center space-x-1">
                    <Button
                      variant={activeTab === 'sites' ? 'default' : 'ghost'}
                      size="sm"
                      onClick={() => setActiveTab('sites')}
                      iconName="Building2"
                      iconPosition="left"
                    >
                      Sitios ({sitesCount})
                    </Button>
                    <Button
                      variant={activeTab === 'supervisors' ? 'default' : 'ghost'}
                      size="sm"
                      onClick={() => setActiveTab('supervisors')}
                      iconName="UserCheck"
                      iconPosition="left"
                    >
                      Supervisores ({supervisors.length})
                    </Button>
                  </div>

                  <div className="flex items-center space-x-2">
                    {userRole === 'admin' && (
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => setShowBulkAssignment((v) => !v)}
                        iconName="Users"
                        iconPosition="left"
                      >
                        Asignaci√≥n masiva
                      </Button>
                    )}

                    <Button variant="default" size="sm" iconName="Plus" iconPosition="left">
                      {activeTab === 'sites' ? 'Nuevo sitio' : 'Nuevo supervisor'}
                    </Button>
                  </div>
                </div>

                {/* Filters */}
                <div className="p-4 border-b border-border">
                  <div className="flex items-center space-x-4">
                    <div className="flex-1">
                      <Input
                        type="search"
                        placeholder={`Buscar ${activeTab === 'sites' ? 'sitios' : 'supervisores'}...`}
                        value={searchTerm}
                        onChange={(e) => {
                          setPage(0); // reset p√°gina al cambiar b√∫squeda
                          setSearchTerm(e?.target?.value);
                        }}
                      />
                    </div>
                    <div className="w-48">
                      <Select
                        options={
                          activeTab === 'sites'
                            ? [
                                { value: 'all', label: 'Todos los estados' },
                                { value: 'active', label: 'Activo' },
                                // placeholders (si m√°s adelante agregas columna status)
                                { value: 'planning', label: 'Planificaci√≥n' },
                                { value: 'completed', label: 'Completado' },
                                { value: 'suspended', label: 'Suspendido' },
                              ]
                            : [
                                { value: 'all', label: 'Todos los estados' },
                                // Mapea aqu√≠ si tuvieras status en perfiles/usuarios.
                              ]
                        }
                        value={filterStatus}
                        onChange={(val) => {
                          setPage(0);
                          setFilterStatus(val);
                        }}
                        placeholder="Filtrar por estado"
                      />
                    </div>
                  </div>
                </div>

                {/* Content */}
                <div className="flex-1 overflow-y-auto p-4">
                  {showBulkAssignment ? (
                    <BulkAssignmentPanel
                      sites={sitesRows}
                      supervisors={supervisors}
                      employees={[]}
                      onBulkAssign={handleBulkAssign}
                      onClose={() => setShowBulkAssignment(false)}
                      userRole={userRole}
                    />
                  ) : activeTab === 'sites' ? (
                    sitesLoading ? (
                      <Loading label="Cargando sitios‚Ä¶" />
                    ) : sitesError ? (
                      <ErrorState message={sitesError?.message} onRetry={refetchSites} />
                    ) : filteredSites.length > 0 ? (
                      <>
                        <div className="space-y-4">
                          {filteredSites.map((site) => (
                            <SiteCard
                              key={site?.id}
                              site={{
                                ...site,
                                status: site?.is_active ? 'active' : 'inactive',
                                progress: site?.progress ?? undefined,
                                supervisor: site?.supervisor || null,
                              }}
                              onEdit={handleSiteEdit}
                              onDelete={handleSiteDelete}
                              onViewDetails={handleSiteViewDetails}
                              onAssignSupervisor={handleAssignSupervisor}
                              userRole={userRole}
                            />
                          ))}
                        </div>

                        {/* Paginaci√≥n */}
                        <div className="flex items-center justify-between mt-4">
                          <div className="text-xs text-muted-foreground">
                            {sitesFetching ? 'Actualizando‚Ä¶ ' : ''} Total: {sitesCount} ¬∑ P√°gina {page + 1} de {totalPages}
                          </div>
                          <div className="flex gap-2">
                            <button
                              onClick={() => setPage((p) => Math.max(0, p - 1))}
                              disabled={page === 0}
                              className="px-3 py-1.5 rounded border disabled:opacity-50"
                            >
                              Anterior
                            </button>
                            <button
                              onClick={() => setPage((p) => (p + 1 < totalPages ? p + 1 : p))}
                              disabled={page + 1 >= totalPages}
                              className="px-3 py-1.5 rounded border disabled:opacity-50"
                            >
                              Siguiente
                            </button>
                          </div>
                        </div>
                      </>
                    ) : (
                      <div className="text-center py-12">
                        <Icon name="Building2" size={48} className="mx-auto text-muted-foreground mb-4" />
                        <h3 className="text-lg font-semibold text-foreground mb-2">No se encontraron sitios</h3>
                        <p className="text-muted-foreground mb-4">
                          {debouncedSearch ? 'Intenta con otros t√©rminos de b√∫squeda' : 'Comienza creando tu primer sitio de construcci√≥n'}
                        </p>
                        {userRole === 'admin' && (
                          <Button iconName="Plus" iconPosition="left">
                            Crear nuevo sitio
                          </Button>
                        )}
                      </div>
                    )
                  ) : supervisorsLoading ? (
                    <Loading label="Cargando supervisores‚Ä¶" />
                  ) : supervisorsError ? (
                    <ErrorState message={supervisorsError?.message} onRetry={refetchSupervisors} />
                  ) : filteredSupervisors.length > 0 ? (
                    filteredSupervisors.map((supervisor) => (
                      <SupervisorCard
                        key={supervisor?.id}
                        supervisor={{
                          id: supervisor?.id,
                          name: supervisor?.full_name,
                          email: supervisor?.email,
                          phone: supervisor?.phone,
                          // M√©tricas por supervisor: si las tienes en el service, m√°p√©alas aqu√≠.
                          assignedSites: supervisor?.assignedSites ?? 0,
                          totalEmployees: supervisor?.totalEmployees ?? 0,
                          status: 'active',
                          avatar: supervisor?.avatar || null,
                        }}
                        onEdit={handleSupervisorEdit}
                        onDelete={handleSupervisorDelete}
                        onViewDetails={handleSupervisorViewDetails}
                        onAssignSites={handleAssignSites}
                        userRole={userRole}
                      />
                    ))
                  ) : (
                    <div className="text-center py-12">
                      <Icon name="UserCheck" size={48} className="mx-auto text-muted-foreground mb-4" />
                      <h3 className="text-lg font-semibold text-foreground mb-2">No se encontraron supervisores</h3>
                      <p className="text-muted-foreground mb-4">
                        {debouncedSearch ? 'Intenta con otros t√©rminos de b√∫squeda' : 'Comienza agregando supervisores a tu equipo'}
                      </p>
                      {userRole === 'admin' && (
                        <Button iconName="Plus" iconPosition="left">
                          Agregar supervisor
                        </Button>
                      )}
                    </div>
                  )}
                </div>
              </div>
            </div>

            {/* Right Panel - Quick Stats */}
            <div className="col-span-12 lg:col-span-3">
              <QuickStatsPanel
                sites={sitesRows}
                supervisors={supervisors}
                onViewReports={handleViewReports}
                onExportData={handleExportData}
                selectedNode={selectedNode}
                isLoading={sitesLoading || supervisorsLoading}
              />
            </div>
          </div>
        </main>
      </div>

      {/* Mobile Bottom Navigation Spacer */}
      <div className="md:hidden h-16" />
    </div>
  );
};

export default ConstructionSiteAndSupervisorManagementHub;

