// /src/pages/employee-login-portal/index.jsx
import React, { useEffect, useState } from "react";
import { useNavigate, useSearchParams } from "react-router-dom";
import { Helmet } from "react-helmet";
import { useAuth } from "../../contexts/AuthContext";

const safeInternalPath = (p) =>
  typeof p === "string" && p.startsWith("/") ? p : "/dashboard";

export default function EmployeeLoginPortal() {
  const navigate = useNavigate();
  const [q] = useSearchParams();
  const next = safeInternalPath(q.get("next") || "/dashboard");

  const {
    user,
    loading,
    authError,
    setAuthError,
    signIn,        // email/password
    sendOTP,       // OTP solicitaci√≥n
    verifyOTP,     // OTP verificaci√≥n
    resetPassword, // NUEVO: enviar email de restablecimiento
  } = useAuth();

  // --- UI state ---
  const [mode, setMode] = useState("password"); // "password" | "otp"
  const [form, setForm] = useState({ email: "", password: "", phone: "", otp: "" });
  const [submitting, setSubmitting] = useState(false);
  const [message, setMessage] = useState("");
  const [info, setInfo] = useState("");

  // Si ya est√° autenticado, redirige al destino solicitado
  useEffect(() => {
    if (!loading && user) navigate(next, { replace: true });
  }, [user, loading, next, navigate]);

  const onChange = (e) => {
    setAuthError?.("");
    setMessage("");
    setInfo("");
    setForm((s) => ({ ...s, [e.target.name]: e.target.value }));
  };

  // --- Email/Password login ---
  const handleEmailLogin = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    setMessage("");
    setInfo("");
    setAuthError?.("");
    try {
      const { email, password } = form;
      const res = await signIn(email, password);
      if (!res?.success) {
        throw new Error(res?.error || "No fue posible iniciar sesi√≥n");
      }
      navigate(next, { replace: true });
    } catch (err) {
      setMessage(err.message || "Error de autenticaci√≥n");
    } finally {
      setSubmitting(false);
    }
  };

  // --- OTP: solicitar c√≥digo (por email o tel√©fono) ---
  const handleSendOtp = async () => {
    setSubmitting(true);
    setMessage("");
    setInfo("");
    setAuthError?.("");
    try {
      const phone = form.phone?.trim() || null;
      const email = form.email?.trim() || null;
      if (!phone && !email) throw new Error("Ingresa tel√©fono o correo para enviar el c√≥digo.");
      const res = await sendOTP(phone, email);
      if (!res?.success) throw new Error(res?.error || "No se pudo enviar el c√≥digo");
      setInfo("C√≥digo enviado. Revisa tu bandeja o SMS.");
    } catch (err) {
      setMessage(err.message || "No se pudo enviar el c√≥digo");
    } finally {
      setSubmitting(false);
    }
  };

  // --- OTP: verificar c√≥digo ---
  const handleVerifyOtp = async () => {
    setSubmitting(true);
    setMessage("");
    setInfo("");
    setAuthError?.("");
    try {
      const { phone, email, otp } = form;
      if (!otp) throw new Error("Ingresa el c√≥digo recibido.");
      const res = await verifyOTP(phone?.trim() || null, email?.trim() || null, otp.trim());
      if (!res?.success) throw new Error(res?.error || "No se pudo verificar el c√≥digo");
      navigate(next, { replace: true });
    } catch (err) {
      setMessage(err.message || "No se pudo verificar el c√≥digo");
    } finally {
      setSubmitting(false);
    }
  };

  // --- Reset password por correo ---
  const handleResetPassword = async () => {
    setSubmitting(true);
    setMessage("");
    setInfo("");
    setAuthError?.("");
    try {
      const email = form.email?.trim();
      if (!email) throw new Error("Escribe tu correo para enviar el enlace de restablecimiento.");
      const res = await resetPassword(email, `${window.location.origin}/auth/reset`);
      if (!res?.success) throw new Error(res?.error || "No se pudo enviar el enlace");
      setInfo("Te enviamos un enlace para restablecer tu contrase√±a. Revisa tu correo.");
    } catch (err) {
      setMessage(err.message || "No se pudo enviar el enlace");
    } finally {
      setSubmitting(false);
    }
  };

  const title = "Portal de Acceso ¬∑ Nova HR";

  return (
    <div className="min-h-screen flex items-center justify-center bg-background p-6">
      <Helmet>
        <title>{title}</title>
        <meta name="description" content="Accede para continuar a tu panel." />
      </Helmet>

      <div className="w-full max-w-md bg-card border border-border rounded-2xl shadow p-6">
        <h1 className="text-xl font-semibold text-foreground mb-1">Iniciar sesi√≥n</h1>
        <p className="text-sm text-muted-foreground mb-6">
          Ser√°s redirigido a: <span className="font-medium">{next}</span>
        </p>

        {(authError || message) && (
          <div className="mb-4 text-sm text-red-600 bg-red-50 border border-red-200 rounded-md p-3">
            {authError || message}
          </div>
        )}
        {info && (
          <div className="mb-4 text-sm text-green-700 bg-green-50 border border-green-200 rounded-md p-3">
            {info}
          </div>
        )}

        {/* Switch de modo */}
        <div className="flex items-center justify-center gap-2 mb-4">
          <button
            type="button"
            onClick={() => { setMode("password"); setMessage(""); setInfo(""); setAuthError?.(""); }}
            className={`px-3 py-1 rounded-md border ${mode === "password" ? "bg-primary text-primary-foreground border-primary" : "border-input"}`}
            disabled={submitting || loading}
          >
            Correo y contrase√±a
          </button>
          <button
            type="button"
            onClick={() => { setMode("otp"); setMessage(""); setInfo(""); setAuthError?.(""); }}
            className={`px-3 py-1 rounded-md border ${mode === "otp" ? "bg-primary text-primary-foreground border-primary" : "border-input"}`}
            disabled={submitting || loading}
          >
            C√≥digo (OTP)
          </button>
        </div>

        {/* --- FORM: EMAIL/PASSWORD --- */}
        {mode === "password" && (
          <form onSubmit={handleEmailLogin} className="space-y-4">
            <div>
              <label className="block text-sm mb-1" htmlFor="email">
                Correo
              </label>
              <input
                id="email"
                name="email"
                type="email"
                required
                autoComplete="email"
                className="w-full rounded-lg border border-input bg-background px-3 py-2"
                value={form.email}
                onChange={onChange}
                disabled={submitting || loading}
              />
            </div>

            <div>
              <label className="block text-sm mb-1" htmlFor="password">
                Contrase√±a
              </label>
              <input
                id="password"
                name="password"
                type="password"
                required
                autoComplete="current-password"
                className="w-full rounded-lg border border-input bg-background px-3 py-2"
                value={form.password}
                onChange={onChange}
                disabled={submitting || loading}
              />
            </div>

            <button
              type="submit"
              disabled={submitting || loading}
              className="w-full rounded-lg bg-primary text-primary-foreground px-3 py-2 font-medium"
            >
              {submitting ? "Ingresando‚Ä¶" : "Ingresar"}
            </button>

            {/* enlace para restablecer contrase√±a */}
            <div className="flex items-center justify-between text-sm pt-1">
              <span className="text-muted-foreground">¬øOlvidaste tu contrase√±a?</span>
              <button
                type="button"
                onClick={handleResetPassword}
                disabled={submitting || loading}
                className="underline underline-offset-2 hover:opacity-80 disabled:opacity-60"
                title="Te enviaremos un enlace al correo escrito arriba"
              >
                Enviar enlace
              </button>
            </div>
          </form>
        )}

        {/* --- FORM: OTP --- */}
        {mode === "otp" && (
          <div className="space-y-4">
            <div className="text-xs text-muted-foreground">
              Puedes recibir un c√≥digo por <b>tel√©fono</b> (SMS) o por <b>correo</b>. Ingresa uno de los dos.
            </div>

            <div>
              <label className="block text-sm mb-1" htmlFor="phone">
                Tel√©fono (con lada, opcional)
              </label>
              <input
                id="phone"
                name="phone"
                type="tel"
                placeholder="+52XXXXXXXXXX"
                className="w-full rounded-lg border border-input bg-background px-3 py-2"
                value={form.phone}
                onChange={onChange}
                disabled={submitting || loading}
              />
            </div>

            <div>
              <label className="block text-sm mb-1" htmlFor="otp_email">
                Correo (opcional si usas tel√©fono)
              </label>
              <input
                id="otp_email"
                name="email"
                type="email"
                className="w-full rounded-lg border border-input bg-background px-3 py-2"
                value={form.email}
                onChange={onChange}
                disabled={submitting || loading}
              />
            </div>

            <div className="flex gap-2">
              <button
                type="button"
                onClick={handleSendOtp}
                disabled={submitting || loading}
                className="flex-1 rounded-lg border border-input px-3 py-2"
              >
                {submitting ? "Enviando‚Ä¶" : "Enviar c√≥digo"}
              </button>
            </div>

            <div>
              <label className="block text-sm mb-1" htmlFor="otp">
                C√≥digo recibido
              </label>
              <input
                id="otp"
                name="otp"
                type="text"
                className="w-full rounded-lg border border-input bg-background px-3 py-2"
                value={form.otp}
                onChange={onChange}
                disabled={submitting || loading}
              />
            </div>

            <button
              type="button"
              onClick={handleVerifyOtp}
              disabled={submitting || loading}
              className="w-full rounded-lg bg-primary text-primary-foreground px-3 py-2 font-medium"
            >
              {submitting ? "Verificando‚Ä¶" : "Verificar y entrar"}
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

