import React, { useEffect, useMemo, useState } from 'react';
import RoleBasedSidebar from '../../components/ui/RoleBasedSidebar';
import NavigationBreadcrumb from '../../components/ui/NavigationBreadcrumb';
import UserContextHeader from '../../components/ui/UserContextHeader';
import NotificationCenter from '../../components/ui/NotificationCenter';
import IncidentCreationForm from './components/IncidentCreationForm';
import PendingApprovalsQueue from './components/PendingApprovalsQueue';
import IncidentHistoryGrid from './components/IncidentHistoryGrid';
import IncidentAnalyticsDashboard from './components/IncidentAnalyticsDashboard';
import Icon from '../../components/AppIcon';
import Button from '../../components/ui/Button';
import { useAuth } from '../../contexts/AuthContext';
import { incidentService } from '../../services/incidentService';

const IncidentRegistrationAndManagementSystem = () => {
  const { userProfile } = useAuth();

  const currentUser = useMemo(
    () => ({
      id: userProfile?.id,
      name: userProfile?.full_name || 'Usuario',
      role:
        userProfile?.role === 'superadmin'
          ? 'SuperAdmin'
          : userProfile?.role === 'admin'
          ? 'Admin'
          : userProfile?.role === 'supervisor'
          ? 'Supervisor'
          : 'Employee',
      site: '‚Äî',
      avatar: null,
    }),
    [userProfile]
  );

  const [activeTab, setActiveTab] = useState('create');

  // estado de datos
  const [incidents, setIncidents] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  // filtros/paginaci√≥n (aj√∫stalos si tus subcomponentes ya traen internos)
  const [queueStatus] = useState('pending'); // para cola
  const [historyStatus, setHistoryStatus] = useState('all'); // para historial
  const [historyPage, setHistoryPage] = useState(0);
  const [historyPageSize] = useState(20);
  const [historyTotal, setHistoryTotal] = useState(0);

  // --------- CARGA INICIAL: cola + historial ----------
  useEffect(() => {
    let mounted = true;

    const bootstrap = async () => {
      try {
        setIsLoading(true);

        // Cargar cola (pendientes)
        const queueRes = await incidentService.listIncidents({
          status: queueStatus, // 'pending'
          page: 0,
          pageSize: 50,
        });

        // Cargar historial (todos o por filtro)
        const histRes = await incidentService.listIncidents({
          status: historyStatus === 'all' ? undefined : historyStatus,
          page: historyPage,
          pageSize: historyPageSize,
        });

        if (!mounted) return;

        // fusi√≥n simple: puedes separar estados si tus componentes lo requieren
        // aqu√≠ mantenemos un √∫nico arreglo y filtramos al mostrar
        const merged = [
          ...(queueRes?.data?.rows || []),
          ...(histRes?.data?.rows || []),
        ];
        // si quieres evitar duplicados por id
        const uniqueById = Object.values(
          merged.reduce((acc, it) => {
            acc[it.id] = it;
            return acc;
          }, {})
        );

        setIncidents(uniqueById);
        setHistoryTotal(histRes?.data?.count || 0);
      } catch (e) {
        console.error('Error loading incidents:', e);
      } finally {
        if (mounted) setIsLoading(false);
      }
    };

    bootstrap();
    return () => {
      mounted = false;
    };
  }, [queueStatus, historyStatus, historyPage, historyPageSize]);

  // --------- UTIL: recargar datos (por ejemplo tras crear/aprobar/rechazar) ----------
  const reload = async () => {
    try {
      setIsLoading(true);
      const [queueRes, histRes] = await Promise.all([
        incidentService.listIncidents({
          status: queueStatus,
          page: 0,
          pageSize: 50,
        }),
        incidentService.listIncidents({
          status: historyStatus === 'all' ? undefined : historyStatus,
          page: historyPage,
          pageSize: historyPageSize,
        }),
      ]);

      const merged = [
        ...(queueRes?.data?.rows || []),
        ...(histRes?.data?.rows || []),
      ];
      const uniqueById = Object.values(
        merged.reduce((acc, it) => {
          acc[it.id] = it;
          return acc;
        }, {})
      );
      setIncidents(uniqueById);
      setHistoryTotal(histRes?.data?.count || 0);
    } catch (e) {
      console.error('Error reloading incidents:', e);
    } finally {
      setIsLoading(false);
    }
  };

  // --------- manejadores de acciones ----------
  const handleIncidentCreated = (created) => {
    // el form ya la cre√≥ en la DB; aqu√≠ refrescamos y pasamos a "queue"
    setActiveTab('queue');
    reload();
  };

  const handleApproveIncident = async (incidentId, comment) => {
    try {
      await incidentService.approveIncident(incidentId, comment);
      await reload();
    } catch (e) {
      console.error('Error approving incident:', e);
    }
  };

  const handleRejectIncident = async (incidentId, reason) => {
    try {
      await incidentService.rejectIncident(incidentId, reason);
      await reload();
    } catch (e) {
      console.error('Error rejecting incident:', e);
    }
  };

  // --------- tabs ----------
  const tabs = [
    { id: 'create', label: 'Registrar Incidente', icon: 'Plus', count: null },
    {
      id: 'queue',
      label: 'Cola de Aprobaciones',
      icon: 'Clock',
      count: incidents?.filter((i) => i?.status === 'pending')?.length || 0,
    },
    {
      id: 'history',
      label: 'Historial',
      icon: 'FileText',
      count: incidents?.length || 0,
    },
    { id: 'analytics', label: 'An√°lisis', icon: 'BarChart3', count: null },
  ];

  // --------- loading inicial ----------
  if (isLoading) {
    return (
      <div className="min-h-screen bg-background flex">
        <RoleBasedSidebar userRole={currentUser?.role?.toLowerCase()} />
        <div className="flex-1 ml-0 md:ml-60">
          <div className="flex items-center justify-center h-screen">
            <div className="text-center">
              <Icon name="Loader2" size={48} className="mx-auto text-primary animate-spin mb-4" />
              <p className="text-lg font-medium text-foreground">Cargando sistema de incidentes...</p>
              <p className="text-sm text-muted-foreground mt-1">Preparando datos y configuraciones</p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // --------- render ----------
  return (
    <div className="min-h-screen bg-background">
      <RoleBasedSidebar userRole={currentUser?.role?.toLowerCase()} />
      <div className="flex-1 ml-0 md:ml-60">
        {/* Header */}
        <header className="bg-card border-b border-border px-6 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-2">
                <Icon name="AlertTriangle" size={24} className="text-primary" />
                <h1 className="text-xl font-semibold text-foreground">
                  Sistema de Gesti√≥n de Incidentes
                </h1>
              </div>
            </div>

            <div className="flex items-center space-x-4">
              <NotificationCenter />
              <UserContextHeader
                user={currentUser}
                onLogout={() => {}}
                onProfileClick={() => {}}
                onSiteChange={() => {}}
              />
            </div>
          </div>
        </header>

        {/* Main */}
        <main className="p-6">
          <NavigationBreadcrumb />

          {/* Tabs */}
          <div className="mb-6">
            <div className="border-b border-border">
              <nav className="-mb-px flex space-x-8">
                {tabs?.map((tab) => (
                  <button
                    key={tab?.id}
                    onClick={() => setActiveTab(tab?.id)}
                    className={`
                      flex items-center space-x-2 py-2 px-1 border-b-2 font-medium text-sm transition-colors duration-150
                      ${
                        activeTab === tab?.id
                          ? 'border-primary text-primary'
                          : 'border-transparent text-muted-foreground hover:text-foreground hover:border-border'
                      }
                    `}
                  >
                    <Icon name={tab?.icon} size={16} />
                    <span>{tab?.label}</span>
                    {tab?.count !== null && tab?.count > 0 && (
                      <span className="bg-primary text-primary-foreground text-xs font-medium px-2 py-0.5 rounded-full">
                        {tab?.count}
                      </span>
                    )}
                  </button>
                ))}
              </nav>
            </div>
          </div>

          {/* Content */}
          <div className="space-y-6">
            {activeTab === 'create' && (
              <div className="grid grid-cols-1 xl:grid-cols-5 gap-6">
                <div className="xl:col-span-2">
                  <IncidentCreationForm
                    // Si como supervisor eliges a qu√© empleado va, p√°salo por prop: targetEmployeeId="uuid"
                    onIncidentCreated={handleIncidentCreated}
                    onCancel={() => setActiveTab('queue')}
                  />
                </div>
                <div className="xl:col-span-3">
                  <PendingApprovalsQueue
                    incidents={incidents?.filter((i) => i?.status === 'pending')}
                    onApprove={handleApproveIncident}
                    onReject={handleRejectIncident}
                    currentUser={currentUser}
                  />
                </div>
              </div>
            )}

            {activeTab === 'queue' && (
              <PendingApprovalsQueue
                incidents={incidents?.filter((i) => i?.status === 'pending')}
                onApprove={handleApproveIncident}
                onReject={handleRejectIncident}
                currentUser={currentUser}
              />
            )}

            {activeTab === 'history' && (
              <IncidentHistoryGrid
                incidents={
                  historyStatus === 'all'
                    ? incidents
                    : incidents?.filter((i) => i?.status === historyStatus)
                }
                currentUser={currentUser}
                statusFilter={historyStatus}
                onStatusFilterChange={(s) => {
                  setHistoryPage(0);
                  setHistoryStatus(s || 'all');
                  reload();
                }}
                page={historyPage}
                onPageChange={(p) => {
                  setHistoryPage(p);
                  reload();
                }}
                pageSize={historyPageSize}
                total={historyTotal}
              />
            )}

            {activeTab === 'analytics' && (
              <IncidentAnalyticsDashboard incidents={incidents} />
            )}
          </div>

          {/* Quick Actions Floating Button */}
          <div className="fixed bottom-6 right-6 z-40">
            <div className="flex flex-col space-y-2">
              {activeTab !== 'create' && (
                <Button
                  onClick={() => setActiveTab('create')}
                  className="rounded-full shadow-lg"
                  iconName="Plus"
                  iconSize={20}
                >
                  Nuevo Incidente
                </Button>
              )}
            </div>
          </div>
        </main>
      </div>
    </div>
  );
};

export default IncidentRegistrationAndManagementSystem;

src/pages/obras-financial-control-manage.content:
import React, { useState, useEffect, useMemo } from 'react';
import { useAuth } from '@/contexts/AuthContext';
import {
  Building,
  DollarSign,
  TrendingUp,
  TrendingDown,
  Eye,
  Plus,
  FileText,
  AlertTriangle,
  CheckCircle,
  Clock,
  Pause,
  X,
  Download,
  Filter,
  Search,
} from 'lucide-react';
import { obrasService, financialAnalyticsService } from '@/services/obrasFinancialService';
import ObraCreationModal from './components/ObraCreationModal';
import ObraDetailModal from './components/ObraDetailModal';
import ExportModal from './components/ExportModal';
import FiltersModal from './components/FiltersModal';

const ObrasFinancialControlManagement = () => {
  const auth = useAuth();
  const { loading } = auth || {};

  // Soporta ambos estilos de autorizaci√≥n: isAdmin()/isSuperAdmin() o hasRole('admin'|'superadmin')
  const canManage = useMemo(() => {
    const byFn =
      (typeof auth?.isAdmin === 'function' && auth?.isAdmin()) ||
      (typeof auth?.isSuperAdmin === 'function' && auth?.isSuperAdmin());
    const byRole =
      typeof auth?.hasRole === 'function' &&
      (auth?.hasRole('admin') || auth?.hasRole('superadmin'));
    return !!(byFn || byRole);
  }, [auth]);

  const [obras, setObras] = useState([]);
  const [filteredObras, setFilteredObras] = useState([]);
  const [overallKPIs, setOverallKPIs] = useState(null);
  const [loadingData, setLoadingData] = useState(true);
  const [error, setError] = useState('');

  // Modals
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [showExportModal, setShowExportModal] = useState(false);
  const [showFiltersModal, setShowFiltersModal] = useState(false);
  const [selectedObra, setSelectedObra] = useState(null);

  // Filtros
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [companyFilter, setCompanyFilter] = useState('all');
  const [dateRangeFilter, setDateRangeFilter] = useState({ start: '', end: '' });

  // Cargar datos si tiene permisos
  useEffect(() => {
    if (loading) return;
    if (!canManage) {
      setError('No tienes permisos para acceder a la gesti√≥n financiera de obras.');
      setLoadingData(false);
      return;
    }
    loadData();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [loading, canManage]);

  const loadData = async () => {
    try {
      setLoadingData(true);
      setError('');

      const [obrasRes, kpisRes] = await Promise.all([
        obrasService?.getAll?.(),
        financialAnalyticsService?.getOverallKPIs?.(),
      ]);

      if (!obrasRes?.ok) {
        setError(`Error al cargar obras: ${obrasRes?.error || 'desconocido'}`);
        setObras([]);
        setFilteredObras([]);
      } else {
        setObras(obrasRes?.data || []);
        setFilteredObras(obrasRes?.data || []);
      }

      if (!kpisRes?.ok) {
        // No bloquea la tabla si fallan KPIs
        console.warn('Error al cargar KPIs:', kpisRes?.error);
        setOverallKPIs(null);
      } else {
        setOverallKPIs(kpisRes?.data || null);
      }
    } catch (err) {
      console.error('Error loading obras data:', err);
      setError('Error al cargar los datos de obras');
    } finally {
      setLoadingData(false);
    }
  };

  // Aplicar filtros
  useEffect(() => {
    let filtered = Array.isArray(obras) ? [...obras] : [];

    // B√∫squeda
    if (searchTerm) {
      const t = searchTerm.toLowerCase();
      filtered = filtered.filter(
        (obra) =>
          obra?.nombre?.toLowerCase?.().includes(t) ||
          obra?.clave?.toLowerCase?.().includes(t) ||
          obra?.empresa_nombre?.toLowerCase?.().includes(t)
      );
    }

    // Estatus
    if (statusFilter !== 'all') {
      filtered = filtered.filter((obra) => obra?.estatus === statusFilter);
    }

    // Empresa
    if (companyFilter !== 'all') {
      filtered = filtered.filter((obra) => obra?.empresa_id === companyFilter);
    }

    // Rango de fechas (usa fecha_inicio)
    if (dateRangeFilter?.start && dateRangeFilter?.end) {
      const startDate = new Date(dateRangeFilter.start);
      const endDate = new Date(dateRangeFilter.end);
      filtered = filtered.filter((obra) => {
        const obraDate = obra?.fecha_inicio ? new Date(obra.fecha_inicio) : null;
        return obraDate && obraDate >= startDate && obraDate <= endDate;
      });
    }

    setFilteredObras(filtered);
  }, [obras, searchTerm, statusFilter, companyFilter, dateRangeFilter]);

  // Helpers UI
  const formatCurrency = (amount) => {
    if (amount === null || amount === undefined || isNaN(amount)) return '$0.00';
    return new Intl.NumberFormat('es-MX', {
      style: 'currency',
      currency: 'MXN',
      minimumFractionDigits: 2,
    }).format(Number(amount));
  };

  const formatPercentage = (value) => {
    const num = Number(value);
    if (isNaN(num)) return '0%';
    return `${num.toFixed(1)}%`;
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'En ejecuci√≥n':
        return <CheckCircle className="h-4 w-4 text-green-500" />;
      case 'Planeaci√≥n':
        return <Clock className="h-4 w-4 text-blue-500" />;
      case 'En pausa':
        return <Pause className="h-4 w-4 text-yellow-500" />;
      case 'Concluida':
        return <CheckCircle className="h-4 w-4 text-emerald-500" />;
      case 'Cancelada':
        return <X className="h-4 w-4 text-red-500" />;
      default:
        return <Clock className="h-4 w-4 text-gray-500" />;
    }
  };

  const getStatusColorClass = (status) => {
    switch (status) {
      case 'En ejecuci√≥n':
        return 'bg-green-100 text-green-800';
      case 'Planeaci√≥n':
        return 'bg-blue-100 text-blue-800';
      case 'En pausa':
        return 'bg-yellow-100 text-yellow-800';
      case 'Concluida':
        return 'bg-emerald-100 text-emerald-800';
      case 'Cancelada':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const handleViewObra = (obra) => {
    setSelectedObra(obra);
    setShowDetailModal(true);
  };

  const handleObraCreated = () => {
    setShowCreateModal(false);
    loadData();
  };

  const handleObraUpdated = () => {
    setShowDetailModal(false);
    loadData();
  };

  // Loading Auth
  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  // Sin permisos
  if (!canManage) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <AlertTriangle className="h-16 w-16 text-red-500 mx-auto mb-4" />
          <h2 className="text-xl font-semibold text-gray-900 mb-2">Acceso Denegado</h2>
          <p className="text-gray-600">
            No tienes permisos para acceder a la gesti√≥n financiera de obras.
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="py-6">
            <div className="flex items-center justify-between">
              <div>
                <h1 className="text-3xl font-bold text-gray-900">Control Financiero de Obras</h1>
                <p className="mt-2 text-gray-600">
                  Gesti√≥n integral de presupuestos, facturas, pagos y KPIs
                </p>
              </div>
              <div className="flex space-x-4">
                <button
                  onClick={() => setShowFiltersModal(true)}
                  className="bg-gray-100 hover:bg-gray-200 text-gray-700 px-4 py-2 rounded-md flex items-center space-x-2 transition-colors"
                >
                  <Filter className="h-4 w-4" />
                  <span>Filtros</span>
                </button>
                <button
                  onClick={() => setShowExportModal(true)}
                  className="bg-blue-100 hover:bg-blue-200 text-blue-700 px-4 py-2 rounded-md flex items-center space-x-2 transition-colors"
                >
                  <Download className="h-4 w-4" />
                  <span>Exportar</span>
                </button>
                <button
                  onClick={() => setShowCreateModal(true)}
                  className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-md flex items-center space-x-2 transition-colors"
                >
                  <Plus className="h-4 w-4" />
                  <span>Nueva Obra</span>
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* KPIs */}
        {overallKPIs && (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
            <div className="bg-white rounded-lg shadow p-6">
              <div className="flex items-center">
                <div className="flex-shrink-0">
                  <Building className="h-8 w-8 text-blue-600" />
                </div>
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-500">Total Obras</p>
                  <p className="text-2xl font-semibold text-gray-900">
                    {overallKPIs?.obras_total || 0}
                  </p>
                  <p className="text-sm text-green-600">
                    {overallKPIs?.obras_activas || 0} activas
                  </p>
                </div>
              </div>
            </div>

            <div className="bg-white rounded-lg shadow p-6">
              <div className="flex items-center">
                <div className="flex-shrink-0">
                  <DollarSign className="h-8 w-8 text-green-600" />
                </div>
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-500">Presupuesto Total</p>
                  <p className="text-2xl font-semibold text-gray-900">
                    {formatCurrency(overallKPIs?.total_presupuesto)}
                  </p>
                </div>
              </div>
            </div>

            <div className="bg-white rounded-lg shadow p-6">
              <div className="flex items-center">
                <div className="flex-shrink-0">
                  <TrendingUp className="h-8 w-8 text-emerald-600" />
                </div>
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-500">Total Facturado</p>
                  <p className="text-2xl font-semibold text-gray-900">
                    {formatCurrency(overallKPIs?.total_facturado)}
                  </p>
                </div>
              </div>
            </div>

            <div className="bg-white rounded-lg shadow p-6">
              <div className="flex items-center">
                <div className="flex-shrink-0">
                  <TrendingDown className="h-8 w-8 text-red-600" />
                </div>
                <div className="ml-4">
                  <p className="text-sm font-medium text-gray-500">Por Cobrar</p>
                  <p className="text-2xl font-semibold text-gray-900">
                    {formatCurrency(overallKPIs?.total_por_cobrar)}
                  </p>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Buscador */}
        <div className="bg-white rounded-lg shadow p-4 mb-6">
          <div className="relative">
            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
              <Search className="h-5 w-5 text-gray-400" />
            </div>
            <input
              type="text"
              placeholder="Buscar obras por nombre, clave o empresa..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e?.target?.value)}
              className="block w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md leading-5 bg-white placeholder-gray-500 focus:outline-none focus:placeholder-gray-400 focus:ring-1 focus:ring-blue-500 focus:border-blue-500"
            />
          </div>
        </div>

        {/* Loading */}
        {loadingData && (
          <div className="bg-white rounded-lg shadow p-8">
            <div className="flex items-center justify-center">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mr-4"></div>
              <span className="text-gray-600">Cargando obras...</span>
            </div>
          </div>
        )}

        {/* Error */}
        {error && !loadingData && (
          <div className="bg-red-50 border border-red-200 rounded-lg p-4 mb-6">
            <div className="flex items-center">
              <AlertTriangle className="h-5 w-5 text-red-500 mr-2" />
              <span className="text-red-700">{error}</span>
            </div>
          </div>
        )}

        {/* Tabla */}
        {!loadingData && !error && (
          <div className="bg-white rounded-lg shadow overflow-hidden">
            <div className="overflow-x-auto">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Obra
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Estado
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Presupuesto Total
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Facturado
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Por Cobrar
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Gastos
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      % Utilidad Real
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Avance Financiero
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Acciones
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {filteredObras?.length > 0 ? (
                    filteredObras.map((obra) => (
                      <tr key={obra?.obra_id || obra?.id} className="hover:bg-gray-50">
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div>
                            <div className="text-sm font-medium text-gray-900">{obra?.nombre}</div>
                            <div className="text-sm text-gray-500">
                              {obra?.clave} ‚Ä¢ {obra?.empresa_nombre}
                            </div>
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="flex items-center space-x-2">
                            {getStatusIcon(obra?.estatus)}
                            <span
                              className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getStatusColorClass(
                                obra?.estatus
                              )}`}
                            >
                              {obra?.estatus}
                            </span>
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          {formatCurrency(obra?.presupuesto_total)}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          {formatCurrency(obra?.facturado_total)}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm">
                          <span
                            className={
                              Number(obra?.por_cobrar) > 0
                                ? 'text-orange-600 font-medium'
                                : 'text-gray-900'
                            }
                          >
                            {formatCurrency(obra?.por_cobrar)}
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          {formatCurrency(obra?.gastos_total)}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm">
                          <span
                            className={
                              Number(obra?.utilidad_pct_real) >= 0
                                ? 'text-green-600 font-medium'
                                : 'text-red-600 font-medium'
                            }
                          >
                            {formatPercentage(obra?.utilidad_pct_real)}
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          <div className="flex items-center space-x-2">
                            <div className="flex-1 bg-gray-200 rounded-full h-2">
                              <div
                                className="bg-blue-600 h-2 rounded-full"
                                style={{
                                  width: `${Math.min(Number(obra?.avance_financiero_pct) || 0, 100)}%`,
                                }}
                              ></div>
                            </div>
                            <span className="text-xs font-medium">
                              {formatPercentage(obra?.avance_financiero_pct)}
                            </span>
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                          <button
                            onClick={() => handleViewObra(obra)}
                            className="text-blue-600 hover:text-blue-900 flex items-center space-x-1"
                          >
                            <Eye className="h-4 w-4" />
                            <span>Ver Detalles</span>
                          </button>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="9" className="px-6 py-12 text-center text-sm text-gray-500">
                        {searchTerm || statusFilter !== 'all' || companyFilter !== 'all' ? (
                          <div>
                            <FileText className="h-12 w-12 text-gray-300 mx-auto mb-4" />
                            <p>No se encontraron obras con los filtros aplicados</p>
                          </div>
                        ) : (
                          <div>
                            <Building className="h-12 w-12 text-gray-300 mx-auto mb-4" />
                            <p>No hay obras registradas</p>
                            <button
                              onClick={() => setShowCreateModal(true)}
                              className="mt-4 text-blue-600 hover:text-blue-800 font-medium"
                            >
                              Crear primera obra
                            </button>
                          </div>
                        )}
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </div>
        )}
      </div>

      {/* Modals */}
      {showCreateModal && (
        <ObraCreationModal onClose={() => setShowCreateModal(false)} onSuccess={handleObraCreated} />
      )}
      {showDetailModal && selectedObra && (
        <ObraDetailModal obra={selectedObra} onClose={() => setShowDetailModal(false)} onSuccess={handleObraUpdated} />
      )}
      {showExportModal && (
        <ExportModal obras={filteredObras} onClose={() => setShowExportModal(false)} />
      )}
      {showFiltersModal && (
        <FiltersModal
          onClose={() => setShowFiltersModal(false)}
          onApplyFilters={(filters) => {
            setStatusFilter(filters?.status || 'all');
            setCompanyFilter(filters?.company || 'all');
            setDateRangeFilter(filters?.dateRange || { start: '', end: '' });
            setShowFiltersModal(false);
          }}
          currentFilters={{
            status: statusFilter,
            company: companyFilter,
            dateRange: dateRangeFilter,
          }}
        />
      )}
    </div>
  );
};

export default ObrasFinancialControlManagement;

