import React, { useEffect, useMemo, useState } from 'react';
import { Helmet } from 'react-helmet';
import RoleBasedSidebar from '@/components/ui/RoleBasedSidebar';
import NavigationBreadcrumb from '@/components/ui/NavigationBreadcrumb';
import UserContextHeader from '@/components/ui/UserContextHeader';
import NotificationCenter from '@/components/ui/NotificationCenter';
import Icon from '@/components/AppIcon';
import Button from '@/components/ui/Button';

// Subcomponentes (ya existentes en tu proyecto)
import PayrollCalculationEngine from './components/PayrollCalculationEngine';
import EmployeePayrollGrid from './components/EmployeePayrollGrid';
import PayrollAuditTrail from './components/PayrollAuditTrail';
import BulkProcessingTools from './components/BulkProcessingTools';
import IntegrationStatusPanel from './components/IntegrationStatusPanel';

// Servicios
import { payrollService } from '@/services/payrollService';
import { getEmployeesWithFilters } from '@/services/employeeService';

// Helpers UI m√≠nimos
const LoadingSpinner = () => (
  <div className="min-h-[200px] flex items-center justify-center">
    <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
  </div>
);

const ErrorBanner = ({ message, onRetry }) => (
  <div className="bg-red-50 border border-red-200 rounded-lg p-4 mb-6">
    <div className="flex items-center justify-between">
      <div className="flex items-center">
        <Icon name="AlertTriangle" className="text-red-600" size={18} />
        <span className="text-red-700 ml-2">{message}</span>
      </div>
      {onRetry && (
        <Button variant="outline" size="sm" onClick={onRetry}>
          Reintentar
        </Button>
      )}
    </div>
  </div>
);

const PayrollCalculationAndManagementInterface = () => {
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);

  // Datos principales
  const [employees, setEmployees] = useState([]);               // [{ id, employeeId, name, ... }]
  const [employeesLoading, setEmployeesLoading] = useState(true);
  const [employeesError, setEmployeesError] = useState('');

  // Selecciones y vista
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [selectedEmployeeIds, setSelectedEmployeeIds] = useState([]);
  const [activeView, setActiveView] = useState('calculation');  // calculation | bulk | audit | integration

  // Estado de procesamiento masivo
  const [processingStatus, setProcessingStatus] = useState(null);

  // Snapshot m√≠nimo de asistencia/n√≥mina del empleado seleccionado (semana actual)
  const [currentAttendance, setCurrentAttendance] = useState(null);
  const [calcLoading, setCalcLoading] = useState(false);
  const [calcError, setCalcError] = useState('');

  // --------- CARGA DE EMPLEADOS (activos) ----------
  const loadEmployees = async () => {
    try {
      setEmployeesLoading(true);
      setEmployeesError('');

      // Usa tu service real. Este m√©todo ya lo citaste en otra p√°gina:
      // getEmployeesWithFilters({ status: 'active' })
      const res = await getEmployeesWithFilters?.({ status: 'active' });

      if (!res?.ok) {
        setEmployees([]);
        setEmployeesError(res?.error || 'No fue posible cargar empleados.');
        return;
      }

      // Normaliza a lo que requiere EmployeePayrollGrid
      const rows = (res?.data || []).map((e) => ({
        id: e?.id,                               // uuid interno
        employeeId: e?.employee_id || e?.id,     // id visible o legajo
        name: e?.full_name || 'Sin nombre',
        position: e?.position || e?.job_title || '‚Äî',
        site: e?.construction_sites?.name || e?.site?.name || '‚Äî',
        dailyWage: Number(e?.daily_salary ?? 0),
        supervisor: e?.supervisor?.full_name || '‚Äî',
        status: (e?.status || 'active')?.toString(),
      }));

      setEmployees(rows);

      // Selecci√≥n inicial (opcional)
      if (!selectedEmployee && rows?.length) {
        setSelectedEmployee(rows[0]);
      }
    } catch (err) {
      console.error(err);
      setEmployeesError('Error inesperado al cargar empleados.');
    } finally {
      setEmployeesLoading(false);
    }
  };

  useEffect(() => {
    loadEmployees();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // --------- ASISTENCIA / ESTIMACI√ìN PARA EMPLEADO SELECCIONADO (SEMANA ACTUAL) ----------
  const getWeekRange = () => {
    const today = new Date();
    const monday = new Date(today);
    monday.setDate(today.getDate() - ((today.getDay() + 6) % 7));
    monday.setHours(0, 0, 0, 0);
    const sunday = new Date(monday);
    sunday.setDate(monday.getDate() + 6);
    const toISO = (d) => d.toISOString().slice(0, 10);
    return { start: toISO(monday), end: toISO(sunday) };
  };

  const loadSelectedAttendance = async () => {
    if (!selectedEmployee?.id) {
      setCurrentAttendance(null);
      return;
    }

    const { start, end } = getWeekRange();
    setCalcLoading(true);
    setCalcError('');

    try {
      // Intentamos obtener la estimaci√≥n semanal persistida
      // (Tu payrollService ya tiene m√©todos de estimaci√≥n)
      const snap =
        (await payrollService?.getWeeklyEstimation?.(selectedEmployee.id, start, end)) ||
        { ok: false };

      if (snap?.ok && snap?.data) {
        setCurrentAttendance(snap.data);
        return;
      }

      // Si no existe, calculamos y persistimos:
      const calc = await payrollService?.calculateFromAttendance?.(selectedEmployee.id, start, end);
      if (!calc?.ok) {
        setCalcError(calc?.error || 'No se pudo calcular la n√≥mina del empleado.');
        setCurrentAttendance(null);
        return;
      }

      const saved = await payrollService?.upsertWeeklyEstimation?.(calc.data);
      if (!saved?.ok) {
        // Si no se pudo persistir, al menos mostramos c√°lculo
        setCurrentAttendance(calc.data);
        return;
      }
      setCurrentAttendance(saved.data);
    } catch (err) {
      console.error(err);
      setCalcError('Error inesperado al obtener/cacular la n√≥mina del empleado.');
      setCurrentAttendance(null);
    } finally {
      setCalcLoading(false);
    }
  };

  useEffect(() => {
    loadSelectedAttendance();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [selectedEmployee?.id]);

  // --------- HANDLERS UI ----------
  const handleEmployeeSelect = (employee) => {
    setSelectedEmployee(employee);
    setActiveView('calculation');
  };

  const handleCalculationUpdate = (calculations) => {
    // Si tu PayrollCalculationEngine devuelve ajustes temporales,
    // aqu√≠ podr√≠as llamar a un m√©todo payrollService.upsertAdjustments(...)
    // o guardar en un estado local y luego "Guardar todo".
    console.log('Calculation updated:', calculations);
  };

  const handleSaveAdjustments = async (data) => {
    // Persistir ajustes manuales (si tu motor los genera)
    // Ejemplo: await payrollService?.upsertManualAdjustments?.(selectedEmployee?.id, data)
    console.log('Saving adjustments:', data);
  };

  const handleBulkAction = async (action, employeeIds) => {
    if (!Array.isArray(employeeIds) || employeeIds.length === 0) return;
    setProcessingStatus({
      action: `Procesando: ${action}`,
      processed: 0,
      total: employeeIds.length,
      status: 'processing',
    });

    const { start, end } = getWeekRange();

    try {
      // Si existe un m√©todo masivo real:
      if (action === 'calculate' && typeof payrollService?.bulkCalculateWeekly === 'function') {
        const resp = await payrollService.bulkCalculateWeekly(employeeIds, start, end, {
          persist: true,
        });
        if (!resp?.ok) throw new Error(resp?.error || 'Error en procesamiento masivo');

        // Marcar como completado
        setProcessingStatus((prev) => ({ ...prev, processed: prev.total, status: 'completed' }));
      } else {
        // Fallback: iterar 1x1
        for (let i = 0; i < employeeIds.length; i++) {
          const id = employeeIds[i];
          const calc = await payrollService?.calculateFromAttendance?.(id, start, end);
          if (calc?.ok) {
            await payrollService?.upsertWeeklyEstimation?.(calc.data);
          }
          setProcessingStatus((prev) => ({
            ...prev,
            processed: i + 1,
            status: i + 1 === prev.total ? 'completed' : 'processing',
          }));
        }
      }
    } catch (err) {
      console.error(err);
      setProcessingStatus((prev) => ({ ...prev, status: 'error' }));
    } finally {
      // Limpieza visual
      setTimeout(() => setProcessingStatus(null), 2500);
    }
  };

  const handleExport = async (format, employeeIds) => {
    if (!Array.isArray(employeeIds) || employeeIds.length === 0) return;
    const { start, end } = getWeekRange();

    // Si el service tiene export real:
    if (typeof payrollService?.exportWeeklyPayroll === 'function') {
      const res = await payrollService.exportWeeklyPayroll(employeeIds, { start, end, format });
      if (res?.ok && res?.data?.blob) {
        const url = URL.createObjectURL(res.data.blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `payroll_${start}_${end}.${format === 'excel' ? 'xlsx' : 'csv'}`;
        a.click();
        URL.revokeObjectURL(url);
        return;
      }
      // fallback a CSV local
    }

    try {
      // Fallback: generar CSV con lo que tengamos (employees + currentAttendance si aplica)
      const header = [
        'employee_id',
        'name',
        'site',
        'daily_wage',
        'period_start',
        'period_end',
        'regular_hours',
        'overtime_hours',
        'gross_total',
        'net_total',
      ].join(',');

      const rows = [];
      for (const id of employeeIds) {
        const emp = employees.find((e) => e.id === id);
        if (!emp) continue;

        // Intentar traer snapshot r√°pido (no bloqueante)
        let snap = null;
        try {
          const r = await payrollService?.getWeeklyEstimation?.(id, start, end);
          if (r?.ok) snap = r?.data;
        } catch {}

        rows.push(
          [
            emp.employeeId,
            JSON.stringify(emp.name || ''),
            JSON.stringify(emp.site || ''),
            emp.dailyWage ?? 0,
            start,
            end,
            snap?.regular_hours ?? '',
            snap?.overtime_hours ?? '',
            snap?.gross_total ?? '',
            snap?.net_total ?? '',
          ].join(',')
        );
      }

      const csv = `${header}\n${rows.join('\n')}`;
      const blob = new Blob([csv], { type: 'text/csv;charset=utf-8;' });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `payroll_${start}_${end}.csv`;
      a.click();
      URL.revokeObjectURL(url);
    } catch (e) {
      console.error('Export error:', e);
    }
  };

  const handleSaveAll = async () => {
    // Si guardas ajustes o confirmas per√≠odos, hazlo aqu√≠
    // Ej: await payrollService?.commitWeek?.(getWeekRange())
    console.log('Saving all changes...');
  };

  const viewOptions = [
    { id: 'calculation', label: 'Motor de C√°lculo', icon: 'Calculator' },
    { id: 'bulk', label: 'Procesamiento Masivo', icon: 'Users' },
    { id: 'audit', label: 'Registro de Auditor√≠a', icon: 'FileText' },
    { id: 'integration', label: 'Estado de Integraciones', icon: 'Link' },
  ];

  const handleLogout = () => {};
  const handleProfileClick = () => {};
  const handleSiteChange = () => {};

  return (
    <>
      <Helmet>
        <title>Gesti√≥n de N√≥mina - AsistenciaPro</title>
        <meta
          name="description"
          content="Interfaz de c√°lculo y gesti√≥n de n√≥mina para empleados de construcci√≥n"
        />
      </Helmet>

      <div className="min-h-screen bg-background">
        {/* Sidebar */}
        <RoleBasedSidebar isCollapsed={sidebarCollapsed} userRole="admin" />

        {/* Main */}
        <div className={`transition-all duration-300 ${sidebarCollapsed ? 'ml-16' : 'ml-60'} pb-16 md:pb-0`}>
          {/* Header */}
          <header className="bg-card border-b border-border px-6 py-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-4">
                <Button
                  variant="ghost"
                  size="sm"
                  iconName={sidebarCollapsed ? 'ChevronRight' : 'ChevronLeft'}
                  onClick={() => setSidebarCollapsed(!sidebarCollapsed)}
                  className="hidden md:flex"
                />
                <div>
                  <h1 className="text-xl font-semibold text-foreground">Gesti√≥n de N√≥mina</h1>
                  <p className="text-sm text-muted-foreground">C√°lculo y procesamiento de n√≥mina semanal</p>
                </div>
              </div>

              <div className="flex items-center space-x-4">
                <NotificationCenter />
                <UserContextHeader
                  user={{
                    name: 'Administraci√≥n',
                    role: 'Admin',
                    site: 'Oficina Central',
                    avatar: null,
                  }}
                  onLogout={handleLogout}
                  onProfileClick={handleProfileClick}
                  onSiteChange={handleSiteChange}
                />
              </div>
            </div>
          </header>

          {/* Content */}
          <main className="p-6">
            <NavigationBreadcrumb />

            {/* Selector de vista */}
            <div className="mb-6">
              <div className="flex items-center space-x-2 bg-muted p-1 rounded-lg w-fit">
                {viewOptions.map((option) => (
                  <button
                    key={option.id}
                    onClick={() => setActiveView(option.id)}
                    className={`flex items-center space-x-2 px-4 py-2 rounded-md text-sm font-medium transition-all duration-150 ${
                      activeView === option.id
                        ? 'bg-background text-foreground shadow-sm'
                        : 'text-muted-foreground hover:text-foreground hover:bg-background/50'
                    }`}
                  >
                    <Icon name={option.icon} size={16} />
                    <span className="hidden md:inline">{option.label}</span>
                  </button>
                ))}
              </div>
            </div>

            {/* Carga/errores de empleados */}
            {employeesLoading ? <LoadingSpinner /> : null}
            {!employeesLoading && employeesError ? (
              <ErrorBanner message={employeesError} onRetry={loadEmployees} />
            ) : null}

            {/* Layout principal */}
            {!employeesLoading && !employeesError && (
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                {/* Panel Izquierdo */}
                <div className="space-y-6">
                  {activeView === 'calculation' && (
                    <>
                      {calcLoading && <LoadingSpinner />}
                      {!calcLoading && calcError && (
                        <ErrorBanner message={calcError} onRetry={loadSelectedAttendance} />
                      )}
                      {!calcLoading && !calcError && (
                        <PayrollCalculationEngine
                          selectedEmployee={selectedEmployee}
                          onCalculationUpdate={handleCalculationUpdate}
                          attendanceData={
                            currentAttendance
                              ? {
                                  workedDays: currentAttendance?.worked_days ?? null,
                                  overtimeHours: currentAttendance?.overtime_hours ?? null,
                                  regularHours: currentAttendance?.regular_hours ?? null,
                                  grossTotal: currentAttendance?.gross_total ?? null,
                                  netTotal: currentAttendance?.net_total ?? null,
                                }
                              : {}
                          }
                          onSaveAdjustments={handleSaveAdjustments}
                        />
                      )}
                    </>
                  )}

                  {activeView === 'bulk' && (
                    <BulkProcessingTools
                      selectedEmployees={selectedEmployeeIds}
                      onBulkProcess={handleBulkAction}
                      onExport={handleExport}
                      processingStatus={processingStatus}
                    />
                  )}

                  {activeView === 'audit' && <PayrollAuditTrail />}

                  {activeView === 'integration' && (
                    <IntegrationStatusPanel
                      onRefreshStatus={() => console.log('Refreshing status...')}
                      onTestConnection={(id) => console.log('Testing connection:', id)}
                      onSyncData={(id) => console.log('Syncing data:', id)}
                    />
                  )}
                </div>

                {/* Panel Derecho - Grid de Empleados */}
                <div>
                  <EmployeePayrollGrid
                    employees={employees}
                    onEmployeeSelect={handleEmployeeSelect}
                    selectedEmployeeId={selectedEmployee?.id}
                    onBulkAction={(action, ids) => {
                      setSelectedEmployeeIds(ids);
                      if (action !== 'select') {
                        handleBulkAction(action, ids);
                      }
                    }}
                    payrollData={{}} // si tu grid muestra totales, puedes enlazar currentAttendance seg√∫n id
                  />
                </div>
              </div>
            )}

            {/* Barra de acciones r√°pidas */}
            <div className="fixed bottom-4 right-4 md:relative md:bottom-auto md:right-auto md:mt-6">
              <div className="flex items-center space-x-2 bg-card border border-border rounded-lg p-2 shadow-lg">
                <Button variant="outline" size="sm" iconName="Save" onClick={handleSaveAll}>
                  <span className="hidden md:inline">Guardar Todo</span>
                </Button>
                <Button
                  variant="outline"
                  size="sm"
                  iconName="Download"
                  onClick={() => handleExport('excel', selectedEmployeeIds?.length ? selectedEmployeeIds : employees.map(e => e.id))}
                >
                  <span className="hidden md:inline">Exportar</span>
                </Button>
                <Button
                  variant="default"
                  size="sm"
                  iconName="Calculator"
                  onClick={() => handleBulkAction('calculate', employees.map((e) => e.id))}
                >
                  <span className="hidden md:inline">Calcular Todo</span>
                </Button>
              </div>
            </div>

            {/* Ayuda de atajos */}
            <div className="mt-8 p-4 bg-muted rounded-lg">
              <h4 className="text-sm font-semibold text-foreground mb-2">Atajos de Teclado</h4>
              <div className="grid grid-cols-2 md:grid-cols-4 gap-2 text-xs text-muted-foreground">
                <div>
                  <kbd className="px-1 py-0.5 bg-background rounded">Ctrl+1</kbd> Motor de C√°lculo
                </div>
                <div>
                  <kbd className="px-1 py-0.5 bg-background rounded">Ctrl+2</kbd> Procesamiento Masivo
                </div>
                <div>
                  <kbd className="px-1 py-0.5 bg-background rounded">Ctrl+3</kbd> Auditor√≠a
                </div>
                <div>
                  <kbd className="px-1 py-0.5 bg-background rounded">Ctrl+S</kbd> Guardar Todo
                </div>
              </div>
            </div>
          </main>
        </div>
      </div>
    </>
  );
};

export default PayrollCalculationAndManagementInterface;

src/pages/performance-optimization-and-p.content:
import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { Server, Container, Activity, Shield, AlertTriangle, Settings, Monitor, RefreshCw, GitBranch, Zap, Globe, Gauge, CheckCircle, XCircle, RotateCcw, Upload } from 'lucide-react';
import { qualityService } from '../../services/qualityService';



// Import components
import DeploymentPipelineControls from './components/DeploymentPipelineControls';
import PerformanceMetricsDashboard from './components/PerformanceMetricsDashboard';
import DockerContainerStatus from './components/DockerContainerStatus';
import NginxConfigurationStatus from './components/NginxConfigurationStatus';
import SecurityHeadersValidation from './components/SecurityHeadersValidation';
import FeatureFlagManagement from './components/FeatureFlagManagement';
import StagingEnvironmentControls from './components/StagingEnvironmentControls';
import RealtimeStabilityMonitor from './components/RealtimeStabilityMonitor';
import VirtualizationControls from './components/VirtualizationControls';
import Icon from '@/components/AppIcon';


const PerformanceOptimizationAndProductionDeploymentCenter = () => {
  const { user, userProfile, loading: authLoading } = useAuth();
  const [activeTab, setActiveTab] = useState('pipeline');
  const [performanceData, setPerformanceData] = useState(null);
  const [deploymentStats, setDeploymentStats] = useState({
    environment: 'staging',
    lastDeploy: null,
    buildStatus: 'idle',
    containers: { active: 0, total: 0 },
    performance: { score: 0, issues: 0 },
    security: { score: 0, warnings: 0 },
    realtime: { connections: 0, status: 'idle' }
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [alerts, setAlerts] = useState([]);

  // Load deployment and performance data
  const loadDeploymentData = useCallback(async () => {
    if (!user || !userProfile) return;

    setLoading(true);
    setError(null);

    try {
      // Load quality metrics
      const qualityRes = await qualityService();
      if (qualityRes?.ok) {
        setPerformanceData(qualityRes?.data);
      }

      // Mock deployment stats - in production this would come from actual deployment APIs
      await new Promise(resolve => setTimeout(resolve, 1200));

      setDeploymentStats({
        environment: process.env?.NODE_ENV || 'development',
        lastDeploy: new Date(Date.now() - 1800000)?.toISOString(),
        buildStatus: 'success',
        containers: { active: 8, total: 12 },
        performance: { score: 91, issues: 3 },
        security: { score: 96, warnings: 2 },
        realtime: { connections: 45, status: 'active' }
      });

      // Generate performance alerts
      const performanceAlerts = [];

      if (performanceData?.performance?.bundleAnalysis?.duplicates) {
        const duplicateSize = parseInt(performanceData?.performance?.bundleAnalysis?.duplicates) || 0;
        if (duplicateSize > 30) {
          performanceAlerts?.push({
            id: 'bundle-duplicates',
            type: 'warning',
            severity: 'medium',
            title: 'Bundle Optimization',
            message: `${duplicateSize}KB of duplicate code detected in bundle`,
            timestamp: new Date()?.toISOString(),
            category: 'performance'
          });
        }
      }

      if (performanceData?.securityScan?.vulnerabilities?.high > 0) {
        performanceAlerts?.push({
          id: 'security-high',
          type: 'error',
          severity: 'high',
          title: 'Security Alert',
          message: `${performanceData?.securityScan?.vulnerabilities?.high} high severity vulnerabilities found`,
          timestamp: new Date()?.toISOString(),
          category: 'security'
        });
      }

      if (performanceData?.performance?.lighthouse?.performance < 90) {
        performanceAlerts?.push({
          id: 'lighthouse-performance',
          type: 'warning',
          severity: 'medium',
          title: 'Performance Score',
          message: `Lighthouse performance score is ${performanceData?.performance?.lighthouse?.performance}/100`,
          timestamp: new Date()?.toISOString(),
          category: 'performance'
        });
      }

      setAlerts(performanceAlerts);

    } catch (err) {
      setError('Failed to load deployment data: ' + (err?.message || 'Unknown error'));
      console.error('Deployment data loading error:', err);
    } finally {
      setLoading(false);
    }
  }, [user, userProfile, performanceData?.performance?.bundleAnalysis?.duplicates, performanceData?.securityScan?.vulnerabilities?.high, performanceData?.performance?.lighthouse?.performance]);

  useEffect(() => {
    loadDeploymentData();
  }, [loadDeploymentData]);

  // Tab configuration
  const tabs = useMemo(() => [
    { id: 'pipeline', name: 'Deployment Pipeline', icon: GitBranch },
    { id: 'performance', name: 'Performance Metrics', icon: Gauge },
    { id: 'docker', name: 'Container Status', icon: Container },
    { id: 'nginx', name: 'Nginx Config', icon: Server },
    { id: 'security', name: 'Security Validation', icon: Shield },
    { id: 'flags', name: 'Feature Flags', icon: Settings },
    { id: 'staging', name: 'Staging Control', icon: Globe },
    { id: 'realtime', name: 'Realtime Monitor', icon: Activity },
    { id: 'virtualization', name: 'Virtualization', icon: Monitor }
  ], []);

  // Quick action handlers
  const handleQuickAction = useCallback(async (action) => {
    try {
      switch (action) {
        case 'deploy-staging':
          setDeploymentStats(prev => ({ ...prev, buildStatus: 'building' }));
          await new Promise(resolve => setTimeout(resolve, 3000));
          setDeploymentStats(prev => ({ 
            ...prev, 
            buildStatus: 'success',
            lastDeploy: new Date()?.toISOString()
          }));
          break;
        case 'run-tests':
          setDeploymentStats(prev => ({ ...prev, buildStatus: 'testing' }));
          await new Promise(resolve => setTimeout(resolve, 2000));
          setDeploymentStats(prev => ({ ...prev, buildStatus: 'success' }));
          break;
        case 'restart-containers':
          setDeploymentStats(prev => ({ 
            ...prev, 
            containers: { ...prev?.containers, active: 0 }
          }));
          await new Promise(resolve => setTimeout(resolve, 2000));
          setDeploymentStats(prev => ({ 
            ...prev, 
            containers: { ...prev?.containers, active: prev?.containers?.total }
          }));
          break;
        case 'optimize-bundles':
          await loadDeploymentData();
          break;
        default:
          console.log(`Action ${action} not implemented`);
      }
    } catch (err) {
      setError(`Failed to execute ${action}: ${err?.message}`);
    }
  }, [loadDeploymentData]);

  // Render tab content
  const renderTabContent = () => {
    const commonProps = { 
      performanceData, 
      deploymentStats, 
      loading, 
      onRefresh: loadDeploymentData 
    };

    switch (activeTab) {
      case 'pipeline':
        return <DeploymentPipelineControls {...commonProps} onAction={handleQuickAction} />;
      case 'performance':
        return <PerformanceMetricsDashboard {...commonProps} />;
      case 'docker':
        return <DockerContainerStatus {...commonProps} />;
      case 'nginx':
        return <NginxConfigurationStatus {...commonProps} />;
      case 'security':
        return <SecurityHeadersValidation {...commonProps} />;
      case 'flags':
        return <FeatureFlagManagement {...commonProps} />;
      case 'staging':
        return <StagingEnvironmentControls {...commonProps} onAction={handleQuickAction} />;
      case 'realtime':
        return <RealtimeStabilityMonitor {...commonProps} />;
      case 'virtualization':
        return <VirtualizationControls {...commonProps} />;
      default:
        return <DeploymentPipelineControls {...commonProps} onAction={handleQuickAction} />;
    }
  };

  if (authLoading || loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="text-center">
          <RefreshCw className="h-8 w-8 animate-spin text-blue-600 mx-auto mb-4" />
          <p className="text-gray-600">Loading Performance Center...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 p-4">
      {/* Header Section */}
      <div className="mb-6">
        <div className="bg-white rounded-lg shadow-sm border p-6">
          <div className="flex items-center justify-between mb-4">
            <div>
              <h1 className="text-3xl font-bold text-gray-900 mb-2">
                Performance Optimization & Production Deployment Center
              </h1>
              <p className="text-gray-600">
                Enterprise-grade application delivery with automated scaling, performance monitoring, and zero-downtime deployments
              </p>
            </div>
            <div className="flex items-center space-x-4">
              {userProfile?.role === 'superadmin' && (
                <div className="bg-purple-50 px-3 py-1 rounded-full">
                  <span className="text-purple-600 text-sm font-medium">Production Access</span>
                </div>
              )}
              <div className="text-sm text-gray-500">
                Environment: <span className="font-medium capitalize">{deploymentStats?.environment}</span>
              </div>
            </div>
          </div>

          {/* Performance Overview Cards */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
            <div className="bg-green-50 rounded-lg p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-green-600 text-sm font-medium">Performance Score</p>
                  <p className="text-2xl font-bold text-green-900">
                    {performanceData?.performance?.lighthouse?.performance || deploymentStats?.performance?.score}/100
                  </p>
                </div>
                <Gauge className="h-8 w-8 text-green-600" />
              </div>
            </div>

            <div className="bg-blue-50 rounded-lg p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-blue-600 text-sm font-medium">Active Containers</p>
                  <p className="text-2xl font-bold text-blue-900">
                    {deploymentStats?.containers?.active}/{deploymentStats?.containers?.total}
                  </p>
                </div>
                <Container className="h-8 w-8 text-blue-600" />
              </div>
            </div>

            <div className="bg-purple-50 rounded-lg p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-purple-600 text-sm font-medium">Security Score</p>
                  <p className="text-2xl font-bold text-purple-900">{deploymentStats?.security?.score}%</p>
                </div>
                <Shield className="h-8 w-8 text-purple-600" />
              </div>
            </div>

            <div className="bg-yellow-50 rounded-lg p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-yellow-600 text-sm font-medium">Realtime Connections</p>
                  <p className="text-2xl font-bold text-yellow-900">{deploymentStats?.realtime?.connections}</p>
                </div>
                <Activity className="h-8 w-8 text-yellow-600" />
              </div>
            </div>
          </div>

          {/* Quick Actions */}
          <div className="flex flex-wrap gap-2">
            <button
              onClick={() => handleQuickAction('deploy-staging')}
              disabled={deploymentStats?.buildStatus === 'building'}
              className="bg-green-600 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-green-700 transition-colors flex items-center space-x-2 disabled:opacity-50"
            >
              {deploymentStats?.buildStatus === 'building' ? (
                <RefreshCw className="h-4 w-4 animate-spin" />
              ) : (
                <Upload className="h-4 w-4" />
              )}
              <span>Deploy Staging</span>
            </button>
            <button
              onClick={() => handleQuickAction('run-tests')}
              disabled={deploymentStats?.buildStatus === 'testing'}
              className="bg-blue-600 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-blue-700 transition-colors flex items-center space-x-2 disabled:opacity-50"
            >
              {deploymentStats?.buildStatus === 'testing' ? (
                <RefreshCw className="h-4 w-4 animate-spin" />
              ) : (
                <CheckCircle className="h-4 w-4" />
              )}
              <span>Run Tests</span>
            </button>
            <button
              onClick={() => handleQuickAction('restart-containers')}
              className="bg-orange-600 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-orange-700 transition-colors flex items-center space-x-2"
            >
              <RotateCcw className="h-4 w-4" />
              <span>Restart Containers</span>
            </button>
            <button
              onClick={() => handleQuickAction('optimize-bundles')}
              className="bg-purple-600 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-purple-700 transition-colors flex items-center space-x-2"
            >
              <Zap className="h-4 w-4" />
              <span>Optimize Bundles</span>
            </button>
            <button
              onClick={loadDeploymentData}
              className="bg-gray-600 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-gray-700 transition-colors flex items-center space-x-2"
            >
              <RefreshCw className="h-4 w-4" />
              <span>Refresh Data</span>
            </button>
          </div>
        </div>

        {/* Alerts Section */}
        {alerts?.length > 0 && (
          <div className="mt-4 bg-white rounded-lg shadow-sm border p-4">
            <h3 className="text-sm font-medium text-gray-900 mb-3 flex items-center">
              <AlertTriangle className="h-4 w-4 text-yellow-500 mr-2" />
              Performance & Security Alerts ({alerts?.length})
            </h3>
            <div className="space-y-2">
              {alerts?.slice(0, 5)?.map((alert) => (
                <div key={alert?.id} className={`flex items-center justify-between p-3 rounded-md text-sm ${
                  alert?.type === 'error' ? 'bg-red-50 text-red-800' :
                  alert?.type === 'warning' ? 'bg-yellow-50 text-yellow-800' : 'bg-blue-50 text-blue-800'
                }`}>
                  <div className="flex items-center space-x-3">
                    <div className={`w-2 h-2 rounded-full ${
                      alert?.severity === 'high' ? 'bg-red-500' :
                      alert?.severity === 'medium' ? 'bg-yellow-500' : 'bg-blue-500'
                    }`} />
                    <span className="font-medium">{alert?.title}</span>
                    <span>{alert?.message}</span>
                    <span className="text-xs bg-gray-200 px-2 py-1 rounded">
                      {alert?.category}
                    </span>
                  </div>
                  <span className="text-xs opacity-75">
                    {new Date(alert?.timestamp)?.toLocaleTimeString()}
                  </span>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Error Display */}
        {error && (
          <div className="mt-4 bg-red-50 border border-red-200 rounded-lg p-4">
            <div className="flex items-center">
              <XCircle className="h-5 w-5 text-red-500 mr-3" />
              <div>
                <h3 className="text-sm font-medium text-red-800">Error</h3>
                <p className="text-sm text-red-700 mt-1">{error}</p>
              </div>
              <button
                onClick={() => setError(null)}
                className="ml-auto text-red-500 hover:text-red-700"
              >
                <XCircle className="h-4 w-4" />
              </button>
            </div>
          </div>
        )}
      </div>

      {/* Tab Navigation */}
      <div className="bg-white rounded-lg shadow-sm border mb-6">
        <div className="border-b border-gray-200">
          <nav className="flex space-x-8 px-6 overflow-x-auto">
            {tabs?.map((tab) => {
              const Icon = tab?.icon;
              return (
                <button
                  key={tab?.id}
                  onClick={() => setActiveTab(tab?.id)}
                  className={`flex items-center space-x-2 py-4 px-1 border-b-2 font-medium text-sm transition-colors whitespace-nowrap ${
                    activeTab === tab?.id
                      ? 'border-blue-500 text-blue-600' :'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                  }`}
                >
                  <Icon className="h-4 w-4" />
                  <span>{tab?.name}</span>
                </button>
              );
            })}
          </nav>
        </div>

        {/* Tab Content */}
        <div className="p-6">
          {renderTabContent()}
        </div>
      </div>
    </div>
  );
};

export default PerformanceOptimizationAndProductionDeploymentCenter;

