import React, { useState, useEffect } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { useBranding } from '../../components/BrandingProvider';
import BrandedHeader from '../../components/ui/BrandedHeader';
import BrandedFooter from '../../components/ui/BrandedFooter';
import Button from '../../components/ui/Button';
import Input from '../../components/ui/Input';
import Select from '../../components/ui/Select';
import NavigationBreadcrumb from '../../components/ui/NavigationBreadcrumb';
import UserContextHeader from '../../components/ui/UserContextHeader';
import NotificationCenter from '../../components/ui/NotificationCenter';
import { supabase } from '../../lib/supabase';
import { Shield, Users, Key, Activity, Clock, AlertTriangle, Check, X, RefreshCw, Eye, EyeOff } from 'lucide-react';

export default function ProductionAuthenticationManagementSystem() {
  const { user, userProfile, signIn, signUp, signOut, isAdmin, isSuperAdmin } = useAuth();
  const { branding } = useBranding();
  const [activeUsers, setActiveUsers] = useState([]);
  const [sessionStats, setSessionStats] = useState({
    totalSessions: 0,
    activeSessions: 0,
    failedAttempts: 0,
    averageSessionDuration: 0
  });
  const [authLogs, setAuthLogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [notification, setNotification] = useState(null);

  // Authentication form state
  const [showAuthForm, setShowAuthForm] = useState(false);
  const [authMode, setAuthMode] = useState('signin'); // 'signin' or 'signup'
  const [authForm, setAuthForm] = useState({
    email: '',
    password: '',
    fullName: '',
    role: 'user',
    confirmPassword: ''
  });
  const [authLoading, setAuthLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  // User management state
  const [selectedUser, setSelectedUser] = useState(null);
  const [userManagementMode, setUserManagementMode] = useState('view'); // 'view', 'edit', 'create'

  const breadcrumbItems = [
    { label: 'Sistema', href: '/admin/system' },
    { label: 'Gesti√≥n de Autenticaci√≥n', href: '/production-authentication-management-system' }
  ];

  useEffect(() => {
    if (isAdmin() || isSuperAdmin()) {
      loadAuthenticationData();
      setupRealtimeSubscription();
    }
  }, [user]);

  const loadAuthenticationData = async () => {
    try {
      setLoading(true);
      await Promise.all([
        loadActiveUsers(),
        loadSessionStats(),
        loadAuthLogs()
      ]);
    } catch (error) {
      setNotification({
        type: 'error',
        message: `Error al cargar datos: ${error?.message}`
      });
    } finally {
      setLoading(false);
    }
  };

  const loadActiveUsers = async () => {
    try {
      const { data, error } = await supabase?.from('user_profiles')?.select(`
          id,
          email,
          full_name,
          role,
          created_at,
          last_sign_in_at,
          is_super_admin,
          is_sso_user,
          confirmed_at,
          banned_until
        `)?.order('last_sign_in_at', { ascending: false, nullsFirst: false });

      if (error) throw error;
      setActiveUsers(data || []);
    } catch (error) {
      console.error('Error loading active users:', error);
      setNotification({
        type: 'error',
        message: 'Error al cargar usuarios activos'
      });
    }
  };

  const loadSessionStats = async () => {
    try {
      // Get session statistics from activity logs
      const { data: logs, error } = await supabase?.from('logs_actividad')?.select('accion, created_at')?.in('accion', ['login', 'logout', 'failed_login'])?.gte('created_at', new Date(Date.now() - 24 * 60 * 60 * 1000)?.toISOString());

      if (error) throw error;

      const totalSessions = logs?.filter(log => log?.accion === 'login')?.length || 0;
      const failedAttempts = logs?.filter(log => log?.accion === 'failed_login')?.length || 0;
      const activeSessions = activeUsers?.filter(user => 
        user?.last_sign_in_at && 
        new Date(user?.last_sign_in_at) > new Date(Date.now() - 8 * 60 * 60 * 1000)
      )?.length || 0;

      setSessionStats({
        totalSessions,
        activeSessions,
        failedAttempts,
        averageSessionDuration: totalSessions > 0 ? 4.2 : 0 // Mock average in hours
      });
    } catch (error) {
      console.error('Error loading session stats:', error);
    }
  };

  const loadAuthLogs = async () => {
    try {
      const { data, error } = await supabase?.from('logs_actividad')?.select(`
          id,
          accion,
          descripcion,
          created_at,
          usuario_id,
          user_profiles:usuario_id (
            full_name,
            email
          )
        `)?.in('modulo', ['Authentication', 'Security'])?.order('created_at', { ascending: false })?.limit(50);

      if (error) throw error;
      setAuthLogs(data || []);
    } catch (error) {
      console.error('Error loading auth logs:', error);
    }
  };

  const setupRealtimeSubscription = () => {
    const channel = supabase?.channel('auth_monitoring')?.on(
        'postgres_changes',
        { event: '*', schema: 'public', table: 'logs_actividad' },
        (payload) => {
          if (['Authentication', 'Security']?.includes(payload?.new?.modulo)) {
            loadAuthLogs();
            loadSessionStats();
          }
        }
      )?.on(
        'postgres_changes',
        { event: '*', schema: 'public', table: 'user_profiles' },
        () => {
          loadActiveUsers();
        }
      )?.subscribe();

    return () => supabase?.removeChannel(channel);
  };

  const handleAuthSubmit = async (e) => {
    e?.preventDefault();
    if (!authForm?.email || !authForm?.password) {
      setNotification({
        type: 'error',
        message: 'Por favor completa todos los campos requeridos'
      });
      return;
    }

    if (authMode === 'signup' && authForm?.password !== authForm?.confirmPassword) {
      setNotification({
        type: 'error',
        message: 'Las contrase√±as no coinciden'
      });
      return;
    }

    try {
      setAuthLoading(true);
      let result;

      if (authMode === 'signin') {
        result = await signIn(authForm?.email, authForm?.password);
      } else {
        result = await signUp(authForm?.email, authForm?.password, authForm?.fullName, authForm?.role);
      }

      if (result?.success) {
        setNotification({
          type: 'success',
          message: authMode === 'signin' ? 'Inicio de sesi√≥n exitoso' : 'Usuario registrado exitosamente'
        });
        setShowAuthForm(false);
        setAuthForm({
          email: '',
          password: '',
          fullName: '',
          role: 'user',
          confirmPassword: ''
        });
        await loadAuthenticationData();
      } else {
        setNotification({
          type: 'error',
          message: result?.error || 'Error en la autenticaci√≥n'
        });
      }
    } catch (error) {
      setNotification({
        type: 'error',
        message: error?.message || 'Error inesperado'
      });
    } finally {
      setAuthLoading(false);
    }
  };

  const handleUserAction = async (userId, action) => {
    try {
      const userToUpdate = activeUsers?.find(u => u?.id === userId);
      if (!userToUpdate) return;

      let updateData = {};
      let successMessage = '';

      switch (action) {
        case 'ban':
          updateData = { banned_until: new Date(Date.now() + 24 * 60 * 60 * 1000)?.toISOString() };
          successMessage = 'Usuario bloqueado por 24 horas';
          break;
        case 'unban':
          updateData = { banned_until: null };
          successMessage = 'Usuario desbloqueado';
          break;
        case 'activate':
          updateData = { confirmed_at: new Date()?.toISOString() };
          successMessage = 'Usuario activado';
          break;
        case 'deactivate':
          updateData = { confirmed_at: null };
          successMessage = 'Usuario desactivado';
          break;
        default:
          return;
      }

      const { error } = await supabase?.from('user_profiles')?.update(updateData)?.eq('id', userId);

      if (error) throw error;

      setNotification({
        type: 'success',
        message: successMessage
      });

      await loadActiveUsers();
    } catch (error) {
      setNotification({
        type: 'error',
        message: `Error al actualizar usuario: ${error?.message}`
      });
    }
  };

  const getStatusBadge = (user) => {
    if (user?.banned_until && new Date(user?.banned_until) > new Date()) {
      return <span className="px-2 py-1 text-xs bg-red-100 text-red-800 rounded-full">Bloqueado</span>;
    }
    if (!user?.confirmed_at) {
      return <span className="px-2 py-1 text-xs bg-yellow-100 text-yellow-800 rounded-full">Pendiente</span>;
    }
    if (user?.last_sign_in_at && new Date(user?.last_sign_in_at) > new Date(Date.now() - 30 * 60 * 1000)) {
      return <span className="px-2 py-1 text-xs bg-green-100 text-green-800 rounded-full">En l√≠nea</span>;
    }
    return <span className="px-2 py-1 text-xs bg-gray-100 text-gray-800 rounded-full">Inactivo</span>;
  };

  const getRoleBadge = (role, isSuperAdmin) => {
    if (isSuperAdmin) {
      return <span className="px-2 py-1 text-xs bg-purple-100 text-purple-800 rounded-full font-medium">SuperAdmin</span>;
    }
    const roleColors = {
      admin: 'bg-blue-100 text-blue-800',
      supervisor: 'bg-orange-100 text-orange-800',
      user: 'bg-gray-100 text-gray-800'
    };
    return (
      <span className={`px-2 py-1 text-xs rounded-full ${roleColors?.[role] || roleColors?.user}`}>
        {role?.charAt(0)?.toUpperCase() + role?.slice(1)}
      </span>
    );
  };

  const formatTimeAgo = (timestamp) => {
    if (!timestamp) return 'Nunca';
    const now = new Date();
    const time = new Date(timestamp);
    const diffInMinutes = Math.floor((now - time) / (1000 * 60));

    if (diffInMinutes < 1) return 'Ahora';
    if (diffInMinutes < 60) return `Hace ${diffInMinutes} min`;
    if (diffInMinutes < 1440) return `Hace ${Math.floor(diffInMinutes / 60)} h`;
    return `Hace ${Math.floor(diffInMinutes / 1440)} d√≠as`;
  };

  if (!isAdmin() && !isSuperAdmin()) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <AlertTriangle className="mx-auto h-12 w-12 text-red-500 mb-4" />
          <h1 className="text-2xl font-bold text-gray-900 mb-2">Acceso Denegado</h1>
          <p className="text-gray-600">No tienes permisos para acceder a esta secci√≥n.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <BrandedHeader />
      <UserContextHeader />
      <NavigationBreadcrumb items={breadcrumbItems} />
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Page Header */}
        <div className="mb-8">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold text-gray-900 flex items-center gap-3">
                <Shield className="h-8 w-8" style={{ color: branding?.color_primario }} />
                Sistema de Gesti√≥n de Autenticaci√≥n
              </h1>
              <p className="text-gray-600 mt-2">
                Control de acceso, monitoreo de sesiones y gesti√≥n de seguridad
              </p>
            </div>
            <div className="flex gap-3">
              <Button
                onClick={() => setShowAuthForm(true)}
                className="flex items-center gap-2"
                style={{ backgroundColor: branding?.color_primario }}
              >
                <Users className="h-4 w-4" />
                Nuevo Usuario
              </Button>
              <Button
                variant="outline"
                onClick={loadAuthenticationData}
                disabled={loading}
                className="flex items-center gap-2"
              >
                <RefreshCw className={`h-4 w-4 ${loading ? 'animate-spin' : ''}`} />
                Actualizar
              </Button>
            </div>
          </div>
        </div>

        {/* Statistics Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Sesiones Activas</p>
                <p className="text-2xl font-bold text-gray-900">{sessionStats?.activeSessions}</p>
              </div>
              <div className="bg-green-100 p-3 rounded-full">
                <Activity className="h-6 w-6 text-green-600" />
              </div>
            </div>
            <p className="text-xs text-gray-500 mt-2">Usuarios conectados en las √∫ltimas 8h</p>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Total Usuarios</p>
                <p className="text-2xl font-bold text-gray-900">{activeUsers?.length}</p>
              </div>
              <div className="bg-blue-100 p-3 rounded-full">
                <Users className="h-6 w-6 text-blue-600" />
              </div>
            </div>
            <p className="text-xs text-gray-500 mt-2">Usuarios registrados en el sistema</p>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Intentos Fallidos</p>
                <p className="text-2xl font-bold text-gray-900">{sessionStats?.failedAttempts}</p>
              </div>
              <div className="bg-red-100 p-3 rounded-full">
                <AlertTriangle className="h-6 w-6 text-red-600" />
              </div>
            </div>
            <p className="text-xs text-gray-500 mt-2">√öltimas 24 horas</p>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Duraci√≥n Promedio</p>
                <p className="text-2xl font-bold text-gray-900">{sessionStats?.averageSessionDuration?.toFixed(1)}h</p>
              </div>
              <div className="bg-purple-100 p-3 rounded-full">
                <Clock className="h-6 w-6 text-purple-600" />
              </div>
            </div>
            <p className="text-xs text-gray-500 mt-2">Duraci√≥n de sesi√≥n promedio</p>
          </div>
        </div>

        {/* Main Content Grid */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* User Management Panel */}
          <div className="lg:col-span-2 bg-white rounded-lg shadow">
            <div className="px-6 py-4 border-b">
              <h2 className="text-xl font-bold text-gray-900">Usuarios Activos</h2>
              <p className="text-sm text-gray-600">Monitoreo y gesti√≥n de usuarios</p>
            </div>
            <div className="p-6">
              {loading ? (
                <div className="flex items-center justify-center py-8">
                  <RefreshCw className="h-6 w-6 animate-spin text-gray-400" />
                  <span className="ml-2 text-gray-600">Cargando usuarios...</span>
                </div>
              ) : (
                <div className="space-y-4">
                  {activeUsers?.map((user) => (
                    <div key={user?.id} className="flex items-center justify-between p-4 border rounded-lg hover:bg-gray-50">
                      <div className="flex items-center space-x-4">
                        <div className="flex-shrink-0">
                          <div className="h-10 w-10 bg-gray-200 rounded-full flex items-center justify-center">
                            <span className="text-sm font-medium text-gray-700">
                              {user?.full_name?.charAt(0)?.toUpperCase() || user?.email?.charAt(0)?.toUpperCase()}
                            </span>
                          </div>
                        </div>
                        <div className="min-w-0 flex-1">
                          <p className="text-sm font-medium text-gray-900">{user?.full_name || 'Sin nombre'}</p>
                          <p className="text-sm text-gray-500">{user?.email}</p>
                          <div className="flex items-center space-x-2 mt-1">
                            {getStatusBadge(user)}
                            {getRoleBadge(user?.role, user?.is_super_admin)}
                          </div>
                        </div>
                        <div className="text-sm text-gray-500">
                          <p>√öltimo acceso:</p>
                          <p>{formatTimeAgo(user?.last_sign_in_at)}</p>
                        </div>
                      </div>
                      <div className="flex items-center space-x-2">
                        {user?.banned_until && new Date(user?.banned_until) > new Date() ? (
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={() => handleUserAction(user?.id, 'unban')}
                            className="text-green-600 border-green-600 hover:bg-green-50"
                          >
                            <Check className="h-4 w-4" />
                            Desbloquear
                          </Button>
                        ) : (
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={() => handleUserAction(user?.id, 'ban')}
                            className="text-red-600 border-red-600 hover:bg-red-50"
                          >
                            <X className="h-4 w-4" />
                            Bloquear
                          </Button>
                        )}
                      </div>
                    </div>
                  ))}
                  {activeUsers?.length === 0 && (
                    <div className="text-center py-8">
                      <Users className="mx-auto h-12 w-12 text-gray-400 mb-4" />
                      <p className="text-gray-600">No hay usuarios registrados</p>
                    </div>
                  )}
                </div>
              )}
            </div>
          </div>

          {/* Activity Log Panel */}
          <div className="bg-white rounded-lg shadow">
            <div className="px-6 py-4 border-b">
              <h2 className="text-xl font-bold text-gray-900">Registro de Actividad</h2>
              <p className="text-sm text-gray-600">Eventos de seguridad recientes</p>
            </div>
            <div className="p-6">
              <div className="space-y-4 max-h-96 overflow-y-auto">
                {authLogs?.map((log) => (
                  <div key={log?.id} className="flex items-start space-x-3 p-3 border rounded-lg">
                    <div className="flex-shrink-0">
                      {log?.accion === 'login' && <Check className="h-5 w-5 text-green-600" />}
                      {log?.accion === 'logout' && <X className="h-5 w-5 text-gray-600" />}
                      {log?.accion === 'failed_login' && <AlertTriangle className="h-5 w-5 text-red-600" />}
                      {!['login', 'logout', 'failed_login']?.includes(log?.accion) && <Key className="h-5 w-5 text-blue-600" />}
                    </div>
                    <div className="min-w-0 flex-1">
                      <p className="text-sm font-medium text-gray-900">
                        {log?.user_profiles?.full_name || 'Usuario desconocido'}
                      </p>
                      <p className="text-sm text-gray-600">{log?.descripcion}</p>
                      <p className="text-xs text-gray-500 mt-1">
                        {formatTimeAgo(log?.created_at)}
                      </p>
                    </div>
                  </div>
                ))}
                {authLogs?.length === 0 && (
                  <div className="text-center py-8">
                    <Activity className="mx-auto h-12 w-12 text-gray-400 mb-4" />
                    <p className="text-gray-600">No hay registros de actividad</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </main>
      {/* Authentication Form Modal */}
      {showAuthForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg shadow-xl max-w-md w-full mx-4">
            <div className="px-6 py-4 border-b">
              <div className="flex items-center justify-between">
                <h2 className="text-xl font-bold text-gray-900">
                  {authMode === 'signin' ? 'Iniciar Sesi√≥n' : 'Registrar Usuario'}
                </h2>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => setShowAuthForm(false)}
                >
                  <X className="h-4 w-4" />
                </Button>
              </div>
            </div>
            <form onSubmit={handleAuthSubmit} className="p-6 space-y-4">
              <div className="flex border-b">
                <button
                  type="button"
                  onClick={() => setAuthMode('signin')}
                  className={`px-4 py-2 text-sm font-medium ${
                    authMode === 'signin' ?'text-blue-600 border-b-2 border-blue-600' :'text-gray-600'
                  }`}
                >
                  Iniciar Sesi√≥n
                </button>
                <button
                  type="button"
                  onClick={() => setAuthMode('signup')}
                  className={`px-4 py-2 text-sm font-medium ${
                    authMode === 'signup' ?'text-blue-600 border-b-2 border-blue-600' :'text-gray-600'
                  }`}
                >
                  Registrar
                </button>
              </div>

              {authMode === 'signup' && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Nombre Completo
                  </label>
                  <Input
                    type="text"
                    value={authForm?.fullName}
                    onChange={(e) => setAuthForm({...authForm, fullName: e?.target?.value})}
                    placeholder="Nombre completo"
                    required
                  />
                </div>
              )}

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Correo Electr√≥nico
                </label>
                <Input
                  type="email"
                  value={authForm?.email}
                  onChange={(e) => setAuthForm({...authForm, email: e?.target?.value})}
                  placeholder="usuario@ejemplo.com"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Contrase√±a
                </label>
                <div className="relative">
                  <Input
                    type={showPassword ? 'text' : 'password'}
                    value={authForm?.password}
                    onChange={(e) => setAuthForm({...authForm, password: e?.target?.value})}
                    placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
                    required
                  />
                  <button
                    type="button"
                    onClick={() => setShowPassword(!showPassword)}
                    className="absolute right-3 top-1/2 transform -translate-y-1/2"
                  >
                    {showPassword ? <EyeOff className="h-4 w-4 text-gray-400" /> : <Eye className="h-4 w-4 text-gray-400" />}
                  </button>
                </div>
              </div>

              {authMode === 'signup' && (
                <>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Confirmar Contrase√±a
                    </label>
                    <Input
                      type="password"
                      value={authForm?.confirmPassword}
                      onChange={(e) => setAuthForm({...authForm, confirmPassword: e?.target?.value})}
                      placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
                      required
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Rol
                    </label>
                    <Select
                      value={authForm?.role}
                      onChange={(e) => setAuthForm({...authForm, role: e?.target?.value})}
                      options={[
                        { value: 'user', label: 'Usuario' },
                        { value: 'supervisor', label: 'Supervisor' },
                        { value: 'admin', label: 'Administrador' }
                      ]}
                    />
                  </div>
                </>
              )}

              <div className="flex justify-end space-x-3 pt-4">
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => setShowAuthForm(false)}
                >
                  Cancelar
                </Button>
                <Button
                  type="submit"
                  disabled={authLoading}
                  style={{ backgroundColor: branding?.color_primario }}
                >
                  {authLoading && <RefreshCw className="h-4 w-4 animate-spin mr-2" />}
                  {authMode === 'signin' ? 'Iniciar Sesi√≥n' : 'Registrar'}
                </Button>
              </div>
            </form>
          </div>
        </div>
      )}
      {/* Notification */}
      <NotificationCenter
        notification={notification}
        onClose={() => setNotification(null)}
      />
      <BrandedFooter />
    </div>
  );
}

src/pages/production-data-services-and-e.content:
import React, { useState, useEffect, useCallback } from 'react';
import { Database, Activity, TrendingUp, AlertTriangle, RefreshCw, CheckCircle, XCircle, Clock, BarChart3, Server } from 'lucide-react';

// Import existing components
import { useBranding } from '@/hooks/useBranding';
import BrandedHeader from '@/components/ui/BrandedHeader';
import Loading from '@/components/ui/Loading';
import ErrorState from '@/components/ui/ErrorState';
import Button from '@/components/ui/Button';

// Import services for monitoring
import { authService } from '@/services/authService';
import { employeeService } from '@/services/employeeService';
import { attendanceService } from '@/services/attendanceService';
import { payrollService } from '@/services/payrollService';
import { incidentService } from '@/services/incidentService';
import { constructionSiteService } from '@/services/constructionSiteService';

const ProductionDataServicesManagementConsole = () => {
  const { branding, loading: brandingLoading } = useBranding();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('overview');
  const [refreshing, setRefreshing] = useState(false);

  // State for service monitoring
  const [serviceHealth, setServiceHealth] = useState({});
  const [errorLogs, setErrorLogs] = useState([]);
  const [performanceMetrics, setPerformanceMetrics] = useState({});
  const [connectionStatus, setConnectionStatus] = useState({});
  const [mockDataStatus, setMockDataStatus] = useState({});

  // Real-time monitoring intervals
  const [monitoringInterval, setMonitoringInterval] = useState(null);

  // Service health check function
  const checkServiceHealth = useCallback(async () => {
    const services = [
      { name: 'Authentication', service: authService, method: 'testConnection' },
      { name: 'Employees', service: employeeService, method: 'listEmployees' },
      { name: 'Attendance', service: attendanceService, method: 'getTodayAttendance' },
      { name: 'Payroll', service: payrollService, method: 'getPayrollSummary' },
      { name: 'Incidents', service: incidentService, method: 'listIncidents' },
      { name: 'Sites', service: constructionSiteService, method: 'listSites' }
    ];

    const healthStatus = {};
    const metrics = {};
    const connections = {};

    for (const { name, service, method } of services) {
      const startTime = Date.now();
      try {
        let result;
        if (method === 'testConnection') {
          result = await service?.[method]();
        } else if (method === 'listEmployees') {
          result = await service?.[method]({ page: 1, pageSize: 1 });
        } else if (method === 'getTodayAttendance') {
          result = await service?.[method]('test-id');
        } else if (method === 'getPayrollSummary') {
          result = await service?.[method]({ startDate: new Date(), endDate: new Date() });
        } else if (method === 'listIncidents') {
          result = await service?.[method]({ page: 1, pageSize: 1 });
        } else if (method === 'listSites') {
          result = await service?.[method]({ activeOnly: true });
        }

        const responseTime = Date.now() - startTime;

        healthStatus[name] = {
          status: result?.ok ? 'healthy' : 'error',
          responseTime,
          error: result?.error || null,
          lastCheck: new Date()?.toISOString()
        };

        metrics[name] = {
          responseTime,
          successRate: result?.ok ? 100 : 0,
          errorCount: result?.ok ? 0 : 1
        };

        connections[name] = {
          connected: !!result?.ok,
          latency: responseTime
        };

      } catch (err) {
        const responseTime = Date.now() - startTime;

        healthStatus[name] = {
          status: 'critical',
          responseTime,
          error: err?.message || 'Service unavailable',
          lastCheck: new Date()?.toISOString()
        };

        metrics[name] = {
          responseTime,
          successRate: 0,
          errorCount: 1
        };

        connections[name] = {
          connected: false,
          latency: responseTime
        };
      }
    }

    setServiceHealth(healthStatus);
    setPerformanceMetrics(metrics);
    setConnectionStatus(connections);
  }, []);

  // Mock data detection function
  const checkMockDataStatus = useCallback(async () => {
    const mockPatterns = [
      'MOCK', 'FAKE', 'DUMMY', 'TODO', 'static.rocket.new', 
      'rocket.new', 'localhost', 'test-data', 'sample-data'
    ];

    const mockStatus = {
      detected: false,
      locations: [],
      severity: 'low',
      recommendations: []
    };

    // In a real implementation, this would scan service responses for mock data patterns
    // For now, we'll simulate the check
    try {
      const employeeResult = await employeeService?.listEmployees({ page: 1, pageSize: 10 });
      if (employeeResult?.ok && employeeResult?.data) {
        const employees = employeeResult?.data;
        const mockEmployees = employees?.filter(emp => 
          mockPatterns?.some(pattern => 
            JSON.stringify(emp)?.toLowerCase()?.includes(pattern?.toLowerCase())
          )
        );

        if (mockEmployees?.length > 0) {
          mockStatus.detected = true;
          mockStatus?.locations?.push('Employees Service');
          mockStatus.severity = 'high';
          mockStatus?.recommendations?.push('Remove mock employee data from production');
        }
      }

      setMockDataStatus(mockStatus);
    } catch (err) {
      console.warn('Mock data check failed:', err?.message);
    }
  }, []);

  // Initialize monitoring
  useEffect(() => {
    const initializeMonitoring = async () => {
      setLoading(true);
      try {
        await Promise.all([
          checkServiceHealth(),
          checkMockDataStatus()
        ]);
      } catch (err) {
        setError(err?.message || 'Failed to initialize monitoring');
      } finally {
        setLoading(false);
      }
    };

    initializeMonitoring();

    // Set up real-time monitoring
    const interval = setInterval(() => {
      checkServiceHealth();
      checkMockDataStatus();
    }, 30000); // Every 30 seconds

    setMonitoringInterval(interval);

    return () => {
      if (interval) clearInterval(interval);
    };
  }, [checkServiceHealth, checkMockDataStatus]);

  // Manual refresh function
  const handleRefresh = async () => {
    setRefreshing(true);
    try {
      await Promise.all([
        checkServiceHealth(),
        checkMockDataStatus()
      ]);
    } catch (err) {
      setError(err?.message || 'Refresh failed');
    } finally {
      setRefreshing(false);
    }
  };

  // Service overview component
  const ServiceOverview = () => {
    const totalServices = Object.keys(serviceHealth)?.length;
    const healthyServices = Object.values(serviceHealth)?.filter(s => s?.status === 'healthy')?.length;
    const errorServices = Object.values(serviceHealth)?.filter(s => s?.status === 'error')?.length;
    const criticalServices = Object.values(serviceHealth)?.filter(s => s?.status === 'critical')?.length;

    return (
      <div className="space-y-6">
        {/* Health Summary Cards */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <div className="bg-white rounded-lg border p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600">Total Services</p>
                <p className="text-2xl font-bold text-gray-900">{totalServices}</p>
              </div>
              <Server className="h-8 w-8 text-blue-600" />
            </div>
          </div>

          <div className="bg-white rounded-lg border p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600">Healthy</p>
                <p className="text-2xl font-bold text-green-600">{healthyServices}</p>
              </div>
              <CheckCircle className="h-8 w-8 text-green-600" />
            </div>
          </div>

          <div className="bg-white rounded-lg border p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600">Errors</p>
                <p className="text-2xl font-bold text-yellow-600">{errorServices}</p>
              </div>
              <AlertTriangle className="h-8 w-8 text-yellow-600" />
            </div>
          </div>

          <div className="bg-white rounded-lg border p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600">Critical</p>
                <p className="text-2xl font-bold text-red-600">{criticalServices}</p>
              </div>
              <XCircle className="h-8 w-8 text-red-600" />
            </div>
          </div>
        </div>

        {/* Service Status List */}
        <div className="bg-white rounded-lg border">
          <div className="px-6 py-4 border-b">
            <h3 className="text-lg font-semibold text-gray-900">Service Status</h3>
          </div>
          <div className="divide-y">
            {Object.entries(serviceHealth)?.map(([serviceName, health]) => (
              <div key={serviceName} className="px-6 py-4 flex items-center justify-between">
                <div className="flex items-center space-x-3">
                  <div className={`h-3 w-3 rounded-full ${
                    health?.status === 'healthy' ? 'bg-green-500' :
                    health?.status === 'error' ? 'bg-yellow-500' : 'bg-red-500'
                  }`} />
                  <div>
                    <p className="font-medium text-gray-900">{serviceName}</p>
                    <p className="text-sm text-gray-500">
                      Response: {health?.responseTime}ms
                    </p>
                  </div>
                </div>
                <div className="text-right">
                  <p className={`text-sm font-medium ${
                    health?.status === 'healthy' ? 'text-green-600' :
                    health?.status === 'error' ? 'text-yellow-600' : 'text-red-600'
                  }`}>
                    {health?.status?.toUpperCase()}
                  </p>
                  {health?.error && (
                    <p className="text-xs text-gray-500 max-w-xs truncate">
                      {health?.error}
                    </p>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  };

  // Error tracking component
  const ErrorTracking = () => (
    <div className="space-y-6">
      <div className="bg-white rounded-lg border">
        <div className="px-6 py-4 border-b">
          <h3 className="text-lg font-semibold text-gray-900">Error Monitoring</h3>
        </div>
        <div className="p-6">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
            <div className="bg-red-50 rounded-lg p-4">
              <div className="flex items-center">
                <XCircle className="h-8 w-8 text-red-600 mr-3" />
                <div>
                  <p className="text-sm text-red-600 font-medium">Critical Errors</p>
                  <p className="text-2xl font-bold text-red-900">
                    {Object.values(serviceHealth)?.filter(s => s?.status === 'critical')?.length}
                  </p>
                </div>
              </div>
            </div>

            <div className="bg-yellow-50 rounded-lg p-4">
              <div className="flex items-center">
                <AlertTriangle className="h-8 w-8 text-yellow-600 mr-3" />
                <div>
                  <p className="text-sm text-yellow-600 font-medium">Warnings</p>
                  <p className="text-2xl font-bold text-yellow-900">
                    {Object.values(serviceHealth)?.filter(s => s?.status === 'error')?.length}
                  </p>
                </div>
              </div>
            </div>

            <div className="bg-blue-50 rounded-lg p-4">
              <div className="flex items-center">
                <TrendingUp className="h-8 w-8 text-blue-600 mr-3" />
                <div>
                  <p className="text-sm text-blue-600 font-medium">Avg Response</p>
                  <p className="text-2xl font-bold text-blue-900">
                    {Object.values(serviceHealth)?.length ? 
                      Math.round(Object.values(serviceHealth)?.reduce((sum, s) => sum + (s?.responseTime || 0), 0) / Object.values(serviceHealth)?.length) 
                      : 0}ms
                  </p>
                </div>
              </div>
            </div>
          </div>

          {/* Mock Data Status */}
          {mockDataStatus?.detected && (
            <div className="bg-red-50 border border-red-200 rounded-lg p-4 mb-6">
              <div className="flex items-center mb-2">
                <AlertTriangle className="h-5 w-5 text-red-600 mr-2" />
                <h4 className="text-red-800 font-medium">Mock Data Detected</h4>
              </div>
              <p className="text-red-700 text-sm mb-2">
                Production environment contains mock data that should be removed.
              </p>
              <div className="space-y-1">
                {mockDataStatus?.locations?.map((location, index) => (
                  <p key={index} className="text-red-600 text-xs">‚Ä¢ {location}</p>
                ))}
              </div>
            </div>
          )}

          {/* Service Errors */}
          <div className="space-y-3">
            {Object.entries(serviceHealth)?.filter(([_, health]) => health?.error)?.map(([serviceName, health]) => (
              <div key={serviceName} className="bg-gray-50 rounded-lg p-4">
                <div className="flex items-center justify-between mb-2">
                  <h4 className="font-medium text-gray-900">{serviceName} Service</h4>
                  <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                    health?.status === 'critical' ? 'bg-red-100 text-red-800' : 'bg-yellow-100 text-yellow-800'
                  }`}>
                    {health?.status?.toUpperCase()}
                  </span>
                </div>
                <p className="text-sm text-gray-700 mb-1">{health?.error}</p>
                <p className="text-xs text-gray-500">
                  Last checked: {new Date(health?.lastCheck)?.toLocaleString()}
                </p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );

  // Performance monitoring component
  const PerformanceMonitoring = () => (
    <div className="space-y-6">
      <div className="bg-white rounded-lg border">
        <div className="px-6 py-4 border-b">
          <h3 className="text-lg font-semibold text-gray-900">Performance Metrics</h3>
        </div>
        <div className="p-6">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {Object.entries(performanceMetrics)?.map(([serviceName, metrics]) => (
              <div key={serviceName} className="bg-gray-50 rounded-lg p-4">
                <h4 className="font-medium text-gray-900 mb-3">{serviceName}</h4>
                <div className="space-y-2">
                  <div className="flex justify-between">
                    <span className="text-sm text-gray-600">Response Time</span>
                    <span className="text-sm font-medium">{metrics?.responseTime}ms</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-sm text-gray-600">Success Rate</span>
                    <span className="text-sm font-medium">{metrics?.successRate}%</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-sm text-gray-600">Error Count</span>
                    <span className="text-sm font-medium">{metrics?.errorCount}</span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );

  if (brandingLoading || loading) {
    return <Loading />;
  }

  if (error) {
    return <ErrorState message={error} onRetry={() => window.location?.reload()} />;
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <BrandedHeader 
        title="Production Data Services Management Console"
        subtitle="Enterprise-grade data layer architecture with standardized service patterns and robust error handling"
        icon={<Database className="h-8 w-8" />}
      />
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Controls */}
        <div className="mb-6 flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <Button
              onClick={handleRefresh}
              disabled={refreshing}
              className="flex items-center space-x-2"
            >
              <RefreshCw className={`h-4 w-4 ${refreshing ? 'animate-spin' : ''}`} />
              <span>Refresh</span>
            </Button>
          </div>

          <div className="flex items-center space-x-2 text-sm text-gray-600">
            <Clock className="h-4 w-4" />
            <span>Auto-refresh: 30s</span>
          </div>
        </div>

        {/* Navigation Tabs */}
        <div className="border-b border-gray-200 mb-6">
          <nav className="-mb-px flex space-x-8">
            {[
              { id: 'overview', label: 'Service Overview', icon: Activity },
              { id: 'errors', label: 'Error Tracking', icon: AlertTriangle },
              { id: 'performance', label: 'Performance', icon: BarChart3 }
            ]?.map((tab) => (
              <button
                key={tab?.id}
                onClick={() => setActiveTab(tab?.id)}
                className={`flex items-center space-x-2 py-2 px-1 border-b-2 font-medium text-sm ${
                  activeTab === tab?.id
                    ? 'border-blue-500 text-blue-600' :'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                <tab.icon className="h-4 w-4" />
                <span>{tab?.label}</span>
              </button>
            ))}
          </nav>
        </div>

        {/* Tab Content */}
        <div className="space-y-6">
          {activeTab === 'overview' && <ServiceOverview />}
          {activeTab === 'errors' && <ErrorTracking />}
          {activeTab === 'performance' && <PerformanceMonitoring />}
        </div>
      </div>
    </div>
  );
};

export default ProductionDataServicesManagementConsole;

