import React, { useState, useEffect } from 'react';
import { Helmet } from 'react-helmet';
import { useAuth } from '../../contexts/AuthContext';
import RoleBasedSidebar from '../../components/ui/RoleBasedSidebar';
import NavigationBreadcrumb from '../../components/ui/NavigationBreadcrumb';
import UserContextHeader from '../../components/ui/UserContextHeader';
import NotificationCenter from '../../components/ui/NotificationCenter';

import Icon from '../../components/AppIcon';
import Button from '../../components/ui/Button';
import PayrollEstimationCards from './components/PayrollEstimationCards';
import WeeklyTimeline from './components/WeeklyTimeline';
import PayrollSummaryTable from './components/PayrollSummaryTable';
import ZeroStateDisplay from './components/ZeroStateDisplay';
import { getCurrentWeekPayroll, calculateWeeklyPayroll, bulkCalculatePayroll } from '../../services/payrollService';
import { getEmployeesWithFilters } from '../../services/employeeService';

const RealTimePayrollEstimationDashboardWithZeroStateHandling = () => {
  const { user, userProfile, signOut, hasRole } = useAuth();
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false);
  const [payrollData, setPayrollData] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [calculating, setCalculating] = useState(false);
  const [error, setError] = useState('');
  const [notification, setNotification] = useState('');
  const [selectedEmployees, setSelectedEmployees] = useState([]);
  const [weekSummary, setWeekSummary] = useState({
    totalEmployees: 0,
    totalRegularHours: 0,
    totalOvertimeHours: 0,
    totalGrossPay: 0,
    totalNetPay: 0,
    averageDailyPay: 0
  });

  // Get current week dates
  const getCurrentWeekDates = () => {
    const today = new Date();
    const dayOfWeek = today?.getDay();
    const daysToMonday = dayOfWeek === 0 ? 6 : dayOfWeek - 1;

    const weekStart = new Date(today);
    weekStart?.setDate(today?.getDate() - daysToMonday);
    weekStart?.setHours(0, 0, 0, 0);

    const weekEnd = new Date(weekStart);
    weekEnd?.setDate(weekStart?.getDate() + 6);
    weekEnd?.setHours(23, 59, 59, 999);

    return { weekStart, weekEnd };
  };

  const { weekStart, weekEnd } = getCurrentWeekDates();

  // Load payroll data
  const loadPayrollData = async () => {
    try {
      setLoading(true);
      setError('');

      const data = await getCurrentWeekPayroll();
      setPayrollData(data);

      // Calculate summary
      const summary = data?.reduce((acc, record) => {
        acc.totalEmployees += 1;
        acc.totalRegularHours += parseFloat(record?.regular_hours || 0);
        acc.totalOvertimeHours += parseFloat(record?.overtime_hours || 0);
        acc.totalGrossPay += parseFloat(record?.gross_total || 0);
        acc.totalNetPay += parseFloat(record?.net_total || 0);
        return acc;
      }, {
        totalEmployees: 0,
        totalRegularHours: 0,
        totalOvertimeHours: 0,
        totalGrossPay: 0,
        totalNetPay: 0
      });

      summary.averageDailyPay = summary?.totalEmployees > 0 
        ? summary?.totalGrossPay / summary?.totalEmployees / 7 
        : 0;

      setWeekSummary(summary);
    } catch (err) {
      setError(`Error al cargar datos de n√≥mina: ${err?.message || 'Error desconocido'}`);
      setPayrollData([]);
      setWeekSummary({
        totalEmployees: 0,
        totalRegularHours: 0,
        totalOvertimeHours: 0,
        totalGrossPay: 0,
        totalNetPay: 0,
        averageDailyPay: 0
      });
    } finally {
      setLoading(false);
    }
  };

  // Load active employees
  const loadEmployees = async () => {
    try {
      const employeeData = await getEmployeesWithFilters({
        status: ['active']
      });
      setEmployees(employeeData || []);
    } catch (err) {
      console.error('Error loading employees:', err);
    }
  };

  useEffect(() => {
    loadPayrollData();
    loadEmployees();
  }, []);

  // Auto-refresh every 30 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      if (!loading && !calculating) {
        loadPayrollData();
      }
    }, 30000);

    return () => clearInterval(interval);
  }, [loading, calculating]);

  const handleCalculateAll = async () => {
    if (employees?.length === 0) {
      setNotification('No hay empleados activos para calcular');
      setTimeout(() => setNotification(''), 3000);
      return;
    }

    try {
      setCalculating(true);
      setError('');

      const employeeIds = employees?.map(emp => emp?.id);
      const weekStartStr = weekStart?.toISOString()?.split('T')?.[0];

      const results = await bulkCalculatePayroll(employeeIds, weekStartStr);

      const successful = results?.filter(r => r?.success)?.length;
      const failed = results?.filter(r => !r?.success)?.length;

      if (failed > 0) {
        setNotification(`C√°lculo completado: ${successful} exitosos, ${failed} fallidos`);
      } else {
        setNotification(`N√≥mina calculada para ${successful} empleados`);
      }

      setTimeout(() => setNotification(''), 5000);

      // Refresh data
      await loadPayrollData();
    } catch (err) {
      setError(`Error al calcular n√≥mina: ${err?.message}`);
      setTimeout(() => setError(''), 5000);
    } finally {
      setCalculating(false);
    }
  };

  const handleCalculateEmployee = async (employeeId) => {
    try {
      setCalculating(true);
      const weekStartStr = weekStart?.toISOString()?.split('T')?.[0];

      await calculateWeeklyPayroll(employeeId, weekStartStr);

      setNotification('N√≥mina recalculada');
      setTimeout(() => setNotification(''), 3000);

      await loadPayrollData();
    } catch (err) {
      setError(`Error al recalcular: ${err?.message}`);
      setTimeout(() => setError(''), 5000);
    } finally {
      setCalculating(false);
    }
  };

  const handleExportPayroll = () => {
    const dataToExport = selectedEmployees?.length > 0
      ? payrollData?.filter(record => selectedEmployees?.includes(record?.employee_id))
      : payrollData;

    console.log('Exporting payroll data:', dataToExport);

    setNotification('Exportaci√≥n de n√≥mina iniciada');
    setTimeout(() => setNotification(''), 3000);
  };

  const handleLogout = async () => {
    try {
      await signOut();
    } catch (err) {
      setError(`Error al cerrar sesi√≥n: ${err?.message}`);
    }
  };

  const formatDateRange = (start, end) => {
    const options = { day: 'numeric', month: 'short' };
    return `${start?.toLocaleDateString('es-ES', options)} - ${end?.toLocaleDateString('es-ES', options)}`;
  };

  const hasPayrollData = payrollData?.length > 0;
  const hasActiveEmployees = employees?.length > 0;

  return (
    <>
      <Helmet>
        <title>Estimaci√≥n de N√≥mina en Tiempo Real - AsistenciaPro</title>
        <meta name="description" content="Dashboard de estimaciones de n√≥mina con actualizaciones en tiempo real y manejo de estados vac√≠os" />
      </Helmet>
      <div className="min-h-screen bg-background">
        {/* Notifications */}
        {notification && (
          <div className="fixed top-4 right-4 z-50 bg-green-500 text-white px-4 py-2 rounded-lg shadow-lg">
            {notification}
          </div>
        )}

        {error && (
          <div className="fixed top-4 right-4 z-50 bg-red-500 text-white px-4 py-2 rounded-lg shadow-lg">
            {error}
            <button 
              onClick={() => setError('')}
              className="ml-2 text-white hover:text-gray-200"
            >
              √ó
            </button>
          </div>
        )}

        {/* Sidebar */}
        <RoleBasedSidebar 
          isCollapsed={sidebarCollapsed}
          userRole={userProfile?.role?.toLowerCase()}
        />

        {/* Main Content */}
        <div className={`transition-all duration-300 ${sidebarCollapsed ? 'ml-16' : 'ml-60'} pb-16 md:pb-0`}>
          {/* Header */}
          <header className="bg-card border-b border-border px-6 py-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-4">
                <Button
                  variant="ghost"
                  size="sm"
                  iconName={sidebarCollapsed ? 'ChevronRight' : 'ChevronLeft'}
                  onClick={() => setSidebarCollapsed(!sidebarCollapsed)}
                  className="hidden md:flex"
                />
                <div>
                  <h1 className="text-xl font-semibold text-foreground">
                    Estimaci√≥n de N√≥mina
                  </h1>
                  <p className="text-sm text-muted-foreground">
                    Semana del {formatDateRange(weekStart, weekEnd)}
                  </p>
                </div>

                {/* Real-time indicator */}
                <div className="flex items-center space-x-2">
                  <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse" />
                  <span className="text-xs text-muted-foreground">Tiempo Real</span>
                </div>
              </div>

              <div className="flex items-center space-x-4">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => loadPayrollData()}
                  disabled={loading}
                  iconName={loading ? 'Loader2' : 'RefreshCw'}
                  className={loading ? 'animate-spin' : ''}
                >
                  Actualizar
                </Button>

                <NotificationCenter />
                <UserContextHeader
                  user={{
                    name: userProfile?.full_name || 'Usuario',
                    role: userProfile?.role === 'superadmin' ? 'SuperAdmin' : 
                          userProfile?.role === 'admin' ? 'Admin' : 
                          userProfile?.role?.charAt(0)?.toUpperCase() + userProfile?.role?.slice(1),
                    site: 'Sistema Central'
                  }}
                  onLogout={handleLogout}
                />
              </div>
            </div>
          </header>

          {/* Content */}
          <main className="p-6">
            <NavigationBreadcrumb />

            {/* Quick Actions Bar */}
            <div className="mb-6 flex items-center justify-between">
              <div className="flex items-center space-x-4">
                <div className="text-sm text-muted-foreground">
                  {hasPayrollData ? (
                    <>Mostrando estimaciones para {payrollData?.length} empleados</>
                  ) : hasActiveEmployees ? (
                    <>No hay datos de n√≥mina para esta semana</>
                  ) : (
                    <>No hay empleados activos</>
                  )}
                </div>

                {calculating && (
                  <div className="flex items-center space-x-2 text-blue-600">
                    <div className="w-4 h-4 border-2 border-blue-600 border-t-transparent rounded-full animate-spin" />
                    <span className="text-sm">Calculando...</span>
                  </div>
                )}
              </div>

              <div className="flex items-center space-x-2">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={handleExportPayroll}
                  disabled={!hasPayrollData || calculating}
                  iconName="Download"
                >
                  Exportar
                </Button>

                <Button
                  size="sm"
                  onClick={handleCalculateAll}
                  disabled={!hasActiveEmployees || calculating}
                  iconName="Calculator"
                >
                  {calculating ? 'Calculando...' : 'Calcular Todo'}
                </Button>
              </div>
            </div>

            {/* Main Content Area */}
            {!hasActiveEmployees ? (
              <ZeroStateDisplay 
                type="no-employees"
                title="No hay empleados activos"
                description="Primero debe registrar empleados activos en el sistema para poder calcular estimaciones de n√≥mina."
                actionLabel="Gestionar Empleados"
                onAction={() => window.location.href = '/admin/employees'}
              />
            ) : !hasPayrollData ? (
              <ZeroStateDisplay 
                type="no-data"
                title="No hay datos de asistencia esta semana"
                description="Las estimaciones aparecer√°n autom√°ticamente cuando los empleados registren su asistencia. Mientras tanto, todos los valores se muestran en $0.00 MXN."
                actionLabel="Calcular Estimaciones"
                onAction={handleCalculateAll}
                loading={calculating}
              />
            ) : (
              <div className="space-y-6">
                {/* Estimation Cards */}
                <PayrollEstimationCards 
                  summary={weekSummary}
                  weekStart={weekStart}
                  weekEnd={weekEnd}
                  loading={loading}
                />

                {/* Weekly Timeline */}
                <WeeklyTimeline 
                  weekStart={weekStart}
                  payrollData={payrollData}
                  loading={loading}
                />

                {/* Payroll Summary Table */}
                <PayrollSummaryTable 
                  payrollData={payrollData}
                  selectedEmployees={selectedEmployees}
                  onSelectionChange={setSelectedEmployees}
                  onCalculateEmployee={handleCalculateEmployee}
                  onExportEmployee={(employee) => {
                    setSelectedEmployees([employee?.employee_id]);
                    handleExportPayroll();
                  }}
                  calculating={calculating}
                  loading={loading}
                />
              </div>
            )}

            {/* Currency Format Notice */}
            <div className="mt-8 p-4 bg-blue-50 border border-blue-200 rounded-lg">
              <div className="flex items-center space-x-2">
                <Icon name="Info" className="w-5 h-5 text-blue-600" />
                <div>
                  <p className="text-sm text-blue-800">
                    <strong>Formato de Moneda:</strong> Todos los valores se muestran en Pesos Mexicanos (MXN) con formato local mexicano.
                  </p>
                  <p className="text-xs text-blue-600 mt-1">
                    Las estimaciones se actualizan autom√°ticamente cada 30 segundos basadas en los registros de asistencia m√°s recientes.
                  </p>
                </div>
              </div>
            </div>
          </main>
        </div>
      </div>
    </>
  );
};

export default RealTimePayrollEstimationDashboardWithZeroStateHandling;

src/pages/role-based-access-control-mana.content:
import React, { useState, useEffect } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { Users, Shield, Crown, AlertTriangle, CheckCircle, Search, RefreshCw, Eye } from 'lucide-react';
import BrandedHeader from '../../components/ui/BrandedHeader';
import BrandedFooter from '../../components/ui/BrandedFooter';
import { supabase } from '../../lib/supabase';

export default function RoleBasedAccessControlManagementSystem() {
  const { user, userProfile, isSuperAdmin } = useAuth();
  const [activeTab, setActiveTab] = useState('roles');
  const [users, setUsers] = useState([]);
  const [roles, setRoles] = useState([]);
  const [selectedUsers, setSelectedUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [roleFilter, setRoleFilter] = useState('all');
  const [isLoading, setIsLoading] = useState(true);
  const [isSaving, setSaving] = useState(false);
  const [saveMessage, setSaveMessage] = useState('');
  const [showRoleModal, setShowRoleModal] = useState(false);
  const [editingRole, setEditingRole] = useState(null);
  const [bulkAction, setBulkAction] = useState('');

  const roleHierarchy = [
    {
      id: 1,
      nombre: 'user',
      descripcion: 'Can view profile, history, and notifications',
      level: 1,
      color: 'green',
      permissions: [
        'view_profile',
        'view_history', 
        'view_notifications',
        'check_in_out'
      ]
    },
    {
      id: 2,
      nombre: 'supervisor',
      descripcion: 'Can view and register attendance for their team',
      level: 2,
      color: 'yellow',
      permissions: [
        'view_profile',
        'view_history',
        'view_notifications',
        'check_in_out',
        'manage_team_attendance',
        'view_team_reports',
        'register_incidents'
      ]
    },
    {
      id: 3,
      nombre: 'admin',
      descripcion: 'Access to payroll, reports, and incident management',
      level: 3,
      color: 'red',
      permissions: [
        'view_profile',
        'view_history',
        'view_notifications',
        'check_in_out',
        'manage_team_attendance',
        'view_team_reports',
        'register_incidents',
        'access_payroll',
        'generate_reports',
        'manage_employees',
        'manage_construction_sites',
        'view_all_incidents'
      ]
    },
    {
      id: 4,
      nombre: 'superadmin',
      descripcion: 'Full system access including visual configuration',
      level: 4,
      color: 'purple',
      permissions: [
        'full_system_access',
        'visual_configuration',
        'system_logs',
        'role_management',
        'user_management',
        'database_management',
        'security_configuration',
        'backup_restore',
        'system_monitoring'
      ]
    }
  ];

  useEffect(() => {
    if (!isSuperAdmin?.()) {
      return;
    }
    loadData();
  }, []);

  const loadData = async () => {
    setIsLoading(true);
    try {
      // Load users
      const { data: usersData, error: usersError } = await supabase
        ?.from('usuarios')
        ?.select('*')
        ?.order('created_at', { ascending: false });

      if (usersError) {
        console.error('Error loading users:', usersError);
      } else {
        setUsers(usersData || []);
      }

      // Load roles (from our predefined hierarchy)
      setRoles(roleHierarchy);

      await logActivity('role_management_access', 'Role Management', 'Accessed role-based access control system');

    } catch (error) {
      console.error('Error loading data:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const logActivity = async (action, module, description) => {
    try {
      await supabase?.from('logs_actividad')?.insert({
        usuario_id: user?.id,
        rol: userProfile?.rol,
        accion: action,
        modulo: module,
        descripcion: description
      });
    } catch (error) {
      console.error('Failed to log activity:', error);
    }
  };

  const filteredUsers = users?.filter(user => {
    const matchesSearch = user?.nombre?.toLowerCase()?.includes(searchTerm?.toLowerCase()) ||
                         user?.correo?.toLowerCase()?.includes(searchTerm?.toLowerCase());
    const matchesRole = roleFilter === 'all' || user?.rol === roleFilter;
    return matchesSearch && matchesRole;
  });

  const handleUserRoleUpdate = async (userId, newRole) => {
    setSaving(true);
    setSaveMessage('');

    try {
      const { error } = await supabase
        ?.from('usuarios')
        ?.update({ rol: newRole })
        ?.eq('id', userId);

      if (error) {
        setSaveMessage(`Error: ${error?.message}`);
      } else {
        setSaveMessage(`Role updated successfully!`);
        await loadData();
        await logActivity('role_update', 'Role Management', `Updated user role to ${newRole}`);
      }
    } catch (error) {
      setSaveMessage(`Error: ${error?.message}`);
    } finally {
      setSaving(false);
      setTimeout(() => setSaveMessage(''), 3000);
    }
  };

  const handleBulkRoleUpdate = async () => {
    if (!bulkAction || selectedUsers?.length === 0) {
      setSaveMessage('Please select users and an action');
      return;
    }

    setSaving(true);
    setSaveMessage('');

    try {
      const { error } = await supabase
        ?.from('usuarios')
        ?.update({ rol: bulkAction })
        ?.in('id', selectedUsers);

      if (error) {
        setSaveMessage(`Error: ${error?.message}`);
      } else {
        setSaveMessage(`Bulk role update completed for ${selectedUsers?.length} users!`);
        setSelectedUsers([]);
        setBulkAction('');
        await loadData();
        await logActivity('bulk_role_update', 'Role Management', `Updated ${selectedUsers?.length} users to ${bulkAction} role`);
      }
    } catch (error) {
      setSaveMessage(`Error: ${error?.message}`);
    } finally {
      setSaving(false);
      setTimeout(() => setSaveMessage(''), 3000);
    }
  };

  const getRoleColor = (roleName) => {
    const role = roleHierarchy?.find(r => r?.nombre === roleName);
    return role?.color || 'gray';
  };

  const getRolePermissions = (roleName) => {
    const role = roleHierarchy?.find(r => r?.nombre === roleName);
    return role?.permissions || [];
  };

  const getPermissionCount = (roleName) => {
    return getRolePermissions(roleName)?.length;
  };

  if (!isSuperAdmin?.()) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <Shield className="w-16 h-16 text-red-500 mx-auto mb-4" />
          <h1 className="text-2xl font-bold text-gray-900 mb-2">Access Denied</h1>
          <p className="text-gray-600">Only SuperAdmins can access the Role-Based Access Control Management System</p>
        </div>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="text-center">
          <RefreshCw className="w-8 h-8 animate-spin text-blue-600 mx-auto mb-4" />
          <p className="text-gray-600">Loading role management system...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <BrandedHeader />
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            Role-Based Access Control Management System
          </h1>
          <p className="text-gray-600">
            Manage user roles, permissions, and system security controls
          </p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
          {/* Left Panel - Role Hierarchy (30%) */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
                <Crown className="w-5 h-5 mr-2 text-yellow-600" />
                Role Hierarchy
              </h3>

              <div className="space-y-4">
                {roleHierarchy?.map((role) => (
                  <div
                    key={role?.id}
                    className={`border-l-4 border-${role?.color}-500 bg-${role?.color}-50 p-3 rounded-r-md`}
                  >
                    <div className="flex items-center justify-between mb-2">
                      <span className={`px-2 py-1 rounded text-xs font-medium bg-${role?.color}-100 text-${role?.color}-800`}>
                        Level {role?.level}
                      </span>
                      <span className="text-xs text-gray-500">
                        {getPermissionCount(role?.nombre)} permissions
                      </span>
                    </div>
                    <h4 className="font-medium text-gray-900 capitalize">{role?.nombre}</h4>
                    <p className="text-xs text-gray-600 mt-1">{role?.descripcion}</p>
                  </div>
                ))}
              </div>

              <div className="mt-6 p-4 bg-blue-50 rounded-md">
                <h4 className="font-medium text-blue-900 mb-2">Security Note</h4>
                <p className="text-xs text-blue-700">
                  Higher-level roles inherit all permissions from lower levels. Changes take effect immediately.
                </p>
              </div>
            </div>
          </div>

          {/* Right Panel - Permission Management (70%) */}
          <div className="lg:col-span-3 space-y-6">
            {/* Tab Navigation */}
            <div className="bg-white rounded-lg shadow-sm border border-gray-200">
              <div className="border-b border-gray-200">
                <nav className="flex space-x-8 px-6">
                  <button
                    onClick={() => setActiveTab('roles')}
                    className={`py-4 px-1 border-b-2 font-medium text-sm ${
                      activeTab === 'roles' ?'border-blue-500 text-blue-600' :'border-transparent text-gray-500 hover:text-gray-700'
                    }`}
                  >
                    <Users className="w-4 h-4 inline mr-2" />
                    User Role Assignment
                  </button>
                  <button
                    onClick={() => setActiveTab('permissions')}
                    className={`py-4 px-1 border-b-2 font-medium text-sm ${
                      activeTab === 'permissions' ?'border-blue-500 text-blue-600' :'border-transparent text-gray-500 hover:text-gray-700'
                    }`}
                  >
                    <Shield className="w-4 h-4 inline mr-2" />
                    Permission Matrix
                  </button>
                  <button
                    onClick={() => setActiveTab('audit')}
                    className={`py-4 px-1 border-b-2 font-medium text-sm ${
                      activeTab === 'audit' ?'border-blue-500 text-blue-600' :'border-transparent text-gray-500 hover:text-gray-700'
                    }`}
                  >
                    <Eye className="w-4 h-4 inline mr-2" />
                    Security Audit
                  </button>
                </nav>
              </div>

              <div className="p-6">
                {activeTab === 'roles' && (
                  <div className="space-y-6">
                    {/* Filters and Search */}
                    <div className="flex flex-col sm:flex-row gap-4">
                      <div className="flex-1">
                        <div className="relative">
                          <Search className="w-5 h-5 absolute left-3 top-3 text-gray-400" />
                          <input
                            type="text"
                            placeholder="Search users by name or email..."
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e?.target?.value)}
                            className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                          />
                        </div>
                      </div>

                      <div className="sm:w-48">
                        <select
                          value={roleFilter}
                          onChange={(e) => setRoleFilter(e?.target?.value)}
                          className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                        >
                          <option value="all">All Roles</option>
                          <option value="user">User</option>
                          <option value="supervisor">Supervisor</option>
                          <option value="admin">Admin</option>
                          <option value="superadmin">SuperAdmin</option>
                        </select>
                      </div>
                    </div>

                    {/* Bulk Actions */}
                    {selectedUsers?.length > 0 && (
                      <div className="bg-blue-50 border border-blue-200 rounded-md p-4">
                        <div className="flex items-center justify-between">
                          <span className="text-sm text-blue-700">
                            {selectedUsers?.length} user{selectedUsers?.length !== 1 ? 's' : ''} selected
                          </span>
                          <div className="flex items-center space-x-2">
                            <select
                              value={bulkAction}
                              onChange={(e) => setBulkAction(e?.target?.value)}
                              className="px-3 py-1 text-sm border border-blue-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                            >
                              <option value="">Select Action</option>
                              <option value="user">Change to User</option>
                              <option value="supervisor">Change to Supervisor</option>
                              <option value="admin">Change to Admin</option>
                            </select>
                            <button
                              onClick={handleBulkRoleUpdate}
                              disabled={!bulkAction || isSaving}
                              className="px-3 py-1 text-sm bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50"
                            >
                              Apply
                            </button>
                          </div>
                        </div>
                      </div>
                    )}

                    {/* User Grid */}
                    <div className="overflow-x-auto">
                      <table className="min-w-full bg-white border border-gray-200 rounded-md">
                        <thead className="bg-gray-50">
                          <tr>
                            <th className="px-4 py-3 text-left">
                              <input
                                type="checkbox"
                                onChange={(e) => {
                                  if (e?.target?.checked) {
                                    setSelectedUsers(filteredUsers?.map(u => u?.id));
                                  } else {
                                    setSelectedUsers([]);
                                  }
                                }}
                                checked={selectedUsers?.length === filteredUsers?.length && filteredUsers?.length > 0}
                                className="w-4 h-4 text-blue-600 rounded focus:ring-blue-500"
                              />
                            </th>
                            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                              User
                            </th>
                            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                              Current Role
                            </th>
                            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                              Permissions
                            </th>
                            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                              Actions
                            </th>
                          </tr>
                        </thead>
                        <tbody className="bg-white divide-y divide-gray-200">
                          {filteredUsers?.map((user) => (
                            <tr key={user?.id} className="hover:bg-gray-50">
                              <td className="px-4 py-4">
                                <input
                                  type="checkbox"
                                  checked={selectedUsers?.includes(user?.id)}
                                  onChange={(e) => {
                                    if (e?.target?.checked) {
                                      setSelectedUsers([...selectedUsers, user?.id]);
                                    } else {
                                      setSelectedUsers(selectedUsers?.filter(id => id !== user?.id));
                                    }
                                  }}
                                  className="w-4 h-4 text-blue-600 rounded focus:ring-blue-500"
                                />
                              </td>
                              <td className="px-4 py-4">
                                <div className="flex items-center">
                                  <div className="flex-shrink-0 h-10 w-10">
                                    <div className="h-10 w-10 rounded-full bg-gray-300 flex items-center justify-center">
                                      <Users className="w-5 h-5 text-gray-600" />
                                    </div>
                                  </div>
                                  <div className="ml-4">
                                    <div className="text-sm font-medium text-gray-900">{user?.nombre}</div>
                                    <div className="text-sm text-gray-500">{user?.correo}</div>
                                  </div>
                                </div>
                              </td>
                              <td className="px-4 py-4">
                                <span className={`px-3 py-1 rounded-full text-xs font-medium ${
                                  getRoleColor(user?.rol) === 'purple' ? 'bg-purple-100 text-purple-800' :
                                  getRoleColor(user?.rol) === 'red' ? 'bg-red-100 text-red-800' :
                                  getRoleColor(user?.rol) === 'yellow'? 'bg-yellow-100 text-yellow-800' : 'bg-green-100 text-green-800'
                                }`}>
                                  {user?.rol || 'Not Assigned'}
                                </span>
                              </td>
                              <td className="px-4 py-4">
                                <div className="text-sm text-gray-500">
                                  {getPermissionCount(user?.rol)} permissions
                                </div>
                              </td>
                              <td className="px-4 py-4">
                                <select
                                  value={user?.rol || ''}
                                  onChange={(e) => handleUserRoleUpdate(user?.id, e?.target?.value)}
                                  disabled={isSaving}
                                  className="text-sm border border-gray-300 rounded-md px-2 py-1 focus:outline-none focus:ring-2 focus:ring-blue-500"
                                >
                                  <option value="">Select Role</option>
                                  <option value="user">User</option>
                                  <option value="supervisor">Supervisor</option>
                                  <option value="admin">Admin</option>
                                  <option value="superadmin">SuperAdmin</option>
                                </select>
                              </td>
                            </tr>
                          ))}
                        </tbody>
                      </table>

                      {filteredUsers?.length === 0 && (
                        <div className="text-center py-8 text-gray-500">
                          No users found matching your criteria
                        </div>
                      )}
                    </div>
                  </div>
                )}

                {activeTab === 'permissions' && (
                  <div className="space-y-6">
                    <h3 className="text-lg font-semibold text-gray-900 mb-4">Permission Matrix</h3>

                    <div className="overflow-x-auto">
                      <table className="min-w-full bg-white border border-gray-200 rounded-md">
                        <thead className="bg-gray-50">
                          <tr>
                            <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                              Permission
                            </th>
                            <th className="px-4 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                              User
                            </th>
                            <th className="px-4 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                              Supervisor
                            </th>
                            <th className="px-4 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                              Admin
                            </th>
                            <th className="px-4 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                              SuperAdmin
                            </th>
                          </tr>
                        </thead>
                        <tbody className="bg-white divide-y divide-gray-200">
                          {[
                            'view_profile', 'view_history', 'view_notifications', 'check_in_out',
                            'manage_team_attendance', 'view_team_reports', 'register_incidents',
                            'access_payroll', 'generate_reports', 'manage_employees',
                            'visual_configuration', 'system_logs', 'role_management'
                          ]?.map((permission) => (
                            <tr key={permission} className="hover:bg-gray-50">
                              <td className="px-4 py-4 text-sm font-medium text-gray-900 capitalize">
                                {permission?.replace(/_/g, ' ')}
                              </td>
                              {roleHierarchy?.map((role) => (
                                <td key={role?.nombre} className="px-4 py-4 text-center">
                                  {role?.permissions?.includes(permission) ? (
                                    <CheckCircle className="w-5 h-5 text-green-500 mx-auto" />
                                  ) : (
                                    <div className="w-5 h-5 bg-gray-200 rounded-full mx-auto"></div>
                                  )}
                                </td>
                              ))}
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>
                  </div>
                )}

                {activeTab === 'audit' && (
                  <div className="space-y-6">
                    <h3 className="text-lg font-semibold text-gray-900 mb-4">Security Audit Trail</h3>

                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
                      <div className="bg-green-50 border border-green-200 rounded-md p-4">
                        <div className="flex items-center">
                          <CheckCircle className="w-8 h-8 text-green-500" />
                          <div className="ml-3">
                            <p className="text-sm font-medium text-green-800">Active Users</p>
                            <p className="text-2xl font-bold text-green-900">
                              {users?.filter(u => u?.rol)?.length}
                            </p>
                          </div>
                        </div>
                      </div>

                      <div className="bg-yellow-50 border border-yellow-200 rounded-md p-4">
                        <div className="flex items-center">
                          <AlertTriangle className="w-8 h-8 text-yellow-500" />
                          <div className="ml-3">
                            <p className="text-sm font-medium text-yellow-800">Unassigned</p>
                            <p className="text-2xl font-bold text-yellow-900">
                              {users?.filter(u => !u?.rol)?.length}
                            </p>
                          </div>
                        </div>
                      </div>

                      <div className="bg-blue-50 border border-blue-200 rounded-md p-4">
                        <div className="flex items-center">
                          <Users className="w-8 h-8 text-blue-500" />
                          <div className="ml-3">
                            <p className="text-sm font-medium text-blue-800">Supervisors</p>
                            <p className="text-2xl font-bold text-blue-900">
                              {users?.filter(u => u?.rol === 'supervisor')?.length}
                            </p>
                          </div>
                        </div>
                      </div>

                      <div className="bg-red-50 border border-red-200 rounded-md p-4">
                        <div className="flex items-center">
                          <Crown className="w-8 h-8 text-red-500" />
                          <div className="ml-3">
                            <p className="text-sm font-medium text-red-800">Admins</p>
                            <p className="text-2xl font-bold text-red-900">
                              {users?.filter(u => ['admin', 'superadmin']?.includes(u?.rol))?.length}
                            </p>
                          </div>
                        </div>
                      </div>
                    </div>

                    <div className="bg-white border border-gray-200 rounded-md p-6">
                      <h4 className="font-medium text-gray-900 mb-4">Security Recommendations</h4>
                      <div className="space-y-3">
                        <div className="flex items-start space-x-3">
                          <CheckCircle className="w-5 h-5 text-green-500 mt-0.5" />
                          <div>
                            <p className="text-sm font-medium text-gray-900">Role-based access control active</p>
                            <p className="text-sm text-gray-600">All users have appropriate role assignments</p>
                          </div>
                        </div>

                        {users?.filter(u => !u?.rol)?.length > 0 && (
                          <div className="flex items-start space-x-3">
                            <AlertTriangle className="w-5 h-5 text-yellow-500 mt-0.5" />
                            <div>
                              <p className="text-sm font-medium text-gray-900">Unassigned users detected</p>
                              <p className="text-sm text-gray-600">
                                {users?.filter(u => !u?.rol)?.length} users need role assignment
                              </p>
                            </div>
                          </div>
                        )}

                        <div className="flex items-start space-x-3">
                          <CheckCircle className="w-5 h-5 text-green-500 mt-0.5" />
                          <div>
                            <p className="text-sm font-medium text-gray-900">Activity logging enabled</p>
                            <p className="text-sm text-gray-600">All role changes are being tracked</p>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                )}

                {saveMessage && (
                  <div className={`p-3 rounded-md text-sm ${
                    saveMessage?.includes('Error') 
                      ? 'bg-red-50 text-red-700 border border-red-200' :'bg-green-50 text-green-700 border border-green-200'
                  }`}>
                    {saveMessage}
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
      <BrandedFooter />
    </div>
  );
}

