import React, { useState, useMemo } from 'react';
import { AlertTriangle, Shield, XCircle, Clock, TrendingUp, Eye } from 'lucide-react';

export default function SecurityAlertPanel({ logs = [] }) {
  const [selectedSeverity, setSelectedSeverity] = useState('all');

  const securityAlerts = useMemo(() => {
    if (!logs?.length) return [];

    const securityActions = [
      'failed_login',
      'unauthorized_access',
      'security_violation',
      'permission_denied',
      'suspicious_activity',
      'account_locked',
      'multiple_failed_attempts',
      'unusual_login_location'
    ];

    return logs
      ?.filter(log => 
        securityActions?.some(action => 
          log?.accion?.toLowerCase()?.includes(action?.replace('_', ''))
        ) ||
        log?.descripcion?.toLowerCase()?.includes('security') ||
        log?.descripcion?.toLowerCase()?.includes('unauthorized') ||
        log?.descripcion?.toLowerCase()?.includes('failed')
      )
      ?.slice(0, 50) // Limit to last 50 security events
      ?.map(log => ({
        ...log,
        severity: getSeverityLevel(log?.accion, log?.descripcion),
        category: getCategoryFromAction(log?.accion)
      }));
  }, [logs]);

  const getSeverityLevel = (action, description) => {
    const text = `${action} ${description}`?.toLowerCase();

    if (text?.includes('unauthorized') || text?.includes('violation') || text?.includes('breach')) {
      return 'critical';
    }
    if (text?.includes('failed') || text?.includes('denied') || text?.includes('locked')) {
      return 'high';
    }
    if (text?.includes('suspicious') || text?.includes('unusual')) {
      return 'medium';
    }

    return 'low';
  };

  const getCategoryFromAction = (action) => {
    const actionLower = action?.toLowerCase();

    if (actionLower?.includes('login') || actionLower?.includes('auth')) {
      return 'authentication';
    }
    if (actionLower?.includes('access') || actionLower?.includes('permission')) {
      return 'authorization';
    }
    if (actionLower?.includes('data') || actionLower?.includes('export')) {
      return 'data_access';
    }

    return 'general';
  };

  const getSeverityIcon = (severity) => {
    switch (severity) {
      case 'critical':
        return <XCircle className="h-4 w-4 text-red-600" />;
      case 'high':
        return <AlertTriangle className="h-4 w-4 text-red-500" />;
      case 'medium':
        return <AlertTriangle className="h-4 w-4 text-yellow-500" />;
      case 'low':
        return <Shield className="h-4 w-4 text-blue-500" />;
      default:
        return <Shield className="h-4 w-4 text-gray-500" />;
    }
  };

  const getSeverityColor = (severity) => {
    switch (severity) {
      case 'critical':
        return 'bg-red-100 border-red-300 text-red-800';
      case 'high':
        return 'bg-red-50 border-red-200 text-red-700';
      case 'medium':
        return 'bg-yellow-50 border-yellow-200 text-yellow-700';
      case 'low':
        return 'bg-blue-50 border-blue-200 text-blue-700';
      default:
        return 'bg-gray-50 border-gray-200 text-gray-700';
    }
  };

  const getCategoryIcon = (category) => {
    switch (category) {
      case 'authentication':
        return <Shield className="h-3 w-3" />;
      case 'authorization':
        return <XCircle className="h-3 w-3" />;
      case 'data_access':
        return <Eye className="h-3 w-3" />;
      default:
        return <AlertTriangle className="h-3 w-3" />;
    }
  };

  const filteredAlerts = selectedSeverity === 'all' 
    ? securityAlerts 
    : securityAlerts?.filter(alert => alert?.severity === selectedSeverity);

  const severityStats = useMemo(() => {
    const stats = { critical: 0, high: 0, medium: 0, low: 0 };
    securityAlerts?.forEach(alert => {
      stats[alert?.severity] = (stats?.[alert?.severity] || 0) + 1;
    });
    return stats;
  }, [securityAlerts]);

  const formatTimeAgo = (dateString) => {
    if (!dateString) return 'N/A';

    const date = new Date(dateString);
    const now = new Date();
    const diffMs = now - date;
    const diffMinutes = Math.floor(diffMs / (1000 * 60));
    const diffHours = Math.floor(diffMinutes / 60);

    if (diffMinutes < 1) {
      return 'Ahora';
    } else if (diffMinutes < 60) {
      return `${diffMinutes}m`;
    } else if (diffHours < 24) {
      return `${diffHours}h`;
    } else {
      return `${Math.floor(diffHours / 24)}d`;
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-sm border h-fit">
      <div className="p-6 border-b border-gray-200">
        <h2 className="text-xl font-semibold text-gray-900 flex items-center">
          <AlertTriangle className="h-5 w-5 text-red-500 mr-2" />
          Alertas de Seguridad
        </h2>
        <p className="text-sm text-gray-500 mt-1">
          {securityAlerts?.length || 0} eventos de seguridad detectados
        </p>
      </div>
      {/* Severity Summary */}
      <div className="p-4 bg-gray-50 border-b border-gray-200">
        <div className="grid grid-cols-2 gap-3">
          <div className="flex items-center justify-between p-2 bg-red-100 rounded-lg">
            <div className="flex items-center">
              <XCircle className="h-4 w-4 text-red-600 mr-2" />
              <span className="text-sm font-medium text-red-800">Cr√≠ticas</span>
            </div>
            <span className="text-lg font-bold text-red-900">{severityStats?.critical}</span>
          </div>

          <div className="flex items-center justify-between p-2 bg-red-50 rounded-lg">
            <div className="flex items-center">
              <AlertTriangle className="h-4 w-4 text-red-500 mr-2" />
              <span className="text-sm font-medium text-red-700">Altas</span>
            </div>
            <span className="text-lg font-bold text-red-800">{severityStats?.high}</span>
          </div>

          <div className="flex items-center justify-between p-2 bg-yellow-50 rounded-lg">
            <div className="flex items-center">
              <AlertTriangle className="h-4 w-4 text-yellow-500 mr-2" />
              <span className="text-sm font-medium text-yellow-700">Medias</span>
            </div>
            <span className="text-lg font-bold text-yellow-800">{severityStats?.medium}</span>
          </div>

          <div className="flex items-center justify-between p-2 bg-blue-50 rounded-lg">
            <div className="flex items-center">
              <Shield className="h-4 w-4 text-blue-500 mr-2" />
              <span className="text-sm font-medium text-blue-700">Bajas</span>
            </div>
            <span className="text-lg font-bold text-blue-800">{severityStats?.low}</span>
          </div>
        </div>
      </div>
      {/* Severity Filter */}
      <div className="p-4 border-b border-gray-200">
        <div className="flex space-x-2">
          {['all', 'critical', 'high', 'medium', 'low']?.map(severity => (
            <button
              key={severity}
              onClick={() => setSelectedSeverity(severity)}
              className={`px-3 py-1 text-xs rounded-full transition-colors ${
                selectedSeverity === severity
                  ? 'bg-blue-100 text-blue-800 border border-blue-300' :'bg-gray-100 text-gray-600 hover:bg-gray-200'
              }`}
            >
              {severity === 'all' ? 'Todas' : severity?.charAt(0)?.toUpperCase() + severity?.slice(1)}
              {severity !== 'all' && (
                <span className="ml-1 text-xs">({severityStats?.[severity] || 0})</span>
              )}
            </button>
          ))}
        </div>
      </div>
      {/* Alerts List */}
      <div className="max-h-96 overflow-y-auto">
        {filteredAlerts?.length === 0 ? (
          <div className="p-6 text-center">
            <Shield className="h-8 w-8 text-gray-400 mx-auto mb-2" />
            <p className="text-sm text-gray-500">
              {selectedSeverity === 'all' ?'No se detectaron alertas de seguridad'
                : `No hay alertas de severidad ${selectedSeverity}`
              }
            </p>
          </div>
        ) : (
          <div className="divide-y divide-gray-100">
            {filteredAlerts?.map((alert, index) => (
              <div
                key={alert?.id || index}
                className={`p-4 border-l-4 ${getSeverityColor(alert?.severity)}`}
              >
                <div className="flex items-start justify-between">
                  <div className="flex items-start space-x-3 flex-1">
                    <div className="flex-shrink-0 mt-0.5">
                      {getSeverityIcon(alert?.severity)}
                    </div>

                    <div className="flex-1 min-w-0">
                      <div className="flex items-center space-x-2 mb-1">
                        <span className="text-sm font-medium capitalize">
                          {alert?.accion?.replace(/_/g, ' ')}
                        </span>
                        <div className="flex items-center space-x-1 text-xs text-gray-500">
                          {getCategoryIcon(alert?.category)}
                          <span className="capitalize">{alert?.category?.replace(/_/g, ' ')}</span>
                        </div>
                      </div>

                      <p className="text-xs text-gray-600 line-clamp-2 mb-2">
                        {alert?.descripcion}
                      </p>

                      <div className="flex items-center justify-between text-xs text-gray-500">
                        <span className="flex items-center">
                          <Clock className="h-3 w-3 mr-1" />
                          {formatTimeAgo(alert?.fecha)}
                        </span>

                        <span className="truncate max-w-24" title={alert?.usuarios?.nombre}>
                          {alert?.usuarios?.nombre || 'Usuario desconocido'}
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
      {/* Summary Footer */}
      {securityAlerts?.length > 0 && (
        <div className="p-4 bg-gray-50 border-t border-gray-200">
          <div className="flex items-center justify-between text-sm">
            <span className="text-gray-600">
              √öltimas {Math.min(50, securityAlerts?.length)} alertas
            </span>

            <div className="flex items-center text-gray-500">
              <TrendingUp className="h-4 w-4 mr-1" />
              <span>Monitoreo activo</span>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

// src/modules/activity-logging-and-security-monitoring-dashboard/components/StatisticsCards.jsx
import React from 'react';
import { Activity, Calendar, AlertTriangle, Users, TrendingUp, TrendingDown, Minus } from 'lucide-react';

export default function StatisticsCards({
  statistics = {},
  previousStatistics = {},
  loading = false
}) {
  const {
    totalLogs = 0,
    todayLogs = 0,
    securityAlerts = 0,
    activeUsers = 0
  } = statistics;

  const {
    totalLogs: prevTotalLogs = null,
    todayLogs: prevTodayLogs = null,
    securityAlerts: prevSecurityAlerts = null,
    activeUsers: prevActiveUsers = null
  } = previousStatistics || {};

  // Helpers
  const formatNumber = (num) => {
    const n = Number(num) || 0;
    if (n >= 1_000_000) return `${(n / 1_000_000).toFixed(1)}M`;
    if (n >= 1_000) return `${(n / 1_000).toFixed(1)}K`;
    return `${n}`;
  };

  const getTrendIcon = (current, previous) => {
    if (typeof previous !== 'number') {
      return <Minus className="h-3 w-3 text-gray-500" aria-hidden="true" />;
    }
    if (current > previous) return <TrendingUp className="h-3 w-3 text-green-500" aria-hidden="true" />;
    if (current < previous) return <TrendingDown className="h-3 w-3 text-red-500" aria-hidden="true" />;
    return <Minus className="h-3 w-3 text-gray-500" aria-hidden="true" />;
  };

  const getColorClasses = (color) => {
    const colorMap = {
      blue:   { bg: 'bg-blue-50',   icon: 'text-blue-600',   border: 'border-blue-200',   bar: 'bg-blue-500' },
      green:  { bg: 'bg-green-50',  icon: 'text-green-600',  border: 'border-green-200',  bar: 'bg-green-500' },
      red:    { bg: 'bg-red-50',    icon: 'text-red-600',    border: 'border-red-200',    bar: 'bg-red-500' },
      purple: { bg: 'bg-purple-50', icon: 'text-purple-600', border: 'border-purple-200', bar: 'bg-purple-500' },
      gray:   { bg: 'bg-gray-50',   icon: 'text-gray-600',   border: 'border-gray-200',   bar: 'bg-gray-500' }
    };
    return colorMap[color] || colorMap.gray;
  };

  // Cards config (valor real para progreso; valor formateado para UI)
  const cards = [
    {
      key: 'totalLogs',
      title: 'Total de Registros',
      valueRaw: Number(totalLogs) || 0,
      value: formatNumber(totalLogs),
      icon: Activity,
      color: 'blue',
      description: 'Actividades registradas',
      trend: getTrendIcon(Number(totalLogs) || 0, typeof prevTotalLogs === 'number' ? prevTotalLogs : null)
    },
    {
      key: 'todayLogs',
      title: 'Actividad de Hoy',
      valueRaw: Number(todayLogs) || 0,
      value: formatNumber(todayLogs),
      icon: Calendar,
      color: 'green',
      description: 'Eventos del d√≠a actual',
      trend: getTrendIcon(Number(todayLogs) || 0, typeof prevTodayLogs === 'number' ? prevTodayLogs : null)
    },
    {
      key: 'securityAlerts',
      title: 'Alertas de Seguridad',
      valueRaw: Number(securityAlerts) || 0,
      value: formatNumber(securityAlerts),
      icon: AlertTriangle,
      color: (Number(securityAlerts) || 0) > 0 ? 'red' : 'gray',
      description: 'Eventos cr√≠ticos detectados',
      trend: getTrendIcon(Number(securityAlerts) || 0, typeof prevSecurityAlerts === 'number' ? prevSecurityAlerts : null)
    },
    {
      key: 'activeUsers',
      title: 'Usuarios Activos',
      valueRaw: Number(activeUsers) || 0,
      value: formatNumber(activeUsers),
      icon: Users,
      color: 'purple',
      description: 'Usuarios √∫nicos hoy',
      trend: getTrendIcon(Number(activeUsers) || 0, typeof prevActiveUsers === 'number' ? prevActiveUsers : null)
    }
  ];

  // Skeleton card for loading state
  const SkeletonCard = () => (
    <div className="bg-gray-50 border border-gray-200 rounded-lg p-6">
      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-3">
          <div className="p-2 rounded-lg bg-white border border-gray-200">
            <div className="h-5 w-5 bg-gray-200 rounded animate-pulse" />
          </div>
          <div>
            <div className="h-3 w-28 bg-gray-200 rounded animate-pulse" />
            <div className="h-6 w-20 bg-gray-200 rounded mt-2 animate-pulse" />
          </div>
        </div>
        <div className="h-3 w-6 bg-gray-200 rounded animate-pulse" />
      </div>
      <div className="mt-4 h-3 w-40 bg-gray-200 rounded animate-pulse" />
      <div className="mt-3 w-full bg-gray-200 rounded-full h-1.5">
        <div className="h-1.5 rounded-full bg-gray-300 w-1/3 animate-pulse" />
      </div>
    </div>
  );

  if (loading) {
    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6" role="status" aria-label="Cargando estad√≠sticas">
        {[...Array(4)].map((_, i) => <SkeletonCard key={i} />)}
      </div>
    );
  }

  return (
    <div
      className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6"
      role="region"
      aria-label="Tarjetas de estad√≠sticas"
    >
      {cards.map((card) => {
        const colors = getColorClasses(card.color);
        const IconCmp = card.icon;

        // Barra de ‚Äúprogreso‚Äù visual: mapea el valor a 10‚Äì100%
        const pct = (() => {
          const v = Math.max(0, Number(card.valueRaw) || 0);
          // Escala simple: cap a 100 y m√≠nimo 10 para que siempre se vea algo
          const scaled = Math.min(100, v > 0 ? Math.max(10, Math.round((v % 100))) : 10);
          return `${scaled}%`;
        })();

        return (
          <div
            key={card.key}
            className={`${colors.bg} ${colors.border} border rounded-lg p-6 transition-all duration-200 hover:shadow-md`}
            role="group"
            aria-label={card.title}
          >
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-3">
                <div className={`p-2 rounded-lg bg-white ${colors.border} border`}>
                  <IconCmp className={`h-5 w-5 ${colors.icon}`} aria-hidden="true" />
                </div>
                <div>
                  <h3 className="text-sm font-medium text-gray-600">{card.title}</h3>
                  <p className="text-2xl font-bold text-gray-900 mt-1" aria-live="polite">
                    {card.value}
                  </p>
                </div>
              </div>

              <div className="flex items-center space-x-1" aria-label="Tendencia">
                {card.trend}
              </div>
            </div>

            <div className="mt-4">
              <p className="text-xs text-gray-500">{card.description}</p>
            </div>

            <div className="mt-3" aria-hidden="true">
              <div className="w-full bg-gray-200 rounded-full h-1.5">
                <div
                  className={`h-1.5 rounded-full transition-all duration-500 ${colors.bar}`}
                  style={{ width: pct }}
                />
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
}

