// src/administrator-employee-management-console/BulkActionsToolbar.jsx
import React, { useEffect, useMemo, useRef, useState } from 'react';
import Icon from '../../../components/AppIcon';
import Button from '../../../components/ui/Button';
import Select from '../../../components/ui/Select';

import { siteDataService } from '../../data/siteDataService';
import { supervisorDataService } from '../../data/supervisorDataService';

const BulkActionsToolbar = ({
  selectedCount,
  onBulkAction,
  onClearSelection,
  userRole = 'admin',
}) => {
  const [showBulkMenu, setShowBulkMenu] = useState(false);
  const [bulkActionType, setBulkActionType] = useState('');
  const [bulkActionValue, setBulkActionValue] = useState('');

  const [sites, setSites] = useState([]);
  const [sitesLoading, setSitesLoading] = useState(false);

  const [supervisors, setSupervisors] = useState([]);
  const [supervisorsLoading, setSupervisorsLoading] = useState(false);

  const mountedRef = useRef(false);

  useEffect(() => {
    mountedRef.current = true;
    return () => { mountedRef.current = false; };
  }, []);

  // Cargar sitios reales
  useEffect(() => {
    const loadSites = async () => {
      setSitesLoading(true);
      try {
        const res = await siteDataService.getSites();
        if (mountedRef.current && res?.success) setSites(res.data || []);
      } catch (e) {
        console.error('Error loading sites:', e);
      } finally {
        setSitesLoading(false);
      }
    };
    loadSites();
  }, []);

  // Cargar supervisores reales
  useEffect(() => {
    const loadSupervisors = async () => {
      setSupervisorsLoading(true);
      try {
        const res = await supervisorDataService.getSupervisors();
        if (mountedRef.current && res?.success) setSupervisors(res.data || []);
      } catch (e) {
        console.error('Error loading supervisors:', e);
      } finally {
        setSupervisorsLoading(false);
      }
    };
    loadSupervisors();
  }, []);

  // Opciones din√°micas
  const siteOptions = useMemo(
    () => (sites || []).map(s => ({ value: s.id, label: s.nombre })),
    [sites]
  );

  const supervisorOptions = useMemo(
    () => (supervisors || []).map(u => ({
      value: u.id,
      label: u.nombre || u.correo
    })),
    [supervisors]
  );

  const statusOptions = useMemo(() => ([
    { value: 'active', label: 'Activo' },
    { value: 'inactive', label: 'Inactivo' },
    { value: 'suspended', label: 'Suspendido' },
    { value: 'terminated', label: 'Terminado' },
  ]), []);

  const bulkActions = useMemo(() => ([
    { value: 'change-site',        label: 'Cambiar sitio de construcci√≥n', icon: 'MapPin' },
    { value: 'change-supervisor',  label: 'Cambiar supervisor',            icon: 'UserCheck' },
    { value: 'change-status',      label: 'Cambiar estado',                icon: 'ToggleLeft' },
    { value: 'export-data',        label: 'Exportar datos seleccionados',  icon: 'Download' },
    { value: 'send-notification',  label: 'Enviar notificaci√≥n',           icon: 'Bell' },
  ]), []);

  const getActionOptions = (actionType) => {
    switch (actionType) {
      case 'change-site':
        return siteOptions;
      case 'change-supervisor':
        return supervisorOptions;
      case 'change-status':
        return statusOptions;
      default:
        return [];
    }
  };

  const handleBulkAction = () => {
    // export-data y send-notification no requieren "valor" previo
    const noValueNeeded = ['export-data', 'send-notification'].includes(bulkActionType);
    if (!bulkActionType) return;
    if (!noValueNeeded && !bulkActionValue) return;

    onBulkAction?.({
      action: bulkActionType,
      value: bulkActionValue,
      selectedCount,
    });

    // Reset
    setBulkActionType('');
    setBulkActionValue('');
    setShowBulkMenu(false);
  };

  const canExecuteAction = () => {
    if (['export-data', 'send-notification'].includes(bulkActionType)) return true;
    return Boolean(bulkActionType && bulkActionValue);
  };

  if (selectedCount === 0) return null;

  return (
    <div className="bg-primary text-primary-foreground p-4 rounded-lg mb-4 shadow-sm">
      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-4">
          <div className="flex items-center space-x-2">
            <Icon name="CheckSquare" size={20} />
            <span className="font-medium">
              {selectedCount} empleado{selectedCount !== 1 ? 's' : ''} seleccionado{selectedCount !== 1 ? 's' : ''}
            </span>
          </div>

          <Button
            variant="ghost"
            size="sm"
            onClick={onClearSelection}
            className="text-primary-foreground hover:bg-primary-foreground/10"
            iconName="X"
            iconSize={16}
          >
            Limpiar selecci√≥n
          </Button>
        </div>

        <div className="flex items-center space-x-2">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => setShowBulkMenu(!showBulkMenu)}
            className="text-primary-foreground hover:bg-primary-foreground/10"
            iconName="Settings"
            iconSize={16}
          >
            Acciones masivas
          </Button>

          <Button
            variant="ghost"
            size="sm"
            onClick={() => onBulkAction?.({ action: 'export-selected', selectedCount })}
            className="text-primary-foreground hover:bg-primary-foreground/10"
            iconName="Download"
            iconSize={16}
          >
            Exportar
          </Button>
        </div>
      </div>

      {/* Men√∫ de acciones masivas */}
      {showBulkMenu && (
        <div className="mt-4 p-4 bg-primary-foreground/10 rounded-lg">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <Select
                label="Acci√≥n"
                options={bulkActions.map(a => ({ value: a.value, label: a.label }))}
                value={bulkActionType}
                onChange={(v) => {
                  setBulkActionType(v);
                  setBulkActionValue(''); // limpiar valor previo al cambiar acci√≥n
                }}
                placeholder="Seleccionar acci√≥n..."
                className="text-foreground"
              />
            </div>

            {bulkActionType && !['export-data', 'send-notification'].includes(bulkActionType) && (
              <div>
                <Select
                  label="Nuevo valor"
                  options={getActionOptions(bulkActionType)}
                  value={bulkActionValue}
                  onChange={setBulkActionValue}
                  placeholder={
                    bulkActionType === 'change-site'
                      ? (sitesLoading ? 'Cargando sitios...' : 'Seleccionar sitio...')
                      : bulkActionType === 'change-supervisor'
                      ? (supervisorsLoading ? 'Cargando supervisores...' : 'Seleccionar supervisor...')
                      : 'Seleccionar valor...'
                  }
                  disabled={
                    (bulkActionType === 'change-site' && sitesLoading) ||
                    (bulkActionType === 'change-supervisor' && supervisorsLoading)
                  }
                  className="text-foreground"
                />
              </div>
            )}

            <div className="flex items-end">
              <Button
                onClick={handleBulkAction}
                disabled={!canExecuteAction()}
                className="w-full bg-primary-foreground text-primary hover:bg-primary-foreground/90"
                iconName="Play"
                iconSize={16}
              >
                Ejecutar
              </Button>
            </div>
          </div>

          {bulkActionType === 'send-notification' && (
            <div className="mt-4">
              <textarea
                placeholder="Mensaje de notificaci√≥n..."
                className="w-full p-3 border border-primary-foreground/20 rounded-md bg-primary-foreground/10 text-primary-foreground placeholder-primary-foreground/60 resize-none"
                rows={3}
                value={bulkActionValue}
                onChange={(e) => setBulkActionValue(e?.target?.value)}
              />
            </div>
          )}

          {bulkActionType && (
            <div className="mt-3 p-3 bg-primary-foreground/5 rounded-md">
              <div className="flex items-start space-x-2">
                <Icon name="Info" size={16} className="text-primary-foreground/80 mt-0.5" />
                <div className="text-sm text-primary-foreground/80">
                  {bulkActionType === 'change-site' &&
                    `Se cambiar√° el sitio de construcci√≥n de ${selectedCount} empleado${selectedCount !== 1 ? 's' : ''}.`}
                  {bulkActionType === 'change-supervisor' &&
                    `Se cambiar√° el supervisor de ${selectedCount} empleado${selectedCount !== 1 ? 's' : ''}.`}
                  {bulkActionType === 'change-status' &&
                    `Se cambiar√° el estado de ${selectedCount} empleado${selectedCount !== 1 ? 's' : ''}.`}
                  {bulkActionType === 'export-data' &&
                    `Se exportar√°n los datos de ${selectedCount} empleado${selectedCount !== 1 ? 's' : ''} seleccionado${selectedCount !== 1 ? 's' : ''}.`}
                  {bulkActionType === 'send-notification' &&
                    `Se enviar√° una notificaci√≥n a ${selectedCount} empleado${selectedCount !== 1 ? 's' : ''}.`}
                </div>
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default BulkActionsToolbar;

// src/administrator-employee-management-console/EmployeeCreationModal.jsx
import React, { useEffect, useMemo, useRef, useState } from 'react';
import Icon from '../../../components/AppIcon';
import Button from '../../../components/ui/Button';
import Input from '../../../components/ui/Input';
import Select from '../../../components/ui/Select';
import { siteDataService } from '../../data/siteDataService';
import { supervisorDataService } from '../../data/supervisorDataService';
import { employeeDataService } from '../../data/employeeDataService';

const EMAIL_RE = /\S+@\S+\.\S+/;

const EmployeeCreationModal = ({
  isOpen,
  onClose,
  onSave,          // callback opcional tras crear
  userRole = 'admin',
}) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    idNumber: '',
    birthDate: '',
    address: '',
    emergencyContact: '',
    site: '',           // obra_id
    supervisor: '',     // supervisor_id
    hireDate: new Date().toISOString().split('T')?.[0],
    dailySalary: '',
    status: 'active',
    puesto: '',         // opcional: cargo/puesto (no mock)
  });

  const [errors, setErrors] = useState({});
  const [currentStep, setCurrentStep] = useState(1);
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Datos reales desde BD
  const [sites, setSites] = useState([]);
  const [sitesLoading, setSitesLoading] = useState(false);
  const [supervisors, setSupervisors] = useState([]);
  const [supervisorsLoading, setSupervisorsLoading] = useState(false);

  const mountedRef = useRef(false);
  const inFlightRef = useRef({ sites: false, sups: false });

  const siteOptions = useMemo(
    () => (sites || []).map(s => ({ value: s.id, label: s.nombre })),
    [sites]
  );
  const supervisorOptions = useMemo(
    () => (supervisors || []).map(u => ({ value: u.id, label: u.nombre || u.correo })),
    [supervisors]
  );

  const steps = [
    { id: 1, title: 'Informaci√≥n Personal', icon: 'User' },
    { id: 2, title: 'Contacto', icon: 'Phone' },
    { id: 3, title: 'Empleo', icon: 'Briefcase' },
    { id: 4, title: 'Confirmaci√≥n', icon: 'CheckCircle' },
  ];

  const handleInputChange = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    if (errors?.[field]) setErrors(prev => ({ ...prev, [field]: '' }));
  };

  const validateStep = (step) => {
    const e = {};
    if (step === 1) {
      if (!formData?.name?.trim()) e.name = 'El nombre es requerido';
      if (!formData?.idNumber?.trim()) e.idNumber = 'El n√∫mero de identificaci√≥n es requerido';
      if (!formData?.birthDate) e.birthDate = 'La fecha de nacimiento es requerida';
    }
    if (step === 2) {
      if (!formData?.email?.trim()) e.email = 'El email es requerido';
      else if (!EMAIL_RE.test(formData?.email)) e.email = 'El email no es v√°lido';
      if (!formData?.phone?.trim()) e.phone = 'El tel√©fono es requerido';
    }
    if (step === 3) {
      if (!formData?.site) e.site = 'El sitio de construcci√≥n es requerido';
      if (!formData?.supervisor) e.supervisor = 'El supervisor es requerido';
      if (!formData?.hireDate) e.hireDate = 'La fecha de contrataci√≥n es requerida';
      if (userRole === 'admin' && (formData?.dailySalary === '' || formData?.dailySalary === null)) {
        e.dailySalary = 'El salario diario es requerido';
      }
      if (formData?.dailySalary !== '' && Number.isNaN(+formData?.dailySalary)) {
        e.dailySalary = 'El salario debe ser num√©rico';
      }
    }
    setErrors(e);
    return Object.keys(e).length === 0;
  };

  const handleNext = () => {
    if (validateStep(currentStep)) {
      setCurrentStep(prev => Math.min(prev + 1, steps.length));
    }
  };

  const handlePrevious = () => setCurrentStep(prev => Math.max(prev - 1, 1));

  const handleSubmit = async () => {
    if (!validateStep(3)) return;
    setIsSubmitting(true);
    try {
      // Mapea a tu tabla `usuarios` v√≠a employeeDataService (sin valores ficticios)
      const payload = {
        name: formData.name,
        email: formData.email,
        phone: formData.phone,
        puesto: formData.puesto || null,                // si no capturas puesto, va null
        obra_id: formData.site,                         // obra real
        supervisor_id: formData.supervisor,             // supervisor real
        hourly_rate: Number(formData.dailySalary) || 0, // usa field existente en BD
        role: 'user',
      };

      const res = await employeeDataService.createEmployee(payload);
      if (!res?.success) throw new Error(res?.error || 'No se pudo crear el empleado');

      if (typeof onSave === 'function') {
        await onSave(res.data);
      }

      // Reset limpio
      setFormData({
        name: '',
        email: '',
        phone: '',
        idNumber: '',
        birthDate: '',
        address: '',
        emergencyContact: '',
        site: '',
        supervisor: '',
        hireDate: new Date().toISOString().split('T')?.[0],
        dailySalary: '',
        status: 'active',
        puesto: '',
      });
      setCurrentStep(1);
      setErrors({});
      onClose?.();
    } catch (err) {
      console.error('Error creating employee:', err);
      setErrors(prev => ({ ...prev, submit: err?.message || 'No se pudo crear el empleado' }));
    } finally {
      setIsSubmitting(false);
    }
  };

  // Cargar sitios y supervisores desde BD al abrir
  useEffect(() => {
    mountedRef.current = true;
    if (!isOpen) return;

    const loadSites = async () => {
      if (inFlightRef.current.sites) return;
      inFlightRef.current.sites = true;
      setSitesLoading(true);
      try {
        const res = await siteDataService.getSites({ activa: true, limit: 500, orderBy: 'created_at', order: 'desc' });
        if (mountedRef.current && res?.success) setSites(res.data || []);
      } catch (e) {
        console.error('Error loading sites:', e);
      } finally {
        setSitesLoading(false);
        inFlightRef.current.sites = false;
      }
    };

    const loadSupervisors = async () => {
      if (inFlightRef.current.sups) return;
      inFlightRef.current.sups = true;
      setSupervisorsLoading(true);
      try {
        const res = await supervisorDataService.getSupervisors({
          roles: ['supervisor', 'admin', 'superadmin'],
          is_active: true,
          limit: 500,
          orderBy: 'created_at',
          order: 'desc',
        });
        if (mountedRef.current && res?.success) setSupervisors(res.data || []);
      } catch (e) {
        console.error('Error loading supervisors:', e);
      } finally {
        setSupervisorsLoading(false);
        inFlightRef.current.sups = false;
      }
    };

    loadSites();
    loadSupervisors();

    return () => { mountedRef.current = false; };
  }, [isOpen]);

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4">
      <div className="bg-card border border-border rounded-lg w-full max-w-2xl max-h-[90vh] overflow-hidden">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-border">
          <div>
            <h2 className="text-xl font-semibold text-foreground">Nuevo Empleado</h2>
            <p className="text-sm text-muted-foreground mt-1">
              Paso {currentStep} de {steps.length}: {steps[currentStep - 1]?.title}
            </p>
          </div>
          <Button variant="ghost" size="icon" onClick={onClose} iconName="X" iconSize={20} />
        </div>

        {/* Steps */}
        <div className="px-6 py-4 border-b border-border">
          <div className="flex items-center justify-between">
            {steps.map((step, idx) => (
              <div key={step.id} className="flex items-center">
                <div className={`flex items-center justify-center w-8 h-8 rounded-full border-2 transition-colors
                  ${currentStep >= step.id ? 'bg-primary border-primary text-primary-foreground' : 'border-border text-muted-foreground'}`}>
                  {currentStep > step.id ? <Icon name="Check" size={16} /> : <Icon name={step.icon} size={16} />}
                </div>
                {idx < steps.length - 1 && (
                  <div className={`w-16 h-0.5 mx-2 ${currentStep > step.id ? 'bg-primary' : 'bg-border'}`} />
                )}
              </div>
            ))}
          </div>
        </div>

        {/* Content */}
        <div className="p-6 overflow-y-auto max-h-96">
          {currentStep === 1 && (
            <div className="space-y-4">
              <Input
                label="Nombre completo"
                placeholder="Ej: Juan P√©rez Garc√≠a"
                value={formData.name}
                onChange={(e) => handleInputChange('name', e?.target?.value)}
                error={errors.name}
                required
              />
              <Input
                label="N√∫mero de identificaci√≥n"
                placeholder="Ej: 12345678A"
                value={formData.idNumber}
                onChange={(e) => handleInputChange('idNumber', e?.target?.value)}
                error={errors.idNumber}
                required
              />
              <Input
                label="Fecha de nacimiento"
                type="date"
                value={formData.birthDate}
                onChange={(e) => handleInputChange('birthDate', e?.target?.value)}
                error={errors.birthDate}
                required
              />
            </div>
          )}

          {currentStep === 2 && (
            <div className="space-y-4">
              <Input
                label="Email"
                type="email"
                placeholder="juan.perez@empresa.com"
                value={formData.email}
                onChange={(e) => handleInputChange('email', e?.target?.value)}
                error={errors.email}
                required
              />
              <Input
                label="Tel√©fono"
                type="tel"
                placeholder="+52 81 0000 0000"
                value={formData.phone}
                onChange={(e) => handleInputChange('phone', e?.target?.value)}
                error={errors.phone}
                required
              />
              <Input
                label="Direcci√≥n"
                placeholder="Calle Principal 123, Ciudad"
                value={formData.address}
                onChange={(e) => handleInputChange('address', e?.target?.value)}
              />
              <Input
                label="Contacto de emergencia"
                placeholder="Nombre y tel√©fono"
                value={formData.emergencyContact}
                onChange={(e) => handleInputChange('emergencyContact', e?.target?.value)}
              />
            </div>
          )}

          {currentStep === 3 && (
            <div className="space-y-4">
              <Select
                label="Sitio de construcci√≥n"
                options={siteOptions}
                value={formData.site}
                onChange={(value) => handleInputChange('site', value)}
                error={errors.site}
                required
                placeholder={sitesLoading ? 'Cargando sitios...' : 'Seleccionar sitio...'}
                disabled={sitesLoading}
              />
              <Select
                label="Supervisor"
                options={supervisorOptions}
                value={formData.supervisor}
                onChange={(value) => handleInputChange('supervisor', value)}
                error={errors.supervisor}
                required
                placeholder={supervisorsLoading ? 'Cargando supervisores...' : 'Seleccionar supervisor...'}
                disabled={supervisorsLoading}
              />
              <Input
                label="Fecha de contrataci√≥n"
                type="date"
                value={formData.hireDate}
                onChange={(e) => handleInputChange('hireDate', e?.target?.value)}
                error={errors.hireDate}
                required
              />
              {userRole === 'admin' && (
                <Input
                  label="Salario diario"
                  type="number"
                  placeholder="800.00"
                  value={formData.dailySalary}
                  onChange={(e) => handleInputChange('dailySalary', e?.target?.value)}
                  error={errors.dailySalary}
                  required
                />
              )}
              <Input
                label="Puesto (opcional)"
                placeholder="Ej: Alba√±il, Electricista..."
                value={formData.puesto}
                onChange={(e) => handleInputChange('puesto', e?.target?.value)}
              />
              {errors.submit && <p className="text-sm text-destructive mt-2">{errors.submit}</p>}
            </div>
          )}

          {currentStep === 4 && (
            <div className="space-y-6">
              <div className="text-center">
                <Icon name="CheckCircle" size={48} className="mx-auto text-success mb-4" />
                <h3 className="text-lg font-semibold text-foreground mb-2">Confirmar creaci√≥n</h3>
                <p className="text-muted-foreground">Revisa la informaci√≥n antes de crear el empleado.</p>
              </div>

              <div className="bg-muted/50 rounded-lg p-4 space-y-3">
                <div className="grid grid-cols-2 gap-4 text-sm">
                  <div>
                    <span className="font-medium text-muted-foreground">Nombre:</span>
                    <p className="text-foreground">{formData.name}</p>
                  </div>
                  <div>
                    <span className="font-medium text-muted-foreground">Email:</span>
                    <p className="text-foreground">{formData.email}</p>
                  </div>
                  <div>
                    <span className="font-medium text-muted-foreground">Sitio:</span>
                    <p className="text-foreground">
                      {siteOptions.find(s => s.value === formData.site)?.label || '‚Äî'}
                    </p>
                  </div>
                  <div>
                    <span className="font-medium text-muted-foreground">Supervisor:</span>
                    <p className="text-foreground">
                      {supervisorOptions.find(s => s.value === formData.supervisor)?.label || '‚Äî'}
                    </p>
                  </div>
                  {formData.puesto ? (
                    <div className="col-span-2">
                      <span className="font-medium text-muted-foreground">Puesto:</span>
                      <p className="text-foreground">{formData.puesto}</p>
                    </div>
                  ) : null}
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Footer */}
        <div className="flex items-center justify-between p-6 border-t border-border">
          <Button
            variant="outline"
            onClick={currentStep === 1 ? onClose : handlePrevious}
            iconName={currentStep === 1 ? 'X' : 'ChevronLeft'}
            iconSize={16}
          >
            {currentStep === 1 ? 'Cancelar' : 'Anterior'}
          </Button>

          <div className="flex space-x-2">
            {currentStep < steps.length ? (
              <Button
                onClick={handleNext}
                iconName="ChevronRight"
                iconPosition="right"
                iconSize={16}
                disabled={sitesLoading || supervisorsLoading}
              >
                Siguiente
              </Button>
            ) : (
              <Button
                onClick={handleSubmit}
                loading={isSubmitting}
                iconName="Plus"
                iconSize={16}
                disabled={isSubmitting}
              >
                Crear Empleado
              </Button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default EmployeeCreationModal;

// src/administrator-employee-management-console/EmployeeDetailPanel.jsx
import React, { useEffect, useMemo, useRef, useState } from 'react';
import Icon from '../../../components/AppIcon';
import Image from '../../../components/AppImage';
import Button from '../../../components/ui/Button';
import Input from '../../../components/ui/Input';
import Select from '../../../components/ui/Select';

import { siteDataService } from '../../data/siteDataService';
import { supervisorDataService } from '../../data/supervisorDataService';
import { employeeDataService } from '../../data/employeeDataService';

const STATUS_OPTIONS = [
  { value: 'active', label: 'Activo' },
  { value: 'inactive', label: 'Inactivo' },
  { value: 'suspended', label: 'Suspendido' },
  { value: 'terminated', label: 'Terminado' },
];

const TABS = [
  { id: 'general', label: 'General', icon: 'User' },
  { id: 'contact', label: 'Contacto', icon: 'Phone' },
  { id: 'employment', label: 'Empleo', icon: 'Briefcase' },
  { id: 'documents', label: 'Documentos', icon: 'FileText' },
];

const getStatusColor = (status) => {
  const colors = {
    active: 'bg-success text-success-foreground',
    inactive: 'bg-secondary text-secondary-foreground',
    suspended: 'bg-warning text-warning-foreground',
    terminated: 'bg-error text-error-foreground',
  };
  return colors[status] || colors.inactive;
};

const EmployeeDetailPanel = ({
  employee,
  onClose,
  onSave,           // callback opcional tras guardar
  onViewAttendance,
  onViewPayroll,
  onViewIncidents,
  userRole = 'admin',
  isEditing = false,
  onToggleEdit,
}) => {
  const [editData, setEditData] = useState(employee || {});
  const [activeTab, setActiveTab] = useState('general');

  const [sites, setSites] = useState([]);
  const [sitesLoading, setSitesLoading] = useState(false);

  const [supervisors, setSupervisors] = useState([]);
  const [supervisorsLoading, setSupervisorsLoading] = useState(false);

  const [saving, setSaving] = useState(false);
  const [formError, setFormError] = useState('');

  const mountedRef = useRef(false);

  useEffect(() => {
    mountedRef.current = true;
    return () => { mountedRef.current = false; };
  }, []);

  // Cargar datos reales de BD
  useEffect(() => {
    if (!employee) return;

    const loadSites = async () => {
      setSitesLoading(true);
      try {
        const res = await siteDataService.getSites({ activa: true, limit: 500 });
        if (mountedRef.current && res?.success) setSites(res.data || []);
      } catch (e) {
        console.error('Error loading sites:', e);
      } finally {
        setSitesLoading(false);
      }
    };

    const loadSupervisors = async () => {
      setSupervisorsLoading(true);
      try {
        const res = await supervisorDataService.getSupervisors({
          roles: ['supervisor', 'admin', 'superadmin'],
          is_active: true,
          limit: 500,
        });
        if (mountedRef.current && res?.success) setSupervisors(res.data || []);
      } catch (e) {
        console.error('Error loading supervisors:', e);
      } finally {
        setSupervisorsLoading(false);
      }
    };

    loadSites();
    loadSupervisors();
  }, [employee?.id]);

  // Mantener editData sincronizado al cambiar el empleado
  useEffect(() => {
    setEditData(employee || {});
    setFormError('');
  }, [employee]);

  const siteOptions = useMemo(
    () => (sites || []).map(s => ({ value: s.id, label: s.nombre })),
    [sites]
  );
  const supervisorOptions = useMemo(
    () => (supervisors || []).map(u => ({ value: u.id, label: u.nombre || u.correo })),
    [supervisors]
  );

  const siteLabel = useMemo(() => {
    const byId = siteOptions.find(s => s.value === (employee?.obra_id || employee?.site || employee?.site_id));
    return byId?.label || employee?.sitio_asignado || '‚Äî';
  }, [employee, siteOptions]);

  const supervisorLabel = useMemo(() => {
    const id = employee?.supervisor_id || employee?.supervisor;
    const found = supervisorOptions.find(s => s.value === id);
    return found?.label || employee?.supervisor_name || '‚Äî';
  }, [employee, supervisorOptions]);

  if (!employee) {
    return (
      <div className="w-full h-full bg-card border border-border rounded-lg flex items-center justify-center">
        <div className="text-center">
          <Icon name="Users" size={48} className="mx-auto text-muted-foreground mb-4" />
          <h3 className="text-lg font-medium text-foreground mb-2">Selecciona un empleado</h3>
          <p className="text-muted-foreground">Haz clic en un empleado de la tabla para ver sus detalles.</p>
        </div>
      </div>
    );
  }

  const handleInputChange = (field, value) => {
    setEditData(prev => ({ ...prev, [field]: value }));
    if (formError) setFormError('');
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'No especificada';
    const d = new Date(dateString);
    if (Number.isNaN(d.getTime())) return 'No especificada';
    return d.toLocaleDateString('es-ES', { day: '2-digit', month: '2-digit', year: 'numeric' });
  };

  const handleSave = async () => {
    try {
      setSaving(true);
      setFormError('');

      // Mapear a columnas reales de `usuarios`
      const payload = {
        nombre: editData?.name ?? editData?.nombre ?? employee?.nombre ?? employee?.name,
        correo: editData?.email ?? employee?.correo ?? employee?.email,
        telefono: editData?.phone ?? employee?.telefono ?? employee?.phone ?? null,
        puesto: editData?.puesto ?? employee?.puesto ?? null,
        obra_id:
          editData?.obra_id ??
          editData?.site ??
          employee?.obra_id ??
          employee?.site ??
          null,
        supervisor_id:
          editData?.supervisor_id ??
          editData?.supervisor ??
          employee?.supervisor_id ??
          null,
        hourly_rate:
          editData?.hourly_rate ??
          editData?.dailySalary ??
          employee?.hourly_rate ??
          0,
        // is_active desde status
        is_active:
          (editData?.status ?? employee?.status ?? 'active') === 'active',
      };

      const res = await employeeDataService.updateEmployee(employee.id, payload);
      if (!res?.success) throw new Error(res?.error || 'No se pudo actualizar el empleado');

      // Refrescar datos locales con respuesta de BD
      const updated = res.data;

      if (typeof onSave === 'function') await onSave(updated);
      onToggleEdit?.(); // salir de edici√≥n
    } catch (e) {
      console.error('Save error:', e);
      setFormError(e?.message || 'Error al guardar cambios');
    } finally {
      setSaving(false);
    }
  };

  return (
    <div className="w-full h-full bg-card border border-border rounded-lg flex flex-col">
      {/* Header */}
      <div className="flex items-center justify-between p-4 border-b border-border">
        <div className="flex items-center space-x-3">
          <div className="flex-shrink-0">
            {employee?.avatar ? (
              <Image src={employee.avatar} alt={employee?.nombre || employee?.name} className="w-12 h-12 rounded-full object-cover" />
            ) : (
              <div className="w-12 h-12 bg-primary text-primary-foreground rounded-full flex items-center justify-center text-sm font-medium">
                {(employee?.nombre || employee?.name || '??')
                  .split(' ')
                  .map(n => n?.[0])
                  .filter(Boolean)
                  .join('')
                  .toUpperCase()}
              </div>
            )}
          </div>
          <div>
            <h2 className="text-lg font-semibold text-foreground">{employee?.nombre || employee?.name}</h2>
            {employee?.employeeId ? (
              <p className="text-sm text-muted-foreground">ID: {employee.employeeId}</p>
            ) : null}
          </div>
        </div>

        <div className="flex items-center space-x-2">
          {isEditing ? (
            <>
              <Button variant="outline" size="sm" onClick={onToggleEdit} iconName="X" iconSize={16}>
                Cancelar
              </Button>
              <Button size="sm" onClick={handleSave} loading={saving} iconName="Save" iconSize={16}>
                Guardar
              </Button>
            </>
          ) : (
            <Button variant="outline" size="sm" onClick={onToggleEdit} iconName="Edit" iconSize={16}>
              Editar
            </Button>
          )}

          <Button variant="ghost" size="icon" onClick={onClose} iconName="X" iconSize={16} />
        </div>
      </div>

      {/* Status & quick actions */}
      <div className="p-4 border-b border-border">
        <div className="flex items-center justify-between mb-2">
          <span
            className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${getStatusColor(
              (employee?.status || (employee?.is_active ? 'active' : 'inactive'))
            )}`}
          >
            {employee?.status
              ? (employee.status === 'active'
                  ? 'Activo'
                  : employee.status === 'inactive'
                    ? 'Inactivo'
                    : employee.status === 'suspended'
                      ? 'Suspendido'
                      : 'Terminado')
              : employee?.is_active
                ? 'Activo'
                : 'Inactivo'}
          </span>

          <div className="flex items-center space-x-2">
            <Button variant="outline" size="sm" onClick={() => onViewAttendance?.(employee)} iconName="Clock" iconSize={16}>
              Asistencia
            </Button>

            {userRole === 'admin' && (
              <Button variant="outline" size="sm" onClick={() => onViewPayroll?.(employee)} iconName="Calculator" iconSize={16}>
                N√≥mina
              </Button>
            )}

            <Button variant="outline" size="sm" onClick={() => onViewIncidents?.(employee)} iconName="AlertTriangle" iconSize={16}>
              Incidentes
            </Button>
          </div>
        </div>
        {formError ? <p className="text-sm text-destructive">{formError}</p> : null}
      </div>

      {/* Tabs */}
      <div className="border-b border-border">
        <nav className="flex space-x-8 px-4">
          {TABS.map((tab) => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={`flex items-center space-x-2 py-3 px-1 border-b-2 font-medium text-sm transition-colors
                ${activeTab === tab.id
                  ? 'border-primary text-primary'
                  : 'border-transparent text-muted-foreground hover:text-foreground hover:border-muted-foreground'}`}
            >
              <Icon name={tab.icon} size={16} />
              <span>{tab.label}</span>
            </button>
          ))}
        </nav>
      </div>

      {/* Content */}
      <div className="flex-1 overflow-y-auto p-4">
        {activeTab === 'general' && (
          <div className="space-y-4">
            {isEditing ? (
              <>
                <Input
                  label="Nombre completo"
                  value={editData?.name ?? editData?.nombre ?? ''}
                  onChange={(e) => handleInputChange('name', e?.target?.value)}
                />
                <Input
                  label="ID de empleado (opcional)"
                  value={editData?.employeeId || ''}
                  onChange={(e) => handleInputChange('employeeId', e?.target?.value)}
                />
                <Input
                  label="Fecha de nacimiento"
                  type="date"
                  value={editData?.birthDate || ''}
                  onChange={(e) => handleInputChange('birthDate', e?.target?.value)}
                />
                <Input
                  label="N√∫mero de identificaci√≥n"
                  value={editData?.idNumber || ''}
                  onChange={(e) => handleInputChange('idNumber', e?.target?.value)}
                />
              </>
            ) : (
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="text-sm font-medium text-muted-foreground">Nombre completo</label>
                  <p className="text-sm text-foreground mt-1">{employee?.nombre || employee?.name}</p>
                </div>
                {employee?.employeeId ? (
                  <div>
                    <label className="text-sm font-medium text-muted-foreground">ID de empleado</label>
                    <p className="text-sm text-foreground mt-1 font-mono">{employee.employeeId}</p>
                  </div>
                ) : null}
                <div>
                  <label className="text-sm font-medium text-muted-foreground">Fecha de nacimiento</label>
                  <p className="text-sm text-foreground mt-1">
                    {employee?.birthDate ? formatDate(employee.birthDate) : 'No especificada'}
                  </p>
                </div>
                <div>
                  <label className="text-sm font-medium text-muted-foreground">N√∫mero de identificaci√≥n</label>
                  <p className="text-sm text-foreground mt-1">{employee?.idNumber || 'No especificado'}</p>
                </div>
              </div>
            )}
          </div>
        )}

        {activeTab === 'contact' && (
          <div className="space-y-4">
            {isEditing ? (
              <>
                <Input
                  label="Email"
                  type="email"
                  value={editData?.email ?? employee?.correo ?? ''}
                  onChange={(e) => handleInputChange('email', e?.target?.value)}
                />
                <Input
                  label="Tel√©fono"
                  type="tel"
                  value={editData?.phone ?? employee?.telefono ?? ''}
                  onChange={(e) => handleInputChange('phone', e?.target?.value)}
                />
                <Input
                  label="Direcci√≥n"
                  value={editData?.address || ''}
                  onChange={(e) => handleInputChange('address', e?.target?.value)}
                />
                <Input
                  label="Contacto de emergencia"
                  value={editData?.emergencyContact || ''}
                  onChange={(e) => handleInputChange('emergencyContact', e?.target?.value)}
                />
              </>
            ) : (
              <div className="space-y-4">
                <div>
                  <label className="text-sm font-medium text-muted-foreground">Email</label>
                  <p className="text-sm text-foreground mt-1">{employee?.email || employee?.correo}</p>
                </div>
                <div>
                  <label className="text-sm font-medium text-muted-foreground">Tel√©fono</label>
                  <p className="text-sm text-foreground mt-1">{employee?.phone || employee?.telefono || 'No especificado'}</p>
                </div>
                <div>
                  <label className="text-sm font-medium text-muted-foreground">Direcci√≥n</label>
                  <p className="text-sm text-foreground mt-1">{employee?.address || 'No especificada'}</p>
                </div>
                <div>
                  <label className="text-sm font-medium text-muted-foreground">Contacto de emergencia</label>
                  <p className="text-sm text-foreground mt-1">{employee?.emergencyContact || 'No especificado'}</p>
                </div>
              </div>
            )}
          </div>
        )}

        {activeTab === 'employment' && (
          <div className="space-y-4">
            {isEditing ? (
              <>
                <Select
                  label="Sitio de construcci√≥n"
                  options={siteOptions}
                  value={
                    editData?.obra_id ??
                    editData?.site ??
                    employee?.obra_id ??
                    employee?.site ??
                    ''
                  }
                  onChange={(value) => handleInputChange('obra_id', value)}
                  placeholder={sitesLoading ? 'Cargando sitios...' : 'Seleccionar sitio...'}
                  disabled={sitesLoading}
                />
                <Select
                  label="Supervisor"
                  options={supervisorOptions}
                  value={
                    editData?.supervisor_id ??
                    editData?.supervisor ??
                    employee?.supervisor_id ??
                    ''
                  }
                  onChange={(value) => handleInputChange('supervisor_id', value)}
                  placeholder={supervisorsLoading ? 'Cargando supervisores...' : 'Seleccionar supervisor...'}
                  disabled={supervisorsLoading}
                />
                <Select
                  label="Estado"
                  options={STATUS_OPTIONS}
                  value={editData?.status ?? (employee?.is_active ? 'active' : 'inactive')}
                  onChange={(value) => handleInputChange('status', value)}
                />
                <Input
                  label="Fecha de contrataci√≥n"
                  type="date"
                  value={editData?.hireDate || ''}
                  onChange={(e) => handleInputChange('hireDate', e?.target?.value)}
                />
                {userRole === 'admin' && (
                  <Input
                    label="Salario (hourly_rate)"
                    type="number"
                    value={editData?.hourly_rate ?? editData?.dailySalary ?? employee?.hourly_rate ?? ''}
                    onChange={(e) => handleInputChange('hourly_rate', e?.target?.value)}
                  />
                )}
                <Input
                  label="Puesto"
                  value={editData?.puesto ?? employee?.puesto ?? ''}
                  onChange={(e) => handleInputChange('puesto', e?.target?.value)}
                />
              </>
            ) : (
              <div className="space-y-4">
                <div>
                  <label className="text-sm font-medium text-muted-foreground">Sitio de construcci√≥n</label>
                  <p className="text-sm text-foreground mt-1">{siteLabel}</p>
                </div>
                <div>
                  <label className="text-sm font-medium text-muted-foreground">Supervisor</label>
                  <p className="text-sm text-foreground mt-1">{supervisorLabel}</p>
                </div>
                <div>
                  <label className="text-sm font-medium text-muted-foreground">Fecha de contrataci√≥n</label>
                  <p className="text-sm text-foreground mt-1">
                    {employee?.hireDate ? formatDate(employee.hireDate) : 'No especificada'}
                  </p>
                </div>
                {userRole === 'admin' && (
                  <div>
                    <label className="text-sm font-medium text-muted-foreground">Salario (hourly_rate)</label>
                    <p className="text-sm text-foreground mt-1">
                      {typeof employee?.hourly_rate === 'number' ? employee.hourly_rate : employee?.dailySalary || 'No especificado'}
                    </p>
                  </div>
                )}
                <div>
                  <label className="text-sm font-medium text-muted-foreground">Puesto</label>
                  <p className="text-sm text-foreground mt-1">{employee?.puesto || 'No especificado'}</p>
                </div>
                <div>
                  <label className="text-sm font-medium text-muted-foreground">√öltima asistencia</label>
                  <p className="text-sm text-foreground mt-1">
                    {employee?.lastAttendance ? formatDate(employee.lastAttendance) : 'Sin registro'}
                  </p>
                </div>
              </div>
            )}
          </div>
        )}

        {activeTab === 'documents' && (
          <div className="space-y-4">
            <div className="text-center py-8">
              <Icon name="FileText" size={48} className="mx-auto text-muted-foreground mb-4" />
              <h3 className="text-lg font-medium text-foreground mb-2">Documentos del empleado</h3>
              <p className="text-muted-foreground mb-4">Gestiona los documentos y archivos del empleado.</p>
              <Button variant="outline" iconName="Upload" iconSize={16}>
                Subir documento
              </Button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default EmployeeDetailPanel;

// src/administrator-employee-management-console/EmployeeFilters.jsx
import React, { useEffect, useMemo, useRef, useState } from 'react';
import Icon from '../../../components/AppIcon';
import Select from '../../../components/ui/Select';
import Input from '../../../components/ui/Input';
import Button from '../../../components/ui/Button';
import { Checkbox } from '../../../components/ui/Checkbox';

import { siteDataService } from '../../data/siteDataService';
import { supervisorDataService } from '../../data/supervisorDataService';

const EmployeeFilters = ({
  filters,
  onFiltersChange,
  onClearFilters,
  savedFilters = [],
  onSaveFilter,
  onLoadFilter,
}) => {
  const [isCollapsed, setIsCollapsed] = useState(false);
  const [filterName, setFilterName] = useState('');
  const [showSaveDialog, setShowSaveDialog] = useState(false);

  const [sites, setSites] = useState([]);
  const [sitesLoading, setSitesLoading] = useState(false);

  const [supervisors, setSupervisors] = useState([]);
  const [supervisorsLoading, setSupervisorsLoading] = useState(false);

  const mountedRef = useRef(false);

  useEffect(() => {
    mountedRef.current = true;
    return () => { mountedRef.current = false; };
  }, []);

  // Cargar sitios reales
  useEffect(() => {
    const loadSites = async () => {
      setSitesLoading(true);
      try {
        const res = await siteDataService.getSites();
        if (mountedRef.current && res?.success) setSites(res.data || []);
      } catch (e) {
        console.error('Error loading sites:', e);
      } finally {
        setSitesLoading(false);
      }
    };
    loadSites();
  }, []);

  // Cargar supervisores reales
  useEffect(() => {
    const loadSupervisors = async () => {
      setSupervisorsLoading(true);
      try {
        const res = await supervisorDataService.getSupervisors();
        if (mountedRef.current && res?.success) setSupervisors(res.data || []);
      } catch (e) {
        console.error('Error loading supervisors:', e);
      } finally {
        setSupervisorsLoading(false);
      }
    };
    loadSupervisors();
  }, []);

  const constructionSiteOptions = useMemo(() => {
    const base = [{ value: 'all', label: 'Todos los sitios' }];
    const dynamic = (sites || []).map(s => ({ value: s.id, label: s.nombre }));
    return base.concat(dynamic);
  }, [sites]);

  const supervisorOptions = useMemo(() => {
    const base = [{ value: 'all', label: 'Todos los supervisores' }];
    const dynamic = (supervisors || []).map(u => ({
      value: u.id,
      label: u.nombre || u.correo,
    }));
    return base.concat(dynamic);
  }, [supervisors]);

  const employmentStatuses = useMemo(() => {
    const current = filters?.status || [];
    return [
      { id: 'active',     label: 'Activo',     checked: current.includes('active') },
      { id: 'inactive',   label: 'Inactivo',   checked: current.includes('inactive') },
      { id: 'suspended',  label: 'Suspendido', checked: current.includes('suspended') },
      { id: 'terminated', label: 'Terminado',  checked: current.includes('terminated') },
    ];
  }, [filters?.status]);

  const handleStatusChange = (statusId, checked) => {
    const currentStatus = filters?.status || [];
    const next = checked ? [...new Set([...currentStatus, statusId])] : currentStatus.filter(s => s !== statusId);
    onFiltersChange({ ...filters, status: next });
  };

  const handleSaveFilter = () => {
    const name = filterName.trim();
    if (!name) return;
    onSaveFilter?.({
      name,
      filters,
      createdAt: new Date(),
    });
    setFilterName('');
    setShowSaveDialog(false);
  };

  const hasActiveFilters = () => {
    return Boolean(
      filters?.search ||
      (filters?.site && filters.site !== 'all') ||
      (filters?.supervisor && filters.supervisor !== 'all') ||
      (filters?.status && filters.status.length > 0) ||
      filters?.hireDateFrom ||
      filters?.hireDateTo
    );
  };

  return (
    <div className="bg-card border border-border rounded-lg">
      {/* Header */}
      <div className="flex items-center justify-between p-4 border-b border-border">
        <div className="flex items-center space-x-2">
          <Icon name="Filter" size={20} className="text-muted-foreground" />
          <h3 className="font-medium text-foreground">Filtros</h3>
          {hasActiveFilters() && (
            <span className="bg-primary text-primary-foreground text-xs px-2 py-1 rounded-full">
              Activos
            </span>
          )}
        </div>

        <div className="flex items-center space-x-2">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => setShowSaveDialog(true)}
            disabled={!hasActiveFilters()}
            iconName="Save"
            iconSize={16}
          >
            Guardar
          </Button>

          <Button
            variant="ghost"
            size="sm"
            onClick={onClearFilters}
            disabled={!hasActiveFilters()}
            iconName="X"
            iconSize={16}
          >
            Limpiar
          </Button>

          <Button
            variant="ghost"
            size="icon"
            onClick={() => setIsCollapsed(!isCollapsed)}
            iconName={isCollapsed ? 'ChevronDown' : 'ChevronUp'}
            iconSize={16}
          />
        </div>
      </div>

      {/* Content */}
      {!isCollapsed && (
        <div className="p-4 space-y-4">
          {/* Search */}
          <Input
            label="B√∫squeda general"
            type="search"
            placeholder="Buscar por nombre, ID, email..."
            value={filters?.search || ''}
            onChange={(e) => onFiltersChange({ ...filters, search: e?.target?.value })}
            className="mb-4"
          />

          {/* Construction Site */}
          <Select
            label="Sitio de construcci√≥n"
            options={constructionSiteOptions}
            value={filters?.site || 'all'}
            onChange={(value) => onFiltersChange({ ...filters, site: value })}
            className="mb-4"
            placeholder={sitesLoading ? 'Cargando sitios...' : 'Seleccionar sitio...'}
            disabled={sitesLoading}
          />

          {/* Supervisor */}
          <Select
            label="Supervisor"
            options={supervisorOptions}
            value={filters?.supervisor || 'all'}
            onChange={(value) => onFiltersChange({ ...filters, supervisor: value })}
            className="mb-4"
            placeholder={supervisorsLoading ? 'Cargando supervisores...' : 'Seleccionar supervisor...'}
            disabled={supervisorsLoading}
          />

          {/* Employment Status */}
          <div className="space-y-2">
            <label className="text-sm font-medium text-foreground">Estado de empleo</label>
            <div className="space-y-2">
              {employmentStatuses.map((status) => (
                <Checkbox
                  key={status.id}
                  label={status.label}
                  checked={status.checked}
                  onChange={(e) => handleStatusChange(status.id, e?.target?.checked)}
                />
              ))}
            </div>
          </div>

          {/* Hire Date Range */}
          <div className="grid grid-cols-2 gap-4">
            <Input
              label="Fecha de contrataci√≥n desde"
              type="date"
              value={filters?.hireDateFrom || ''}
              onChange={(e) => onFiltersChange({ ...filters, hireDateFrom: e?.target?.value })}
            />
            <Input
              label="Fecha de contrataci√≥n hasta"
              type="date"
              value={filters?.hireDateTo || ''}
              onChange={(e) => onFiltersChange({ ...filters, hireDateTo: e?.target?.value })}
            />
          </div>

          {/* Saved Filters */}
          {savedFilters?.length > 0 && (
            <div className="pt-4 border-t border-border">
              <label className="text-sm font-medium text-foreground mb-2 block">Filtros guardados</label>
              <div className="space-y-2">
                {savedFilters.map((savedFilter, index) => (
                  <button
                    key={index}
                    onClick={() => onLoadFilter?.(savedFilter)}
                    className="w-full flex items-center justify-between p-2 text-sm bg-muted hover:bg-muted/80 rounded-md transition-colors duration-150 ease-out-cubic"
                  >
                    <span className="text-foreground">{savedFilter?.name}</span>
                    <Icon name="ChevronRight" size={14} className="text-muted-foreground" />
                  </button>
                ))}
              </div>
            </div>
          )}
        </div>
      )}

      {/* Save Filter Dialog */}
      {showSaveDialog && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-card border border-border rounded-lg p-6 w-96 max-w-full mx-4">
            <h3 className="text-lg font-semibold text-foreground mb-4">Guardar filtro</h3>

            <Input
              label="Nombre del filtro"
              placeholder="Ej: Empleados activos - Obra Central"
              value={filterName}
              onChange={(e) => setFilterName(e?.target?.value)}
              className="mb-4"
            />

            <div className="flex justify-end space-x-2">
              <Button
                variant="outline"
                onClick={() => {
                  setShowSaveDialog(false);
                  setFilterName('');
                }}
              >
                Cancelar
              </Button>
              <Button onClick={handleSaveFilter} disabled={!filterName.trim()}>
                Guardar
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default EmployeeFilters;

// src/administrator-employee-management-console/EmployeeTable.jsx
import React, { useState } from 'react';
import Icon from '../../../components/AppIcon';
import Image from '../../../components/AppImage';
import Button from '../../../components/ui/Button';

const EmployeeTable = ({
  employees,
  selectedEmployees,
  onEmployeeSelect,
  onSelectAll,
  onEmployeeClick,
  onSort,
  sortConfig,
  onDelete,
  onRestore,
  userRole = 'admin',
  showDeleteActions = false,
  loading = false,
}) => {
  const [hoveredRow, setHoveredRow] = useState(null);

  const getStatusColor = (status) => {
    const colors = {
      active: 'bg-success text-success-foreground',
      inactive: 'bg-secondary text-secondary-foreground',
      suspended: 'bg-warning text-warning-foreground',
      terminated: 'bg-error text-error-foreground',
      deleted: 'bg-red-100 text-red-800',
    };
    return colors?.[status] || colors?.inactive;
  };

  const getStatusLabel = (status) => {
    const labels = {
      active: 'Activo',
      inactive: 'Inactivo',
      suspended: 'Suspendido',
      terminated: 'Terminado',
      deleted: 'Eliminado',
    };
    return labels?.[status] || status || '‚Äî';
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    const d = new Date(dateString);
    if (Number.isNaN(d.getTime())) return 'N/A';
    return d.toLocaleDateString('es-ES', { day: '2-digit', month: '2-digit', year: 'numeric' });
  };

  const formatLastAttendance = (lastAttendance) => {
    if (!lastAttendance) return 'Sin registro';
    const date = new Date(lastAttendance);
    if (Number.isNaN(date.getTime())) return 'Sin registro';
    const now = new Date();
    const diffTime = Math.abs(now - date);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    if (diffDays === 1) return 'Ayer';
    if (diffDays <= 7) return `Hace ${diffDays} d√≠as`;
    return formatDate(lastAttendance);
  };

  const handleSort = (column) => {
    const direction = sortConfig?.column === column && sortConfig?.direction === 'asc' ? 'desc' : 'asc';
    onSort?.({ column, direction });
  };

  const getSortIcon = (column) => {
    if (sortConfig?.column !== column) return 'ArrowUpDown';
    return sortConfig?.direction === 'asc' ? 'ArrowUp' : 'ArrowDown';
  };

  const isAllSelected = employees?.length > 0 && selectedEmployees?.length === employees?.length;
  const isIndeterminate = selectedEmployees?.length > 0 && selectedEmployees?.length < employees?.length;

  // Click fila (evita clics en botones)
  const handleEmployeeRowClick = (employee, event) => {
    if (event?.target?.closest('button') || event?.target?.closest('.action-button')) return;
    onEmployeeClick?.(employee);
  };

  // Helpers de compatibilidad (nombres distintos seg√∫n servicio/joins)
  const getEmployeeId = (e) => e?.employee_id ?? e?.employeeId ?? e?.id ?? 'N/A';
  const getFullName = (e) => e?.full_name ?? e?.name ?? e?.nombre ?? 'Sin nombre';
  const getEmail = (e) =>
    e?.user_profiles?.email ??
    e?.email ??
    e?.correo ??
    e?.user?.email ??
    'Sin email';
  const getAvatar = (e) => e?.profile_picture_url ?? e?.avatar ?? null;
  const getSiteName = (e) =>
    e?.construction_sites?.name ??
    e?.construction_site?.name ??
    e?.obras?.nombre ??
    e?.sitio_asignado ??
    e?.site ??
    'Sin asignar';
  const getSupervisorName = (e) =>
    e?.supervisor?.full_name ??
    e?.supervisor?.nombre ??
    e?.supervisor_name ??
    e?.supervisor ??
    'Sin supervisor';
  const getHireDate = (e) => e?.hire_date ?? e?.hireDate ?? e?.fecha_contratacion ?? null;
  const getLastAttendance = (e) => e?.last_attendance_date ?? e?.lastAttendance ?? null;

  return (
    <div className="bg-card border border-border rounded-lg overflow-hidden">
      {/* Loading State */}
      {loading && employees?.length === 0 && (
        <div className="p-12 text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-muted-foreground">Cargando empleados...</p>
        </div>
      )}

      {/* Table Header */}
      <div className="overflow-x-auto">
        <table className="w-full">
          <thead className="bg-muted/50 border-b border-border">
            <tr>
              <th className="w-12 p-4">
                <input
                  type="checkbox"
                  checked={isAllSelected}
                  ref={(el) => {
                    if (el) el.indeterminate = isIndeterminate;
                  }}
                  onChange={(e) => onSelectAll?.(e?.target?.checked)}
                  className="w-4 h-4 text-primary bg-background border-border rounded focus:ring-primary focus:ring-2"
                />
              </th>

              <th className="text-left p-4">
                <button
                  onClick={() => handleSort('employee_id')}
                  className="flex items-center space-x-1 text-sm font-medium text-foreground hover:text-primary transition-colors duration-150 ease-out-cubic"
                >
                  <span>ID</span>
                  <Icon name={getSortIcon('employee_id')} size={14} />
                </button>
              </th>

              <th className="text-left p-4">
                <button
                  onClick={() => handleSort('full_name')}
                  className="flex items-center space-x-1 text-sm font-medium text-foreground hover:text-primary transition-colors duration-150 ease-out-cubic"
                >
                  <span>Empleado</span>
                  <Icon name={getSortIcon('full_name')} size={14} />
                </button>
              </th>

              <th className="text-left p-4">
                <button
                  onClick={() => handleSort('site')}
                  className="flex items-center space-x-1 text-sm font-medium text-foreground hover:text-primary transition-colors duration-150 ease-out-cubic"
                >
                  <span>Sitio</span>
                  <Icon name={getSortIcon('site')} size={14} />
                </button>
              </th>

              <th className="text-left p-4">
                <button
                  onClick={() => handleSort('supervisor')}
                  className="flex items-center space-x-1 text-sm font-medium text-foreground hover:text-primary transition-colors duration-150 ease-out-cubic"
                >
                  <span>Supervisor</span>
                  <Icon name={getSortIcon('supervisor')} size={14} />
                </button>
              </th>

              <th className="text-left p-4">
                <button
                  onClick={() => handleSort('hire_date')}
                  className="flex items-center space-x-1 text-sm font-medium text-foreground hover:text-primary transition-colors duration-150 ease-out-cubic"
                >
                  <span>Fecha de contrataci√≥n</span>
                  <Icon name={getSortIcon('hire_date')} size={14} />
                </button>
              </th>

              <th className="text-left p-4">
                <button
                  onClick={() => handleSort('status')}
                  className="flex items-center space-x-1 text-sm font-medium text-foreground hover:text-primary transition-colors duration-150 ease-out-cubic"
                >
                  <span>Estado</span>
                  <Icon name={getSortIcon('status')} size={14} />
                </button>
              </th>

              <th className="text-left p-4">
                <button
                  onClick={() => handleSort('last_attendance_date')}
                  className="flex items-center space-x-1 text-sm font-medium text-foreground hover:text-primary transition-colors duration-150 ease-out-cubic"
                >
                  <span>√öltima asistencia</span>
                  <Icon name={getSortIcon('last_attendance_date')} size={14} />
                </button>
              </th>

              <th className="text-center p-4 w-32">
                <span className="text-sm font-medium text-foreground">Acciones</span>
              </th>
            </tr>
          </thead>

          <tbody>
            {employees?.map((employee) => {
              const id = employee?.id;
              const empId = getEmployeeId(employee);
              const name = getFullName(employee);
              const email = getEmail(employee);
              const avatar = getAvatar(employee);
              const siteName = getSiteName(employee);
              const supervisorName = getSupervisorName(employee);
              const hireDate = getHireDate(employee);
              const lastAttendance = getLastAttendance(employee);

              return (
                <tr
                  key={id}
                  className={`
                    border-b border-border hover:bg-muted/30 transition-colors duration-150 ease-out-cubic cursor-pointer
                    ${selectedEmployees?.includes(id) ? 'bg-primary/5' : ''}
                    ${employee?.status === 'deleted' ? 'bg-red-50' : ''}
                  `}
                  onMouseEnter={() => setHoveredRow(id)}
                  onMouseLeave={() => setHoveredRow(null)}
                  onClick={(e) => handleEmployeeRowClick(employee, e)}
                >
                  <td className="p-4" onClick={(e) => e?.stopPropagation()}>
                    <input
                      type="checkbox"
                      checked={selectedEmployees?.includes(id)}
                      onChange={(e) => onEmployeeSelect?.(id, e?.target?.checked)}
                      className="w-4 h-4 text-primary bg-background border-border rounded focus:ring-primary focus:ring-2"
                    />
                  </td>

                  <td className="p-4">
                    <span className="text-sm font-mono text-muted-foreground">{empId}</span>
                  </td>

                  <td className="p-4">
                    <div className="flex items-center space-x-3">
                      <div className="flex-shrink-0">
                        {avatar ? (
                          <Image src={avatar} alt={name} className="w-8 h-8 rounded-full object-cover" />
                        ) : (
                          <div className="w-8 h-8 bg-primary text-primary-foreground rounded-full flex items-center justify-center text-xs font-medium">
                            {String(name || '')
                              .split(' ')
                              .map((n) => n?.[0])
                              .join('')
                              .toUpperCase()}
                          </div>
                        )}
                      </div>
                      <div className="min-w-0 flex-1">
                        <p className="text-sm font-medium text-foreground truncate">{name}</p>
                        <p className="text-xs text-muted-foreground truncate">{email}</p>
                      </div>
                    </div>
                  </td>

                  <td className="p-4">
                    <div className="flex items-center space-x-2">
                      <Icon name="MapPin" size={14} className="text-muted-foreground" />
                      <span className="text-sm text-foreground">{siteName}</span>
                    </div>
                  </td>

                  <td className="p-4">
                    <span className="text-sm text-foreground">{supervisorName}</span>
                  </td>

                  <td className="p-4">
                    <span className="text-sm text-muted-foreground">{formatDate(hireDate)}</span>
                  </td>

                  <td className="p-4">
                    <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(employee?.status)}`}>
                      {getStatusLabel(employee?.status)}
                    </span>
                  </td>

                  <td className="p-4">
                    <span className="text-sm text-muted-foreground">{formatLastAttendance(lastAttendance)}</span>
                  </td>

                  <td className="p-4" onClick={(e) => e?.stopPropagation()}>
                    <div className="flex items-center justify-center space-x-1 action-button">
                      {/* View */}
                      <Button
                        variant="ghost"
                        size="icon"
                        onClick={() => onEmployeeClick?.(employee)}
                        iconName="Eye"
                        iconSize={16}
                        className="h-8 w-8 hover:bg-blue-100 hover:text-blue-600"
                        title="Ver detalles"
                      />

                      {hoveredRow === id && (
                        <>
                          {/* Edit */}
                          <Button
                            variant="ghost"
                            size="icon"
                            onClick={(e) => {
                              e?.stopPropagation();
                              onEmployeeClick?.(employee);
                            }}
                            iconName="Edit"
                            iconSize={16}
                            className="h-8 w-8 hover:bg-blue-100 hover:text-blue-600"
                            title="Editar empleado"
                          />

                          {/* Delete / Restore */}
                          {showDeleteActions && (
                            <>
                              {employee?.status === 'deleted' ? (
                                <Button
                                  variant="ghost"
                                  size="icon"
                                  onClick={(e) => {
                                    e?.stopPropagation();
                                    onRestore?.(employee);
                                  }}
                                  iconName="RotateCcw"
                                  iconSize={16}
                                  className="h-8 w-8 hover:bg-green-100 hover:text-green-600"
                                  title="Restaurar empleado"
                                />
                              ) : (
                                <Button
                                  variant="ghost"
                                  size="icon"
                                  onClick={(e) => {
                                    e?.stopPropagation();
                                    onDelete?.(employee);
                                  }}
                                  iconName="Trash2"
                                  iconSize={16}
                                  className="h-8 w-8 hover:bg-red-100 hover:text-red-600"
                                  title="Eliminar empleado"
                                />
                              )}
                            </>
                          )}

                          {/* More */}
                          <Button
                            variant="ghost"
                            size="icon"
                            onClick={(e) => {
                              e?.stopPropagation();
                              // abrir men√∫ "m√°s opciones"
                            }}
                            iconName="MoreHorizontal"
                            iconSize={16}
                            className="h-8 w-8 hover:bg-gray-100 hover:text-gray-600"
                            title="M√°s opciones"
                          />
                        </>
                      )}
                    </div>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>

      {/* Empty State */}
      {!loading && employees?.length === 0 && (
        <div className="p-12 text-center">
          <Icon name="Users" size={48} className="mx-auto text-muted-foreground mb-4" />
          <h3 className="text-lg font-medium text-foreground mb-2">No se encontraron empleados</h3>
          <p className="text-muted-foreground">Ajusta los filtros o agrega nuevos empleados al sistema.</p>
        </div>
      )}
    </div>
  );
};

export default EmployeeTable;

