// advanced-payroll-calculation-engine-with-comprehensive-wage-management/components/BulkProcessingTools.jsx
import React, { useState, useMemo } from 'react';
import { Users, PlayCircle, Download, FileText, Clock, AlertCircle } from 'lucide-react';
import CurrencyDisplay from '../../../components/ui/CurrencyDisplay';

/**
 * Props:
 * - selectedEmployees: string[] (ids)
 * - employees: Array<{ id: string, full_name?: string, employee_id?: string, payroll?: {...} }>
 * - onBulkCalculate: () => Promise<void>
 * - processing: boolean
 * - weekRange?: { start?: Date, end?: Date }
 * - avgProcessMsPerEmployee?: number  // opcional, para mostrar ETA real (no mock)
 */
export default function BulkProcessingTools({
  selectedEmployees = [],
  employees = [],
  onBulkCalculate,
  processing = false,
  weekRange,
  avgProcessMsPerEmployee, // opcional: si no viene, no mostramos ETA
}) {
  const [bulkOptions, setBulkOptions] = useState({
    includeOvertimeCalculation: true,
    includeBonuses: false,
    includeDeductions: true,
    autoApprove: false,
    generateReports: true,
  });

  const [progress, setProgress] = useState({
    total: 0,
    completed: 0,
    errors: [],
    currentEmployee: null,
  });

  const selectedEmployeeData = useMemo(
    () => employees?.filter((emp) => selectedEmployees?.includes(emp?.id)) || [],
    [employees, selectedEmployees]
  );

  const estimatedSeconds = useMemo(() => {
    if (!avgProcessMsPerEmployee || !Number.isFinite(avgProcessMsPerEmployee)) return null;
    const totalMs = (selectedEmployees?.length || 0) * avgProcessMsPerEmployee;
    return Math.max(0, Math.round(totalMs / 1000));
  }, [avgProcessMsPerEmployee, selectedEmployees?.length]);

  const handleBulkProcess = async () => {
    if (!selectedEmployees?.length || processing) return;

    setProgress({
      total: selectedEmployees.length,
      completed: 0,
      errors: [],
      currentEmployee: null,
    });

    try {
      await onBulkCalculate?.({
        options: bulkOptions,
        onTick: (info) => {
          // Permite que el caller reporte progreso (opcional)
          // info: { completed, total, currentEmployeeName, error? }
          setProgress((prev) => ({
            total: info?.total ?? prev.total,
            completed: info?.completed ?? prev.completed,
            errors: info?.error ? [...prev.errors, info.error] : prev.errors,
            currentEmployee: info?.currentEmployeeName ?? prev.currentEmployee,
          }));
        },
      });
    } catch (error) {
      setProgress((prev) => ({
        ...prev,
        errors: [...prev.errors, error?.message || 'Error desconocido en procesamiento masivo'],
      }));
    }
  };

  // CSV helpers
  const escapeCSV = (value) => {
    const str = value == null ? '' : String(value);
    if (/[",\n]/.test(str)) {
      return `"${str.replace(/"/g, '""')}"`;
    }
    return str;
  };

  const toISODate = (d) => {
    try {
      if (d instanceof Date) return d.toISOString().split('T')[0];
      const dd = new Date(d);
      return Number.isNaN(dd.getTime()) ? undefined : dd.toISOString().split('T')[0];
    } catch {
      return undefined;
    }
  };

  const handleExportPayroll = () => {
    const headers = [
      'Empleado',
      'C√≥digo',
      'Horas Regulares',
      'Horas Extra',
      'Salario Base',
      'Pago Horas Extra',
      'Bonificaciones',
      'Deducciones',
      'Salario Bruto',
      'Salario Neto',
    ];

    const rows = selectedEmployeeData?.map((emp) => {
      const p = emp?.payroll || {};
      return [
        emp?.full_name ?? emp?.name ?? '‚Äî',
        emp?.employee_id ?? emp?.codigo ?? emp?.id ?? '‚Äî',
        p?.regularHours ?? 0,
        p?.overtimeHours ?? 0,
        p?.basePay ?? 0,
        p?.overtimePay ?? 0,
        p?.bonuses ?? 0,
        p?.deductions ?? 0,
        p?.grossPay ?? 0,
        p?.netPay ?? 0,
      ]
        .map(escapeCSV)
        .join(',');
    });

    const csvContent = [headers.join(','), ...(rows || [])].join('\n');
    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const url = window.URL?.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    const startStr = toISODate(weekRange?.start) || 'periodo';
    a.download = `nomina_${startStr}.csv`;
    document.body?.appendChild(a);
    a?.click();
    document.body?.removeChild(a);
    window.URL?.revokeObjectURL(url);
  };

  // Totales ‚Äúlive‚Äù
  const totalGross = useMemo(
    () => selectedEmployeeData?.reduce((sum, emp) => sum + (emp?.payroll?.grossPay || 0), 0) || 0,
    [selectedEmployeeData]
  );
  const totalRegular = useMemo(
    () => selectedEmployeeData?.reduce((sum, emp) => sum + (emp?.payroll?.regularHours || 0), 0) || 0,
    [selectedEmployeeData]
  );
  const totalOvertime = useMemo(
    () => selectedEmployeeData?.reduce((sum, emp) => sum + (emp?.payroll?.overtimeHours || 0), 0) || 0,
    [selectedEmployeeData]
  );

  return (
    <div className="space-y-6">
      {/* Processing Options */}
      <div className="bg-white rounded-lg shadow">
        <div className="p-6">
          <h2 className="text-xl font-bold text-gray-900 mb-4">
            <Users className="h-6 w-6 inline mr-2 text-blue-600" />
            Herramientas de Procesamiento Masivo
          </h2>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {/* Selection Summary */}
            <div>
              <h3 className="text-lg font-semibold text-gray-900 mb-3">Empleados Seleccionados</h3>

              <div className="bg-blue-50 rounded-lg p-4 mb-4">
                <div className="flex items-center justify-between">
                  <span className="text-blue-800 font-medium">
                    {selectedEmployees?.length || 0} empleados seleccionados
                  </span>
                  <Clock className="h-5 w-5 text-blue-600" />
                </div>
                {typeof estimatedSeconds === 'number' && (
                  <p className="text-blue-600 text-sm mt-1">Tiempo estimado: ~{estimatedSeconds} segundos</p>
                )}
              </div>

              <div className="space-y-2 max-h-32 overflow-y-auto">
                {selectedEmployeeData?.map((employee) => (
                  <div key={employee?.id} className="flex items-center justify-between p-2 bg-gray-50 rounded">
                    <span className="text-sm font-medium">{employee?.full_name ?? employee?.name ?? '‚Äî'}</span>
                    <span className="text-xs text-gray-500">{employee?.employee_id ?? employee?.id ?? '‚Äî'}</span>
                  </div>
                ))}
              </div>
            </div>

            {/* Processing Options */}
            <div>
              <h3 className="text-lg font-semibold text-gray-900 mb-3">Opciones de Procesamiento</h3>

              <div className="space-y-3">
                <label className="flex items-center">
                  <input
                    type="checkbox"
                    checked={bulkOptions?.includeOvertimeCalculation}
                    onChange={(e) =>
                      setBulkOptions((prev) => ({
                        ...prev,
                        includeOvertimeCalculation: e?.target?.checked,
                      }))
                    }
                    className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                  />
                  <span className="ml-2 text-sm text-gray-700">Incluir c√°lculo de horas extra</span>
                </label>

                <label className="flex items-center">
                  <input
                    type="checkbox"
                    checked={bulkOptions?.includeBonuses}
                    onChange={(e) =>
                      setBulkOptions((prev) => ({
                        ...prev,
                        includeBonuses: e?.target?.checked,
                      }))
                    }
                    className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                  />
                  <span className="ml-2 text-sm text-gray-700">Incluir bonificaciones autom√°ticas</span>
                </label>

                <label className="flex items-center">
                  <input
                    type="checkbox"
                    checked={bulkOptions?.includeDeductions}
                    onChange={(e) =>
                      setBulkOptions((prev) => ({
                        ...prev,
                        includeDeductions: e?.target?.checked,
                      }))
                    }
                    className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                  />
                  <span className="ml-2 text-sm text-gray-700">Aplicar deducciones (impuestos, incidencias)</span>
                </label>

                <label className="flex items-center">
                  <input
                    type="checkbox"
                    checked={bulkOptions?.autoApprove}
                    onChange={(e) =>
                      setBulkOptions((prev) => ({
                        ...prev,
                        autoApprove: e?.target?.checked,
                      }))
                    }
                    className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                  />
                  <span className="ml-2 text-sm text-gray-700">Auto-aprobar n√≥minas calculadas</span>
                </label>

                <label className="flex items-center">
                  <input
                    type="checkbox"
                    checked={bulkOptions?.generateReports}
                    onChange={(e) =>
                      setBulkOptions((prev) => ({
                        ...prev,
                        generateReports: e?.target?.checked,
                      }))
                    }
                    className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                  />
                  <span className="ml-2 text-sm text-gray-700">Generar reportes autom√°ticamente</span>
                </label>
              </div>
            </div>
          </div>

          {/* Action Buttons */}
          <div className="flex items-center space-x-4 mt-6">
            <button
              onClick={handleBulkProcess}
              disabled={selectedEmployees?.length === 0 || processing}
              className="flex-1 px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center"
            >
              <PlayCircle className="h-5 w-5 mr-2" />
              {processing ? 'Procesando...' : 'Iniciar Procesamiento Masivo'}
            </button>

            <button
              onClick={handleExportPayroll}
              disabled={selectedEmployees?.length === 0}
              className="px-6 py-3 bg-green-600 text-white rounded-lg hover:bg-green-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center"
            >
              <Download className="h-5 w-5 mr-2" />
              Exportar CSV
            </button>
          </div>
        </div>
      </div>

      {/* Progress Tracking */}
      {processing && (
        <div className="bg-white rounded-lg shadow">
          <div className="p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">
              <Clock className="h-5 w-5 inline mr-2 text-blue-600" />
              Progreso de Procesamiento
            </h3>

            <div className="mb-4">
              <div className="flex justify-between text-sm text-gray-600 mb-2">
                <span>
                  Progreso: {progress?.completed} de {progress?.total}
                </span>
                <span>{progress?.total ? Math.round((progress?.completed / progress?.total) * 100) : 0}%</span>
              </div>
              <div className="w-full bg-gray-200 rounded-full h-2">
                <div
                  className="bg-blue-600 h-2 rounded-full transition-all duration-300"
                  style={{
                    width: `${
                      progress?.total ? (progress?.completed / progress?.total) * 100 : 0
                    }%`,
                  }}
                ></div>
              </div>
            </div>

            {progress?.currentEmployee && (
              <div className="flex items-center text-sm text-gray-600 mb-4">
                <Clock className="h-4 w-4 mr-2 animate-spin" />
                Procesando: {progress?.currentEmployee}
              </div>
            )}

            {progress?.errors?.length > 0 && (
              <div className="space-y-2">
                <h4 className="text-sm font-medium text-red-800 flex items-center">
                  <AlertCircle className="h-4 w-4 mr-2" />
                  Errores encontrados:
                </h4>
                {progress?.errors?.map((error, index) => (
                  <div key={index} className="text-sm text-red-600 bg-red-50 p-2 rounded">
                    {error}
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      )}

      {/* Quick Stats */}
      <div className="bg-white rounded-lg shadow">
        <div className="p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">
            <FileText className="h-5 w-5 inline mr-2 text-blue-600" />
            Resumen de Procesamiento
          </h3>

          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <div className="text-center">
              <div className="text-2xl font-bold text-blue-600">{selectedEmployees?.length || 0}</div>
              <div className="text-sm text-gray-600">Empleados Seleccionados</div>
            </div>

            <div className="text-center">
              <div className="text-2xl font-bold text-green-600">
                <CurrencyDisplay amount={totalGross} />
              </div>
              <div className="text-sm text-gray-600">Total Salario Bruto</div>
            </div>

            <div className="text-center">
              <div className="text-2xl font-bold text-purple-600">{totalRegular}h</div>
              <div className="text-sm text-gray-600">Horas Regulares</div>
            </div>

            <div className="text-center">
              <div className="text-2xl font-bold text-orange-600">{totalOvertime}h</div>
              <div className="text-sm text-gray-600">Horas Extra</div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

// advanced-payroll-calculation-engine-with-comprehensive-wage-management/components/EmployeePayrollGrid.jsx
import React, { useMemo, useState, useCallback } from 'react';
import { Check, Clock, DollarSign, User, MapPin, Eye, Calculator } from 'lucide-react';
import CurrencyDisplay from '../../../components/ui/CurrencyDisplay';

/**
 * Props:
 * - employees: Array<{
 *     id: string,
 *     full_name?: string, name?: string,
 *     email?: string | { user_profiles?: { email?: string } },
 *     employee_id?: string,
 *     construction_sites?: { name?: string },
 *     site?: string, site_name?: string
 *   }>
 * - payrollData: Array<{ employeeId: string, id?: string, regularHours?: number, overtimeHours?: number, grossPay?: number, netPay?: number, processed?: boolean }>
 * - selectedEmployees: string[]
 * - onSelectionChange: (ids: string[]) => void
 * - onCalculatePayroll: (employeeId: string) => void | Promise<void>
 * - onProcessPayroll: (payrollId: string) => void | Promise<void>
 * - processing: boolean
 */
export default function EmployeePayrollGrid({
  employees = [],
  payrollData = [],
  selectedEmployees = [],
  onSelectionChange,
  onCalculatePayroll,
  onProcessPayroll,
  processing = false
}) {
  const [sortField, setSortField] = useState('full_name');
  const [sortDirection, setSortDirection] = useState('asc');
  const [filterStatus, setFilterStatus] = useState('all');

  // Index payroll by employeeId para O(1)
  const payrollByEmployeeId = useMemo(() => {
    const map = new Map();
    for (const p of payrollData) {
      if (p && p.employeeId != null) map.set(String(p.employeeId), p);
    }
    return map;
  }, [payrollData]);

  // Combina empleado + n√≥mina con fallbacks consistentes
  const combinedData = useMemo(() => {
    return (employees || []).map((employee) => {
      const payroll = payrollByEmployeeId.get(String(employee?.id)) || {};
      const fullName = employee?.full_name ?? employee?.name ?? 'Sin nombre';
      const email =
        employee?.user_profiles?.email ??
        employee?.email ??
        'Sin email';
      const siteName =
        employee?.construction_sites?.name ??
        employee?.site_name ??
        employee?.site ??
        'Sin asignar';
      const code = employee?.employee_id ?? 'N/A';
      return {
        ...employee,
        _fullName: fullName,
        _email: email,
        _siteName: siteName,
        _code: code,
        payroll
      };
    });
  }, [employees, payrollByEmployeeId]);

  // Filtro por estado de n√≥mina
  const filteredData = useMemo(() => {
    if (filterStatus === 'all') return combinedData;

    return combinedData.filter((item) => {
      const p = item?.payroll || {};
      if (filterStatus === 'processed') return !!p?.processed;
      if (filterStatus === 'pending') return !p?.processed && (p?.grossPay || 0) > 0;
      if (filterStatus === 'no_payroll') return !(p?.grossPay > 0);
      return true;
    });
  }, [combinedData, filterStatus]);

  // Ordenamiento estable y seguro
  const sortedData = useMemo(() => {
    const arr = filteredData.slice(); // no mutar
    const dir = sortDirection === 'asc' ? 1 : -1;

    arr.sort((a, b) => {
      let aVal;
      let bVal;

      switch (sortField) {
        case 'full_name':
          aVal = a?._fullName?.toLowerCase?.() || '';
          bVal = b?._fullName?.toLowerCase?.() || '';
          break;
        case 'employee_id':
          aVal = String(a?._code ?? '');
          bVal = String(b?._code ?? '');
          break;
        case 'grossPay':
          aVal = Number(a?.payroll?.grossPay || 0);
          bVal = Number(b?.payroll?.grossPay || 0);
          break;
        default:
          // fallback: intenta por campo directo
          aVal = a?.[sortField];
          bVal = b?.[sortField];
          if (typeof aVal === 'string') aVal = aVal.toLowerCase();
          if (typeof bVal === 'string') bVal = bVal.toLowerCase();
          if (aVal == null) aVal = '';
          if (bVal == null) bVal = '';
      }

      if (aVal > bVal) return dir;
      if (aVal < bVal) return -dir;
      return 0;
    });

    return arr;
  }, [filteredData, sortField, sortDirection]);

  const handleSort = useCallback((field) => {
    setSortField((prev) => (prev === field ? prev : field));
    setSortDirection((prevDir) => (sortField === field ? (prevDir === 'asc' ? 'desc' : 'asc') : 'asc'));
  }, [sortField]);

  const handleSelectAll = useCallback(() => {
    if (!sortedData.length) {
      onSelectionChange?.([]);
      return;
    }
    const allIds = sortedData.map((item) => item?.id).filter(Boolean);
    const allSelected = allIds.length > 0 && allIds.every((id) => selectedEmployees.includes(id));
    onSelectionChange?.(allSelected ? [] : allIds);
  }, [sortedData, selectedEmployees, onSelectionChange]);

  const handleSelectEmployee = useCallback(
    (employeeId) => {
      if (!employeeId) return;
      if (selectedEmployees.includes(employeeId)) {
        onSelectionChange?.(selectedEmployees.filter((id) => id !== employeeId));
      } else {
        onSelectionChange?.([...selectedEmployees, employeeId]);
      }
    },
    [selectedEmployees, onSelectionChange]
  );

  const getStatusBadge = (payroll) => {
    if (payroll?.processed) {
      return <span className="px-2 py-1 bg-green-100 text-green-800 text-xs rounded-full">Procesada</span>;
    }
    if ((payroll?.grossPay || 0) > 0) {
      return <span className="px-2 py-1 bg-yellow-100 text-yellow-800 text-xs rounded-full">Pendiente</span>;
    }
    return <span className="px-2 py-1 bg-gray-100 text-gray-800 text-xs rounded-full">Sin N√≥mina</span>;
  };

  const allVisibleSelected =
    sortedData.length > 0 &&
    sortedData.every((item) => selectedEmployees.includes(item?.id));

  return (
    <div className="bg-white rounded-lg shadow">
      <div className="p-6">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-xl font-bold text-gray-900">
            <User className="h-6 w-6 inline mr-2 text-blue-600" />
            N√≥mina de Empleados
          </h2>

          <div className="flex items-center space-x-4">
            <select
              value={filterStatus}
              onChange={(e) => setFilterStatus(e?.target?.value)}
              className="px-3 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
            >
              <option value="all">Todos los Estados</option>
              <option value="processed">Procesadas</option>
              <option value="pending">Pendientes</option>
              <option value="no_payroll">Sin N√≥mina</option>
            </select>

            <span className="text-sm text-gray-600">
              {selectedEmployees?.length || 0} de {sortedData?.length || 0} seleccionados
            </span>
          </div>
        </div>

        {/* Tabla */}
        <div className="overflow-x-auto">
          <table className="w-full table-auto">
            <thead>
              <tr className="bg-gray-50">
                <th className="px-4 py-3 text-left">
                  <input
                    type="checkbox"
                    checked={allVisibleSelected}
                    onChange={handleSelectAll}
                    className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                    aria-label="Seleccionar todos"
                  />
                </th>

                <th
                  className="px-4 py-3 text-left text-sm font-medium text-gray-700 cursor-pointer hover:bg-gray-100"
                  onClick={() => handleSort('full_name')}
                >
                  <div className="flex items-center">
                    Empleado
                    {sortField === 'full_name' && (
                      <span className="ml-1">{sortDirection === 'asc' ? '‚Üë' : '‚Üì'}</span>
                    )}
                  </div>
                </th>

                <th
                  className="px-4 py-3 text-left text-sm font-medium text-gray-700 cursor-pointer hover:bg-gray-100"
                  onClick={() => handleSort('employee_id')}
                >
                  <div className="flex items-center">
                    C√≥digo
                    {sortField === 'employee_id' && (
                      <span className="ml-1">{sortDirection === 'asc' ? '‚Üë' : '‚Üì'}</span>
                    )}
                  </div>
                </th>

                <th className="px-4 py-3 text-left text-sm font-medium text-gray-700">Sitio</th>

                <th className="px-4 py-3 text-left text-sm font-medium text-gray-700">
                  <Clock className="h-4 w-4 inline mr-1" />
                  Horas
                </th>

                <th
                  className="px-4 py-3 text-left text-sm font-medium text-gray-700 cursor-pointer hover:bg-gray-100"
                  onClick={() => handleSort('grossPay')}
                >
                  <div className="flex items-center">
                    <DollarSign className="h-4 w-4 mr-1" />
                    Salario Bruto
                    {sortField === 'grossPay' && (
                      <span className="ml-1">{sortDirection === 'asc' ? '‚Üë' : '‚Üì'}</span>
                    )}
                  </div>
                </th>

                <th className="px-4 py-3 text-left text-sm font-medium text-gray-700">Estado</th>

                <th className="px-4 py-3 text-left text-sm font-medium text-gray-700">Acciones</th>
              </tr>
            </thead>

            <tbody className="divide-y divide-gray-200">
              {sortedData?.map((employee) => (
                <tr key={employee?.id} className="hover:bg-gray-50">
                  <td className="px-4 py-3">
                    <input
                      type="checkbox"
                      checked={selectedEmployees?.includes(employee?.id) || false}
                      onChange={() => handleSelectEmployee(employee?.id)}
                      className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                      aria-label={`Seleccionar ${employee?._fullName}`}
                    />
                  </td>

                  <td className="px-4 py-3">
                    <div className="flex items-center">
                      <div className="w-10 h-10 bg-blue-100 rounded-full flex items-center justify-center mr-3">
                        <User className="h-5 w-5 text-blue-600" />
                      </div>
                      <div>
                        <p className="font-medium text-gray-900">{employee?._fullName}</p>
                        <p className="text-sm text-gray-500">{employee?._email}</p>
                      </div>
                    </div>
                  </td>

                  <td className="px-4 py-3 text-sm text-gray-900">{employee?._code}</td>

                  <td className="px-4 py-3 text-sm text-gray-900">
                    <div className="flex items-center">
                      <MapPin className="h-4 w-4 text-gray-400 mr-1" />
                      {employee?._siteName}
                    </div>
                  </td>

                  <td className="px-4 py-3 text-sm">
                    <div>
                      <p className="text-gray-900">
                        Regular: {employee?.payroll?.regularHours || 0}h
                      </p>
                      <p className="text-gray-500">
                        Extra: {employee?.payroll?.overtimeHours || 0}h
                      </p>
                    </div>
                  </td>

                  <td className="px-4 py-3 text-sm font-medium">
                    <div>
                      <p className="text-gray-900">
                        <CurrencyDisplay amount={employee?.payroll?.grossPay || 0} />
                      </p>
                      <p className="text-gray-500">
                        Neto:{' '}
                        <CurrencyDisplay amount={employee?.payroll?.netPay || 0} />
                      </p>
                    </div>
                  </td>

                  <td className="px-4 py-3">{getStatusBadge(employee?.payroll)}</td>

                  <td className="px-4 py-3">
                    <div className="flex items-center space-x-2">
                      <button
                        onClick={() => onCalculatePayroll?.(employee?.id)}
                        disabled={processing}
                        className="p-2 text-blue-600 hover:bg-blue-100 rounded-full disabled:opacity-50"
                        title="Calcular n√≥mina"
                      >
                        <Calculator className="h-4 w-4" />
                      </button>

                      <button
                        className="p-2 text-gray-600 hover:bg-gray-100 rounded-full"
                        title="Ver detalles"
                      >
                        <Eye className="h-4 w-4" />
                      </button>

                      {employee?.payroll?.grossPay > 0 && !employee?.payroll?.processed && (
                        <button
                          onClick={() => onProcessPayroll?.(employee?.payroll?.id)}
                          className="p-2 text-green-600 hover:bg-green-100 rounded-full"
                          title="Procesar n√≥mina"
                        >
                          <Check className="h-4 w-4" />
                        </button>
                      )}
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {sortedData?.length === 0 && (
          <div className="text-center py-12">
            <User className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <p className="text-gray-500">No hay empleados que coincidan con el filtro seleccionado</p>
          </div>
        )}
      </div>
    </div>
  );
}

// advanced-payroll-calculation-engine-with-comprehensive-wage-management/components/PayrollAuditTrail.jsx
import React, { useMemo, useState, useCallback } from 'react';
import { FileText, Clock, User, Filter, Trash2, Download } from 'lucide-react';
import CurrencyDisplay from '../../../components/ui/CurrencyDisplay';

export default function PayrollAuditTrail({ auditEntries = [], onClearAudit }) {
  const [filterType, setFilterType] = useState('all');
  const [sortOrder, setSortOrder] = useState('desc');

  const ACTION_LABELS = {
    calculate_payroll: 'C√°lculo de N√≥mina',
    bulk_calculate: 'C√°lculo Masivo',
    calculate_aguinaldo: 'C√°lculo Aguinaldo',
    calculate_severance: 'C√°lculo Finiquito',
    process_payroll: 'Procesar N√≥mina',
    approve_payroll: 'Aprobar N√≥mina',
    modify_rates: 'Modificar Tarifas',
  };

  const ACTION_COLORS = {
    calculate_payroll: 'bg-blue-100 text-blue-800',
    bulk_calculate: 'bg-purple-100 text-purple-800',
    calculate_aguinaldo: 'bg-green-100 text-green-800',
    calculate_severance: 'bg-orange-100 text-orange-800',
    process_payroll: 'bg-emerald-100 text-emerald-800',
    approve_payroll: 'bg-teal-100 text-teal-800',
    modify_rates: 'bg-yellow-100 text-yellow-800',
    default: 'bg-gray-100 text-gray-800',
  };

  const getActionLabel = (action) => ACTION_LABELS[action] ?? action ?? '‚Äî';
  const getActionColor = (action) => ACTION_COLORS[action] ?? ACTION_COLORS.default;

  // Filtrado y ordenado inmutable
  const sortedEntries = useMemo(() => {
    const filtered =
      filterType === 'all'
        ? [...auditEntries]
        : auditEntries.filter((e) => e?.action === filterType);

    return filtered
      .slice()
      .sort((a, b) => {
        const dateA = new Date(a?.timestamp || 0).getTime();
        const dateB = new Date(b?.timestamp || 0).getTime();
        return sortOrder === 'desc' ? dateB - dateA : dateA - dateB;
      });
  }, [auditEntries, filterType, sortOrder]);

  // CSV helpers
  const csvEscape = (val) => {
    const s = String(val ?? '');
    // Si contiene comillas, comas o saltos de l√≠nea -> envolver en comillas dobles y duplicar comillas
    if (/[",\n\r]/.test(s)) {
      return `"${s.replace(/"/g, '""')}"`;
    }
    return s;
  };

  const exportAuditLog = useCallback(() => {
    if (!sortedEntries.length) return;

    const headers = [
      'Fecha/Hora',
      'Acci√≥n',
      'Empleado',
      'Monto',
      'Usuario',
      'Detalles',
    ];

    const rows = sortedEntries.map((entry) => [
      new Date(entry?.timestamp || 0).toISOString(),
      getActionLabel(entry?.action),
      entry?.employee ?? '',
      typeof entry?.amount === 'number' ? entry.amount : (Number(entry?.amount) || 0),
      entry?.user ?? '',
      entry?.details ?? '',
    ]);

    const csv =
      [headers, ...rows]
        .map((row) => row.map(csvEscape).join(','))
        .join('\n');

    const blob = new Blob([csv], { type: 'text/csv;charset=utf-8;' });

    // SSR-safe
    if (typeof window !== 'undefined') {
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `auditoria_nomina_${new Date().toISOString().split('T')[0]}.csv`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);
    }
  }, [sortedEntries]);

  const totalAmount = useMemo(
    () => sortedEntries.reduce((sum, e) => sum + (Number(e?.amount) || 0), 0),
    [sortedEntries]
  );

  const uniqueEmployees = useMemo(
    () => new Set(sortedEntries.map((e) => e?.employee).filter(Boolean)).size,
    [sortedEntries]
  );

  const uniqueUsers = useMemo(
    () => new Set(sortedEntries.map((e) => e?.user).filter(Boolean)).size,
    [sortedEntries]
  );

  return (
    <div className="bg-white rounded-lg shadow">
      <div className="p-6">
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-xl font-bold text-gray-900">
            <FileText className="h-6 w-6 inline mr-2 text-blue-600" />
            Auditor√≠a de N√≥minas
          </h2>

          <div className="flex items-center space-x-4">
            <button
              onClick={exportAuditLog}
              disabled={!sortedEntries.length}
              className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 disabled:opacity-50 flex items-center"
            >
              <Download className="h-4 w-4 mr-2" />
              Exportar
            </button>

            <button
              onClick={onClearAudit}
              disabled={!auditEntries.length}
              className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 disabled:opacity-50 flex items-center"
            >
              <Trash2 className="h-4 w-4 mr-2" />
              Limpiar
            </button>
          </div>
        </div>

        {/* Filtros */}
        <div className="flex items-center space-x-4 mb-6">
          <div className="flex items-center space-x-2">
            <Filter className="h-4 w-4 text-gray-400" />
            <select
              value={filterType}
              onChange={(e) => setFilterType(e.target.value)}
              className="px-3 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
            >
              <option value="all">Todas las Acciones</option>
              <option value="calculate_payroll">C√°lculo de N√≥mina</option>
              <option value="bulk_calculate">C√°lculo Masivo</option>
              <option value="calculate_aguinaldo">Aguinaldos</option>
              <option value="calculate_severance">Finiquitos</option>
              <option value="process_payroll">Procesar N√≥mina</option>
              <option value="approve_payroll">Aprobar N√≥mina</option>
              <option value="modify_rates">Modificar Tarifas</option>
            </select>
          </div>

          <select
            value={sortOrder}
            onChange={(e) => setSortOrder(e.target.value)}
            className="px-3 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="desc">M√°s Reciente Primero</option>
            <option value="asc">M√°s Antiguo Primero</option>
          </select>

          <div className="text-sm text-gray-600">
            {sortedEntries.length} de {auditEntries.length || 0} entradas
          </div>
        </div>

        {/* Entradas */}
        <div className="space-y-4 max-h-96 overflow-y-auto">
          {sortedEntries.map((entry) => (
            <div key={entry?.id ?? `${entry?.timestamp}-${entry?.user}-${entry?.action}`} className="border border-gray-200 rounded-lg p-4">
              <div className="flex items-start justify-between">
                <div className="flex items-start space-x-4 flex-1">
                  <div className="flex-shrink-0">
                    <div className="w-10 h-10 bg-blue-100 rounded-full flex items-center justify-center">
                      <FileText className="h-5 w-5 text-blue-600" />
                    </div>
                  </div>

                  <div className="flex-1">
                    <div className="flex items-center space-x-2 mb-2">
                      <span className={`px-2 py-1 rounded-full text-xs font-medium ${getActionColor(entry?.action)}`}>
                        {getActionLabel(entry?.action)}
                      </span>

                      <div className="flex items-center text-sm text-gray-500">
                        <Clock className="h-3 w-3 mr-1" />
                        {new Date(entry?.timestamp || 0).toLocaleString()}
                      </div>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
                      <div>
                        <p className="text-gray-600 mb-1">Empleado:</p>
                        <p className="font-medium text-gray-900">{entry?.employee ?? '‚Äî'}</p>
                      </div>

                      <div>
                        <p className="text-gray-600 mb-1">Monto:</p>
                        <p className="font-bold text-green-600">
                          <CurrencyDisplay amount={Number(entry?.amount) || 0} />
                        </p>
                      </div>

                      <div>
                        <p className="text-gray-600 mb-1">Procesado por:</p>
                        <div className="flex items-center">
                          <User className="h-3 w-3 mr-1 text-gray-400" />
                          <p className="font-medium text-gray-900">{entry?.user ?? '‚Äî'}</p>
                        </div>
                      </div>
                    </div>

                    {entry?.details && (
                      <div className="mt-3 p-2 bg-gray-50 rounded text-sm text-gray-700">
                        <p className="font-medium text-gray-600 mb-1">Detalles:</p>
                        <p>{entry.details}</p>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Empty states */}
        {!sortedEntries.length && (
          <div className="text-center py-12">
            <FileText className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            {auditEntries.length === 0 ? (
              <>
                <p className="text-gray-500 mb-2">No hay registros de auditor√≠a disponibles</p>
                <p className="text-gray-400 text-sm">Los registros aparecer√°n aqu√≠ despu√©s de realizar c√°lculos de n√≥mina</p>
              </>
            ) : (
              <>
                <p className="text-gray-500 mb-2">No hay registros que coincidan con el filtro seleccionado</p>
                <p className="text-gray-400 text-sm">Intenta cambiar los filtros para ver m√°s resultados</p>
              </>
            )}
          </div>
        )}

        {/* Resumen */}
        {!!sortedEntries.length && (
          <div className="mt-6 p-4 bg-gray-50 rounded-lg">
            <h4 className="font-medium text-gray-900 mb-3">Resumen de Auditor√≠a</h4>

            <div className="grid grid-cols-1 md:grid-cols-4 gap-4 text-sm">
              <div className="text-center">
                <p className="text-2xl font-bold text-blue-600">{sortedEntries.length}</p>
                <p className="text-gray-600">Total Acciones</p>
              </div>

              <div className="text-center">
                <p className="text-2xl font-bold text-green-600">
                  <CurrencyDisplay amount={totalAmount} />
                </p>
                <p className="text-gray-600">Monto Total</p>
              </div>

              <div className="text-center">
                <p className="text-2xl font-bold text-purple-600">{uniqueEmployees}</p>
                <p className="text-gray-600">Empleados Afectados</p>
              </div>

              <div className="text-center">
                <p className="text-2xl font-bold text-orange-600">{uniqueUsers}</p>
                <p className="text-gray-600">Usuarios Activos</p>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

// advanced-payroll-calculation-engine-with-comprehensive-wage-management/components/PayrollCalculationDashboard.jsx
import React, { useMemo, useState } from 'react';
import { Calculator, Clock, DollarSign, Percent, Calendar, TrendingUp } from 'lucide-react';
import CurrencyDisplay from '../../../components/ui/CurrencyDisplay';

export default function PayrollCalculationDashboard({
  weekRange,
  onWeekRangeChange,
  payrollData = [],
  onCalculatePayroll,
  onCalculateAguinaldo,
  onCalculateSeverance,
  processing
}) {
  const [selectedEmployee, setSelectedEmployee] = useState('');
  const [customRates, setCustomRates] = useState({
    regularRate: 0,
    overtimeMultiplier: 1.5,
    nightDifferential: 1.25,
    holidayPremium: 2.0
  });

  const safeISO = (d) => {
    if (!d) return '';
    try {
      const dt = d instanceof Date ? d : new Date(d);
      return isNaN(dt.getTime()) ? '' : dt.toISOString().split('T')[0];
    } catch {
      return '';
    }
  };

  const handleWeekChange = (field, value) => {
    const asDate = value ? new Date(value) : null;
    onWeekRangeChange?.({
      ...weekRange,
      [field]: asDate
    });
  };

  const selectedPayroll = useMemo(() => {
    if (!selectedEmployee) return null;
    return (
      payrollData.find(
        (p) => String(p?.employeeId) === String(selectedEmployee) || String(p?.id) === String(selectedEmployee)
      ) || null
    );
  }, [selectedEmployee, payrollData]);

  const calculatePayroll = async () => {
    if (!selectedEmployee) return;
    await onCalculatePayroll?.(selectedEmployee, { rates: customRates, range: weekRange });
  };

  const calculateSpecialPayment = async (type) => {
    if (!selectedEmployee) return;
    if (type === 'aguinaldo') {
      await onCalculateAguinaldo?.(selectedEmployee);
    } else if (type === 'severance') {
      await onCalculateSeverance?.(selectedEmployee, 'without_cause');
    }
  };

  const regularHours = Number(selectedPayroll?.regularHours) || 0;
  const overtimeHours = Number(selectedPayroll?.overtimeHours) || 0;
  const holidayHours = Number(selectedPayroll?.holidayHours) || 0;

  const overtimeRate = Number(customRates?.regularRate) * Number(customRates?.overtimeMultiplier || 1);
  const holidayRate = Number(customRates?.regularRate) * Number(customRates?.holidayPremium || 1);

  return (
    <div className="bg-white rounded-lg shadow">
      <div className="p-6">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-xl font-bold text-gray-900">
            <Calculator className="h-6 w-6 inline mr-2 text-blue-600" />
            Panel de C√°lculo de N√≥minas
          </h2>

          <div className="flex items-center space-x-4">
            <div className="flex items-center">
              <Calendar className="h-4 w-4 text-gray-400 mr-2" />
              <span className="text-sm text-gray-600">Per√≠odo de N√≥mina</span>
            </div>
          </div>
        </div>

        {/* Rango de fechas */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6 p-4 bg-gray-50 rounded-lg">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Fecha de Inicio</label>
            <input
              type="date"
              value={safeISO(weekRange?.start)}
              onChange={(e) => handleWeekChange('start', e?.target?.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">Fecha de Fin</label>
            <input
              type="date"
              value={safeISO(weekRange?.end)}
              onChange={(e) => handleWeekChange('end', e?.target?.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
            />
          </div>
        </div>

        {/* Configuraci√≥n de tarifas */}
        <div className="mb-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Configuraci√≥n de Tarifas</h3>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                <Clock className="h-4 w-4 inline mr-1" />
                Tarifa Regular
              </label>
              <div className="relative">
                <span className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-500">$</span>
                <input
                  type="number"
                  value={customRates?.regularRate}
                  onChange={(e) =>
                    setCustomRates((prev) => ({
                      ...prev,
                      regularRate: parseFloat(e?.target?.value) || 0
                    }))
                  }
                  className="w-full pl-8 pr-3 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
                  placeholder="0.00"
                  step="0.01"
                />
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                <TrendingUp className="h-4 w-4 inline mr-1" />
                Multiplicador Horas Extra
              </label>
              <div className="relative">
                <input
                  type="number"
                  value={customRates?.overtimeMultiplier}
                  onChange={(e) =>
                    setCustomRates((prev) => ({
                      ...prev,
                      overtimeMultiplier: parseFloat(e?.target?.value) || 1.5
                    }))
                  }
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
                  placeholder="1.5"
                  step="0.1"
                />
                <span className="absolute right-3 top-1/2 -translate-y-1/2 text-gray-500">x</span>
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                <Percent className="h-4 w-4 inline mr-1" />
                Diferencial Nocturno
              </label>
              <div className="relative">
                <input
                  type="number"
                  value={customRates?.nightDifferential}
                  onChange={(e) =>
                    setCustomRates((prev) => ({
                      ...prev,
                      nightDifferential: parseFloat(e?.target?.value) || 1.25
                    }))
                  }
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
                  placeholder="1.25"
                  step="0.1"
                />
                <span className="absolute right-3 top-1/2 -translate-y-1/2 text-gray-500">x</span>
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                <DollarSign className="h-4 w-4 inline mr-1" />
                Prima Dominical
              </label>
              <div className="relative">
                <input
                  type="number"
                  value={customRates?.holidayPremium}
                  onChange={(e) =>
                    setCustomRates((prev) => ({
                      ...prev,
                      holidayPremium: parseFloat(e?.target?.value) || 2.0
                    }))
                  }
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
                  placeholder="2.0"
                  step="0.1"
                />
                <span className="absolute right-3 top-1/2 -translate-y-1/2 text-gray-500">x</span>
              </div>
            </div>
          </div>
        </div>

        {/* Selecci√≥n de empleado */}
        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">Seleccionar Empleado</label>
          <select
            value={selectedEmployee}
            onChange={(e) => setSelectedEmployee(e?.target?.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Selecciona un empleado...</option>
            {payrollData.map((e) => (
              <option key={e?.employeeId || e?.id} value={e?.employeeId || e?.id}>
                {e?.employeeName || e?.name || 'Empleado sin nombre'}
              </option>
            ))}
          </select>
        </div>

        {/* Botones de acci√≥n */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <button
            onClick={calculatePayroll}
            disabled={!selectedEmployee || processing}
            className="w-full px-4 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center"
          >
            <Calculator className="h-5 w-5 mr-2" />
            {processing ? 'Calculando...' : 'Calcular N√≥mina Semanal'}
          </button>

          <button
            onClick={() => calculateSpecialPayment('aguinaldo')}
            disabled={!selectedEmployee || processing}
            className="w-full px-4 py-3 bg-green-600 text-white rounded-lg hover:bg-green-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center"
          >
            <DollarSign className="h-5 w-5 mr-2" />
            Calcular Aguinaldo
          </button>

          <button
            onClick={() => calculateSpecialPayment('severance')}
            disabled={!selectedEmployee || processing}
            className="w-full px-4 py-3 bg-orange-600 text-white rounded-lg hover:bg-orange-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center"
          >
            <TrendingUp className="h-5 w-5 mr-2" />
            Calcular Finiquito
          </button>
        </div>

        {/* Vista previa de c√°lculos: solo si hay datos reales del empleado seleccionado */}
        {selectedEmployee && selectedPayroll && (
          <div className="mt-6 p-4 bg-gray-50 rounded-lg">
            <h4 className="font-semibold text-gray-900 mb-3">Vista Previa de C√°lculos</h4>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
              <div className="flex justify-between">
                <span className="text-gray-600">Horas Regulares:</span>
                <span className="font-medium">
                  {regularHours.toFixed(1)}h √ó <CurrencyDisplay amount={Number(customRates?.regularRate) || 0} />
                </span>
              </div>

              <div className="flex justify-between">
                <span className="text-gray-600">Horas Extra:</span>
                <span className="font-medium">
                  {overtimeHours.toFixed(1)}h √ó <CurrencyDisplay amount={overtimeRate || 0} />
                </span>
              </div>

              <div className="flex justify-between">
                <span className="text-gray-600">Horas Festivas/Dominical:</span>
                <span className="font-medium">
                  {holidayHours.toFixed(1)}h √ó <CurrencyDisplay amount={holidayRate || 0} />
                </span>
              </div>
            </div>
          </div>
        )}

        {/* Si no hay datos de n√≥mina del empleado seleccionado, no mostramos n√∫meros ficticios */}
        {selectedEmployee && !selectedPayroll && (
          <div className="mt-6 p-4 bg-yellow-50 border border-yellow-200 rounded-lg text-sm text-yellow-800">
            No hay datos de n√≥mina para el empleado seleccionado en el per√≠odo actual. Ajusta fechas o calcula la n√≥mina.
          </div>
        )}
      </div>
    </div>
  );
}

