  const supervisorOptions = [
    { value: 'all', label: 'Todos los Supervisores' },
    { value: 'carlos-martinez', label: 'Carlos Mart√≠nez' },
    { value: 'ana-rodriguez', label: 'Ana Rodr√≠guez' },
    { value: 'luis-garcia', label: 'Luis Garc√≠a' },
    { value: 'maria-lopez', label: 'Mar√≠a L√≥pez' }
  ];

  const statusOptions = [
    { value: 'all', label: 'Todos los Estados' },
    { value: 'complete', label: 'Completo' },
    { value: 'incomplete', label: 'Incompleto' },
    { value: 'late', label: 'Tard√≠o' },
