// comprehensive-employee-registration-and-profile-management/components/EmployeeProfileEditor.jsx
import React, { useState, useEffect } from 'react';
import Button from '../../../components/ui/Button';
import Input from '../../../components/ui/Input';
import Select from '../../../components/ui/Select';
import { X, User, Save } from 'lucide-react';

export function EmployeeProfileEditor({
  employee,
  constructionSites = [],
  supervisors = [],
  onSubmit,
  onClose,
  branding,
}) {
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});
  const [hasChanges, setHasChanges] = useState(false);

  const [formData, setFormData] = useState({
    fullName: '',
    phone: '',
    address: '',
    birthDate: '',
    position: 'alba√±il',
    salaryType: 'daily',
    hourlyRate: '',
    dailySalary: '',
    siteId: '',
    supervisorId: '',
    emergencyContact: '',
    idNumber: '',
    profilePicture: null,
  });

  useEffect(() => {
    if (employee) {
      setFormData({
        fullName: employee?.full_name || '',
        phone: employee?.phone || '',
        address: employee?.address || '',
        birthDate: employee?.birth_date || '',
        position: employee?.position || 'alba√±il',
        salaryType: employee?.salary_type || 'daily',
        hourlyRate:
          typeof employee?.hourly_rate === 'number'
            ? String(employee?.hourly_rate)
            : employee?.hourly_rate?.toString?.() || '',
        dailySalary:
          typeof employee?.daily_salary === 'number'
            ? String(employee?.daily_salary)
            : employee?.daily_salary?.toString?.() || '',
        siteId: employee?.site_id || '',
        supervisorId: employee?.supervisor_id || '',
        emergencyContact: employee?.emergency_contact || '',
        idNumber: employee?.id_number || '',
        profilePicture: employee?.profile_picture_url || null,
      });
    }
  }, [employee]);

  const handleInputChange = (field, value) => {
    setFormData((prev) => ({
      ...prev,
      [field]: value,
    }));
    setHasChanges(true);

    if (errors?.[field]) {
      setErrors((prev) => ({
        ...prev,
        [field]: null,
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData?.fullName?.trim()) {
      newErrors.fullName = 'El nombre completo es requerido';
    }

    if (!formData?.position) {
      newErrors.position = 'El puesto es requerido';
    }

    if (formData?.salaryType === 'hourly') {
      const v = parseFloat(formData?.hourlyRate);
      if (!v || v <= 0) newErrors.hourlyRate = 'El salario por hora debe ser mayor a 0';
    }

    if (formData?.salaryType === 'daily') {
      const v = parseFloat(formData?.dailySalary);
      if (!v || v <= 0) newErrors.dailySalary = 'El salario diario debe ser mayor a 0';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e?.preventDefault();

    if (!validateForm()) return;
    if (!hasChanges) {
      onClose?.();
      return;
    }

    try {
      setLoading(true);
      const payload = {
        full_name: formData.fullName?.trim(),
        phone: formData.phone?.trim() || null,
        address: formData.address?.trim() || null,
        birth_date: formData.birthDate || null,
        position: formData.position,
        salary_type: formData.salaryType,
        hourly_rate:
          formData.salaryType === 'hourly' ? Number(parseFloat(formData.hourlyRate).toFixed(2)) : 0,
        daily_salary:
        formData.salaryType === 'daily' ? Number(parseFloat(formData.dailySalary).toFixed(2)) : 0,
        site_id: formData.siteId || null,
        supervisor_id: formData.supervisorId || null,
        emergency_contact: formData.emergencyContact?.trim() || null,
        id_number: formData.idNumber?.trim() || null,
        profile_picture_url: formData.profilePicture || null,
      };

      const result = await onSubmit?.(payload);
      if (result?.success) onClose?.();
    } catch (error) {
      // eslint-disable-next-line no-console
      console.error('Error updating employee:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCancel = () => {
    if (hasChanges) {
      if (window.confirm('¬øEst√°s seguro de que quieres cancelar? Se perder√°n los cambios no guardados.')) {
        onClose?.();
      }
    } else {
      onClose?.();
    }
  };

  const safeDate = (d) => {
    if (!d) return '‚Äî';
    try {
      const dt = d instanceof Date ? d : new Date(d);
      if (isNaN(dt.getTime())) return '‚Äî';
      return dt.toLocaleDateString('es-ES', { day: '2-digit', month: '2-digit', year: 'numeric' });
    } catch {
      return '‚Äî';
    }
  };

  if (!employee) return null;

  const currency = branding?.simbolo_moneda || '$';

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg shadow-xl max-w-4xl w-full max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="px-6 py-4 border-b flex items-center justify-between">
          <div>
            <h2 className="text-2xl font-bold text-gray-900 flex items-center gap-2">
              <User className="h-6 w-6" />
              Editar Perfil de Empleado
            </h2>
            <p className="text-gray-600 mt-1">
              ID: {employee?.employee_id || 'N/A'} | {employee?.user_profiles?.email || employee?.email || 'Sin email'}
            </p>
          </div>
          <Button variant="ghost" size="sm" onClick={handleCancel}>
            <X className="h-4 w-4" />
          </Button>
        </div>

        <form onSubmit={handleSubmit} className="p-6">
          {/* Informaci√≥n Personal */}
          <div className="mb-8">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Informaci√≥n Personal</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Nombre Completo *</label>
                <Input
                  type="text"
                  value={formData?.fullName}
                  onChange={(e) => handleInputChange('fullName', e?.target?.value)}
                  placeholder="Juan P√©rez Garc√≠a"
                  error={errors?.fullName}
                />
                {errors?.fullName && <p className="text-red-500 text-sm mt-1">{errors?.fullName}</p>}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Tel√©fono</label>
                <Input
                  type="tel"
                  value={formData?.phone}
                  onChange={(e) => handleInputChange('phone', e?.target?.value)}
                  placeholder="(555) 123-4567"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Fecha de Nacimiento</label>
                <Input
                  type="date"
                  value={formData?.birthDate || ''}
                  onChange={(e) => handleInputChange('birthDate', e?.target?.value)}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">N√∫mero de Identificaci√≥n</label>
                <Input
                  type="text"
                  value={formData?.idNumber}
                  onChange={(e) => handleInputChange('idNumber', e?.target?.value)}
                  placeholder="RFC, CURP, INE, etc."
                />
              </div>

              <div className="md:col-span-2">
                <label className="block text-sm font-medium text-gray-700 mb-2">Direcci√≥n</label>
                <textarea
                  value={formData?.address}
                  onChange={(e) => handleInputChange('address', e?.target?.value)}
                  placeholder="Direcci√≥n completa"
                  rows={3}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Contacto de Emergencia</label>
                <Input
                  type="text"
                  value={formData?.emergencyContact}
                  onChange={(e) => handleInputChange('emergencyContact', e?.target?.value)}
                  placeholder="Nombre y tel√©fono"
                />
              </div>
            </div>
          </div>

          {/* Detalles de Empleo */}
          <div className="mb-8">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Detalles de Empleo</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Puesto *</label>
                <Select
                  value={formData?.position}
                  onChange={(value) => handleInputChange('position', value)}
                  options={[
                    { value: 'alba√±il', label: 'Alba√±il' },
                    { value: 'ayudante', label: 'Ayudante' },
                    { value: 'supervisor', label: 'Supervisor' },
                    { value: 'administrativo', label: 'Administrativo' },
                    { value: 'electricista', label: 'Electricista' },
                    { value: 'plomero', label: 'Plomero' },
                    { value: 'pintor', label: 'Pintor' },
                    { value: 'carpintero', label: 'Carpintero' },
                    { value: 'soldador', label: 'Soldador' },
                    { value: 'operador_maquinaria', label: 'Operador de Maquinaria' },
                  ]}
                  error={errors?.position}
                />
                {errors?.position && <p className="text-red-500 text-sm mt-1">{errors?.position}</p>}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Estado del Empleado</label>
                <div className="flex items-center space-x-4">
                  <span
                    className={`px-3 py-1 text-sm rounded-full ${
                      employee?.status === 'active'
                        ? 'bg-green-100 text-green-800'
                        : employee?.status === 'suspended'
                        ? 'bg-yellow-100 text-yellow-800'
                        : 'bg-red-100 text-red-800'
                    }`}
                  >
                    {employee?.status === 'active' && 'Activo'}
                    {employee?.status === 'suspended' && 'Suspendido'}
                    {employee?.status === 'inactive' && 'Inactivo'}
                  </span>
                  <span className="text-sm text-gray-600">
                    Empleado desde: {safeDate(employee?.hire_date)}
                  </span>
                </div>
              </div>
            </div>
          </div>

          {/* Informaci√≥n Salarial */}
          <div className="mb-8">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Informaci√≥n Salarial</h3>

            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-2">Tipo de Salario</label>
              <div className="flex gap-4">
                <label className="flex items-center">
                  <input
                    type="radio"
                    name="salaryType"
                    value="daily"
                    checked={formData?.salaryType === 'daily'}
                    onChange={(e) => handleInputChange('salaryType', e?.target?.value)}
                    className="mr-2"
                  />
                  Diario
                </label>
                <label className="flex items-center">
                  <input
                    type="radio"
                    name="salaryType"
                    value="hourly"
                    checked={formData?.salaryType === 'hourly'}
                    onChange={(e) => handleInputChange('salaryType', e?.target?.value)}
                    className="mr-2"
                  />
                  Por Hora
                </label>
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {formData?.salaryType === 'daily' && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Salario Diario * ({currency})
                  </label>
                  <Input
                    type="number"
                    step="0.01"
                    min="0"
                    value={formData?.dailySalary}
                    onChange={(e) => handleInputChange('dailySalary', e?.target?.value)}
                    placeholder="300.00"
                    error={errors?.dailySalary}
                  />
                  {errors?.dailySalary && <p className="text-red-500 text-sm mt-1">{errors?.dailySalary}</p>}
                </div>
              )}

              {formData?.salaryType === 'hourly' && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Salario por Hora * ({currency})
                  </label>
                  <Input
                    type="number"
                    step="0.01"
                    min="0"
                    value={formData?.hourlyRate}
                    onChange={(e) => handleInputChange('hourlyRate', e?.target?.value)}
                    placeholder="37.50"
                    error={errors?.hourlyRate}
                  />
                  {errors?.hourlyRate && <p className="text-red-500 text-sm mt-1">{errors?.hourlyRate}</p>}
                </div>
              )}

              <div className="bg-gray-50 p-4 rounded-lg">
                <p className="text-sm text-gray-600 mb-2">
                  <strong>Equivalencias:</strong>
                </p>
                {formData?.salaryType === 'daily' && formData?.dailySalary && (
                  <p className="text-sm">
                    Por hora: {currency}
                    {(parseFloat(formData?.dailySalary) / 8)?.toFixed(2)}
                  </p>
                )}
                {formData?.salaryType === 'hourly' && formData?.hourlyRate && (
                  <p className="text-sm">
                    Por d√≠a: {currency}
                    {(parseFloat(formData?.hourlyRate) * 8)?.toFixed(2)}
                  </p>
                )}
              </div>
            </div>
          </div>

          {/* Asignaci√≥n */}
          <div className="mb-8">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Asignaci√≥n</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Sitio de Construcci√≥n</label>
                <Select
                  value={formData?.siteId}
                  onChange={(value) => handleInputChange('siteId', value)}
                  options={[
                    { value: '', label: 'Sin asignar' },
                    ...constructionSites.map((site) => ({
                      value: site?.id,
                      label: `${site?.name || site?.nombre || 'Sitio'} - ${
                        site?.location || site?.direccion || 'Ubicaci√≥n no especificada'
                      }`,
                    })),
                  ]}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Supervisor</label>
                <Select
                  value={formData?.supervisorId}
                  onChange={(value) => handleInputChange('supervisorId', value)}
                  options={[
                    { value: '', label: 'Sin supervisor asignado' },
                    ...supervisors.map((s) => ({
                      value: s?.id,
                      label: `${s?.full_name || s?.nombre || 'Supervisor'} (${s?.email || s?.correo || 'sin email'})`,
                    })),
                  ]}
                />
              </div>
            </div>
          </div>

          {/* Botones */}
          <div className="flex justify-end space-x-3 pt-6 border-t">
            <Button type="button" variant="outline" onClick={handleCancel}>
              Cancelar
            </Button>
            <Button
              type="submit"
              disabled={loading || !hasChanges}
              className="flex items-center gap-2"
              style={{ backgroundColor: branding?.color_primario }}
            >
              {loading && <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>}
              <Save className="h-4 w-4" />
              {hasChanges ? 'Guardar Cambios' : 'Sin Cambios'}
            </Button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default EmployeeProfileEditor;

// comprehensive-employee-registration-and-profile-management/components/EmployeeRegistrationWizard.jsx
import React, { useState } from 'react';
import Button from '../../../components/ui/Button';
import Input from '../../../components/ui/Input';
import Select from '../../../components/ui/Select';
import { X, User, Briefcase, MapPin, DollarSign, ChevronLeft, ChevronRight } from 'lucide-react';

export function EmployeeRegistrationWizard({
  constructionSites = [],
  supervisors = [],
  onSubmit,
  onClose,
  branding,
}) {
  const [currentStep, setCurrentStep] = useState(1);
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});

  const [formData, setFormData] = useState({
    // Personal Information
    fullName: '',
    email: '',
    phone: '',
    address: '',
    birthDate: '',
    idNumber: '',
    emergencyContact: '',

    // Employment Details
    employeeId: '',
    hireDate: new Date()?.toISOString()?.split('T')?.[0],
    position: 'alba√±il',
    role: 'user',

    // Salary Information
    salaryType: 'daily',
    hourlyRate: '',
    dailySalary: '',

    // Assignment
    siteId: '',
    supervisorId: '',

    // Additional
    profilePicture: null,
    tempPassword: 'AsistenciaPro2024',
  });

  const steps = [
    { id: 1, title: 'Informaci√≥n Personal', icon: User },
    { id: 2, title: 'Detalles de Empleo', icon: Briefcase },
    { id: 3, title: 'Salario y Beneficios', icon: DollarSign },
    { id: 4, title: 'Asignaci√≥n', icon: MapPin },
  ];

  const validateEmail = (email) =>
    /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(String(email || '').trim());

  const validateStep = (step) => {
    const newErrors = {};

    switch (step) {
      case 1: {
        if (!formData?.fullName?.trim()) newErrors.fullName = 'El nombre completo es requerido';
        if (!formData?.email?.trim()) {
          newErrors.email = 'El correo electr√≥nico es requerido';
        } else if (!validateEmail(formData?.email)) {
          newErrors.email = 'Formato de correo electr√≥nico inv√°lido';
        }
        if (!formData?.phone?.trim()) newErrors.phone = 'El tel√©fono es requerido';
        break;
      }
      case 2: {
        if (!formData?.position) newErrors.position = 'El puesto es requerido';
        if (!formData?.hireDate) newErrors.hireDate = 'La fecha de contrataci√≥n es requerida';
        break;
      }
      case 3: {
        if (
          formData?.salaryType === 'hourly' &&
          (!formData?.hourlyRate || parseFloat(formData?.hourlyRate) <= 0)
        ) {
          newErrors.hourlyRate = 'El salario por hora debe ser mayor a 0';
        }
        if (
          formData?.salaryType === 'daily' &&
          (!formData?.dailySalary || parseFloat(formData?.dailySalary) <= 0)
        ) {
          newErrors.dailySalary = 'El salario diario debe ser mayor a 0';
        }
        break;
      }
      case 4:
      default:
        break;
    }

    setErrors(newErrors);
    return Object.keys(newErrors)?.length === 0;
  };

  const handleNext = () => {
    if (validateStep(currentStep)) {
      setCurrentStep((prev) => Math.min(prev + 1, steps?.length));
    }
  };

  const handlePrevious = () => {
    setCurrentStep((prev) => Math.max(prev - 1, 1));
  };

  const safeNumber = (v) => {
    const n = parseFloat(v);
    if (Number.isFinite(n)) return Number(n.toFixed(2));
    return 0;
  };

  const generateEmployeeIdIfNeeded = () => formData?.employeeId?.trim() || `EMP${Date.now().toString().slice(-6)}`;

  const handleSubmit = async () => {
    if (!validateStep(currentStep)) return;

    try {
      setLoading(true);

      // Normaliza payload para backend
      const payload = {
        // user / profile
        email: formData.email?.trim(),
        full_name: formData.fullName?.trim(),
        phone: formData.phone?.trim() || null,
        address: formData.address?.trim() || null,
        birth_date: formData.birthDate || null,
        id_number: formData.idNumber?.trim() || null,
        emergency_contact: formData.emergencyContact?.trim() || null,

        // employee
        employee_id: generateEmployeeIdIfNeeded(),
        hire_date: formData.hireDate,
        position: formData.position,
        role: formData.role,

        // salary
        salary_type: formData.salaryType,
        hourly_rate: formData.salaryType === 'hourly' ? safeNumber(formData.hourlyRate) : 0,
        daily_salary: formData.salaryType === 'daily' ? safeNumber(formData.dailySalary) : 0,

        // assignment
        site_id: formData.siteId || null,
        supervisor_id: formData.supervisorId || null,

        // additional
        profile_picture_url: formData.profilePicture || null,
        temp_password: formData.tempPassword || null,
      };

      const result = await onSubmit?.(payload);
      if (result?.success) onClose?.();
    } catch (error) {
      // eslint-disable-next-line no-console
      console.error('Error submitting form:', error);
    } finally {
      setLoading(false);
    }
  };

  const currency = branding?.simbolo_moneda || '$';

  const renderStepContent = () => {
    switch (currentStep) {
      case 1:
        return (
          <div className="space-y-6">
            <h3 className="text-lg font-semibold text-gray-900 flex items-center gap-2">
              <User className="h-5 w-5" />
              Informaci√≥n Personal
            </h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Nombre Completo *</label>
                <Input
                  type="text"
                  value={formData?.fullName}
                  onChange={(e) => setFormData({ ...formData, fullName: e?.target?.value })}
                  placeholder="Juan P√©rez Garc√≠a"
                  error={errors?.fullName}
                />
                {errors?.fullName && <p className="text-red-500 text-sm mt-1">{errors?.fullName}</p>}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Correo Electr√≥nico *</label>
                <Input
                  type="email"
                  value={formData?.email}
                  onChange={(e) => setFormData({ ...formData, email: e?.target?.value })}
                  placeholder="juan.perez@email.com"
                  error={errors?.email}
                />
                {errors?.email && <p className="text-red-500 text-sm mt-1">{errors?.email}</p>}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Tel√©fono *</label>
                <Input
                  type="tel"
                  value={formData?.phone}
                  onChange={(e) => setFormData({ ...formData, phone: e?.target?.value })}
                  placeholder="(555) 123-4567"
                  error={errors?.phone}
                />
                {errors?.phone && <p className="text-red-500 text-sm mt-1">{errors?.phone}</p>}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Fecha de Nacimiento</label>
                <Input
                  type="date"
                  value={formData?.birthDate}
                  onChange={(e) => setFormData({ ...formData, birthDate: e?.target?.value })}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">N√∫mero de Identificaci√≥n</label>
                <Input
                  type="text"
                  value={formData?.idNumber}
                  onChange={(e) => setFormData({ ...formData, idNumber: e?.target?.value })}
                  placeholder="RFC, CURP, INE, etc."
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Contacto de Emergencia</label>
                <Input
                  type="text"
                  value={formData?.emergencyContact}
                  onChange={(e) => setFormData({ ...formData, emergencyContact: e?.target?.value })}
                  placeholder="Nombre y tel√©fono"
                />
              </div>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Direcci√≥n</label>
              <textarea
                value={formData?.address}
                onChange={(e) => setFormData({ ...formData, address: e?.target?.value })}
                placeholder="Direcci√≥n completa"
                rows={3}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              />
            </div>
          </div>
        );

      case 2:
        return (
          <div className="space-y-6">
            <h3 className="text-lg font-semibold text-gray-900 flex items-center gap-2">
              <Briefcase className="h-5 w-5" />
              Detalles de Empleo
            </h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">ID del Empleado</label>
                <Input
                  type="text"
                  value={formData?.employeeId}
                  onChange={(e) => setFormData({ ...formData, employeeId: e?.target?.value })}
                  placeholder="Se generar√° autom√°ticamente si se deja vac√≠o"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Fecha de Contrataci√≥n *</label>
                <Input
                  type="date"
                  value={formData?.hireDate}
                  onChange={(e) => setFormData({ ...formData, hireDate: e?.target?.value })}
                  error={errors?.hireDate}
                />
                {errors?.hireDate && <p className="text-red-500 text-sm mt-1">{errors?.hireDate}</p>}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Puesto *</label>
                <Select
                  value={formData?.position}
                  onChange={(value) => setFormData({ ...formData, position: value })}
                  options={[
                    { value: 'alba√±il', label: 'Alba√±il' },
                    { value: 'ayudante', label: 'Ayudante' },
                    { value: 'supervisor', label: 'Supervisor' },
                    { value: 'administrativo', label: 'Administrativo' },
                    { value: 'electricista', label: 'Electricista' },
                    { value: 'plomero', label: 'Plomero' },
                    { value: 'pintor', label: 'Pintor' },
                    { value: 'carpintero', label: 'Carpintero' },
                    { value: 'soldador', label: 'Soldador' },
                    { value: 'operador_maquinaria', label: 'Operador de Maquinaria' },
                  ]}
                  error={errors?.position}
                />
                {errors?.position && <p className="text-red-500 text-sm mt-1">{errors?.position}</p>}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Rol del Usuario</label>
                <Select
                  value={formData?.role}
                  onChange={(value) => setFormData({ ...formData, role: value })}
                  options={[
                    { value: 'user', label: 'Usuario' },
                    { value: 'supervisor', label: 'Supervisor' },
                    { value: 'admin', label: 'Administrador' },
                  ]}
                />
                <p className="text-xs text-gray-500 mt-1">Determina los permisos de acceso al sistema</p>
              </div>
            </div>
          </div>
        );

      case 3:
        return (
          <div className="space-y-6">
            <h3 className="text-lg font-semibold text-gray-900 flex items-center gap-2">
              <DollarSign className="h-5 w-5" />
              Salario y Beneficios
            </h3>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Tipo de Salario</label>
              <div className="flex gap-4">
                <label className="flex items-center">
                  <input
                    type="radio"
                    name="salaryType"
                    value="daily"
                    checked={formData?.salaryType === 'daily'}
                    onChange={(e) => setFormData({ ...formData, salaryType: e?.target?.value })}
                    className="mr-2"
                  />
                  Diario
                </label>
                <label className="flex items-center">
                  <input
                    type="radio"
                    name="salaryType"
                    value="hourly"
                    checked={formData?.salaryType === 'hourly'}
                    onChange={(e) => setFormData({ ...formData, salaryType: e?.target?.value })}
                    className="mr-2"
                  />
                  Por Hora
                </label>
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {formData?.salaryType === 'daily' && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Salario Diario * ({currency})
                  </label>
                  <Input
                    type="number"
                    step="0.01"
                    min="0"
                    value={formData?.dailySalary}
                    onChange={(e) => setFormData({ ...formData, dailySalary: e?.target?.value })}
                    placeholder="300.00"
                    error={errors?.dailySalary}
                  />
                  {errors?.dailySalary && <p className="text-red-500 text-sm mt-1">{errors?.dailySalary}</p>}
                </div>
              )}

              {formData?.salaryType === 'hourly' && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Salario por Hora * ({currency})
                  </label>
                  <Input
                    type="number"
                    step="0.01"
                    min="0"
                    value={formData?.hourlyRate}
                    onChange={(e) => setFormData({ ...formData, hourlyRate: e?.target?.value })}
                    placeholder="37.50"
                    error={errors?.hourlyRate}
                  />
                  {errors?.hourlyRate && <p className="text-red-500 text-sm mt-1">{errors?.hourlyRate}</p>}
                </div>
              )}
            </div>

            {formData?.salaryType === 'daily' && formData?.dailySalary && (
              <div className="bg-blue-50 p-4 rounded-lg">
                <p className="text-sm text-blue-800">
                  <strong>Equivalente por hora:</strong> {currency}
                  {(parseFloat(formData?.dailySalary) / 8)?.toFixed(2)} (basado en 8 horas por d√≠a)
                </p>
              </div>
            )}
            {formData?.salaryType === 'hourly' && formData?.hourlyRate && (
              <div className="bg-blue-50 p-4 rounded-lg">
                <p className="text-sm text-blue-800">
                  <strong>Equivalente diario:</strong> {currency}
                  {(parseFloat(formData?.hourlyRate) * 8)?.toFixed(2)} (basado en 8 horas por d√≠a)
                </p>
              </div>
            )}
          </div>
        );

      case 4:
        return (
          <div className="space-y-6">
            <h3 className="text-lg font-semibold text-gray-900 flex items-center gap-2">
              <MapPin className="h-5 w-5" />
              Asignaci√≥n
            </h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Sitio de Construcci√≥n</label>
                <Select
                  value={formData?.siteId}
                  onChange={(value) => setFormData({ ...formData, siteId: value })}
                  options={[
                    { value: '', label: 'Sin asignar' },
                    ...constructionSites?.map((site) => ({
                      value: site?.id,
                      label: `${site?.name || site?.nombre || 'Sitio'} - ${
                        site?.location || site?.direccion || 'Ubicaci√≥n no especificada'
                      }`,
                    })),
                  ]}
                />
                <p className="text-xs text-gray-500 mt-1">El empleado ser√° asignado a este sitio de construcci√≥n</p>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Supervisor</label>
                <Select
                  value={formData?.supervisorId}
                  onChange={(value) => setFormData({ ...formData, supervisorId: value })}
                  options={[
                    { value: '', label: 'Sin supervisor asignado' },
                    ...supervisors?.map((s) => ({
                      value: s?.id,
                      label: `${s?.full_name || s?.nombre || 'Supervisor'} (${s?.email || s?.correo || 'sin email'})`,
                    })),
                  ]}
                />
                <p className="text-xs text-gray-500 mt-1">
                  {formData?.role === 'supervisor' || formData?.role === 'admin'
                    ? 'Los supervisores y administradores no requieren supervisor asignado'
                    : 'Supervisor directo del empleado'}
                </p>
              </div>
            </div>

            <div className="bg-gray-50 p-4 rounded-lg">
              <h4 className="font-medium text-gray-900 mb-3">Resumen del Empleado</h4>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
                <div>
                  <p>
                    <strong>Nombre:</strong> {formData?.fullName}
                  </p>
                  <p>
                    <strong>Email:</strong> {formData?.email}
                  </p>
                  <p>
                    <strong>Tel√©fono:</strong> {formData?.phone}
                  </p>
                  <p>
                    <strong>Puesto:</strong> {formData?.position}
                  </p>
                </div>
                <div>
                  <p>
                    <strong>Rol:</strong> {formData?.role}
                  </p>
                  <p>
                    <strong>Tipo de Salario:</strong> {formData?.salaryType === 'daily' ? 'Diario' : 'Por Hora'}
                  </p>
                  <p>
                    <strong>Salario:</strong>
                    {formData?.salaryType === 'daily'
                      ? ` ${currency}${formData?.dailySalary || 0}/d√≠a`
                      : ` ${currency}${formData?.hourlyRate || 0}/hora`}
                  </p>
                  <p>
                    <strong>Sitio:</strong>{' '}
                    {constructionSites?.find((s) => s?.id === formData?.siteId)?.name ||
                      constructionSites?.find((s) => s?.id === formData?.siteId)?.nombre ||
                      'Sin asignar'}
                  </p>
                </div>
              </div>
            </div>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg shadow-xl max-w-4xl w-full max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="px-6 py-4 border-b flex items-center justify-between">
          <h2 className="text-2xl font-bold text-gray-900">Registro de Empleado</h2>
          <Button variant="ghost" size="sm" onClick={onClose}>
            <X className="h-4 w-4" />
          </Button>
        </div>

        {/* Progress Indicator */}
        <div className="px-6 py-4 border-b">
          <div className="flex items-center justify-between mb-4">
            {steps?.map((step, index) => {
              const StepIcon = step?.icon;
              const isActive = currentStep === step?.id;
              const isCompleted = currentStep > step?.id;

              return (
                <div key={step?.id} className="flex items-center">
                  <div
                    className={`flex items-center justify-center w-10 h-10 rounded-full border-2 ${
                      isCompleted
                        ? 'bg-green-500 border-green-500 text-white'
                        : isActive
                        ? 'border-blue-500 text-blue-500'
                        : 'border-gray-300 text-gray-400'
                    }`}
                  >
                    <StepIcon className="h-5 w-5" />
                  </div>
                  {index < steps?.length - 1 && (
                    <div className={`flex-1 h-1 mx-4 ${isCompleted ? 'bg-green-500' : 'bg-gray-300'}`} />
                  )}
                </div>
              );
            })}
          </div>
          <div className="text-center">
            <p className="text-sm font-medium text-gray-900">
              Paso {currentStep} de {steps?.length}: {steps?.find((s) => s?.id === currentStep)?.title}
            </p>
          </div>
        </div>

        {/* Content */}
        <div className="px-6 py-6">{renderStepContent()}</div>

        {/* Footer */}
        <div className="px-6 py-4 border-t flex items-center justify-between">
          <Button
            variant="outline"
            onClick={handlePrevious}
            disabled={currentStep === 1}
            className="flex items-center gap-2"
          >
            <ChevronLeft className="h-4 w-4" />
            Anterior
          </Button>

          <div className="flex gap-3">
            {currentStep < steps?.length ? (
              <Button
                onClick={handleNext}
                className="flex items-center gap-2"
                style={{ backgroundColor: branding?.color_primario }}
              >
                Siguiente
                <ChevronRight className="h-4 w-4" />
              </Button>
            ) : (
              <Button
                onClick={handleSubmit}
                disabled={loading}
                className="flex items-center gap-2"
                style={{ backgroundColor: branding?.color_primario }}
              >
                {loading && <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>}
                Registrar Empleado
              </Button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default EmployeeRegistrationWizard;

src/pages/comprehensive-reporting-and-ex.content:
import React, { useState } from 'react';
import Icon from '../../../components/AppIcon';
import Button from '../../../components/ui/Button';
import Input from '../../../components/ui/Input';
import Select from '../../../components/ui/Select';
import { Checkbox } from '../../../components/ui/Checkbox';

const ReportBuilder = ({ selectedTemplate, onReportChange }) => {
  const [reportConfig, setReportConfig] = useState({
    name: selectedTemplate?.name || 'Nuevo Reporte',
    dateRange: 'last_30_days',
    sites: [],
    employees: [],
    departments: [],
    fields: selectedTemplate?.fields || [],
    filters: {},
    groupBy: '',
    sortBy: 'date',
    sortOrder: 'desc'
  });

  const [activeTab, setActiveTab] = useState('fields');

  const dateRangeOptions = [
    { value: 'today', label: 'Hoy' },
    { value: 'yesterday', label: 'Ayer' },
    { value: 'last_7_days', label: '√öltimos 7 d√≠as' },
    { value: 'last_30_days', label: '√öltimos 30 d√≠as' },
    { value: 'this_month', label: 'Este mes' },
    { value: 'last_month', label: 'Mes anterior' },
    { value: 'this_year', label: 'Este a√±o' },
    { value: 'custom', label: 'Rango personalizado' }
  ];

  const availableFields = [
    { id: 'employee_name', name: 'Nombre del Empleado', category: 'employee' },
    { id: 'employee_id', name: 'ID Empleado', category: 'employee' },
    { id: 'site_name', name: 'Sitio de Trabajo', category: 'site' },
    { id: 'department', name: 'Departamento', category: 'employee' },
    { id: 'date', name: 'Fecha', category: 'time' },
    { id: 'check_in', name: 'Hora de Entrada', category: 'time' },
    { id: 'check_out', name: 'Hora de Salida', category: 'time' },
    { id: 'lunch_start', name: 'Inicio de Almuerzo', category: 'time' },
    { id: 'lunch_end', name: 'Fin de Almuerzo', category: 'time' },
    { id: 'hours_worked', name: 'Horas Trabajadas', category: 'calculation' },
    { id: 'overtime_hours', name: 'Horas Extra', category: 'calculation' },
    { id: 'base_salary', name: 'Salario Base', category: 'payroll' },
    { id: 'overtime_pay', name: 'Pago Horas Extra', category: 'payroll' },
    { id: 'deductions', name: 'Deducciones', category: 'payroll' },
    { id: 'bonuses', name: 'Bonificaciones', category: 'payroll' },
    { id: 'total_pay', name: 'Pago Total', category: 'payroll' },
    { id: 'attendance_status', name: 'Estado de Asistencia', category: 'status' },
    { id: 'incident_type', name: 'Tipo de Incidente', category: 'incidents' },
    { id: 'incident_description', name: 'Descripci√≥n del Incidente', category: 'incidents' }
  ];

  const siteOptions = [
    { value: 'site_1', label: 'Obra Central' },
    { value: 'site_2', label: 'Proyecto Norte' },
    { value: 'site_3', label: 'Edificio Sur' },
    { value: 'site_4', label: 'Complejo Este' }
  ];

  const groupByOptions = [
    { value: '', label: 'Sin agrupar' },
    { value: 'employee', label: 'Por Empleado' },
    { value: 'site', label: 'Por Sitio' },
    { value: 'department', label: 'Por Departamento' },
    { value: 'date', label: 'Por Fecha' },
    { value: 'week', label: 'Por Semana' },
    { value: 'month', label: 'Por Mes' }
  ];

  const sortOptions = [
    { value: 'date', label: 'Fecha' },
    { value: 'employee_name', label: 'Nombre' },
    { value: 'hours_worked', label: 'Horas Trabajadas' },
    { value: 'total_pay', label: 'Pago Total' }
  ];

  const tabs = [
    { id: 'fields', name: 'Campos', icon: 'Columns' },
    { id: 'filters', name: 'Filtros', icon: 'Filter' },
    { id: 'grouping', name: 'Agrupaci√≥n', icon: 'Group' },
    { id: 'formatting', name: 'Formato', icon: 'Palette' }
  ];

  const updateConfig = (key, value) => {
    const newConfig = { ...reportConfig, [key]: value };
    setReportConfig(newConfig);
    onReportChange(newConfig);
  };

  const toggleField = (fieldId) => {
    const currentFields = reportConfig?.fields || [];
    const newFields = currentFields?.includes(fieldId)
      ? currentFields?.filter(id => id !== fieldId)
      : [...currentFields, fieldId];
    updateConfig('fields', newFields);
  };

  const fieldCategories = availableFields?.reduce((acc, field) => {
    if (!acc?.[field?.category]) {
      acc[field.category] = [];
    }
    acc?.[field?.category]?.push(field);
    return acc;
  }, {});

  const categoryNames = {
    employee: 'Empleado',
    site: 'Sitio',
    time: 'Tiempo',
    calculation: 'C√°lculos',
    payroll: 'N√≥mina',
    status: 'Estado',
    incidents: 'Incidentes'
  };

  return (
    <div className="bg-card rounded-lg border border-border p-6">
      <div className="flex items-center justify-between mb-6">
        <div>
          <h2 className="text-lg font-semibold text-foreground">Constructor de Reportes</h2>
          <p className="text-sm text-muted-foreground">Personaliza tu reporte seg√∫n tus necesidades</p>
        </div>
        <div className="flex items-center space-x-2">
          <Button variant="outline" iconName="Save">
            Guardar Plantilla
          </Button>
          <Button variant="default" iconName="Play">
            Generar Reporte
          </Button>
        </div>
      </div>
      {/* Report Name */}
      <div className="mb-6">
        <Input
          label="Nombre del Reporte"
          value={reportConfig?.name}
          onChange={(e) => updateConfig('name', e?.target?.value)}
          placeholder="Ingresa el nombre del reporte"
        />
      </div>
      {/* Tabs */}
      <div className="border-b border-border mb-6">
        <nav className="flex space-x-8">
          {tabs?.map((tab) => (
            <button
              key={tab?.id}
              onClick={() => setActiveTab(tab?.id)}
              className={`
                flex items-center space-x-2 py-2 px-1 border-b-2 font-medium text-sm
                transition-colors duration-150 ease-out-cubic
                ${activeTab === tab?.id
                  ? 'border-primary text-primary' :'border-transparent text-muted-foreground hover:text-foreground hover:border-muted-foreground'
                }
              `}
            >
              <Icon name={tab?.icon} size={16} />
              <span>{tab?.name}</span>
            </button>
          ))}
        </nav>
      </div>
      {/* Tab Content */}
      <div className="space-y-6">
        {activeTab === 'fields' && (
          <div>
            <h3 className="text-sm font-medium text-foreground mb-4">Seleccionar Campos</h3>
            <div className="space-y-4">
              {Object.entries(fieldCategories)?.map(([category, fields]) => (
                <div key={category} className="border border-border rounded-lg p-4">
                  <h4 className="font-medium text-foreground mb-3 flex items-center">
                    <Icon name="Folder" size={16} className="mr-2 text-muted-foreground" />
                    {categoryNames?.[category]}
                  </h4>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-2">
                    {fields?.map((field) => (
                      <Checkbox
                        key={field?.id}
                        label={field?.name}
                        checked={reportConfig?.fields?.includes(field?.id) || false}
                        onChange={() => toggleField(field?.id)}
                      />
                    ))}
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {activeTab === 'filters' && (
          <div className="space-y-4">
            <h3 className="text-sm font-medium text-foreground mb-4">Filtros de Datos</h3>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <Select
                label="Rango de Fechas"
                options={dateRangeOptions}
                value={reportConfig?.dateRange}
                onChange={(value) => updateConfig('dateRange', value)}
              />

              <Select
                label="Sitios de Trabajo"
                options={siteOptions}
                value={reportConfig?.sites}
                onChange={(value) => updateConfig('sites', value)}
                multiple
                searchable
                placeholder="Seleccionar sitios..."
              />
            </div>

            {reportConfig?.dateRange === 'custom' && (
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <Input
                  label="Fecha de Inicio"
                  type="date"
                  value={reportConfig?.startDate || ''}
                  onChange={(e) => updateConfig('startDate', e?.target?.value)}
                />
                <Input
                  label="Fecha de Fin"
                  type="date"
                  value={reportConfig?.endDate || ''}
                  onChange={(e) => updateConfig('endDate', e?.target?.value)}
                />
              </div>
            )}

            <div className="border border-border rounded-lg p-4">
              <h4 className="font-medium text-foreground mb-3">Filtros Avanzados</h4>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <Input
                  label="Horas M√≠nimas Trabajadas"
                  type="number"
                  placeholder="0"
                  value={reportConfig?.minHours || ''}
                  onChange={(e) => updateConfig('minHours', e?.target?.value)}
                />
                <Input
                  label="Horas M√°ximas Trabajadas"
                  type="number"
                  placeholder="24"
                  value={reportConfig?.maxHours || ''}
                  onChange={(e) => updateConfig('maxHours', e?.target?.value)}
                />
              </div>
            </div>
          </div>
        )}

        {activeTab === 'grouping' && (
          <div className="space-y-4">
            <h3 className="text-sm font-medium text-foreground mb-4">Agrupaci√≥n y Ordenamiento</h3>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <Select
                label="Agrupar Por"
                options={groupByOptions}
                value={reportConfig?.groupBy}
                onChange={(value) => updateConfig('groupBy', value)}
              />

              <Select
                label="Ordenar Por"
                options={sortOptions}
                value={reportConfig?.sortBy}
                onChange={(value) => updateConfig('sortBy', value)}
              />
            </div>

            <div className="flex items-center space-x-4">
              <span className="text-sm font-medium text-foreground">Orden:</span>
              <div className="flex items-center space-x-4">
                <label className="flex items-center space-x-2">
                  <input
                    type="radio"
                    name="sortOrder"
