import React, { useEffect, useMemo, useState } from 'react';
import Icon from '../../../components/AppIcon';
import Button from '../../../components/ui/Button';
import Select from '../../../components/ui/Select';
import { Checkbox } from '../../../components/ui/Checkbox';

const BulkAssignmentPanel = ({
  sites = [],
  supervisors = [],
  employees = [],
  onBulkAssign,
  onClose,
  userRole = 'admin',
}) => {
  const [assignmentType, setAssignmentType] = useState('employee-to-site');
  const [selectedItems, setSelectedItems] = useState([]);
  const [targetSelection, setTargetSelection] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);

  // b√∫squeda local para la lista de origen (empleados o sitios)
  const [sourceSearch, setSourceSearch] = useState('');

  const assignmentTypes = [
    { value: 'employee-to-site', label: 'Asignar empleados a sitio' },
    { value: 'employee-to-supervisor', label: 'Asignar empleados a supervisor' },
    { value: 'site-to-supervisor', label: 'Asignar sitios a supervisor' },
  ];

  // Normalizaci√≥n de opciones (memoizado)
  const sourceOptions = useMemo(() => {
    switch (assignmentType) {
      case 'employee-to-site':
      case 'employee-to-supervisor':
        return (employees || []).map((emp) => ({
          value: emp?.id,
          label: `${emp?.name || emp?.full_name || 'Empleado'} - ${emp?.currentSite || 'Sin sitio'}`,
          description: emp?.position || emp?.role || '',
          raw: emp,
        }));
      case 'site-to-supervisor':
        return (sites || []).map((site) => ({
          value: site?.id,
          label: `${site?.name} - ${site?.location || 'Sin ubicaci√≥n'}`,
          description: `${site?.employeeCount ?? 0} empleados`,
          raw: site,
        }));
      default:
        return [];
    }
  }, [assignmentType, employees, sites]);

  const targetOptions = useMemo(() => {
    switch (assignmentType) {
      case 'employee-to-site':
        return (sites || []).map((site) => ({
          value: site?.id,
          label: site?.name,
          description: site?.location || '',
          raw: site,
        }));
      case 'employee-to-supervisor':
      case 'site-to-supervisor':
        return (supervisors || []).map((sup) => ({
          value: sup?.id,
          label: sup?.name || sup?.full_name || 'Supervisor',
          description: `${sup?.assignedSites ?? 0} sitios asignados`,
          raw: sup,
        }));
      default:
        return [];
    }
  }, [assignmentType, sites, supervisors]);

  // Filtrado de la lista de origen por texto
  const filteredSourceOptions = useMemo(() => {
    const term = sourceSearch.trim().toLowerCase();
    if (!term) return sourceOptions;
    return sourceOptions.filter(
      (opt) =>
        opt.label.toLowerCase().includes(term) ||
        (opt.description || '').toLowerCase().includes(term)
    );
  }, [sourceOptions, sourceSearch]);

  // Reset de selecci√≥n al cambiar de tipo de asignaci√≥n
  useEffect(() => {
    setSelectedItems([]);
    setTargetSelection('');
    setSourceSearch('');
  }, [assignmentType]);

  const allFilteredIds = useMemo(
    () => filteredSourceOptions.map((o) => o.value),
    [filteredSourceOptions]
  );

  const isAllFilteredSelected =
    allFilteredIds.length > 0 &&
    allFilteredIds.every((id) => selectedItems.includes(id));

  const handleItemToggle = (itemId) => {
    setSelectedItems((prev) =>
      prev.includes(itemId) ? prev.filter((id) => id !== itemId) : [...prev, itemId]
    );
  };

  const handleSelectAllFiltered = () => {
    setSelectedItems((prev) =>
      isAllFilteredSelected
        ? prev.filter((id) => !allFilteredIds.includes(id))
        : Array.from(new Set([...prev, ...allFilteredIds]))
    );
  };

  const canAssign = selectedItems.length > 0 && !!targetSelection && !isProcessing;

  const handleBulkAssign = async () => {
    if (!canAssign) return;
    setIsProcessing(true);
    try {
      await onBulkAssign?.({
        type: assignmentType,
        sourceIds: selectedItems,
        targetId: targetSelection,
      });
      setSelectedItems([]);
      setTargetSelection('');
    } catch (error) {
      // eslint-disable-next-line no-console
      console.error('Error en asignaci√≥n masiva:', error);
    } finally {
      setIsProcessing(false);
    }
  };

  const summaryText = useMemo(() => {
    const sourceCount = selectedItems.length;
    const targetName = targetOptions.find((opt) => opt.value === targetSelection)?.label;
    if (!sourceCount || !targetName) return '';
    switch (assignmentType) {
      case 'employee-to-site':
        return `Asignar ${sourceCount} empleado${sourceCount > 1 ? 's' : ''} al sitio "${targetName}"`;
      case 'employee-to-supervisor':
        return `Asignar ${sourceCount} empleado${sourceCount > 1 ? 's' : ''} al supervisor "${targetName}"`;
      case 'site-to-supervisor':
        return `Asignar ${sourceCount} sitio${sourceCount > 1 ? 's' : ''} al supervisor "${targetName}"`;
      default:
        return '';
    }
  }, [assignmentType, selectedItems, targetOptions, targetSelection]);

  if (userRole !== 'admin') {
    return (
      <div className="bg-card border border-border rounded-lg p-6">
        <div className="text-center">
          <Icon name="Lock" size={48} className="mx-auto text-muted-foreground mb-4" />
          <h3 className="text-lg font-semibold text-foreground mb-2">Acceso Restringido</h3>
          <p className="text-muted-foreground">No tienes permisos para realizar asignaciones masivas.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-card border border-border rounded-lg p-6">
      {/* Header */}
      <div className="flex items-center justify-between mb-6">
        <h3 className="text-lg font-semibold text-foreground">Asignaci√≥n Masiva</h3>
        <Button variant="ghost" size="icon" onClick={onClose} aria-label="Cerrar">
          <Icon name="X" size={20} />
        </Button>
      </div>

      <div className="space-y-6">
        {/* Assignment Type Selection */}
        <div>
          <Select
            label="Tipo de asignaci√≥n"
            options={assignmentTypes}
            value={assignmentType}
            onChange={setAssignmentType}
            className="mb-4"
          />
        </div>

        {/* Source Selection */}
        <div>
          <div className="flex items-center justify-between mb-3">
            <label className="text-sm font-medium text-foreground">
              Seleccionar elementos ({selectedItems.length} seleccionados)
            </label>
            <div className="flex items-center gap-2">
              <input
                type="search"
                placeholder="Buscar‚Ä¶"
                value={sourceSearch}
                onChange={(e) => setSourceSearch(e.target.value)}
                className="px-2 py-1 text-sm border border-border rounded"
              />
              <Button
                variant="outline"
                size="sm"
                onClick={handleSelectAllFiltered}
                disabled={filteredSourceOptions.length === 0}
                iconName={
                  isAllFilteredSelected ? 'Square' : 'CheckSquare'
                }
                iconPosition="left"
                title={
                  filteredSourceOptions.length === 0
                    ? 'No hay elementos para seleccionar'
                    : isAllFilteredSelected
                    ? 'Deseleccionar visibles'
                    : 'Seleccionar visibles'
                }
              >
                {isAllFilteredSelected ? 'Deseleccionar visibles' : 'Seleccionar visibles'}
              </Button>
            </div>
          </div>

          <div className="max-h-64 overflow-y-auto border border-border rounded-md p-2 space-y-2">
            {filteredSourceOptions.length === 0 ? (
              <div className="text-sm text-muted-foreground px-2 py-4 text-center">
                {sourceOptions.length === 0
                  ? 'No hay elementos disponibles.'
                  : 'Sin resultados para el filtro.'}
              </div>
            ) : (
              filteredSourceOptions.map((option) => (
                <div
                  key={option.value}
                  className="flex items-center space-x-3 p-2 hover:bg-muted rounded-md"
                >
                  <Checkbox
                    checked={selectedItems.includes(option.value)}
                    onChange={() => handleItemToggle(option.value)}
                    aria-label={`Seleccionar ${option.label}`}
                  />
                  <div className="flex-1">
                    <p className="text-sm font-medium text-foreground">{option.label}</p>
                    {option.description && (
                      <p className="text-xs text-muted-foreground">{option.description}</p>
                    )}
                  </div>
                </div>
              ))
            )}
          </div>
          <div className="mt-2 text-xs text-muted-foreground">
            Mostrando {filteredSourceOptions.length} de {sourceOptions.length}
          </div>
        </div>

        {/* Target Selection */}
        <div>
          <Select
            label="Asignar a"
            options={targetOptions}
            value={targetSelection}
            onChange={(val) => setTargetSelection(val)}
            placeholder="Selecciona el destino"
            searchable
          />
        </div>

        {/* Assignment Summary */}
        {summaryText && (
          <div className="bg-primary/10 border border-primary/20 rounded-md p-4">
            <div className="flex items-center space-x-2">
              <Icon name="Info" size={16} className="text-primary" />
              <p className="text-sm font-medium text-primary">{summaryText}</p>
            </div>
          </div>
        )}

        {/* Action Buttons */}
        <div className="flex items-center justify-end space-x-3 pt-4 border-top border-border">
          <Button variant="outline" onClick={onClose}>
            Cancelar
          </Button>
          <Button
            variant="default"
            onClick={handleBulkAssign}
            disabled={!canAssign}
            loading={isProcessing}
            iconName="Users"
            iconPosition="left"
          >
            Asignar ({selectedItems.length})
          </Button>
        </div>
      </div>
    </div>
  );
};

export default BulkAssignmentPanel;

import React, { useCallback, useMemo, useRef, useState } from 'react';
import Icon from '../../../components/AppIcon';
import Button from '../../../components/ui/Button';

const OrganizationalTree = ({
  sites = [],
  supervisors = [],
  onNodeSelect,
  selectedNode,
  onDragDrop,
  userRole = 'admin',
}) => {
  const [expandedNodes, setExpandedNodes] = useState(new Set(['root']));
  const [draggedItem, setDraggedItem] = useState(null);
  const [search, setSearch] = useState('');

  const treeRef = useRef(null);

  // --- Helpers ---
  const toggleNode = useCallback((nodeId) => {
    setExpandedNodes((prev) => {
      const next = new Set(prev);
      if (next.has(nodeId)) next.delete(nodeId);
      else next.add(nodeId);
      return next;
    });
  }, []);

  // Combinaciones de drop permitidas (aj√∫stalas a tu l√≥gica)
  const canDrop = useCallback((dragType, dropType) => {
    // Reglas comunes:
    if (dragType === dropType) return false;

    // Mover sitio a supervisor o a "unassigned"
    if (dragType === 'site' && (dropType === 'supervisor' || dropType === 'unassigned')) return true;

    // (Opcional) mover supervisor dentro de root (para reordenar):
    if (dragType === 'supervisor' && dropType === 'root') return true;

    return false;
  }, []);

  const handleDragStart = (e, item, type) => {
    if (userRole !== 'admin') return;
    setDraggedItem({ item, type });
    e.dataTransfer.effectAllowed = 'move';
  };

  const handleDragOver = (e, targetType) => {
    if (!draggedItem || userRole !== 'admin') return;
    if (!canDrop(draggedItem?.type, targetType)) return;
    e.preventDefault();
    e.dataTransfer.dropEffect = 'move';
  };

  const handleDrop = (e, targetItem, targetType) => {
    e?.preventDefault?.();
    if (!draggedItem || userRole !== 'admin') return;
    if (!canDrop(draggedItem?.type, targetType)) return;
    onDragDrop?.(draggedItem, { item: targetItem, type: targetType });
    setDraggedItem(null);
  };

  // --- Filtro de b√∫squeda ---
  const normalizedSearch = search.trim().toLowerCase();
  const filteredSupervisors = useMemo(() => {
    if (!normalizedSearch) return supervisors;
    return supervisors.filter(
      (s) =>
        (s?.name || s?.full_name || '')
          .toLowerCase()
          .includes(normalizedSearch) ||
        (s?.email || '').toLowerCase().includes(normalizedSearch)
    );
  }, [supervisors, normalizedSearch]);

  // Agrupamos sitios por supervisorId; si no existe, van a "unassigned"
  const groupedData = useMemo(() => {
    const acc = {};
    const passSearch = (txt = '') =>
      !normalizedSearch || String(txt).toLowerCase().includes(normalizedSearch);

    (sites || []).forEach((site) => {
      // Si hay b√∫squeda, filtra por nombre/ubicaci√≥n del sitio tambi√©n
      if (
        normalizedSearch &&
        !(
          passSearch(site?.name) ||
          passSearch(site?.location) ||
          passSearch(site?.code)
        )
      ) {
        // igual puede aparecer si su supervisor matchea b√∫squeda, as√≠ que no lo excluimos aqu√≠
        // dejamos que el match de supervisor lo muestre si corresponde
      }

      const sid = site?.supervisor?.id || 'unassigned';
      if (!acc[sid]) acc[sid] = [];
      acc[sid].push(site);
    });

    // Si hay b√∫squeda solo por supervisor, filtramos por los supervisores que matchean
    if (normalizedSearch) {
      const allowedSupervisorIds = new Set(filteredSupervisors.map((s) => s?.id));
      // Mant√©n unassigned siempre que haya sitios que individualmente hagan match por nombre/ubicaci√≥n
      const filtered = {};
      Object.entries(acc).forEach(([supId, list]) => {
        if (supId === 'unassigned') {
          filtered[supId] = list.filter(
            (site) =>
              passSearch(site?.name) ||
              passSearch(site?.location) ||
              passSearch(site?.code)
          );
          if (filtered[supId].length === 0) delete filtered[supId];
        } else if (allowedSupervisorIds.has(supId)) {
          // Si el supervisor matchea, dejamos todos sus sitios
          filtered[supId] = list;
        } else {
          // Si el supervisor NO matchea, mostramos solo los sitios que por s√≠ mismos matcheen
          const onlyMatchingSites = list.filter(
            (site) =>
              passSearch(site?.name) ||
              passSearch(site?.location) ||
              passSearch(site?.code)
          );
          if (onlyMatchingSites.length > 0) filtered[supId] = onlyMatchingSites;
        }
      });
      return filtered;
    }

    return acc;
  }, [sites, filteredSupervisors, normalizedSearch]);

  // Expandir/Colapsar todo
  const expandAll = () => {
    const allIds = ['root', ...filteredSupervisors.map((s) => s?.id)];
    setExpandedNodes(new Set(allIds));
  };
  const collapseAll = () => setExpandedNodes(new Set(['root']));

  // --- TreeNode ---
  const TreeNode = ({
    id,
    label,
    icon,
    type,
    data,
    children,
    level = 0,
    isExpandable = false,
    count = null,
  }) => {
    const isExpanded = expandedNodes.has(id);
    const isSelected = selectedNode?.id === id && selectedNode?.type === type;
    const isDragging = draggedItem?.item?.id === data?.id;

    const onKeyDown = (e) => {
      // Accesibilidad de √°rbol
      if (e.key === 'ArrowRight' && isExpandable && !isExpanded) {
        e.preventDefault();
        toggleNode(id);
      } else if (e.key === 'ArrowLeft' && isExpandable && isExpanded) {
        e.preventDefault();
        toggleNode(id);
      } else if (e.key === 'Enter' || e.key === ' ') {
        e.preventDefault();
        onNodeSelect?.({ id, type, data });
      }
    };

    return (
      <div role="treeitem" aria-expanded={!!isExpandable ? isExpanded : undefined}>
        <div
          className={`
            flex items-center space-x-2 p-2 rounded-md cursor-pointer transition-all duration-150 ease-out-cubic outline-none
            ${isSelected ? 'bg-primary text-primary-foreground' : 'hover:bg-muted'}
            ${isDragging ? 'opacity-50' : ''}
          `}
          style={{ paddingLeft: `${level * 20 + 8}px` }}
          tabIndex={0}
          onKeyDown={onKeyDown}
          onClick={() => onNodeSelect?.({ id, type, data })}
          draggable={userRole === 'admin' && !!data}
          onDragStart={(e) => handleDragStart(e, data, type)}
          onDragOver={(e) => handleDragOver(e, type)}
          onDrop={(e) => handleDrop(e, data, type)}
        >
          {isExpandable ? (
            <Button
              variant="ghost"
              size="icon"
              className="w-4 h-4 p-0"
              onClick={(e) => {
                e.stopPropagation();
                toggleNode(id);
              }}
              aria-label={isExpanded ? 'Colapsar' : 'Expandir'}
              title={isExpanded ? 'Colapsar' : 'Expandir'}
            >
              <Icon name={isExpanded ? 'ChevronDown' : 'ChevronRight'} size={12} />
            </Button>
          ) : (
            <div className="w-4" />
          )}

          <Icon
            name={icon}
            size={16}
            className={isSelected ? 'text-primary-foreground' : 'text-muted-foreground'}
          />

          <span
            className={`text-sm font-medium ${
              isSelected ? 'text-primary-foreground' : 'text-foreground'
            }`}
          >
            {label}
          </span>

          {count !== null && (
            <span
              className={`ml-auto text-xs px-2 py-0.5 rounded-full ${
                isSelected
                  ? 'bg-primary-foreground/20 text-primary-foreground'
                  : 'bg-muted text-muted-foreground'
              }`}
            >
              {count}
            </span>
          )}
        </div>

        {isExpandable && isExpanded && children && <div className="ml-2">{children}</div>}
      </div>
    );
  };

  const totalSites = sites?.length || 0;
  const unassigned = groupedData?.unassigned || [];

  return (
    <div className="bg-card border border-border rounded-lg p-4 h-full overflow-y-auto">
      {/* Header */}
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-lg font-semibold text-foreground">Estructura Organizacional</h3>
        <div className="flex items-center gap-2">
          <input
            type="search"
            placeholder="Buscar supervisor o sitio‚Ä¶"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="px-2 py-1 text-sm border border-border rounded"
            aria-label="Buscar en √°rbol"
          />
          <Button
            variant="ghost"
            size="sm"
            onClick={expandAll}
            iconName="Expand"
            iconPosition="left"
            title="Expandir todo"
          >
            Expandir
          </Button>
          <Button
            variant="ghost"
            size="sm"
            onClick={collapseAll}
            iconName="Minimize2"
            iconPosition="left"
            title="Colapsar todo"
          >
            Colapsar
          </Button>
        </div>
      </div>

      {/* √Årbol */}
      <div role="tree" ref={treeRef} className="space-y-1">
        <TreeNode
          id="root"
          label="AsistenciaPro"
          icon="Building"
          type="root"
          isExpandable={true}
          count={totalSites}
        >
          {/* Supervisores con sus sitios */}
          {filteredSupervisors.length > 0 ? (
            filteredSupervisors.map((supervisor) => {
              const sitesUnder = groupedData?.[supervisor?.id] || [];
              const supLabel = supervisor?.name || supervisor?.full_name || 'Supervisor';
              return (
                <TreeNode
                  key={supervisor?.id}
                  id={supervisor?.id}
                  label={supLabel}
                  icon="UserCheck"
                  type="supervisor"
                  data={supervisor}
                  level={1}
                  isExpandable={(sitesUnder?.length || 0) > 0}
                  count={sitesUnder?.length || 0}
                >
                  {sitesUnder.map((site) => (
                    <TreeNode
                      key={site?.id}
                      id={site?.id}
                      label={site?.name}
                      icon="Building2"
                      type="site"
                      data={site}
                      level={2}
                      count={site?.employeeCount ?? null}
                    />
                  ))}
                </TreeNode>
              );
            })
          ) : (
            <div className="px-3 py-2 text-xs text-muted-foreground">
              {normalizedSearch ? 'Sin coincidencias para la b√∫squeda' : 'Sin supervisores'}
            </div>
          )}

          {/* Sitios sin supervisor */}
          {unassigned?.length > 0 && (
            <TreeNode
              id="unassigned"
              label="Sitios sin supervisor"
              icon="AlertTriangle"
              type="unassigned"
              level={1}
              isExpandable={true}
              count={unassigned?.length}
            >
              {unassigned.map((site) => (
                <TreeNode
                  key={site?.id}
                  id={site?.id}
                  label={site?.name}
                  icon="Building2"
                  type="site"
                  data={site}
                  level={2}
                  count={site?.employeeCount ?? null}
                />
              ))}
            </TreeNode>
          )}
        </TreeNode>
      </div>

      {/* Hint DnD */}
      {userRole === 'admin' && (
        <div className="mt-4 p-3 bg-muted/50 rounded-md">
          <div className="flex items-center space-x-2 text-xs text-muted-foreground">
            <Icon name="Info" size={12} />
            <span>Arrastra y suelta para reorganizar. Permite: sitio ‚Üí supervisor / sitio ‚Üí sin supervisor.</span>
          </div>
        </div>
      )}
    </div>
  );
};

export default OrganizationalTree;

import React, { useMemo } from 'react';
import Icon from '../../../components/AppIcon';
import Button from '../../../components/ui/Button';

const numberFmt = new Intl.NumberFormat('es-MX');

/**
 * QuickStatsPanel
 * - sites: [{ id, name, location, status, employeeCount, progress, supervisor }]
 * - supervisors: [{ id, name, email, assignedSites, totalEmployees, experience, status }]
 */
const QuickStatsPanel = ({
  sites = [],
  supervisors = [],
  onViewReports,
  onExportData,
  selectedNode,
}) => {
  // -------- Helpers
  const safeNum = (v, d = 0) => (Number.isFinite(Number(v)) ? Number(v) : d);
  const asPct = (v) => (Number.isFinite(Number(v)) ? `${v}%` : '‚Äî');

  // -------- Estad√≠sticas generales (memo)
  const stats = useMemo(() => {
    const totalSites = safeNum(sites?.length, 0);
    const activeSites = safeNum(sites?.filter((s) => s?.status === 'active')?.length, 0);
    const totalEmployees = safeNum(
      sites?.reduce((sum, s) => sum + safeNum(s?.employeeCount, 0), 0),
      0
    );
    const totalSupervisors = safeNum(supervisors?.length, 0);
    const unassignedSites = safeNum(sites?.filter((s) => !s?.supervisor)?.length, 0);
    const avgEmployeesPerSite =
      totalSites > 0 ? Math.round(totalEmployees / totalSites) : 0;

    return {
      totalSites,
      activeSites,
      totalEmployees,
      totalSupervisors,
      unassignedSites,
      avgEmployeesPerSite,
    };
  }, [sites, supervisors]);

  // -------- Resumen del nodo seleccionado (memo)
  const selectedStats = useMemo(() => {
    if (!selectedNode) return null;

    if (selectedNode?.type === 'site') {
      const d = selectedNode?.data || {};
      return {
        title: d?.name || 'Sitio',
        subtitle: d?.location || 'Ubicaci√≥n no especificada',
        stats: [
          { label: 'Empleados', value: safeNum(d?.employeeCount, 0), icon: 'Users' },
          { label: 'Progreso', value: asPct(d?.progress), icon: 'TrendingUp' },
          { label: 'Estado', value: d?.status || '‚Äî', icon: 'Activity' },
          { label: 'Supervisor', value: d?.supervisor?.name || 'Sin asignar', icon: 'UserCheck' },
        ],
      };
    }

    if (selectedNode?.type === 'supervisor') {
      const d = selectedNode?.data || {};
      return {
        title: d?.name || 'Supervisor',
        subtitle: d?.email || '‚Äî',
        stats: [
          { label: 'Sitios', value: safeNum(d?.assignedSites, 0), icon: 'Building2' },
          { label: 'Empleados', value: safeNum(d?.totalEmployees, 0), icon: 'Users' },
          { label: 'Experiencia', value: Number.isFinite(d?.experience) ? `${d?.experience} a√±os` : '‚Äî', icon: 'Award' },
          { label: 'Estado', value: d?.status || '‚Äî', icon: 'Activity' },
        ],
      };
    }

    return null;
  }, [selectedNode]);

  // -------- Color map (Tailwind seguro)
  const colorMap = {
    primary: { bg: 'bg-primary/10', text: 'text-primary', badge: 'bg-primary/10 text-primary' },
    success: { bg: 'bg-success/10', text: 'text-success', badge: 'bg-success/10 text-success' },
    warning: { bg: 'bg-warning/10', text: 'text-warning', badge: 'bg-warning/10 text-warning' },
    error:   { bg: 'bg-error/10',   text: 'text-error',   badge: 'bg-error/10 text-error' },
    secondary: { bg: 'bg-secondary/10', text: 'text-secondary', badge: 'bg-secondary/10 text-secondary' },
    muted: { bg: 'bg-muted', text: 'text-muted-foreground', badge: 'bg-muted text-muted-foreground' },
  };

  const StatCard = ({ icon, label, value, color = 'primary' }) => {
    const c = colorMap[color] || colorMap.muted;
    return (
      <div className="bg-card border border-border rounded-lg p-4">
        <div className="flex items-center space-x-3">
          <div className={`flex items-center justify-center w-10 h-10 rounded-lg ${c.bg}`}>
            <Icon name={icon} size={20} className={c.text} />
          </div>
          <div>
            <p className="text-2xl font-bold text-foreground">{numberFmt.format(safeNum(value, 0))}</p>
            <p className="text-sm text-muted-foreground">{label}</p>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="space-y-6">
      {/* Selected Node Details */}
      {selectedStats && (
        <div className="bg-card border border-border rounded-lg p-6">
          <div className="flex items-center justify-between mb-4">
            <div>
              <h3 className="text-lg font-semibold text-foreground">{selectedStats.title}</h3>
              <p className="text-sm text-muted-foreground">{selectedStats.subtitle}</p>
            </div>
            <div className="flex items-center space-x-2">
              <Button
                variant="outline"
                size="sm"
                onClick={() => onViewReports?.(selectedNode)}
                iconName="BarChart3"
                iconPosition="left"
              >
                Ver reportes
              </Button>
              <Button
                variant="outline"
                size="sm"
                onClick={() => onExportData?.(selectedNode)}
                iconName="Download"
                iconPosition="left"
              >
                Exportar
              </Button>
            </div>
          </div>

          <div className="grid grid-cols-2 gap-4">
            {selectedStats.stats.map((stat, i) => (
              <div key={i} className="flex items-center space-x-3 p-3 bg-muted/50 rounded-md">
                <Icon name={stat.icon} size={16} className="text-muted-foreground" />
                <div>
                  <p className="text-sm font-medium text-foreground">
                    {typeof stat.value === 'number' ? numberFmt.format(stat.value) : stat.value}
                  </p>
                  <p className="text-xs text-muted-foreground">{stat.label}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Overall Statistics */}
      <div className="bg-card border border-border rounded-lg p-6">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-foreground">Estad√≠sticas Generales</h3>
          <Button
            variant="outline"
            size="sm"
            onClick={() => onExportData?.({ type: 'all' })}
            iconName="Download"
            iconPosition="left"
          >
            Exportar todo
          </Button>
        </div>

        <div className="grid grid-cols-1 gap-4">
          <StatCard icon="Building2" label="Total de sitios" value={stats.totalSites} color="primary" />
