import React, { useState } from 'react';
import Icon from '../../../components/AppIcon';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, PieChart, Pie, Cell, LineChart, Line } from 'recharts';

const IncidentAnalyticsDashboard = ({ incidents }) => {
  const [timeRange, setTimeRange] = useState('month');

  // Calculate analytics data
  const totalIncidents = incidents?.length;
  const pendingIncidents = incidents?.filter(i => i?.status === 'pending')?.length;
  const approvedIncidents = incidents?.filter(i => i?.status === 'approved')?.length;
  const rejectedIncidents = incidents?.filter(i => i?.status === 'rejected')?.length;

  // Incident types distribution
  const typeDistribution = incidents?.reduce((acc, incident) => {
    acc[incident.type] = (acc?.[incident?.type] || 0) + 1;
    return acc;
  }, {});

  const pieData = Object.entries(typeDistribution)?.map(([type, count]) => ({
    name: type,
    value: count,
    label: type === 'absence' ? 'Ausencia' :
           type === 'permit' ? 'Permiso' :
           type === 'tardiness' ? 'Tardanza' :
           type === 'medical' ? 'M√©dico' :
           type === 'emergency' ? 'Emergencia' :
           type === 'training' ? 'Capacitaci√≥n' : 'Otro'
  }));

  // Monthly trend data
  const monthlyData = incidents?.reduce((acc, incident) => {
    const month = new Date(incident.submittedAt)?.toLocaleDateString('es-ES', { month: 'short' });
    const existing = acc?.find(item => item?.month === month);
    if (existing) {
      existing.count += 1;
      if (incident?.status === 'approved') existing.approved += 1;
      if (incident?.status === 'rejected') existing.rejected += 1;
    } else {
      acc?.push({
        month,
        count: 1,
        approved: incident?.status === 'approved' ? 1 : 0,
        rejected: incident?.status === 'rejected' ? 1 : 0
      });
    }
    return acc;
  }, []);

  // Site distribution
  const siteData = incidents?.reduce((acc, incident) => {
    acc[incident.site] = (acc?.[incident?.site] || 0) + 1;
    return acc;
  }, {});

  const siteChartData = Object.entries(siteData)?.map(([site, count]) => ({
    site,
    count
  }));

  // Top employees with most incidents
  const employeeData = incidents?.reduce((acc, incident) => {
    acc[incident.employeeName] = (acc?.[incident?.employeeName] || 0) + 1;
    return acc;
  }, {});

  const topEmployees = Object.entries(employeeData)?.sort(([,a], [,b]) => b - a)?.slice(0, 5)?.map(([name, count]) => ({ name, count }));

  const COLORS = ['#2563EB', '#F59E0B', '#DC2626', '#059669', '#7C3AED', '#EC4899'];

  const StatCard = ({ title, value, icon, color, description, trend }) => (
    <div className="bg-card border border-border rounded-lg p-6">
      <div className="flex items-center justify-between">
        <div>
          <p className="text-sm font-medium text-muted-foreground">{title}</p>
          <p className="text-2xl font-bold text-foreground mt-1">{value}</p>
          {description && (
            <p className="text-xs text-muted-foreground mt-1">{description}</p>
          )}
        </div>
        <div className={`p-3 rounded-full ${color}`}>
          <Icon name={icon} size={24} color="white" />
        </div>
      </div>
      {trend && (
        <div className="flex items-center mt-4 text-xs">
          <Icon 
            name={trend?.direction === 'up' ? 'TrendingUp' : 'TrendingDown'} 
            size={12} 
            className={trend?.direction === 'up' ? 'text-success' : 'text-error'} 
          />
          <span className={`ml-1 ${trend?.direction === 'up' ? 'text-success' : 'text-error'}`}>
            {trend?.percentage}%
          </span>
          <span className="text-muted-foreground ml-1">vs mes anterior</span>
        </div>
      )}
    </div>
  );

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-xl font-semibold text-foreground">An√°lisis de Incidentes</h2>
          <p className="text-sm text-muted-foreground mt-1">
            Resumen y tendencias de incidentes registrados
          </p>
        </div>
        <div className="flex items-center space-x-2">
          <select
            value={timeRange}
            onChange={(e) => setTimeRange(e?.target?.value)}
            className="px-3 py-2 border border-border rounded-md bg-input text-foreground text-sm focus:outline-none focus:ring-2 focus:ring-ring"
          >
            <option value="week">Esta Semana</option>
            <option value="month">Este Mes</option>
            <option value="quarter">Este Trimestre</option>
            <option value="year">Este A√±o</option>
          </select>
        </div>
      </div>
      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatCard
          title="Total Incidentes"
          value={totalIncidents}
          icon="FileText"
          color="bg-primary"
          description="Todos los incidentes registrados"
          trend={{ direction: 'up', percentage: 12 }}
        />
        <StatCard
          title="Pendientes"
          value={pendingIncidents}
          icon="Clock"
          color="bg-warning"
          description="Esperando aprobaci√≥n"
          trend={{ direction: 'down', percentage: 5 }}
        />
        <StatCard
          title="Aprobados"
          value={approvedIncidents}
          icon="CheckCircle"
          color="bg-success"
          description="Incidentes aprobados"
          trend={{ direction: 'up', percentage: 8 }}
        />
        <StatCard
          title="Rechazados"
          value={rejectedIncidents}
          icon="XCircle"
          color="bg-error"
          description="Incidentes rechazados"
          trend={{ direction: 'down', percentage: 15 }}
        />
      </div>
      {/* Charts Row 1 */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Incident Types Distribution */}
        <div className="bg-card border border-border rounded-lg p-6">
          <h3 className="text-lg font-semibold text-foreground mb-4">
            Distribuci√≥n por Tipo
          </h3>
          <div className="h-64">
            <ResponsiveContainer width="100%" height="100%">
              <PieChart>
                <Pie
                  data={pieData}
                  cx="50%"
                  cy="50%"
                  labelLine={false}
                  label={({ name, percent }) => `${name} ${(percent * 100)?.toFixed(0)}%`}
                  outerRadius={80}
                  fill="#8884d8"
                  dataKey="value"
                >
                  {pieData?.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={COLORS?.[index % COLORS?.length]} />
                  ))}
                </Pie>
                <Tooltip />
              </PieChart>
            </ResponsiveContainer>
          </div>
        </div>

        {/* Monthly Trend */}
        <div className="bg-card border border-border rounded-lg p-6">
          <h3 className="text-lg font-semibold text-foreground mb-4">
            Tendencia Mensual
          </h3>
          <div className="h-64">
            <ResponsiveContainer width="100%" height="100%">
              <LineChart data={monthlyData}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="month" />
                <YAxis />
                <Tooltip />
                <Line 
                  type="monotone" 
                  dataKey="count" 
                  stroke="#2563EB" 
                  strokeWidth={2}
                  name="Total"
                />
                <Line 
                  type="monotone" 
                  dataKey="approved" 
                  stroke="#059669" 
                  strokeWidth={2}
                  name="Aprobados"
                />
                <Line 
                  type="monotone" 
                  dataKey="rejected" 
                  stroke="#DC2626" 
                  strokeWidth={2}
                  name="Rechazados"
                />
              </LineChart>
            </ResponsiveContainer>
          </div>
        </div>
      </div>
      {/* Charts Row 2 */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Incidents by Site */}
        <div className="bg-card border border-border rounded-lg p-6">
          <h3 className="text-lg font-semibold text-foreground mb-4">
            Incidentes por Sitio
          </h3>
          <div className="h-64">
            <ResponsiveContainer width="100%" height="100%">
              <BarChart data={siteChartData}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="site" />
                <YAxis />
                <Tooltip />
                <Bar dataKey="count" fill="#2563EB" />
              </BarChart>
            </ResponsiveContainer>
          </div>
        </div>

        {/* Top Employees */}
        <div className="bg-card border border-border rounded-lg p-6">
          <h3 className="text-lg font-semibold text-foreground mb-4">
            Empleados con M√°s Incidentes
          </h3>
          <div className="space-y-3">
            {topEmployees?.map((employee, index) => (
              <div key={employee?.name} className="flex items-center justify-between p-3 bg-muted rounded-lg">
                <div className="flex items-center space-x-3">
                  <div className="flex items-center justify-center w-8 h-8 bg-primary text-primary-foreground rounded-full text-sm font-medium">
                    {index + 1}
                  </div>
                  <span className="text-sm font-medium text-foreground">
                    {employee?.name}
                  </span>
                </div>
                <div className="flex items-center space-x-2">
                  <span className="text-sm text-muted-foreground">
                    {employee?.count} incidentes
                  </span>
                  <div className="w-16 bg-border rounded-full h-2">
                    <div 
                      className="bg-primary h-2 rounded-full"
                      style={{ width: `${(employee?.count / Math.max(...topEmployees?.map(e => e?.count))) * 100}%` }}
                    />
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
      {/* Quick Insights */}
      <div className="bg-card border border-border rounded-lg p-6">
        <h3 className="text-lg font-semibold text-foreground mb-4">
          Insights R√°pidos
        </h3>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="p-4 bg-primary/5 border border-primary/20 rounded-lg">
            <div className="flex items-center space-x-2 mb-2">
              <Icon name="TrendingUp" size={16} className="text-primary" />
              <span className="text-sm font-medium text-primary">Tendencia</span>
            </div>
            <p className="text-xs text-muted-foreground">
              Los incidentes m√©dicos han aumentado un 15% este mes, considere revisar las condiciones de seguridad.
            </p>
          </div>

          <div className="p-4 bg-warning/5 border border-warning/20 rounded-lg">
            <div className="flex items-center space-x-2 mb-2">
              <Icon name="AlertTriangle" size={16} className="text-warning" />
              <span className="text-sm font-medium text-warning">Atenci√≥n</span>
            </div>
            <p className="text-xs text-muted-foreground">
              {pendingIncidents} incidentes pendientes requieren revisi√≥n urgente para mantener el flujo de trabajo.
            </p>
          </div>

          <div className="p-4 bg-success/5 border border-success/20 rounded-lg">
            <div className="flex items-center space-x-2 mb-2">
              <Icon name="CheckCircle" size={16} className="text-success" />
              <span className="text-sm font-medium text-success">Positivo</span>
            </div>
            <p className="text-xs text-muted-foreground">
              El tiempo promedio de aprobaci√≥n ha mejorado un 20% comparado con el mes anterior.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default IncidentAnalyticsDashboard;

import React, { useState, useRef } from 'react';
import Button from '../../../components/ui/Button';
import Input from '../../../components/ui/Input';
import Select from '../../../components/ui/Select';
import { AlertCircle, Upload, X, FileText, Image } from 'lucide-react';
import { incidentService } from '../../../services/incidentService';
import { useAuth } from '../../../contexts/AuthContext';

export function IncidentCreationForm({ onIncidentCreated, onCancel, targetEmployeeId = null }) {
  const { user } = useAuth()
  const fileInputRef = useRef(null)

  const [formData, setFormData] = useState({
    type: '',
    date: new Date()?.toISOString()?.split('T')?.[0],
    description: '',
    employee_id: targetEmployeeId || user?.id
  })

  const [attachments, setAttachments] = useState([])
  const [loading, setLoading] = useState(false)
  const [message, setMessage] = useState('')
  const [uploadProgress, setUploadProgress] = useState(0)

  const incidentTypes = [
    { value: 'falta', label: 'Falta' },
    { value: 'permiso', label: 'Permiso' },
    { value: 'retardo', label: 'Retardo' },
    { value: 'incapacidad', label: 'Incapacidad M√©dica' }
  ]

  const handleInputChange = (name, value) => {
    setFormData(prev => ({
      ...prev,
      [name]: value
    }))
  }

  const handleFileSelect = (event) => {
    const files = Array.from(event?.target?.files || [])

    if (attachments?.length + files?.length > 5) {
      setMessage('M√°ximo 5 archivos permitidos')
      return
    }

    // Validate file types and sizes
    const validFiles = []
    const maxSize = 50 * 1024 * 1024 // 50MB

    for (const file of files) {
      if (file?.size > maxSize) {
        setMessage(`Archivo ${file?.name} es demasiado grande (m√°ximo 50MB)`)
        continue
      }

      const validTypes = [
        'image/jpeg', 'image/png', 'image/webp', 'image/jpg', 'image/heic',
        'application/pdf'
      ]

      if (!validTypes?.includes(file?.type)) {
        setMessage(`Tipo de archivo no permitido: ${file?.name}`)
        continue
      }

      validFiles?.push(file)
    }

    if (validFiles?.length > 0) {
      setAttachments(prev => [...prev, ...validFiles])
      setMessage('')
    }

    // Clear file input
    if (fileInputRef?.current) {
      fileInputRef.current.value = ''
    }
  }

  const removeAttachment = (index) => {
    setAttachments(prev => prev?.filter((_, i) => i !== index))
  }

  const getFileIcon = (file) => {
    if (file?.type?.startsWith('image/')) {
      return <Image className="h-4 w-4 text-blue-600" />
    }
    return <FileText className="h-4 w-4 text-red-600" />
  }

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes'
    const k = 1024
    const sizes = ['Bytes', 'KB', 'MB', 'GB']
    const i = Math.floor(Math.log(bytes) / Math.log(k))
    return parseFloat((bytes / Math.pow(k, i))?.toFixed(2)) + ' ' + sizes?.[i]
  }

  const handleSubmit = async (e) => {
    e?.preventDefault()

    if (!formData?.type || !formData?.date || !formData?.description?.trim()) {
      setMessage('Por favor complete todos los campos requeridos')
      return
    }

    if (attachments?.length === 0 && formData?.type === 'incapacidad') {
      setMessage('Para incapacidades m√©dicas debe adjuntar documentos de respaldo')
      return
    }

    setLoading(true)
    setMessage('')
    setUploadProgress(0)

    try {
      const result = await incidentService?.createIncident(formData, attachments)

      if (result?.success) {
        setMessage('‚úÖ Incidencia creada exitosamente')

        // Reset form
        setFormData({
          type: '',
          date: new Date()?.toISOString()?.split('T')?.[0],
          description: '',
          employee_id: targetEmployeeId || user?.id
        })
        setAttachments([])

        // Notify parent component
        onIncidentCreated?.(result?.incident)

        setTimeout(() => {
          setMessage('')
          onCancel?.()
        }, 2000)
      } else {
        setMessage(`‚ùå Error: ${result?.error}`)
      }
    } catch (error) {
      setMessage('‚ùå Error al crear la incidencia')
      console.error('Incident creation error:', error)
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className="bg-white rounded-lg shadow-md p-6">
      <div className="flex items-center justify-between mb-6">
        <h3 className="text-lg font-semibold text-gray-900">
          Registrar Nueva Incidencia
        </h3>
        {onCancel && (
          <Button
            variant="ghost"
            size="sm"
            onClick={onCancel}
            iconName="X"
            iconPosition="left"
          >
            Cancelar
          </Button>
        )}
      </div>

      {message && (
        <div className={`mb-4 p-3 rounded-md ${
          message?.includes('‚ùå') 
            ? 'bg-red-50 border border-red-200' :'bg-blue-50 border border-blue-200'
        }`}>
          <p className={`text-sm ${
            message?.includes('‚ùå') ? 'text-red-800' : 'text-blue-800'
          }`}>
            {message}
          </p>
        </div>
      )}

      <form onSubmit={handleSubmit} className="space-y-6">
        {/* Incident Type */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Tipo de Incidencia *
          </label>
          <Select
            options={incidentTypes}
            value={formData?.type}
            onChange={(value) => handleInputChange('type', value)}
            placeholder="Seleccionar tipo de incidencia"
            required
          />
        </div>

        {/* Date */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Fecha *
          </label>
          <Input
            type="date"
            value={formData?.date}
            onChange={(e) => handleInputChange('date', e?.target?.value)}
            required
            max={new Date()?.toISOString()?.split('T')?.[0]}
          />
        </div>

        {/* Description */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Descripci√≥n / Justificaci√≥n *
          </label>
          <textarea
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            rows={4}
            value={formData?.description}
            onChange={(e) => handleInputChange('description', e?.target?.value)}
            placeholder="Describe los detalles de la incidencia..."
            required
          />
        </div>

        {/* File Attachments */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Documentos de Respaldo
            {formData?.type === 'incapacidad' && <span className="text-red-500"> *</span>}
          </label>

          <div className="space-y-3">
            {/* Upload Button */}
            <div 
              className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center hover:border-blue-400 transition-colors cursor-pointer"
              onClick={() => fileInputRef?.current?.click()}
            >
              <Upload className="h-8 w-8 text-gray-400 mx-auto mb-2" />
              <p className="text-sm text-gray-600 mb-1">
                Haz clic para subir archivos o arrastra aqu√≠
              </p>
              <p className="text-xs text-gray-500">
                Im√°genes (JPG, PNG, HEIC) y PDFs hasta 50MB cada uno
              </p>
              <p className="text-xs text-gray-500">
                M√°ximo 5 archivos por incidencia
              </p>
            </div>

            <input
              ref={fileInputRef}
              type="file"
              multiple
              accept="image/*,.pdf"
              onChange={handleFileSelect}
              className="hidden"
            />

            {/* Selected Files */}
            {attachments?.length > 0 && (
              <div className="space-y-2">
                <p className="text-sm font-medium text-gray-700">
                  Archivos seleccionados ({attachments?.length}/5):
                </p>

                {attachments?.map((file, index) => (
                  <div 
                    key={index} 
                    className="flex items-center justify-between p-3 bg-gray-50 rounded-md"
                  >
                    <div className="flex items-center space-x-3">
                      {getFileIcon(file)}
                      <div>
                        <p className="text-sm font-medium text-gray-900 truncate max-w-xs">
                          {file?.name}
                        </p>
                        <p className="text-xs text-gray-500">
                          {formatFileSize(file?.size)}
                        </p>
                      </div>
                    </div>

                    <Button
                      type="button"
                      variant="ghost"
                      size="sm"
                      onClick={() => removeAttachment(index)}
                      className="text-red-600 hover:text-red-800"
                    >
                      <X className="h-4 w-4" />
                    </Button>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* File requirements by type */}
          {formData?.type && (
            <div className="mt-3 p-3 bg-yellow-50 border border-yellow-200 rounded-md">
              <div className="flex items-start space-x-2">
                <AlertCircle className="h-4 w-4 text-yellow-600 mt-0.5" />
                <div className="text-sm text-yellow-800">
                  {formData?.type === 'incapacidad' && (
                    <p>
                      <strong>Incapacidad m√©dica:</strong> Debe adjuntar receta m√©dica, 
                      certificado de incapacidad o documento oficial del m√©dico.
                    </p>
                  )}
                  {formData?.type === 'permiso' && (
                    <p>
                      <strong>Permiso:</strong> Se recomienda adjuntar documentos de respaldo 
                      si el permiso es por motivos m√©dicos o legales.
                    </p>
                  )}
                  {formData?.type === 'retardo' && (
                    <p>
                      <strong>Retardo:</strong> Puede adjuntar documentos que justifiquen 
                      el motivo del retardo (m√©dicos, tr√°fico, etc.).
                    </p>
                  )}
                  {formData?.type === 'falta' && (
                    <p>
                      <strong>Falta:</strong> Se recomienda adjuntar documentos que 
                      justifiquen la ausencia.
                    </p>
                  )}
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Submit Button */}
        <div className="flex justify-end space-x-4">
          {onCancel && (
            <Button
              type="button"
              variant="outline"
              onClick={onCancel}
              disabled={loading}
            >
              Cancelar
            </Button>
          )}

          <Button
            type="submit"
            disabled={loading || !formData?.type || !formData?.description?.trim()}
            className="bg-blue-600 hover:bg-blue-700 text-white"
          >
            {loading ? (
              <div className="flex items-center space-x-2">
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
                <span>Creando incidencia...</span>
              </div>
            ) : (
              <div className="flex items-center space-x-2">
                <AlertCircle className="h-4 w-4" />
                <span>Registrar Incidencia</span>
              </div>
            )}
          </Button>
        </div>
      </form>

      {/* Help Text */}
      <div className="mt-6 p-4 bg-blue-50 border border-blue-200 rounded-md">
        <div className="flex items-start space-x-2">
          <AlertCircle className="h-4 w-4 text-blue-600 mt-0.5" />
          <div className="text-sm text-blue-800">
            <p className="font-medium mb-2">Informaci√≥n importante:</p>
            <ul className="space-y-1 text-blue-700">
              <li>‚Ä¢ Solo supervisores y administradores pueden registrar incidencias</li>
              <li>‚Ä¢ Las incapacidades m√©dicas requieren documentos de respaldo obligatorios</li>
              <li>‚Ä¢ Los archivos se almacenan de forma segura y solo son visibles para el equipo autorizado</li>
              <li>‚Ä¢ Una vez creada la incidencia, ser√° revisada por el administrador</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}

