import React, { useState } from 'react';
import Icon from '../../../components/AppIcon';
import Button from '../../../components/ui/Button';
import Select from '../../../components/ui/Select';

const PendingApprovalsQueue = ({ incidents, onApprove, onReject, currentUser }) => {
  const [selectedIncidents, setSelectedIncidents] = useState([]);
  const [filterStatus, setFilterStatus] = useState('pending');
  const [sortBy, setSortBy] = useState('priority');
  const [expandedIncident, setExpandedIncident] = useState(null);

  const statusOptions = [
    { value: 'all', label: 'Todos los Estados' },
    { value: 'pending', label: 'Pendientes' },
    { value: 'approved', label: 'Aprobados' },
    { value: 'rejected', label: 'Rechazados' },
    { value: 'under_review', label: 'En Revisi√≥n' }
  ];

  const sortOptions = [
    { value: 'priority', label: 'Por Prioridad' },
    { value: 'date', label: 'Por Fecha' },
    { value: 'employee', label: 'Por Empleado' },
    { value: 'type', label: 'Por Tipo' }
  ];

  const getStatusColor = (status) => {
    const colors = {
      pending: 'bg-warning/10 text-warning border-warning/20',
      approved: 'bg-success/10 text-success border-success/20',
      rejected: 'bg-error/10 text-error border-error/20',
      under_review: 'bg-primary/10 text-primary border-primary/20'
    };
    return colors?.[status] || colors?.pending;
  };

  const getPriorityIcon = (priority) => {
    return priority === 'high' ? 'AlertTriangle' : 'Clock';
  };

  const getPriorityColor = (priority) => {
    return priority === 'high' ? 'text-error' : 'text-muted-foreground';
  };

  const getTypeIcon = (type) => {
    const icons = {
      absence: 'UserX',
      permit: 'FileText',
      tardiness: 'Clock',
      medical: 'Heart',
      emergency: 'AlertTriangle',
      training: 'GraduationCap',
      other: 'HelpCircle'
    };
    return icons?.[type] || icons?.other;
  };

  const filteredIncidents = incidents?.filter(incident => filterStatus === 'all' || incident?.status === filterStatus)?.sort((a, b) => {
      switch (sortBy) {
        case 'priority':
          if (a?.priority === 'high' && b?.priority !== 'high') return -1;
          if (a?.priority !== 'high' && b?.priority === 'high') return 1;
          return new Date(b.submittedAt) - new Date(a.submittedAt);
        case 'date':
          return new Date(b.submittedAt) - new Date(a.submittedAt);
        case 'employee':
          return a?.employeeName?.localeCompare(b?.employeeName);
        case 'type':
          return a?.type?.localeCompare(b?.type);
        default:
          return 0;
      }
    });

  const handleSelectIncident = (incidentId) => {
    setSelectedIncidents(prev => 
      prev?.includes(incidentId)
        ? prev?.filter(id => id !== incidentId)
        : [...prev, incidentId]
    );
  };

  const handleSelectAll = () => {
    const pendingIds = filteredIncidents?.filter(incident => incident?.status === 'pending')?.map(incident => incident?.id);

    setSelectedIncidents(prev => 
      prev?.length === pendingIds?.length ? [] : pendingIds
    );
  };

  const handleBulkAction = async (action) => {
    const selectedPendingIncidents = filteredIncidents?.filter(
      incident => selectedIncidents?.includes(incident?.id) && incident?.status === 'pending'
    );

    for (const incident of selectedPendingIncidents) {
      if (action === 'approve') {
        await onApprove(incident?.id, 'Aprobaci√≥n masiva');
      } else if (action === 'reject') {
        await onReject(incident?.id, 'Rechazo masivo');
      }
    }

    setSelectedIncidents([]);
  };

  const formatDate = (date) => {
    return new Date(date)?.toLocaleDateString('es-ES', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const formatDateRange = (startDate, endDate) => {
    const start = new Date(startDate)?.toLocaleDateString('es-ES', {
      day: '2-digit',
      month: '2-digit'
    });

    if (!endDate) return start;

    const end = new Date(endDate)?.toLocaleDateString('es-ES', {
      day: '2-digit',
      month: '2-digit'
    });

    return `${start} - ${end}`;
  };

  const pendingCount = incidents?.filter(i => i?.status === 'pending')?.length;
  const selectedPendingCount = selectedIncidents?.filter(id => 
    incidents?.find(i => i?.id === id)?.status === 'pending'
  )?.length;

  return (
    <div className="bg-card border border-border rounded-lg">
      {/* Header */}
      <div className="p-6 border-b border-border">
        <div className="flex items-center justify-between mb-4">
          <div>
            <h2 className="text-xl font-semibold text-foreground">Cola de Aprobaciones</h2>
            <p className="text-sm text-muted-foreground mt-1">
              {pendingCount} incidentes pendientes de revisi√≥n
            </p>
          </div>
          <div className="flex items-center space-x-2">
            <Icon name="Clock" size={20} className="text-warning" />
            <span className="text-sm font-medium text-warning">
              {pendingCount} Pendientes
            </span>
          </div>
        </div>

        {/* Filters and Actions */}
        <div className="flex flex-col sm:flex-row gap-4">
          <div className="flex-1 grid grid-cols-1 sm:grid-cols-2 gap-3">
            <Select
              options={statusOptions}
              value={filterStatus}
              onChange={setFilterStatus}
              placeholder="Filtrar por estado..."
            />
            <Select
              options={sortOptions}
              value={sortBy}
              onChange={setSortBy}
              placeholder="Ordenar por..."
            />
          </div>

          {selectedPendingCount > 0 && (
            <div className="flex items-center space-x-2">
              <span className="text-sm text-muted-foreground">
                {selectedPendingCount} seleccionados
              </span>
              <Button
                variant="outline"
                size="sm"
                onClick={() => handleBulkAction('approve')}
                iconName="Check"
                iconSize={16}
              >
                Aprobar
              </Button>
              <Button
                variant="outline"
                size="sm"
                onClick={() => handleBulkAction('reject')}
                iconName="X"
                iconSize={16}
              >
                Rechazar
              </Button>
            </div>
          )}
        </div>
      </div>
      {/* Incidents List */}
      <div className="max-h-96 overflow-y-auto">
        {filteredIncidents?.length === 0 ? (
          <div className="p-8 text-center">
            <Icon name="Inbox" size={48} className="mx-auto text-muted-foreground mb-4" />
            <h3 className="text-lg font-medium text-foreground mb-2">
              No hay incidentes
            </h3>
            <p className="text-sm text-muted-foreground">
              {filterStatus === 'pending' ?'No hay incidentes pendientes de aprobaci√≥n' :'No se encontraron incidentes con los filtros aplicados'
              }
            </p>
          </div>
        ) : (
          <div className="divide-y divide-border">
            {/* Select All Header */}
            {filteredIncidents?.some(i => i?.status === 'pending') && (
              <div className="p-4 bg-muted/30">
                <label className="flex items-center space-x-3 cursor-pointer">
                  <input
                    type="checkbox"
                    checked={selectedIncidents?.length === filteredIncidents?.filter(i => i?.status === 'pending')?.length}
                    onChange={handleSelectAll}
                    className="w-4 h-4 text-primary border-border rounded focus:ring-ring"
                  />
                  <span className="text-sm font-medium text-foreground">
                    Seleccionar todos los pendientes
                  </span>
                </label>
              </div>
            )}

            {filteredIncidents?.map((incident) => (
              <div key={incident?.id} className="p-4 hover:bg-muted/50 transition-colors duration-150">
                <div className="flex items-start space-x-4">
                  {/* Checkbox */}
                  {incident?.status === 'pending' && (
                    <input
                      type="checkbox"
                      checked={selectedIncidents?.includes(incident?.id)}
                      onChange={() => handleSelectIncident(incident?.id)}
                      className="w-4 h-4 text-primary border-border rounded focus:ring-ring mt-1"
                    />
                  )}

                  {/* Incident Info */}
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center justify-between mb-2">
                      <div className="flex items-center space-x-3">
                        <Icon 
                          name={getTypeIcon(incident?.type)} 
                          size={16} 
                          className="text-muted-foreground" 
                        />
                        <span className="text-sm font-medium text-foreground">
                          {incident?.employeeName}
                        </span>
                        <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium border ${getStatusColor(incident?.status)}`}>
                          {incident?.status === 'pending' && 'Pendiente'}
                          {incident?.status === 'approved' && 'Aprobado'}
                          {incident?.status === 'rejected' && 'Rechazado'}
                          {incident?.status === 'under_review' && 'En Revisi√≥n'}
                        </span>
                        {incident?.priority === 'high' && (
                          <Icon 
                            name="AlertTriangle" 
                            size={14} 
                            className="text-error" 
                          />
                        )}
                      </div>
                      <div className="flex items-center space-x-2">
                        <span className="text-xs text-muted-foreground">
                          {formatDate(incident?.submittedAt)}
                        </span>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => setExpandedIncident(
                            expandedIncident === incident?.id ? null : incident?.id
                          )}
                          iconName={expandedIncident === incident?.id ? 'ChevronUp' : 'ChevronDown'}
                          iconSize={16}
                        >
                          {expandedIncident === incident?.id ? 'Menos' : 'M√°s'}
                        </Button>
                      </div>
                    </div>

                    <div className="flex items-center space-x-4 text-sm text-muted-foreground mb-2">
                      <span className="capitalize">{incident?.type}</span>
                      <span>‚Ä¢</span>
                      <span>{formatDateRange(incident?.startDate, incident?.endDate)}</span>
                      <span>‚Ä¢</span>
                      <span>{incident?.site}</span>
                    </div>

                    <p className="text-sm text-foreground line-clamp-2">
                      {incident?.description}
                    </p>

                    {/* Expanded Details */}
                    {expandedIncident === incident?.id && (
                      <div className="mt-4 p-4 bg-muted rounded-lg space-y-3">
                        <div>
                          <h4 className="text-sm font-medium text-foreground mb-1">Motivo:</h4>
                          <p className="text-sm text-muted-foreground capitalize">{incident?.reason}</p>
                        </div>

                        {incident?.attachments && incident?.attachments?.length > 0 && (
                          <div>
                            <h4 className="text-sm font-medium text-foreground mb-2">Documentos:</h4>
                            <div className="flex flex-wrap gap-2">
                              {incident?.attachments?.map((attachment, index) => (
                                <div key={index} className="flex items-center space-x-2 bg-background px-2 py-1 rounded text-xs">
                                  <Icon name="Paperclip" size={12} />
                                  <span>{attachment?.name}</span>
                                </div>
                              ))}
                            </div>
                          </div>
                        )}

                        {incident?.status === 'pending' && (
                          <div className="flex items-center space-x-2 pt-2">
                            <Button
                              variant="outline"
                              size="sm"
                              onClick={() => onApprove(incident?.id, 'Aprobado por supervisor')}
                              iconName="Check"
                              iconSize={16}
                            >
                              Aprobar
                            </Button>
                            <Button
                              variant="outline"
                              size="sm"
                              onClick={() => onReject(incident?.id, 'Rechazado por supervisor')}
                              iconName="X"
                              iconSize={16}
                            >
                              Rechazar
                            </Button>
                          </div>
                        )}
                      </div>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default PendingApprovalsQueue;

src/pages/obras-financial-control-manage.content:
import React, { useState } from 'react';
import { X, Download, FileSpreadsheet, FileText, Filter } from 'lucide-react';

const ExportModal = ({ obras, onClose }) => {
  const [exportFormat, setExportFormat] = useState('xlsx');
  const [exportType, setExportType] = useState('summary');
  const [includeFilters, setIncludeFilters] = useState({
    status: 'all',
    dateRange: { start: '', end: '' },
    includeKPIs: true,
    includeDetails: false
  });
  const [loading, setLoading] = useState(false);

  // Format currency for export
  const formatCurrency = (amount) => {
    if (!amount && amount !== 0) return 0;
    return amount;
  };

  // Format percentage for export
  const formatPercentage = (value) => {
    if (!value && value !== 0) return 0;
    return parseFloat(value?.toFixed(2));
  };

  // Prepare data for export
  const prepareExportData = () => {
    let filteredObras = obras;

    // Apply status filter
    if (includeFilters?.status !== 'all') {
      filteredObras = filteredObras?.filter(obra => obra?.estatus === includeFilters?.status);
    }

    // Apply date range filter
    if (includeFilters?.dateRange?.start && includeFilters?.dateRange?.end) {
      filteredObras = filteredObras?.filter(obra => {
        const obraDate = new Date(obra?.fecha_inicio);
        const startDate = new Date(includeFilters?.dateRange?.start);
        const endDate = new Date(includeFilters?.dateRange?.end);
        return obraDate >= startDate && obraDate <= endDate;
      });
    }

    if (exportType === 'summary') {
      return filteredObras?.map(obra => ({
        'Clave': obra?.clave || '',
        'Nombre': obra?.nombre || '',
        'Estado': obra?.estatus || '',
        'Empresa': obra?.empresa_nombre || '',
        'Dependencia': obra?.dependencia_nombre || 'N/A',
        'Presupuesto Total': formatCurrency(obra?.presupuesto_total),
        '√ìrdenes de Cambio': formatCurrency(obra?.presupuesto_total - obra?.presupuesto_inicial),
        'Facturado Total': formatCurrency(obra?.facturado_total),
        'Pagado Total': formatCurrency(obra?.pagado_total),
        'Por Cobrar': formatCurrency(obra?.por_cobrar),
        'Gastos Total': formatCurrency(obra?.gastos_total),
        'Costo Directo': formatCurrency(obra?.costo_directo),
        'Utilidad Bruta': formatCurrency(obra?.utilidad_bruta),
        'Utilidad vs Presupuesto': formatCurrency(obra?.utilidad_vs_presupuesto),
        '% Utilidad Real': formatPercentage(obra?.utilidad_pct_real),
        '% Avance Financiero': formatPercentage(obra?.avance_financiero_pct),
        '% Margen Presupuestado': formatPercentage(obra?.margen_presupuestado_pct),
        'Fecha Inicio': obra?.fecha_inicio || '',
        'Fecha Compromiso': obra?.fecha_fin_compromiso || ''
      }));
    }

    // For detailed report, return comprehensive data
    return filteredObras;
  };

  // Generate CSV content
  const generateCSV = (data) => {
    if (!data?.length) return '';

    const headers = Object.keys(data?.[0]);
    const csvContent = [
      headers?.join(','),
      ...data?.map(row => 
        headers?.map(header => {
          const value = row?.[header];
          // Escape commas and quotes in values
          if (typeof value === 'string' && (value?.includes(',') || value?.includes('"'))) {
            return `"${value?.replace(/"/g, '""')}"`;
          }
          return value ?? '';
        })?.join(',')
      )
    ]?.join('\n');

    return csvContent;
  };

  // Generate Excel-compatible content (CSV with BOM for proper encoding)
  const generateExcel = (data) => {
    const csv = generateCSV(data);
    return '\uFEFF' + csv; // Add BOM for proper UTF-8 encoding in Excel
  };

  // Generate PDF content (simplified - in a real app, you'd use a PDF library)
  const generatePDFContent = (data) => {
    // This is a simplified version. In production, you'd use jsPDF or similar
    return `
REPORTE FINANCIERO DE OBRAS
Generado: ${new Date()?.toLocaleString('es-MX')}
Total de obras: ${data?.length}

${data?.map(obra => `
OBRA: ${obra?.['Clave']} - ${obra?.['Nombre']}
Estado: ${obra?.['Estado']}
Empresa: ${obra?.['Empresa']}
Presupuesto Total: $${obra?.['Presupuesto Total']?.toLocaleString('es-MX')}
Facturado: $${obra?.['Facturado Total']?.toLocaleString('es-MX')}
Por Cobrar: $${obra?.['Por Cobrar']?.toLocaleString('es-MX')}
% Utilidad Real: ${obra?.['% Utilidad Real']}%
% Avance: ${obra?.['% Avance Financiero']}%
----------------------------------------
`)?.join('')}
    `;
  };

  // Handle export
  const handleExport = async () => {
    try {
      setLoading(true);

      const data = prepareExportData();

      if (!data?.length) {
        alert('No hay datos para exportar con los filtros seleccionados');
        return;
      }

      let content;
      let mimeType;
      let fileName;

      switch (exportFormat) {
        case 'xlsx':
          content = generateExcel(data);
          mimeType = 'text/csv';
          fileName = `obras-financiero-${exportType}-${new Date()?.toISOString()?.split('T')?.[0]}.csv`;
          break;
        case 'csv':
          content = generateCSV(data);
          mimeType = 'text/csv';
          fileName = `obras-financiero-${exportType}-${new Date()?.toISOString()?.split('T')?.[0]}.csv`;
          break;
        case 'pdf':
          content = generatePDFContent(data);
          mimeType = 'text/plain';
          fileName = `obras-financiero-${exportType}-${new Date()?.toISOString()?.split('T')?.[0]}.txt`;
          break;
        default:
          throw new Error('Formato no soportado');
      }

      // Create and download file
      const blob = new Blob([content], { type: mimeType });
      const url = window.URL?.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = fileName;
      document.body?.appendChild(link);
      link?.click();
      document.body?.removeChild(link);
      window.URL?.revokeObjectURL(url);

      onClose();
    } catch (error) {
      console.error('Error exporting data:', error);
      alert('Error al exportar los datos. Por favor intente nuevamente.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg shadow-xl max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <div className="flex items-center space-x-3">
            <Download className="h-6 w-6 text-blue-600" />
            <h2 className="text-xl font-semibold text-gray-900">Exportar Reportes</h2>
          </div>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600 transition-colors"
          >
            <X className="h-6 w-6" />
          </button>
        </div>

        <div className="p-6 space-y-6">
          {/* Export Type Selection */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-3">
              Tipo de Reporte
            </label>
            <div className="space-y-2">
              <label className="flex items-center">
                <input
                  type="radio"
                  name="exportType"
                  value="summary"
                  checked={exportType === 'summary'}
                  onChange={(e) => setExportType(e?.target?.value)}
                  className="form-radio h-4 w-4 text-blue-600 border-gray-300 focus:ring-blue-500"
                />
                <span className="ml-2 text-sm text-gray-700">
                  Resumen Ejecutivo (KPIs principales por obra)
                </span>
              </label>
              <label className="flex items-center">
                <input
                  type="radio"
                  name="exportType"
                  value="detailed"
                  checked={exportType === 'detailed'}
                  onChange={(e) => setExportType(e?.target?.value)}
                  className="form-radio h-4 w-4 text-blue-600 border-gray-300 focus:ring-blue-500"
                />
                <span className="ml-2 text-sm text-gray-700">
                  Reporte Detallado (incluye transacciones)
                </span>
              </label>
            </div>
          </div>

          {/* Format Selection */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-3">
              Formato de Exportaci√≥n
            </label>
            <div className="grid grid-cols-3 gap-3">
              <button
                type="button"
                onClick={() => setExportFormat('xlsx')}
                className={`p-4 border-2 rounded-lg flex flex-col items-center space-y-2 transition-colors ${
                  exportFormat === 'xlsx' ?'border-blue-500 bg-blue-50 text-blue-700' :'border-gray-300 hover:border-gray-400'
                }`}
              >
                <FileSpreadsheet className="h-8 w-8" />
                <span className="text-sm font-medium">Excel (.xlsx)</span>
              </button>
              <button
                type="button"
                onClick={() => setExportFormat('csv')}
                className={`p-4 border-2 rounded-lg flex flex-col items-center space-y-2 transition-colors ${
                  exportFormat === 'csv' ?'border-blue-500 bg-blue-50 text-blue-700' :'border-gray-300 hover:border-gray-400'
                }`}
              >
                <FileText className="h-8 w-8" />
                <span className="text-sm font-medium">CSV</span>
              </button>
              <button
                type="button"
                onClick={() => setExportFormat('pdf')}
                className={`p-4 border-2 rounded-lg flex flex-col items-center space-y-2 transition-colors ${
                  exportFormat === 'pdf' ?'border-blue-500 bg-blue-50 text-blue-700' :'border-gray-300 hover:border-gray-400'
                }`}
              >
                <FileText className="h-8 w-8" />
                <span className="text-sm font-medium">PDF</span>
              </button>
            </div>
          </div>

          {/* Filters */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-3">
              Filtros de Exportaci√≥n
            </label>
            <div className="space-y-4">
              {/* Status Filter */}
              <div>
                <label className="block text-sm text-gray-600 mb-1">Estado de Obra</label>
                <select
                  value={includeFilters?.status}
                  onChange={(e) => setIncludeFilters(prev => ({ ...prev, status: e?.target?.value }))}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-1 focus:ring-blue-500"
                >
                  <option value="all">Todas las obras</option>
                  <option value="Planeaci√≥n">En Planeaci√≥n</option>
                  <option value="En ejecuci√≥n">En Ejecuci√≥n</option>
                  <option value="En pausa">En Pausa</option>
                  <option value="Concluida">Concluidas</option>
                  <option value="Cancelada">Canceladas</option>
                </select>
              </div>

              {/* Date Range Filter */}
              <div className="grid grid-cols-2 gap-3">
                <div>
                  <label className="block text-sm text-gray-600 mb-1">Fecha Inicio Desde</label>
                  <input
                    type="date"
                    value={includeFilters?.dateRange?.start}
                    onChange={(e) => setIncludeFilters(prev => ({ 
                      ...prev, 
                      dateRange: { ...prev?.dateRange, start: e?.target?.value }
                    }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-1 focus:ring-blue-500"
                  />
                </div>
                <div>
                  <label className="block text-sm text-gray-600 mb-1">Fecha Inicio Hasta</label>
                  <input
                    type="date"
                    value={includeFilters?.dateRange?.end}
                    onChange={(e) => setIncludeFilters(prev => ({ 
                      ...prev, 
                      dateRange: { ...prev?.dateRange, end: e?.target?.value }
                    }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-1 focus:ring-blue-500"
                  />
                </div>
              </div>

              {/* Include Options */}
              <div className="space-y-2">
                <label className="flex items-center">
                  <input
                    type="checkbox"
                    checked={includeFilters?.includeKPIs}
                    onChange={(e) => setIncludeFilters(prev => ({ ...prev, includeKPIs: e?.target?.checked }))}
                    className="form-checkbox h-4 w-4 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
                  />
                  <span className="ml-2 text-sm text-gray-700">Incluir KPIs financieros</span>
                </label>
                <label className="flex items-center">
                  <input
                    type="checkbox"
                    checked={includeFilters?.includeDetails}
                    onChange={(e) => setIncludeFilters(prev => ({ ...prev, includeDetails: e?.target?.checked }))}
                    className="form-checkbox h-4 w-4 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
                  />
                  <span className="ml-2 text-sm text-gray-700">Incluir detalles de transacciones</span>
                </label>
              </div>
            </div>
          </div>

          {/* Summary */}
          <div className="bg-gray-50 rounded-lg p-4">
            <h3 className="text-sm font-medium text-gray-900 mb-2">Resumen de Exportaci√≥n</h3>
            <div className="text-sm text-gray-600 space-y-1">
              <p>‚Ä¢ Formato: {exportFormat?.toUpperCase()}</p>
              <p>‚Ä¢ Tipo: {exportType === 'summary' ? 'Resumen Ejecutivo' : 'Reporte Detallado'}</p>
              <p>‚Ä¢ Total de obras disponibles: {obras?.length || 0}</p>
              <p>‚Ä¢ Obras filtradas: {prepareExportData()?.length || 0}</p>
            </div>
          </div>
        </div>

        {/* Actions */}
        <div className="flex justify-end space-x-4 p-6 border-t border-gray-200">
          <button
            type="button"
            onClick={onClose}
            className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
          >
            Cancelar
          </button>
          <button
            type="button"
            onClick={handleExport}
            disabled={loading}
            className="px-4 py-2 text-sm font-medium text-white bg-blue-600 border border-transparent rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {loading ? (
              <div className="flex items-center space-x-2">
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
                <span>Exportando...</span>
              </div>
            ) : (
              <div className="flex items-center space-x-2">
                <Download className="h-4 w-4" />
                <span>Exportar</span>
              </div>
            )}
          </button>
        </div>
      </div>
    </div>
  );
};

export default ExportModal;

import React, { useState, useEffect } from 'react';
import { X, Filter, Calendar, Building } from 'lucide-react';
import { companiesService } from '../../../services/obrasFinancialService';

const FiltersModal = ({ onClose, onApplyFilters, currentFilters }) => {
  const [filters, setFilters] = useState({
    status: currentFilters?.status || 'all',
    company: currentFilters?.company || 'all',
    dateRange: currentFilters?.dateRange || { start: '', end: '' }
  });

  const [companies, setCompanies] = useState([]);
  const [loading, setLoading] = useState(true);

  // Load companies for filter options
  useEffect(() => {
    loadCompanies();
  }, []);

  const loadCompanies = async () => {
    try {
      setLoading(true);
      const { data, error } = await companiesService?.getAll();

      if (!error && data) {
        setCompanies(data?.filter(c => c?.activo));
      }
    } catch (err) {
      console.error('Error loading companies:', err);
    } finally {
      setLoading(false);
    }
  };

  // Handle filter changes
  const handleFilterChange = (filterType, value) => {
    setFilters(prev => ({
      ...prev,
      [filterType]: value
    }));
  };

  // Handle date range changes
  const handleDateRangeChange = (dateType, value) => {
    setFilters(prev => ({
      ...prev,
      dateRange: {
        ...prev?.dateRange,
        [dateType]: value
      }
    }));
  };

  // Reset all filters
  const handleResetFilters = () => {
    setFilters({
      status: 'all',
      company: 'all',
      dateRange: { start: '', end: '' }
    });
  };

  // Apply filters
  const handleApplyFilters = () => {
    onApplyFilters(filters);
  };

  // Check if filters have been applied
  const hasActiveFilters = () => {
    return (
      filters?.status !== 'all' ||
      filters?.company !== 'all' ||
      filters?.dateRange?.start ||
      filters?.dateRange?.end
    );
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg shadow-xl max-w-lg w-full max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <div className="flex items-center space-x-3">
            <Filter className="h-6 w-6 text-blue-600" />
            <h2 className="text-xl font-semibold text-gray-900">Filtros</h2>
          </div>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600 transition-colors"
          >
            <X className="h-6 w-6" />
          </button>
        </div>

        <div className="p-6 space-y-6">
          {/* Status Filter */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Estado de la Obra
            </label>
            <select
              value={filters?.status}
              onChange={(e) => handleFilterChange('status', e?.target?.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-1 focus:ring-blue-500 focus:border-blue-500"
            >
              <option value="all">Todos los estados</option>
              <option value="Planeaci√≥n">En Planeaci√≥n</option>
              <option value="En ejecuci√≥n">En Ejecuci√≥n</option>
              <option value="En pausa">En Pausa</option>
              <option value="Concluida">Concluidas</option>
              <option value="Cancelada">Canceladas</option>
            </select>
          </div>

          {/* Company Filter */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Empresa/Cliente
            </label>
            {loading ? (
              <div className="w-full px-3 py-2 border border-gray-300 rounded-md bg-gray-50 text-gray-500">
                <div className="flex items-center space-x-2">
