import React, { useState, useEffect } from 'react';
import { Clock, LogIn, LogOut, Coffee, Square, MapPin } from 'lucide-react';

export default function AttendanceActionButtons({ todayAttendance, onAction }) {
  const [currentTime, setCurrentTime] = useState(new Date());
  const [location, setLocation] = useState('');
  const [notes, setNotes] = useState('');
  const [showLocationModal, setShowLocationModal] = useState(false);
  const [pendingAction, setPendingAction] = useState(null);
  const [gettingLocation, setGettingLocation] = useState(false);

  // Update current time every second
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  // Get current location
  const getCurrentLocation = () => {
    setGettingLocation(true);
    if (navigator.geolocation) {
      navigator.geolocation?.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position?.coords;
          setLocation(`${latitude?.toFixed(6)}, ${longitude?.toFixed(6)}`);
          setGettingLocation(false);
        },
        (error) => {
          setLocation('Ubicaci√≥n no disponible');
          setGettingLocation(false);
        }
      );
    } else {
      setLocation('Geolocalizaci√≥n no soportada');
      setGettingLocation(false);
    }
  };

  const handleActionClick = (action) => {
    if (action === 'clock_in' || action === 'clock_out') {
      setPendingAction(action);
      setShowLocationModal(true);
      setLocation('');
      setNotes('');
    } else {
      onAction(action);
    }
  };

  const handleConfirmAction = () => {
    if (pendingAction) {
      onAction(pendingAction, location, notes);
      setShowLocationModal(false);
      setPendingAction(null);
      setLocation('');
      setNotes('');
    }
  };

  const isClockInDisabled = todayAttendance?.clock_in && !todayAttendance?.clock_out;
  const isClockOutDisabled = !todayAttendance?.clock_in || todayAttendance?.clock_out;
  const isLunchStartDisabled = !todayAttendance?.clock_in || todayAttendance?.lunch_start;
  const isLunchEndDisabled = !todayAttendance?.lunch_start || todayAttendance?.lunch_end;

  // Calculate hours worked today
  const calculateHoursWorked = () => {
    if (!todayAttendance?.clock_in) return '0:00';

    const clockIn = new Date(todayAttendance?.clock_in);
    const clockOut = todayAttendance?.clock_out ? new Date(todayAttendance?.clock_out) : new Date();

    let totalMinutes = (clockOut - clockIn) / (1000 * 60);

    // Subtract lunch break if exists
    if (todayAttendance?.lunch_start && todayAttendance?.lunch_end) {
      const lunchStart = new Date(todayAttendance?.lunch_start);
      const lunchEnd = new Date(todayAttendance?.lunch_end);
      const lunchMinutes = (lunchEnd - lunchStart) / (1000 * 60);
      totalMinutes -= lunchMinutes;
    }

    const hours = Math.floor(totalMinutes / 60);
    const minutes = Math.floor(totalMinutes % 60);
    return `${hours}:${minutes?.toString()?.padStart(2, '0')}`;
  };

  return (
    <>
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <div>
            <h3 className="text-lg font-semibold text-gray-900">
              Control de Asistencia
            </h3>
            <p className="text-sm text-gray-600">
              {currentTime.toLocaleTimeString('es-ES')} - {currentTime.toLocaleDateString('es-ES')}
            </p>
          </div>

          {/* Hours worked today */}
          <div className="text-right">
            <div className="text-2xl font-bold text-blue-600">
              {calculateHoursWorked()}
            </div>
            <p className="text-sm text-gray-600">Horas trabajadas hoy</p>
          </div>
        </div>

        {/* Current Status */}
        <div className="bg-gray-50 rounded-lg p-4 mb-6">
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-center">
            <div>
              <div className="text-xs text-gray-600 mb-1">Entrada</div>
              <div className={`text-sm font-medium ${
                todayAttendance?.clock_in ? 'text-green-600' : 'text-gray-400'
              }`}>
                {todayAttendance?.clock_in ? 
                  new Date(todayAttendance?.clock_in)?.toLocaleTimeString('es-ES', { 
                    hour: '2-digit', 
                    minute: '2-digit' 
                  }) : 
                  '--:--'
                }
              </div>
            </div>

            <div>
              <div className="text-xs text-gray-600 mb-1">Comida</div>
              <div className={`text-sm font-medium ${
                todayAttendance?.lunch_start ? 'text-orange-600' : 'text-gray-400'
              }`}>
                {todayAttendance?.lunch_start && todayAttendance?.lunch_end ? 'Terminada' : todayAttendance?.lunch_start ?'En comida' : '--:--'
                }
              </div>
            </div>

            <div>
              <div className="text-xs text-gray-600 mb-1">Salida</div>
              <div className={`text-sm font-medium ${
                todayAttendance?.clock_out ? 'text-red-600' : 'text-gray-400'
              }`}>
                {todayAttendance?.clock_out ? 
                  new Date(todayAttendance?.clock_out)?.toLocaleTimeString('es-ES', { 
                    hour: '2-digit', 
                    minute: '2-digit' 
                  }) : 
                  '--:--'
                }
              </div>
            </div>

            <div>
              <div className="text-xs text-gray-600 mb-1">Estado</div>
              <span className={`inline-flex px-2 py-1 text-xs font-medium rounded-full ${
                todayAttendance?.clock_out ? 'bg-gray-100 text-gray-800' : todayAttendance?.lunch_start && !todayAttendance?.lunch_end ?'bg-orange-100 text-orange-800': todayAttendance?.clock_in ?'bg-green-100 text-green-800': 'bg-red-100 text-red-800'
              }`}>
                {todayAttendance?.clock_out ? 'Finalizado' :
                 todayAttendance?.lunch_start && !todayAttendance?.lunch_end ? 'En comida': todayAttendance?.clock_in ?'Trabajando': 'Sin entrada'
                }
              </span>
            </div>
          </div>
        </div>

        {/* Action Buttons */}
        <div className="grid grid-cols-2 lg:grid-cols-4 gap-4">
          {/* Clock In */}
          <button
            onClick={() => handleActionClick('clock_in')}
            disabled={isClockInDisabled}
            className={`flex flex-col items-center justify-center p-4 rounded-lg border-2 transition-all ${
              isClockInDisabled
                ? 'border-gray-200 bg-gray-50 text-gray-400 cursor-not-allowed' :'border-green-200 bg-green-50 text-green-700 hover:bg-green-100 hover:border-green-300'
            }`}
          >
            <LogIn className="h-8 w-8 mb-2" />
            <span className="text-sm font-medium">Entrada</span>
            <span className="text-xs opacity-75 mt-1">
              {isClockInDisabled ? 'Ya registrada' : 'Marcar llegada'}
            </span>
          </button>

          {/* Clock Out */}
          <button
            onClick={() => handleActionClick('clock_out')}
            disabled={isClockOutDisabled}
            className={`flex flex-col items-center justify-center p-4 rounded-lg border-2 transition-all ${
              isClockOutDisabled
                ? 'border-gray-200 bg-gray-50 text-gray-400 cursor-not-allowed' :'border-red-200 bg-red-50 text-red-700 hover:bg-red-100 hover:border-red-300'
            }`}
          >
            <LogOut className="h-8 w-8 mb-2" />
            <span className="text-sm font-medium">Salida</span>
            <span className="text-xs opacity-75 mt-1">
              {isClockOutDisabled ? 'No disponible' : 'Marcar salida'}
            </span>
          </button>

          {/* Lunch Start */}
          <button
            onClick={() => handleActionClick('lunch_start')}
            disabled={isLunchStartDisabled}
            className={`flex flex-col items-center justify-center p-4 rounded-lg border-2 transition-all ${
              isLunchStartDisabled
                ? 'border-gray-200 bg-gray-50 text-gray-400 cursor-not-allowed' :'border-orange-200 bg-orange-50 text-orange-700 hover:bg-orange-100 hover:border-orange-300'
            }`}
          >
            <Coffee className="h-8 w-8 mb-2" />
            <span className="text-sm font-medium">Iniciar Comida</span>
            <span className="text-xs opacity-75 mt-1">
              {isLunchStartDisabled ? 'No disponible' : 'Marcar inicio'}
            </span>
          </button>

          {/* Lunch End */}
          <button
            onClick={() => handleActionClick('lunch_end')}
            disabled={isLunchEndDisabled}
            className={`flex flex-col items-center justify-center p-4 rounded-lg border-2 transition-all ${
              isLunchEndDisabled
                ? 'border-gray-200 bg-gray-50 text-gray-400 cursor-not-allowed' :'border-blue-200 bg-blue-50 text-blue-700 hover:bg-blue-100 hover:border-blue-300'
            }`}
          >
            <Square className="h-8 w-8 mb-2" />
            <span className="text-sm font-medium">Terminar Comida</span>
            <span className="text-xs opacity-75 mt-1">
              {isLunchEndDisabled ? 'No disponible' : 'Regresar trabajo'}
            </span>
          </button>
        </div>

        {/* GPS Notice */}
        <div className="mt-4 bg-blue-50 border border-blue-200 rounded-lg p-3">
          <div className="flex items-start space-x-2">
            <MapPin className="h-4 w-4 text-blue-600 mt-0.5" />
            <div>
              <p className="text-sm text-blue-800">
                <strong>Verificaci√≥n de Ubicaci√≥n:</strong> Las marcas de entrada y salida 
                incluyen verificaci√≥n de ubicaci√≥n GPS para mayor precisi√≥n.
              </p>
            </div>
          </div>
        </div>
      </div>
      {/* Location Modal */}
      {showLocationModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg shadow-xl max-w-md w-full p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">
              {pendingAction === 'clock_in' ? 'Confirmar Entrada' : 'Confirmar Salida'}
            </h3>

            <div className="space-y-4">
              {/* Location */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Ubicaci√≥n
                </label>
                <div className="flex space-x-2">
                  <input
                    type="text"
                    value={location}
                    onChange={(e) => setLocation(e?.target?.value)}
                    placeholder="Ubicaci√≥n autom√°tica o manual"
                    className="flex-1 px-3 py-2 border border-gray-300 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                  <button
                    onClick={getCurrentLocation}
                    disabled={gettingLocation}
                    className="px-3 py-2 bg-blue-600 text-white rounded-lg text-sm hover:bg-blue-700 disabled:bg-gray-400"
                  >
                    {gettingLocation ? '...' : 'GPS'}
                  </button>
                </div>
              </div>

              {/* Notes */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Notas (opcional)
                </label>
                <textarea
                  value={notes}
                  onChange={(e) => setNotes(e?.target?.value)}
                  placeholder="Observaciones adicionales..."
                  rows={3}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
            </div>

            {/* Actions */}
            <div className="flex space-x-3 mt-6">
              <button
                onClick={() => {
                  setShowLocationModal(false);
                  setPendingAction(null);
                }}
                className="flex-1 px-4 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50"
              >
                Cancelar
              </button>
              <button
                onClick={handleConfirmAction}
                className={`flex-1 px-4 py-2 text-white rounded-lg ${
                  pendingAction === 'clock_in' ?'bg-green-600 hover:bg-green-700' :'bg-red-600 hover:bg-red-700'
                }`}
              >
                {pendingAction === 'clock_in' ? 'Marcar Entrada' : 'Marcar Salida'}
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

import React, { useState } from 'react';
import { Users, Phone, User, ChevronDown, ChevronUp } from 'lucide-react';

export default function CoworkersList({ coworkers, siteInfo }) {
  const [expanded, setExpanded] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');

  if (!coworkers || coworkers?.length === 0) {
    return (
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-gray-900">
            Compa√±eros de Trabajo
          </h3>
          <Users className="h-5 w-5 text-gray-400" />
        </div>

        <div className="text-center py-8">
          <Users className="h-12 w-12 text-gray-300 mx-auto mb-3" />
          <p className="text-gray-500">
            No hay compa√±eros en este sitio
          </p>
        </div>
      </div>
    );
  }

  const filteredCoworkers = coworkers?.filter(coworker =>
    coworker?.full_name?.toLowerCase()?.includes(searchTerm?.toLowerCase()) ||
    coworker?.position?.toLowerCase()?.includes(searchTerm?.toLowerCase())
  ) || [];

  const displayedCoworkers = expanded ? filteredCoworkers : filteredCoworkers?.slice(0, 4);

  const getStatusColor = (lastSignIn) => {
    if (!lastSignIn) return 'bg-gray-100 text-gray-600';

    const now = new Date();
    const signInDate = new Date(lastSignIn);
    const diffHours = (now - signInDate) / (1000 * 60 * 60);

    if (diffHours <= 8) return 'bg-green-100 text-green-700';
    if (diffHours <= 24) return 'bg-yellow-100 text-yellow-700';
    return 'bg-gray-100 text-gray-600';
  };

  const getStatusText = (lastSignIn) => {
    if (!lastSignIn) return 'Sin actividad';

    const now = new Date();
    const signInDate = new Date(lastSignIn);
    const diffHours = (now - signInDate) / (1000 * 60 * 60);

    if (diffHours <= 1) return 'En l√≠nea';
    if (diffHours <= 8) return 'Activo hoy';
    if (diffHours <= 24) return 'Activo ayer';
    return 'Inactivo';
  };

  return (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
      {/* Header */}
      <div className="flex items-center justify-between mb-4">
        <div>
          <h3 className="text-lg font-semibold text-gray-900">
            Compa√±eros de Trabajo
          </h3>
          {siteInfo?.name && (
            <p className="text-sm text-gray-600">
              en {siteInfo?.name}
            </p>
          )}
        </div>
        <div className="flex items-center space-x-2">
          <span className="text-sm text-gray-500">
            {coworkers?.length} personas
          </span>
          <Users className="h-5 w-5 text-gray-400" />
        </div>
      </div>
      {/* Search */}
      {coworkers?.length > 4 && (
        <div className="mb-4">
          <input
            type="text"
            placeholder="Buscar compa√±eros..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e?.target?.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
          />
        </div>
      )}
      {/* Coworkers List */}
      <div className="space-y-3">
        {displayedCoworkers?.map((coworker) => (
          <div 
            key={coworker?.id}
            className="flex items-center space-x-3 p-3 bg-gray-50 rounded-lg hover:bg-gray-100 transition-colors"
          >
            {/* Avatar */}
            <div className="flex-shrink-0">
              <div className="h-10 w-10 rounded-full bg-blue-100 flex items-center justify-center">
                <User className="h-5 w-5 text-blue-600" />
              </div>
            </div>

            {/* Info */}
            <div className="flex-1 min-w-0">
              <div className="flex items-center justify-between">
                <div>
                  <h4 className="text-sm font-medium text-gray-900 truncate">
                    {coworker?.full_name}
                  </h4>
                  <p className="text-xs text-gray-600 capitalize">
                    {coworker?.position || 'Sin posici√≥n'}
                  </p>
                </div>

                {/* Status indicator */}
                <div className="text-right">
                  <span className={`inline-flex px-2 py-1 text-xs font-medium rounded-full ${
                    getStatusColor(coworker?.user_profiles?.last_sign_in_at)
                  }`}>
                    {getStatusText(coworker?.user_profiles?.last_sign_in_at)}
                  </span>
                </div>
              </div>

              {/* Contact Info */}
              <div className="mt-2 flex items-center space-x-4">
                {coworker?.phone && (
                  <button
                    onClick={() => window.open(`tel:${coworker?.phone}`)}
                    className="flex items-center space-x-1 text-xs text-blue-600 hover:text-blue-700"
                  >
                    <Phone className="h-3 w-3" />
                    <span>Llamar</span>
                  </button>
                )}

                {coworker?.user_profiles?.email && (
                  <button
                    onClick={() => window.open(`mailto:${coworker?.user_profiles?.email}`)}
                    className="text-xs text-green-600 hover:text-green-700"
                  >
                    Email
                  </button>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
      {/* Show More/Less Button */}
      {filteredCoworkers?.length > 4 && (
        <div className="mt-4 text-center">
          <button
            onClick={() => setExpanded(!expanded)}
            className="inline-flex items-center space-x-1 text-sm text-blue-600 hover:text-blue-700"
          >
            {expanded ? (
              <>
                <ChevronUp className="h-4 w-4" />
                <span>Ver menos</span>
              </>
            ) : (
              <>
                <ChevronDown className="h-4 w-4" />
                <span>Ver todos ({filteredCoworkers?.length})</span>
              </>
            )}
          </button>
        </div>
      )}
      {/* No Results */}
      {searchTerm && filteredCoworkers?.length === 0 && (
        <div className="text-center py-4">
          <p className="text-sm text-gray-500">
            No se encontraron compa√±eros con "{searchTerm}"
          </p>
        </div>
      )}
      {/* Team Collaboration Features */}
      <div className="border-t border-gray-100 pt-4 mt-4">
        <h5 className="text-sm font-medium text-gray-700 mb-2">
          Colaboraci√≥n R√°pida
        </h5>
        <div className="grid grid-cols-2 gap-3">
          <button
            onClick={() => {
              const phones = coworkers
                ?.filter(c => c?.phone)
                ?.map(c => c?.phone)
                ?.join(',');
              if (phones) window.open(`sms:${phones}`);
            }}
            className="flex items-center justify-center px-3 py-2 text-sm bg-blue-50 text-blue-700 rounded-lg hover:bg-blue-100 transition-colors"
          >
            <Phone className="h-4 w-4 mr-1" />
            Mensaje Grupal
          </button>

          <button
            onClick={() => {
              const message = encodeURIComponent(`Hola equipo de ${siteInfo?.name || 'trabajo'}, necesito coordinar algo importante.`);
              const phones = coworkers
                ?.filter(c => c?.phone)
                ?.map(c => c?.phone)
                ?.join(',');
              if (phones) window.open(`https://wa.me/?phone=${phones?.split(',')?.[0]}&text=${message}`);
            }}
            className="flex items-center justify-center px-3 py-2 text-sm bg-green-50 text-green-700 rounded-lg hover:bg-green-100 transition-colors"
          >
            <Users className="h-4 w-4 mr-1" />
            WhatsApp
          </button>
        </div>
      </div>
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { DollarSign, TrendingUp, Calculator, Calendar } from 'lucide-react';

export default function PayrollSummaryCard({ payrollEstimation, weeklyTimecard, workerProfile }) {
  const [estimatedPayroll, setEstimatedPayroll] = useState(null);

  useEffect(() => {
    if (weeklyTimecard && workerProfile) {
      calculateEstimatedPayroll();
    }
  }, [weeklyTimecard, workerProfile]);

  const calculateEstimatedPayroll = () => {
    const regularHours = parseFloat(weeklyTimecard?.totalRegularHours || 0);
    const overtimeHours = parseFloat(weeklyTimecard?.totalOvertimeHours || 0);

    let baseRate = 0;
    if (workerProfile?.salary_type === 'hourly') {
      baseRate = parseFloat(workerProfile?.hourly_rate || 0);
    } else {
      // Convert daily salary to hourly (8 hours per day)
      baseRate = parseFloat(workerProfile?.daily_salary || 0) / 8;
    }

    const basePay = baseRate * regularHours;
    const overtimePay = baseRate * overtimeHours * 1.5; // 1.5x for overtime
    const grossTotal = basePay + overtimePay;
    const netTotal = grossTotal * 0.85; // Simplified deductions (15%)

    setEstimatedPayroll({
      regularHours,
      overtimeHours,
      basePay,
      overtimePay,
      grossTotal,
      netTotal,
      baseRate
    });
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('es-MX', {
      style: 'currency',
      currency: 'MXN'
    })?.format(amount || 0);
  };

  return (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
      {/* Header */}
      <div className="flex items-center justify-between mb-6">
        <h3 className="text-lg font-semibold text-gray-900 flex items-center">
          <DollarSign className="h-5 w-5 mr-2" />
          Resumen de N√≥mina
        </h3>
        <div className="text-sm text-gray-600">
          Semana actual
        </div>
      </div>
      {/* Current Week Estimation */}
      {estimatedPayroll ? (
        <div className="space-y-4">
          {/* Main amount display */}
          <div className="text-center bg-gradient-to-r from-green-50 to-blue-50 rounded-lg p-4 border border-green-200">
            <div className="text-3xl font-bold text-green-600 mb-1">
              {formatCurrency(estimatedPayroll?.netTotal)}
            </div>
            <div className="text-sm text-gray-600">
              Estimaci√≥n Neta Semanal
            </div>
            <div className="text-xs text-gray-500 mt-1">
              (Despu√©s de deducciones estimadas)
            </div>
          </div>

          {/* Breakdown */}
          <div className="space-y-3">
            <div className="flex justify-between items-center py-2 border-b border-gray-100">
              <span className="text-sm text-gray-600">
                Horas regulares ({estimatedPayroll?.regularHours?.toFixed(1)}h √ó {formatCurrency(estimatedPayroll?.baseRate)})
              </span>
              <span className="text-sm font-medium text-gray-900">
                {formatCurrency(estimatedPayroll?.basePay)}
              </span>
            </div>

            {estimatedPayroll?.overtimeHours > 0 && (
              <div className="flex justify-between items-center py-2 border-b border-gray-100">
                <span className="text-sm text-gray-600">
                  Horas extra ({estimatedPayroll?.overtimeHours?.toFixed(1)}h √ó {formatCurrency(estimatedPayroll?.baseRate * 1.5)})
                </span>
                <span className="text-sm font-medium text-orange-600">
                  {formatCurrency(estimatedPayroll?.overtimePay)}
                </span>
              </div>
            )}

            <div className="flex justify-between items-center py-2 border-b border-gray-100">
              <span className="text-sm font-semibold text-gray-700">
                Total Bruto
              </span>
              <span className="text-sm font-semibold text-blue-600">
                {formatCurrency(estimatedPayroll?.grossTotal)}
              </span>
            </div>

            <div className="flex justify-between items-center py-2">
              <span className="text-sm text-gray-600">
                Deducciones estimadas (15%)
              </span>
              <span className="text-sm text-red-600">
                -{formatCurrency(estimatedPayroll?.grossTotal * 0.15)}
              </span>
            </div>
          </div>

          {/* Rate information */}
          <div className="bg-blue-50 rounded-lg p-3 border border-blue-200">
            <div className="flex items-center justify-between">
              <span className="text-sm text-blue-800">
                {workerProfile?.salary_type === 'hourly' ? 'Tarifa por Hora' : 'Salario Diario'}
              </span>
              <span className="text-sm font-medium text-blue-900">
                {workerProfile?.salary_type === 'hourly' 
                  ? formatCurrency(workerProfile?.hourly_rate)
                  : formatCurrency(workerProfile?.daily_salary)
                }
              </span>
            </div>
            {workerProfile?.salary_type !== 'hourly' && (
              <div className="text-xs text-blue-700 mt-1">
                Equivale a {formatCurrency(estimatedPayroll?.baseRate)}/hora
              </div>
            )}
          </div>
        </div>
      ) : (
        <div className="text-center py-8">
          <Calculator className="h-12 w-12 text-gray-300 mx-auto mb-3" />
          <p className="text-gray-500 text-sm">
            No hay datos suficientes para calcular n√≥mina
          </p>
          <p className="text-gray-400 text-xs mt-1">
            Complete las horas de trabajo para ver la estimaci√≥n
          </p>
        </div>
      )}
      {/* Last official payroll (if available) */}
      {payrollEstimation && (
        <div className="border-t border-gray-100 pt-4 mt-4">
          <h5 className="text-sm font-medium text-gray-700 mb-3 flex items-center">
            <Calendar className="h-4 w-4 mr-1" />
            √öltimo Pago Oficial
          </h5>

          <div className="space-y-2 text-sm">
            <div className="flex justify-between">
              <span className="text-gray-600">Semana:</span>
              <span className="text-gray-900">
                {new Date(payrollEstimation?.week_start)?.toLocaleDateString('es-ES')} - {' '}
                {new Date(payrollEstimation?.week_end)?.toLocaleDateString('es-ES')}
              </span>
            </div>

            <div className="flex justify-between">
              <span className="text-gray-600">Horas trabajadas:</span>
              <span className="text-gray-900">
                {parseFloat(payrollEstimation?.regular_hours || 0)?.toFixed(1)}h regular, {' '}
                {parseFloat(payrollEstimation?.overtime_hours || 0)?.toFixed(1)}h extra
              </span>
            </div>

            <div className="flex justify-between">
              <span className="text-gray-600">Monto neto:</span>
              <span className="font-medium text-green-600">
                {formatCurrency(payrollEstimation?.net_total)}
              </span>
            </div>
          </div>
        </div>
      )}
      {/* Performance indicators */}
      {estimatedPayroll && (
        <div className="border-t border-gray-100 pt-4 mt-4">
          <div className="grid grid-cols-2 gap-4">
            <div className="text-center">
              <div className="flex items-center justify-center space-x-1">
                <TrendingUp className="h-4 w-4 text-green-500" />
                <span className="text-sm font-medium text-gray-700">
                  Promedio/D√≠a
                </span>
              </div>
              <div className="text-lg font-bold text-green-600">
                {weeklyTimecard?.dailyRecords?.filter(r => r?.total_hours > 0)?.length > 0 
                  ? formatCurrency(estimatedPayroll?.netTotal / weeklyTimecard?.dailyRecords?.filter(r => r?.total_hours > 0)?.length)
                  : formatCurrency(0)
                }
              </div>
            </div>

            <div className="text-center">
              <div className="flex items-center justify-center space-x-1">
                <DollarSign className="h-4 w-4 text-blue-500" />
                <span className="text-sm font-medium text-gray-700">
                  Extra/Mes*
                </span>
              </div>
              <div className="text-lg font-bold text-blue-600">
                {formatCurrency((estimatedPayroll?.overtimePay || 0) * 4)}
              </div>
              <div className="text-xs text-gray-500">*Estimado</div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

