            className="bg-blue-600 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-blue-700 transition-colors flex items-center space-x-2 disabled:opacity-50"
          >
            {scanning ? (
              <RefreshCw className="h-4 w-4 animate-spin" />
            ) : (
              <Shield className="h-4 w-4" />
            )}
            <span>{scanning ? 'Scanning...' : 'Run Security Scan'}</span>
          </button>
        </div>

        <div className="text-center mb-6">
          <div className={`text-6xl font-bold mb-2 ${getSecurityScoreColor(securityScore)}`}>
            {securityScore}
          </div>
          <div className="text-lg text-gray-700">Overall Security Score</div>
          <div className="text-sm text-gray-500">Based on vulnerabilities, compliance, and policies</div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="text-center p-4 bg-green-50 rounded-lg">
            <CheckCircle className="h-8 w-8 text-green-500 mx-auto mb-2" />
            <div className="text-2xl font-bold text-green-900">
              {vulnerabilities?.filter(v => v?.status === 'resolved')?.length}
            </div>
            <div className="text-sm text-green-700">Resolved Issues</div>
          </div>

          <div className="text-center p-4 bg-red-50 rounded-lg">
            <XCircle className="h-8 w-8 text-red-500 mx-auto mb-2" />
            <div className="text-2xl font-bold text-red-900">
              {vulnerabilities?.filter(v => v?.status === 'open')?.length}
            </div>
            <div className="text-sm text-red-700">Open Vulnerabilities</div>
          </div>

          <div className="text-center p-4 bg-blue-50 rounded-lg">
            <Activity className="h-8 w-8 text-blue-500 mx-auto mb-2" />
            <div className="text-2xl font-bold text-blue-900">
              {penetrationTests?.filter(t => t?.status === 'completed')?.length}
            </div>
            <div className="text-sm text-blue-700">Penetration Tests</div>
          </div>
        </div>
      </div>
      {/* Vulnerabilities */}
      <div className="bg-white rounded-lg border overflow-hidden">
        <div className="bg-gray-50 px-6 py-4 border-b">
          <h3 className="text-lg font-semibold text-gray-900">Security Vulnerabilities</h3>
          <p className="text-sm text-gray-600 mt-1">Identified security issues and remediation status</p>
        </div>

        <div className="p-6">
          <div className="space-y-4">
            {vulnerabilities?.map((vuln) => (
              <div key={vuln?.id} className={`border rounded-lg p-4 ${
                vuln?.status === 'resolved' ? 'opacity-75' : ''
              }`}>
                <div className="flex items-start justify-between mb-3">
                  <div className="flex items-center space-x-3">
                    <div className={`p-2 rounded ${
                      vuln?.severity === 'high' ? 'bg-red-100' :
                      vuln?.severity === 'medium'? 'bg-yellow-100' : 'bg-blue-100'
                    }`}>
                      <AlertTriangle className={`h-5 w-5 ${
                        vuln?.severity === 'high' ? 'text-red-600' :
                        vuln?.severity === 'medium'? 'text-yellow-600' : 'text-blue-600'
                      }`} />
                    </div>
                    <div>
                      <h4 className="font-semibold text-gray-900">{vuln?.title}</h4>
                      <p className="text-sm text-gray-600 mt-1">{vuln?.description}</p>
                    </div>
                  </div>
                  <div className="flex items-center space-x-2">
                    <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getSeverityColor(vuln?.severity)}`}>
                      {vuln?.severity}
                    </span>
                    <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${
                      vuln?.status === 'resolved' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                    }`}>
                      {vuln?.status}
                    </span>
                  </div>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
                  <div>
                    <span className="text-gray-600">CVSS Score: </span>
                    <span className="font-medium">{vuln?.cvss}</span>
                  </div>
                  <div>
                    <span className="text-gray-600">Category: </span>
                    <span className="font-medium capitalize">{vuln?.category}</span>
                  </div>
                  <div>
                    <span className="text-gray-600">Discovered: </span>
                    <span className="font-medium">{new Date(vuln?.discoveredAt)?.toLocaleDateString()}</span>
                  </div>
                  {vuln?.resolvedAt && (
                    <div>
                      <span className="text-gray-600">Resolved: </span>
                      <span className="font-medium">{new Date(vuln?.resolvedAt)?.toLocaleDateString()}</span>
                    </div>
                  )}
                </div>

                <div className="mt-3 p-3 bg-blue-50 rounded-md">
                  <div className="text-sm">
                    <strong>Recommendation: </strong>
                    {vuln?.recommendation}
                  </div>
                  {vuln?.affectedAssets?.length > 0 && (
                    <div className="text-sm mt-2">
                      <strong>Affected Assets: </strong>
                      {vuln?.affectedAssets?.join(', ')}
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
      {/* Compliance Status */}
      <div className="bg-white rounded-lg border overflow-hidden">
        <div className="bg-gray-50 px-6 py-4 border-b">
          <h3 className="text-lg font-semibold text-gray-900">Compliance Frameworks</h3>
          <p className="text-sm text-gray-600 mt-1">Regulatory compliance status and audit results</p>
        </div>

        <div className="p-6">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            {complianceStatus?.frameworks?.map((framework) => (
              <div key={framework?.name} className="border rounded-lg p-4">
                <div className="flex items-center justify-between mb-3">
                  <h4 className="font-semibold text-gray-900">{framework?.name}</h4>
                  <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getComplianceColor(framework?.status)}`}>
                    {framework?.status?.replace('_', ' ')}
                  </span>
                </div>

                <div className="mb-4">
                  <div className="flex items-center justify-between mb-2">
                    <span className="text-sm text-gray-600">Compliance Score</span>
                    <span className={`font-semibold ${getSecurityScoreColor(framework?.score)}`}>
                      {framework?.score}%
                    </span>
                  </div>
                  <div className="w-full bg-gray-200 rounded-full h-2">
                    <div 
                      className={`h-2 rounded-full ${
                        framework?.score >= 90 ? 'bg-green-500' :
                        framework?.score >= 70 ? 'bg-yellow-500': 'bg-red-500'
                      }`}
                      style={{ width: `${framework?.score}%` }}
                    />
                  </div>
                </div>

                <div className="space-y-2 text-sm">
                  <div className="flex justify-between">
                    <span className="text-gray-600">Open Issues:</span>
                    <span className="font-medium">{framework?.issues}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">Last Audit:</span>
                    <span className="font-medium">{new Date(framework?.lastAudit)?.toLocaleDateString()}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600">Next Audit:</span>
                    <span className="font-medium">{new Date(framework?.nextAudit)?.toLocaleDateString()}</span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
      {/* Security Audit Logs */}
      <div className="bg-white rounded-lg border overflow-hidden">
        <div className="bg-gray-50 px-6 py-4 border-b">
          <h3 className="text-lg font-semibold text-gray-900">Security Audit Trail</h3>
          <p className="text-sm text-gray-600 mt-1">Recent security events and system activities</p>
        </div>

        <div className="p-6">
          <div className="space-y-3">
            {auditLogs?.map((log) => (
              <div key={log?.id} className={`flex items-center space-x-3 p-3 rounded-lg border ${
                log?.severity === 'warning' ? 'bg-yellow-50 border-yellow-200' :
                log?.severity === 'error'? 'bg-red-50 border-red-200' : 'bg-gray-50 border-gray-200'
              }`}>
                <div className={`p-2 rounded-full ${
                  log?.severity === 'warning' ? 'bg-yellow-100' :
                  log?.severity === 'error'? 'bg-red-100' : 'bg-blue-100'
                }`}>
                  <FileText className={`h-4 w-4 ${
                    log?.severity === 'warning' ? 'text-yellow-600' :
                    log?.severity === 'error'? 'text-red-600' : 'text-blue-600'
                  }`} />
                </div>
                <div className="flex-1">
                  <div className="flex items-center justify-between">
                    <span className="font-medium text-gray-900">{log?.event}</span>
                    <span className="text-sm text-gray-500">
                      {new Date(log?.timestamp)?.toLocaleString()}
                    </span>
                  </div>
                  <div className="text-sm text-gray-600 mt-1">{log?.details}</div>
                  <div className="text-xs text-gray-500 mt-1">
                    User: {log?.user} ‚Ä¢ Category: {log?.category}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default SecurityComplianceMonitor;

src/pages/real-time-payroll-automation-e.content:
import React, { useState } from 'react';
import { User, MapPin, Clock, DollarSign, TrendingUp, AlertCircle, CheckCircle, RefreshCw } from 'lucide-react';
import brandingService from '../../../services/brandingService';
import { supabase } from '../../../lib/supabase';
import { useAuth } from '../../../contexts/AuthContext';

export default function EmployeePayrollGrid({ payrollData, loading, currentWeek, onEmployeeUpdate }) {
  const { user, userProfile } = useAuth();
  const [updatingEmployees, setUpdatingEmployees] = useState(new Set());
  const [error, setError] = useState('');

  const recalculateEmployeePayroll = async (employeeId) => {
    if (!currentWeek?.start || updatingEmployees?.has(employeeId)) return;

    setUpdatingEmployees(prev => new Set([...prev, employeeId]));
    setError('');

    try {
      const { error: calcError } = await supabase
        ?.rpc('calculate_weekly_payroll', {
          p_employee_id: employeeId,
          p_week_start: currentWeek?.start
        });

      if (calcError) throw calcError;

      // Log the recalculation activity
      await supabase?.from('logs_actividad')?.insert({
        usuario_id: user?.id,
        rol: userProfile?.role,
        accion: 'payroll_recalculation',
        modulo: 'Payroll Automation',
        descripcion: `N√≥mina recalculada para empleado: ${employeeId}`
      });

      // Trigger parent component update
      if (onEmployeeUpdate) {
        onEmployeeUpdate();
      }

    } catch (error) {
      setError(`Error recalculando n√≥mina: ${error?.message}`);
    } finally {
      setUpdatingEmployees(prev => {
        const newSet = new Set(prev);
        newSet?.delete(employeeId);
        return newSet;
      });
    }
  };

  const getPayrollStatus = (employee) => {
    if (updatingEmployees?.has(employee?.id)) {
      return { text: 'Calculando...', color: 'yellow', icon: RefreshCw, spin: true };
    }

    if (!employee?.payroll) {
      return { text: 'Sin datos', color: 'gray', icon: AlertCircle };
    }

    if (employee?.payroll?.grossTotal > 0) {
      return { text: 'Calculado', color: 'green', icon: CheckCircle };
    }

    return { text: 'En proceso', color: 'yellow', icon: Clock };
  };

  const formatHours = (hours) => {
    return `${parseFloat(hours || 0)?.toFixed(1)}h`;
  };

  const formatLastUpdate = (timestamp) => {
    if (!timestamp) return 'Nunca';

    const date = new Date(timestamp);
    const now = new Date();
    const diffMs = now?.getTime() - date?.getTime();
    const diffMins = Math.floor(diffMs / (1000 * 60));

    if (diffMins < 1) return 'Ahora mismo';
    if (diffMins < 60) return `${diffMins}m`;

    const diffHours = Math.floor(diffMins / 60);
    if (diffHours < 24) return `${diffHours}h`;

    return date?.toLocaleDateString('es-MX');
  };

  if (loading && payrollData?.length === 0) {
    return (
      <div className="bg-white rounded-lg shadow">
        <div className="px-6 py-4 border-b border-gray-200">
          <h2 className="text-xl font-semibold text-gray-900 flex items-center">
            <User className="h-6 w-6 text-blue-600 mr-2" />
            N√≥mina de Empleados
          </h2>
        </div>
        <div className="p-8 text-center">
          <RefreshCw className="h-12 w-12 text-gray-400 mx-auto animate-spin mb-4" />
          <p className="text-gray-500">Cargando datos de n√≥mina...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-white rounded-lg shadow">
      {/* Header */}
      <div className="px-6 py-4 border-b border-gray-200">
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-xl font-semibold text-gray-900 flex items-center">
              <User className="h-6 w-6 text-blue-600 mr-2" />
              N√≥mina de Empleados
            </h2>
            <p className="text-sm text-gray-600 mt-1">
              {payrollData?.length} empleados ‚Ä¢ Actualizaci√≥n en tiempo real
            </p>
          </div>
          {loading && (
            <RefreshCw className="h-5 w-5 text-blue-600 animate-spin" />
          )}
        </div>
      </div>
      {/* Error Alert */}
      {error && (
        <div className="mx-6 mt-4 bg-red-50 border border-red-200 rounded-lg p-4">
          <div className="flex items-center">
            <AlertCircle className="h-5 w-5 text-red-500 mr-2" />
            <span className="text-red-700 text-sm">{error}</span>
          </div>
        </div>
      )}
      {/* Employee Grid */}
      <div className="p-6">
        {payrollData?.length === 0 ? (
          <div className="text-center py-12">
            <User className="h-16 w-16 text-gray-300 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">Sin empleados encontrados</h3>
            <p className="text-gray-500">No hay empleados con datos para la semana seleccionada.</p>
          </div>
        ) : (
          <div className="space-y-4 max-h-96 overflow-y-auto">
            {payrollData?.map((employee) => {
              const status = getPayrollStatus(employee);
              const StatusIcon = status?.icon;

              return (
                <div key={employee?.id} className="border border-gray-200 rounded-lg p-4 hover:shadow-md transition-shadow">
                  <div className="flex items-center justify-between mb-3">
                    <div className="flex items-center">
                      <div className="bg-blue-100 rounded-full p-2 mr-3">
                        <User className="h-4 w-4 text-blue-600" />
                      </div>
                      <div>
                        <h3 className="font-medium text-gray-900">{employee?.name}</h3>
                        <div className="flex items-center text-sm text-gray-500 mt-1">
                          <span className="bg-gray-100 px-2 py-1 rounded text-xs mr-2">
                            {employee?.position || 'Sin puesto'}
                          </span>
                          {employee?.site && (
                            <div className="flex items-center">
                              <MapPin className="h-3 w-3 mr-1" />
                              <span>{employee?.site}</span>
                            </div>
                          )}
                        </div>
                      </div>
                    </div>

                    <div className="flex items-center space-x-2">
                      <div className={`flex items-center px-2 py-1 rounded-full text-xs font-medium ${
                        status?.color === 'green' ? 'bg-green-100 text-green-800' :
                        status?.color === 'yellow'? 'bg-yellow-100 text-yellow-800' : 'bg-gray-100 text-gray-800'
                      }`}>
                        <StatusIcon className={`h-3 w-3 mr-1 ${status?.spin ? 'animate-spin' : ''}`} />
                        {status?.text}
                      </div>

                      <button
                        onClick={() => recalculateEmployeePayroll(employee?.id)}
                        disabled={updatingEmployees?.has(employee?.id)}
                        className="p-1 text-gray-400 hover:text-blue-600 transition-colors disabled:opacity-50"
                        title="Recalcular n√≥mina"
                      >
                        <RefreshCw className={`h-4 w-4 ${updatingEmployees?.has(employee?.id) ? 'animate-spin' : ''}`} />
                      </button>
                    </div>
                  </div>
                  {employee?.payroll ? (
                    <div className="grid grid-cols-2 lg:grid-cols-4 gap-4">
                      {/* Hours */}
                      <div className="bg-blue-50 rounded-lg p-3">
                        <div className="flex items-center mb-1">
                          <Clock className="h-4 w-4 text-blue-600 mr-1" />
                          <span className="text-xs text-blue-600 font-medium">Horas</span>
                        </div>
                        <p className="text-sm text-gray-900">
                          <span className="font-medium">{formatHours(employee?.payroll?.regularHours)}</span>
                          {employee?.payroll?.overtimeHours > 0 && (
                            <span className="text-orange-600 ml-1">
                              +{formatHours(employee?.payroll?.overtimeHours)} extra
                            </span>
                          )}
                        </p>
                      </div>

                      {/* Base Pay */}
                      <div className="bg-green-50 rounded-lg p-3">
                        <div className="flex items-center mb-1">
                          <DollarSign className="h-4 w-4 text-green-600 mr-1" />
                          <span className="text-xs text-green-600 font-medium">Pago Base</span>
                        </div>
                        <p className="text-sm font-medium text-gray-900">
                          {brandingService?.formatCurrency(employee?.payroll?.basePay || 0)}
                        </p>
                      </div>

                      {/* Overtime Pay */}
                      <div className="bg-orange-50 rounded-lg p-3">
                        <div className="flex items-center mb-1">
                          <TrendingUp className="h-4 w-4 text-orange-600 mr-1" />
                          <span className="text-xs text-orange-600 font-medium">Extra</span>
                        </div>
                        <p className="text-sm font-medium text-gray-900">
                          {brandingService?.formatCurrency(employee?.payroll?.overtimePay || 0)}
                        </p>
                      </div>

                      {/* Total */}
                      <div className="bg-purple-50 rounded-lg p-3">
                        <div className="flex items-center mb-1">
                          <DollarSign className="h-4 w-4 text-purple-600 mr-1" />
                          <span className="text-xs text-purple-600 font-medium">Total</span>
                        </div>
                        <p className="text-sm font-bold text-purple-900">
                          {brandingService?.formatCurrency(employee?.payroll?.grossTotal || 0)}
                        </p>
                      </div>
                    </div>
                  ) : (
                    <div className="bg-gray-50 rounded-lg p-6 text-center">
                      <AlertCircle className="h-8 w-8 text-gray-400 mx-auto mb-2" />
                      <p className="text-sm text-gray-500">Sin registros de asistencia esta semana</p>
                      <button
                        onClick={() => recalculateEmployeePayroll(employee?.id)}
                        disabled={updatingEmployees?.has(employee?.id)}
                        className="mt-2 text-sm text-blue-600 hover:text-blue-800 transition-colors disabled:opacity-50"
                      >
                        Calcular n√≥mina
                      </button>
                    </div>
                  )}
                  {/* Last Update */}
                  <div className="mt-3 text-xs text-gray-500 text-right">
                    Actualizado: {formatLastUpdate(employee?.payroll?.lastUpdated)}
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
}

import React from 'react';
import { Calendar, Clock, DollarSign, Users, TrendingUp, CheckCircle, AlertTriangle, RefreshCw } from 'lucide-react';
import brandingService from '../../../services/brandingService';

export default function PayrollCalculationDashboard({ 
  currentWeek, 
  nextCutoff, 
  lastProcessing, 
  processingSummary, 
  automationStatus,
  onTriggerCalculation,
  loading 
}) {

  const formatTimeUntilCutoff = () => {
    if (!nextCutoff) return 'N/A';

    const now = new Date();
    const diffMs = nextCutoff?.getTime() - now?.getTime();
    const diffDays = Math.floor(diffMs / (1000 * 60 * 60 * 24));
    const diffHours = Math.floor((diffMs % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));

    if (diffDays > 0) {
      return `${diffDays}d ${diffHours}h`;
    } else if (diffHours > 0) {
      return `${diffHours}h`;
    } else {
      return 'Pr√≥ximo corte';
    }
  };

  const getProcessingStatus = () => {
    if (loading) return { text: 'Procesando...', color: 'yellow', icon: RefreshCw };
    if (processingSummary?.processedCount === processingSummary?.totalEmployees && processingSummary?.totalEmployees > 0) {
      return { text: 'Completado', color: 'green', icon: CheckCircle };
    }
    if (processingSummary?.processedCount > 0) {
      return { text: 'Parcial', color: 'yellow', icon: AlertTriangle };
    }
    return { text: 'Pendiente', color: 'gray', icon: Clock };
  };

  const processingStatus = getProcessingStatus();
  const StatusIcon = processingStatus?.icon;

  return (
    <div className="bg-white rounded-lg shadow">
      {/* Header */}
      <div className="px-6 py-4 border-b border-gray-200">
        <h2 className="text-xl font-semibold text-gray-900 flex items-center">
          <TrendingUp className="h-6 w-6 text-blue-600 mr-2" />
          Dashboard de C√°lculos
        </h2>
        <p className="text-sm text-gray-600 mt-1">
          Monitoreo en tiempo real del procesamiento de n√≥mina
        </p>
      </div>

      <div className="p-6 space-y-6">
        {/* Current Week Info */}
        <div className="bg-blue-50 rounded-lg p-4">
          <div className="flex items-center justify-between">
            <div>
              <h3 className="text-sm font-medium text-blue-900">Semana Actual</h3>
              <p className="text-lg font-bold text-blue-700">
                {currentWeek?.start && currentWeek?.end 
                  ? `${new Date(currentWeek?.start)?.toLocaleDateString('es-MX')} - ${new Date(currentWeek?.end)?.toLocaleDateString('es-MX')}`
                  : 'Cargando...'
                }
              </p>
            </div>
            <Calendar className="h-8 w-8 text-blue-600" />
          </div>
        </div>

        {/* Processing Status */}
        <div className="grid grid-cols-2 gap-4">
          <div className={`rounded-lg p-4 ${
            processingStatus?.color === 'green' ? 'bg-green-50' :
            processingStatus?.color === 'yellow' ? 'bg-yellow-50' :
            processingStatus?.color === 'gray' ? 'bg-gray-50' : 'bg-gray-50'
          }`}>
            <div className="flex items-center">
              <StatusIcon className={`h-5 w-5 mr-2 ${
                processingStatus?.color === 'green' ? 'text-green-600' :
                processingStatus?.color === 'yellow'? 'text-yellow-600' : 'text-gray-600'
              } ${loading ? 'animate-spin' : ''}`} />
              <div>
                <p className="text-sm font-medium text-gray-900">Estado</p>
                <p className={`text-sm ${
                  processingStatus?.color === 'green' ? 'text-green-700' :
                  processingStatus?.color === 'yellow'? 'text-yellow-700' : 'text-gray-700'
                }`}>
                  {processingStatus?.text}
                </p>
              </div>
            </div>
          </div>

          <div className="bg-purple-50 rounded-lg p-4">
            <div className="flex items-center">
              <Clock className="h-5 w-5 text-purple-600 mr-2" />
              <div>
                <p className="text-sm font-medium text-gray-900">Pr√≥ximo Corte</p>
                <p className="text-sm text-purple-700">{formatTimeUntilCutoff()}</p>
              </div>
            </div>
          </div>
        </div>

        {/* Key Metrics */}
        <div className="grid grid-cols-2 gap-4">
          <div className="bg-gray-50 rounded-lg p-4">
            <div className="flex items-center">
              <Users className="h-8 w-8 text-gray-600 mr-3" />
              <div>
                <p className="text-2xl font-bold text-gray-900">
                  {processingSummary?.processedCount || 0}
                </p>
                <p className="text-sm text-gray-600">
                  de {processingSummary?.totalEmployees || 0} empleados
                </p>
              </div>
            </div>
          </div>

          <div className="bg-green-50 rounded-lg p-4">
            <div className="flex items-center">
