import React from 'react';
import Icon from '../../../components/AppIcon';
import CurrencyDisplay from '../../../components/ui/CurrencyDisplay';

const PayrollEstimationCards = ({ summary, weekStart, weekEnd, loading = false }) => {
  const cards = [
    {
      label: 'Total Empleados',
      value: summary?.totalEmployees || 0,
      icon: 'Users',
      color: 'blue',
      format: 'number'
    },
    {
      label: 'Horas Regulares',
      value: summary?.totalRegularHours || 0,
      icon: 'Clock',
      color: 'green',
      format: 'hours'
    },
    {
      label: 'Horas Extra',
      value: summary?.totalOvertimeHours || 0,
      icon: 'Clock4',
      color: 'orange',
      format: 'hours'
    },
    {
      label: 'Total Bruto',
      value: summary?.totalGrossPay || 0,
      icon: 'DollarSign',
      color: 'purple',
      format: 'currency'
    },
    {
      label: 'Total Neto',
      value: summary?.totalNetPay || 0,
      icon: 'Wallet',
      color: 'emerald',
      format: 'currency'
    },
    {
      label: 'Promedio Diario',
      value: summary?.averageDailyPay || 0,
      icon: 'TrendingUp',
      color: 'pink',
      format: 'currency'
    }
  ];

  const getColorClasses = (color) => {
    const colorMap = {
      blue: 'bg-blue-50 border-blue-200 text-blue-600',
      green: 'bg-green-50 border-green-200 text-green-600',
      orange: 'bg-orange-50 border-orange-200 text-orange-600',
      purple: 'bg-purple-50 border-purple-200 text-purple-600',
      emerald: 'bg-emerald-50 border-emerald-200 text-emerald-600',
      pink: 'bg-pink-50 border-pink-200 text-pink-600'
    };
    return colorMap?.[color] || colorMap?.blue;
  };

  const formatValue = (value, format) => {
    if (loading) return '---';

    switch (format) {
      case 'currency':
        return <CurrencyDisplay amount={value} />;
      case 'hours':
        return `${value?.toFixed(1)}h`;
      case 'number':
        return value?.toLocaleString('es-MX');
      default:
        return value;
    }
  };

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
      {cards?.map((card, index) => (
        <div 
          key={index}
          className="bg-card border border-border rounded-lg p-6 hover:shadow-md transition-shadow duration-200"
        >
          <div className="flex items-center justify-between">
            <div className="flex-1">
              <p className="text-sm font-medium text-muted-foreground mb-1">
                {card?.label}
              </p>
              <div className={`text-2xl font-bold ${loading ? 'animate-pulse' : ''}`}>
                {formatValue(card?.value, card?.format)}
              </div>

              {/* Show zero state hint for currency values */}
              {!loading && card?.format === 'currency' && card?.value === 0 && (
                <p className="text-xs text-muted-foreground mt-1">
                  Se actualizar√° con datos de asistencia
                </p>
              )}
            </div>

            <div className={`w-12 h-12 rounded-lg border flex items-center justify-center ${getColorClasses(card?.color)}`}>
              <Icon name={card?.icon} size={20} />
            </div>
          </div>

          {/* Progress bar for hours */}
          {card?.format === 'hours' && !loading && (
            <div className="mt-4">
              <div className="flex justify-between text-xs text-muted-foreground mb-1">
                <span>0h</span>
                <span>40h/sem</span>
              </div>
              <div className="w-full bg-gray-200 rounded-full h-2">
                <div
                  className={`h-2 rounded-full transition-all duration-500 ${
                    card?.color === 'green' ? 'bg-green-500' : 'bg-orange-500'
                  }`}
                  style={{
                    width: `${Math.min((card?.value / 40) * 100, 100)}%`
                  }}
                />
              </div>
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default PayrollEstimationCards;

import React, { useState } from 'react';
import Icon from '../../../components/AppIcon';
import Button from '../../../components/ui/Button';
import CurrencyDisplay from '../../../components/ui/CurrencyDisplay';

const PayrollSummaryTable = ({ 
  payrollData, 
  selectedEmployees, 
  onSelectionChange, 
  onCalculateEmployee, 
  onExportEmployee,
  calculating = false,
  loading = false 
}) => {
  const [sortConfig, setSortConfig] = useState({ key: 'full_name', direction: 'asc' });

  const handleSort = (key) => {
    let direction = 'asc';
    if (sortConfig?.key === key && sortConfig?.direction === 'asc') {
      direction = 'desc';
    }
    setSortConfig({ key, direction });
  };

  const handleSelectAll = (checked) => {
    if (checked) {
      onSelectionChange(payrollData?.map(record => record?.employee_profiles?.employee_id));
    } else {
      onSelectionChange([]);
    }
  };

  const handleSelectRecord = (employeeId, checked) => {
    if (checked) {
      onSelectionChange([...selectedEmployees, employeeId]);
    } else {
      onSelectionChange(selectedEmployees?.filter(id => id !== employeeId));
    }
  };

  const sortedData = [...payrollData]?.sort((a, b) => {
    const aValue = a?.employee_profiles?.[sortConfig?.key] || a?.[sortConfig?.key];
    const bValue = b?.employee_profiles?.[sortConfig?.key] || b?.[sortConfig?.key];

    if (aValue < bValue) return sortConfig?.direction === 'asc' ? -1 : 1;
    if (aValue > bValue) return sortConfig?.direction === 'asc' ? 1 : -1;
    return 0;
  });

  const SortableHeader = ({ children, sortKey, className = '' }) => (
    <th 
      className={`px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-50 ${className}`}
      onClick={() => handleSort(sortKey)}
    >
      <div className="flex items-center space-x-1">
        <span>{children}</span>
        {sortConfig?.key === sortKey && (
          <Icon 
            name={sortConfig?.direction === 'asc' ? 'ChevronUp' : 'ChevronDown'} 
            size={14} 
          />
        )}
      </div>
    </th>
  );

  if (loading) {
    return (
      <div className="bg-card border border-border rounded-lg overflow-hidden">
        <div className="px-6 py-4 border-b">
          <h3 className="text-lg font-semibold">Resumen de N√≥mina Semanal</h3>
        </div>
        <div className="p-6">
          <div className="animate-pulse space-y-4">
            <div className="h-4 bg-gray-200 rounded w-full"></div>
            <div className="h-4 bg-gray-200 rounded w-3/4"></div>
            <div className="h-4 bg-gray-200 rounded w-1/2"></div>
          </div>
        </div>
      </div>
    );
  }

  if (payrollData?.length === 0) {
    return (
      <div className="bg-card border border-border rounded-lg overflow-hidden">
        <div className="px-6 py-4 border-b">
          <h3 className="text-lg font-semibold">Resumen de N√≥mina Semanal</h3>
        </div>
        <div className="p-8 text-center text-muted-foreground">
          <Icon name="FileText" size={48} className="mx-auto mb-4 text-gray-300" />
          <p>No hay datos de n√≥mina para mostrar</p>
          <p className="text-sm">Los empleados aparecer√°n aqu√≠ cuando se calculen las estimaciones</p>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-card border border-border rounded-lg overflow-hidden">
      <div className="px-6 py-4 border-b">
        <div className="flex items-center justify-between">
          <h3 className="text-lg font-semibold">Resumen de N√≥mina Semanal</h3>
          <div className="text-sm text-muted-foreground">
            {selectedEmployees?.length > 0 && `${selectedEmployees?.length} seleccionados`}
          </div>
        </div>
      </div>
      <div className="overflow-x-auto">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left">
                <input
                  type="checkbox"
                  checked={selectedEmployees?.length === payrollData?.length}
                  onChange={(e) => handleSelectAll(e?.target?.checked)}
                  className="rounded border-gray-300"
                />
              </th>
              <SortableHeader sortKey="full_name">Empleado</SortableHeader>
              <SortableHeader sortKey="construction_sites.name">Proyecto</SortableHeader>
              <SortableHeader sortKey="regular_hours">Horas Reg.</SortableHeader>
              <SortableHeader sortKey="overtime_hours">Horas Extra</SortableHeader>
              <SortableHeader sortKey="base_pay">Pago Base</SortableHeader>
              <SortableHeader sortKey="overtime_pay">Pago Extra</SortableHeader>
              <SortableHeader sortKey="gross_total">Total Bruto</SortableHeader>
              <SortableHeader sortKey="net_total">Total Neto</SortableHeader>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Acciones
              </th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {sortedData?.map((record) => {
              const employee = record?.employee_profiles;
              const isSelected = selectedEmployees?.includes(employee?.employee_id);

              return (
                <tr 
                  key={record?.id} 
                  className={`hover:bg-gray-50 ${isSelected ? 'bg-blue-50' : ''}`}
                >
                  <td className="px-6 py-4 whitespace-nowrap">
                    <input
                      type="checkbox"
                      checked={isSelected}
                      onChange={(e) => handleSelectRecord(employee?.employee_id, e?.target?.checked)}
                      className="rounded border-gray-300"
                    />
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="flex items-center">
                      <div className="flex-shrink-0 h-8 w-8">
                        <div className="h-8 w-8 bg-gray-300 rounded-full flex items-center justify-center">
                          <span className="text-xs font-medium text-gray-700">
                            {employee?.full_name?.charAt(0)?.toUpperCase()}
                          </span>
                        </div>
                      </div>
                      <div className="ml-3">
                        <div className="text-sm font-medium text-gray-900">
                          {employee?.full_name}
                        </div>
                        <div className="text-sm text-gray-500">
                          ID: {employee?.employee_id}
                        </div>
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm text-gray-900">
                      {employee?.construction_sites?.name || 'Sin asignar'}
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {parseFloat(record?.regular_hours || 0)?.toFixed(1)}h
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {parseFloat(record?.overtime_hours || 0)?.toFixed(1)}h
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    <CurrencyDisplay amount={parseFloat(record?.base_pay || 0)} />
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    <CurrencyDisplay amount={parseFloat(record?.overtime_pay || 0)} />
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                    <CurrencyDisplay amount={parseFloat(record?.gross_total || 0)} />
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-green-600">
                    <CurrencyDisplay amount={parseFloat(record?.net_total || 0)} />
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                    <div className="flex items-center space-x-2">
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => onCalculateEmployee(record?.employee_id)}
                        disabled={calculating}
                        iconName="RefreshCw"
                        className="p-1"
                      />
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => onExportEmployee(employee)}
                        iconName="Download"
                        className="p-1"
                      />
                    </div>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
      {/* Summary Footer */}
      <div className="bg-gray-50 px-6 py-4 border-t">
        <div className="flex justify-between items-center text-sm">
          <div className="text-muted-foreground">
            Mostrando {payrollData?.length} empleados
          </div>
          <div className="font-medium">
            Total General: <CurrencyDisplay 
              amount={payrollData?.reduce((sum, record) => sum + parseFloat(record?.net_total || 0), 0)} 
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default PayrollSummaryTable;

import React from 'react';
import CurrencyDisplay from '../../../components/ui/CurrencyDisplay';

const WeeklyTimeline = ({ weekStart, payrollData, loading = false }) => {
  // Generate days of the week
  const weekDays = [];
  for (let i = 0; i < 7; i++) {
    const day = new Date(weekStart);
    day?.setDate(weekStart?.getDate() + i);
    weekDays?.push({
      date: day,
      dayName: day?.toLocaleDateString('es-ES', { weekday: 'short' }),
      dayNumber: day?.getDate(),
      isToday: day?.toDateString() === new Date()?.toDateString()
    });
  }

  // Calculate daily totals (mock data since we don't have daily breakdown)
  const dailyTotals = weekDays?.map((day, index) => {
    // Mock progressive accumulation for demonstration
    const progress = Math.min((index + 1) / 7, 1);
    const totalPay = payrollData?.reduce((sum, record) => sum + (parseFloat(record?.gross_total) || 0), 0);

    return {
      ...day,
      amount: totalPay * progress / 7,
      hours: payrollData?.reduce((sum, record) => sum + (parseFloat(record?.regular_hours) || 0), 0) * progress / 7,
      employees: Math.floor(payrollData?.length * progress)
    };
  });

  const maxAmount = Math.max(...dailyTotals?.map(d => d?.amount));

  if (loading) {
    return (
      <div className="bg-card border border-border rounded-lg p-6">
        <h3 className="text-lg font-semibold text-foreground mb-4">
          Progresi√≥n Semanal
        </h3>
        <div className="animate-pulse space-y-4">
          <div className="h-32 bg-gray-200 rounded"></div>
          <div className="flex justify-between">
            {[...Array(7)]?.map((_, i) => (
              <div key={i} className="text-center">
                <div className="h-4 bg-gray-200 rounded w-8 mb-2"></div>
                <div className="h-3 bg-gray-200 rounded w-6"></div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-card border border-border rounded-lg p-6">
      <div className="flex items-center justify-between mb-6">
        <h3 className="text-lg font-semibold text-foreground">
          Progresi√≥n Semanal de Ingresos
        </h3>
        <div className="text-sm text-muted-foreground">
          {payrollData?.length === 0 ? (
            'Sin datos de asistencia'
          ) : (
            `${payrollData?.length} empleados activos`
          )}
        </div>
      </div>
      {payrollData?.length === 0 ? (
        <div className="text-center py-8">
          <div className="flex justify-between items-end h-32 mb-4">
            {dailyTotals?.map((day, index) => (
              <div key={index} className="flex flex-col items-center flex-1">
                <div
                  className="bg-gray-200 w-8 rounded-t transition-all duration-500"
                  style={{ height: '8px' }}
                />
              </div>
            ))}
          </div>

          <div className="grid grid-cols-7 gap-2 text-center">
            {dailyTotals?.map((day, index) => (
              <div key={index} className={`text-sm ${day?.isToday ? 'font-bold text-blue-600' : 'text-muted-foreground'}`}>
                <div className="font-medium">{day?.dayName}</div>
                <div className="text-xs">{day?.dayNumber}</div>
                <div className="mt-2 text-xs">
                  <CurrencyDisplay amount={0} />
                </div>
              </div>
            ))}
          </div>

          <p className="text-muted-foreground text-sm mt-4">
            Los valores aparecer√°n cuando se registre asistencia diaria
          </p>
        </div>
      ) : (
        <div>
          {/* Chart */}
          <div className="flex justify-between items-end h-32 mb-4">
            {dailyTotals?.map((day, index) => (
              <div key={index} className="flex flex-col items-center flex-1 group">
                <div className="relative">
                  {/* Tooltip */}
                  <div className="absolute bottom-full mb-2 left-1/2 transform -translate-x-1/2 bg-gray-800 text-white text-xs px-2 py-1 rounded opacity-0 group-hover:opacity-100 transition-opacity whitespace-nowrap">
                    <div>{day?.employees} empleados</div>
                    <div>{day?.hours?.toFixed(1)}h trabajadas</div>
                    <div><CurrencyDisplay amount={day?.amount} /></div>
                  </div>

                  {/* Bar */}
                  <div
                    className={`w-8 rounded-t transition-all duration-500 ${
                      day?.isToday 
                        ? 'bg-blue-500 hover:bg-blue-600' :'bg-green-500 hover:bg-green-600'
                    }`}
                    style={{ 
                      height: maxAmount > 0 ? `${Math.max((day?.amount / maxAmount) * 120, 8)}px` : '8px' 
                    }}
                  />
                </div>
              </div>
            ))}
          </div>

          {/* Labels */}
          <div className="grid grid-cols-7 gap-2 text-center">
            {dailyTotals?.map((day, index) => (
              <div key={index} className={`text-sm ${day?.isToday ? 'font-bold text-blue-600' : 'text-muted-foreground'}`}>
                <div className="font-medium">{day?.dayName}</div>
                <div className="text-xs">{day?.dayNumber}</div>
                <div className="mt-2 text-xs">
                  <CurrencyDisplay amount={day?.amount} />
                </div>
              </div>
            ))}
          </div>

          {/* Summary */}
          <div className="mt-6 pt-4 border-t border-border">
            <div className="flex justify-between text-sm">
              <div className="text-muted-foreground">
                Progreso de la semana
              </div>
              <div className="font-medium">
                {((new Date()?.getDay() + 6) % 7 + 1)} de 7 d√≠as
              </div>
            </div>
            <div className="mt-2 w-full bg-gray-200 rounded-full h-2">
              <div
                className="bg-blue-500 h-2 rounded-full transition-all duration-500"
                style={{
                  width: `${Math.min(((new Date()?.getDay() + 6) % 7 + 1) / 7 * 100, 100)}%`
                }}
              />
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default WeeklyTimeline;

import React from 'react';
import Icon from '../../../components/AppIcon';
import Button from '../../../components/ui/Button';
