      <div className="max-w-md mx-auto">
        <div className="w-24 h-24 bg-muted rounded-full flex items-center justify-center mx-auto mb-6">
          <Icon 
            name={getIconName()} 
            size={32} 
            className={`text-muted-foreground ${type === 'calculating' ? 'animate-spin' : ''}`} 
          />
        </div>

        <h3 className="text-xl font-semibold text-foreground mb-3">
          {title}
        </h3>

        <p className="text-muted-foreground mb-6 max-w-sm mx-auto">
          {description}
        </p>

        {/* Zero Values Example for no-data state */}
        {type === 'no-data' && (
          <div className="mb-6 p-4 bg-gray-50 rounded-lg border">
            <h4 className="text-sm font-medium text-gray-700 mb-2">
              Vista Previa de Estimaci√≥n
            </h4>
            <div className="text-center space-y-1">
              <div className="text-lg font-medium text-gray-900">
                Empleado Ejemplo
              </div>
              <div className="text-sm text-gray-500">
                Horas trabajadas: 0h ‚Ä¢ Horas extra: 0h
              </div>
              <div className="text-lg font-bold text-green-600">
                <CurrencyDisplay amount={0} />
              </div>
              <div className="text-xs text-gray-400">
                Se actualizar√° autom√°ticamente con datos reales
              </div>
            </div>
          </div>
        )}

        {actionLabel && onAction && (
          <Button
            onClick={onAction}
            disabled={loading}
            iconName={loading ? 'Loader2' : 'Plus'}
            className={loading ? 'animate-spin' : ''}
          >
            {loading ? 'Cargando...' : actionLabel}
          </Button>
        )}

        {/* Additional Help Text */}
        {type === 'no-data' && (
          <div className="mt-8 text-xs text-muted-foreground">
            <p>
              üí° <strong>Tip:</strong> Los valores cambiar√°n autom√°ticamente cuando se registren datos de asistencia
            </p>
          </div>
        )}
      </div>
      {/* Status Cards for Troubleshooting */}
      {type === 'no-employees' && (
        <div className="mt-8 max-w-2xl mx-auto grid grid-cols-1 md:grid-cols-3 gap-4">
          {[
            { 
              title: '1. Registrar Empleados', 
              description: 'Agregue empleados al sistema',
              icon: 'UserPlus',
              status: 'pending'
            },
            { 
              title: '2. Asignar Proyectos', 
              description: 'Asigne empleados a proyectos activos',
              icon: 'Building',
              status: 'disabled'
            },
            { 
              title: '3. Registrar Asistencia', 
              description: 'Los empleados registran entrada/salida',
              icon: 'Clock',
              status: 'disabled'
            }
          ]?.map((step, index) => (
            <div key={index} className="p-4 border rounded-lg bg-white">
              <div className="flex items-center space-x-3 mb-2">
                <div className={`w-8 h-8 rounded-full flex items-center justify-center ${
                  step?.status === 'pending' ? 'bg-blue-100 text-blue-600' : 'bg-gray-100 text-gray-400'
                }`}>
                  <Icon name={step?.icon} size={16} />
                </div>
                <h4 className="text-sm font-medium text-gray-900">
                  {step?.title}
                </h4>
              </div>
              <p className="text-xs text-gray-500">
                {step?.description}
              </p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ZeroStateDisplay;

src/pages/role-based-permission-enforcem.content:
import React, { useState } from 'react';
import { Eye, AlertTriangle, CheckCircle2, XCircle, Clock, User } from 'lucide-react';

export default function RealTimeAccessValidation({
  validationQueue,
  roleHierarchy,
  users,
  onValidateAccess,
  onEmergencyOverride
}) {
  const [testUserId, setTestUserId] = useState('');
  const [testFeature, setTestFeature] = useState('');
  const [overrideReason, setOverrideReason] = useState('');

  const features = [
    'dashboard',
    'employee_management',
    'payroll_calculation',
    'reports',
    'system_settings',
    'role_management',
    'security_monitoring',
    'attendance_approval',
    'site_management'
  ];

  const handleTestValidation = () => {
    if (testUserId && testFeature) {
      onValidateAccess(testUserId, testFeature);
      setTestFeature('');
    }
  };

  const handleEmergencyOverride = (validation) => {
    if (overrideReason?.trim()) {
      onEmergencyOverride(validation?.userId, validation?.feature, overrideReason);
      setOverrideReason('');
    }
  };

  const getFeatureDescription = (feature) => {
    const descriptions = {
      'dashboard': 'Panel Principal',
      'employee_management': 'Gesti√≥n de Empleados',
      'payroll_calculation': 'C√°lculo de N√≥minas',
      'reports': 'Reportes',
      'system_settings': 'Configuraci√≥n Sistema',
      'role_management': 'Gesti√≥n de Roles',
      'security_monitoring': 'Monitoreo Seguridad',
      'attendance_approval': 'Aprobaci√≥n Asistencias',
      'site_management': 'Gesti√≥n de Sitios'
    };
    return descriptions?.[feature] || feature;
  };

  const getValidationIcon = (hasAccess) => {
    if (hasAccess) {
      return <CheckCircle2 className="h-5 w-5 text-green-600" />;
    } else {
      return <XCircle className="h-5 w-5 text-red-600" />;
    }
  };

  const getTimeAgo = (timestamp) => {
    const now = new Date();
    const diff = Math.floor((now - timestamp) / 1000); // seconds

    if (diff < 60) return `hace ${diff}s`;
    if (diff < 3600) return `hace ${Math.floor(diff / 60)}m`;
    if (diff < 86400) return `hace ${Math.floor(diff / 3600)}h`;
    return `hace ${Math.floor(diff / 86400)}d`;
  };

  return (
    <div className="bg-white rounded-lg shadow">
      <div className="p-6">
        <h2 className="text-xl font-bold text-gray-900 mb-6">
          <Eye className="h-6 w-6 inline mr-2 text-blue-600" />
          Validaci√≥n de Acceso en Tiempo Real
        </h2>

        {/* Test Access Panel */}
        <div className="bg-gray-50 rounded-lg p-4 mb-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Probar Validaci√≥n de Acceso</h3>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Usuario
              </label>
              <select
                value={testUserId}
                onChange={(e) => setTestUserId(e?.target?.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
              >
                <option value="">Seleccionar usuario...</option>
                {users?.map(user => (
                  <option key={user?.id} value={user?.id}>
                    {user?.full_name} ({roleHierarchy?.[user?.role]?.label || user?.role})
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Funcionalidad
              </label>
              <select
                value={testFeature}
                onChange={(e) => setTestFeature(e?.target?.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
              >
                <option value="">Seleccionar funcionalidad...</option>
                {features?.map(feature => (
                  <option key={feature} value={feature}>
                    {getFeatureDescription(feature)}
                  </option>
                ))}
              </select>
            </div>

            <div className="flex items-end">
              <button
                onClick={handleTestValidation}
                disabled={!testUserId || !testFeature}
                className="w-full px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Probar Acceso
              </button>
            </div>
          </div>
        </div>

        {/* Real-time Validation Queue */}
        <div className="space-y-4">
          <div className="flex items-center justify-between">
            <h3 className="text-lg font-semibold text-gray-900">
              <Clock className="h-5 w-5 inline mr-2" />
              Validaciones Recientes
            </h3>
            <span className="text-sm text-gray-600">
              {validationQueue?.length || 0} validaciones en cola
            </span>
          </div>

          <div className="space-y-3 max-h-96 overflow-y-auto">
            {validationQueue?.map((validation) => (
              <div key={validation?.id} className={`border rounded-lg p-4 ${
                validation?.hasAccess ? 'border-green-200 bg-green-50' : 'border-red-200 bg-red-50'
              }`}>
                <div className="flex items-start justify-between">
                  <div className="flex items-start space-x-3 flex-1">
                    <div className="flex-shrink-0 mt-1">
                      {getValidationIcon(validation?.hasAccess)}
                    </div>

                    <div className="flex-1">
                      <div className="flex items-center space-x-2 mb-2">
                        <User className="h-4 w-4 text-gray-400" />
                        <span className="font-medium text-gray-900">
                          {validation?.userName}
                        </span>
                        <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                          roleHierarchy?.[validation?.userRole]?.color || 'bg-gray-100 text-gray-800'
                        }`}>
                          {roleHierarchy?.[validation?.userRole]?.label || validation?.userRole}
                        </span>
                      </div>

                      <div className="grid grid-cols-1 md:grid-cols-2 gap-2 text-sm">
                        <div>
                          <p className="text-gray-600">Funci√≥n solicitada:</p>
                          <p className="font-medium text-gray-900">
                            {getFeatureDescription(validation?.feature)}
                          </p>
                        </div>

                        <div>
                          <p className="text-gray-600">Roles requeridos:</p>
                          <div className="flex flex-wrap gap-1">
                            {validation?.requiredRoles?.map(role => (
                              <span key={role} className={`px-1 py-0.5 rounded text-xs font-medium ${
                                roleHierarchy?.[role]?.color || 'bg-gray-100 text-gray-800'
                              }`}>
                                {roleHierarchy?.[role]?.label || role}
                              </span>
                            ))}
                          </div>
                        </div>
                      </div>

                      <div className="flex items-center justify-between mt-3">
                        <div className="flex items-center text-xs text-gray-500">
                          <Clock className="h-3 w-3 mr-1" />
                          {getTimeAgo(validation?.timestamp)}
                        </div>

                        <div className={`px-3 py-1 rounded-full text-xs font-bold ${
                          validation?.hasAccess 
                            ? 'bg-green-100 text-green-800' :'bg-red-100 text-red-800'
                        }`}>
                          {validation?.hasAccess ? 'ACCESO PERMITIDO' : 'ACCESO DENEGADO'}
                        </div>
                      </div>

                      {/* Emergency Override for Denied Access */}
                      {!validation?.hasAccess && (
                        <div className="mt-3 p-3 bg-yellow-50 border border-yellow-200 rounded">
                          <div className="flex items-center mb-2">
                            <AlertTriangle className="h-4 w-4 text-yellow-600 mr-2" />
                            <span className="text-sm font-medium text-yellow-800">
                              Override de Emergencia
                            </span>
                          </div>

                          <div className="flex items-center space-x-2">
                            <input
                              type="text"
                              value={overrideReason}
                              onChange={(e) => setOverrideReason(e?.target?.value)}
                              placeholder="Raz√≥n del override..."
                              className="flex-1 px-2 py-1 text-sm border border-yellow-300 rounded focus:ring-yellow-500 focus:border-yellow-500"
                            />
                            <button
                              onClick={() => handleEmergencyOverride(validation)}
                              disabled={!overrideReason?.trim()}
                              className="px-3 py-1 bg-yellow-600 text-white text-sm rounded hover:bg-yellow-700 disabled:opacity-50"
                            >
                              Override
                            </button>
                          </div>
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>

          {validationQueue?.length === 0 && (
            <div className="text-center py-12 text-gray-500">
              <Eye className="h-12 w-12 mx-auto mb-4 opacity-50" />
              <p className="text-lg font-medium mb-2">No hay validaciones recientes</p>
              <p className="text-sm">
                Use el panel de prueba arriba para simular validaciones de acceso
              </p>
            </div>
          )}
        </div>

        {/* Validation Statistics */}
        {validationQueue?.length > 0 && (
          <div className="mt-6 p-4 bg-gray-50 rounded-lg">
            <h4 className="font-medium text-gray-900 mb-3">Estad√≠sticas de Validaci√≥n</h4>

            <div className="grid grid-cols-1 md:grid-cols-4 gap-4 text-sm">
              <div className="text-center">
                <p className="text-2xl font-bold text-blue-600">
                  {validationQueue?.length}
                </p>
                <p className="text-gray-600">Total Validaciones</p>
              </div>

              <div className="text-center">
                <p className="text-2xl font-bold text-green-600">
                  {validationQueue?.filter(v => v?.hasAccess)?.length}
                </p>
                <p className="text-gray-600">Accesos Permitidos</p>
              </div>

              <div className="text-center">
                <p className="text-2xl font-bold text-red-600">
                  {validationQueue?.filter(v => !v?.hasAccess)?.length}
                </p>
                <p className="text-gray-600">Accesos Denegados</p>
              </div>

              <div className="text-center">
                <p className="text-2xl font-bold text-purple-600">
                  {Math.round((validationQueue?.filter(v => v?.hasAccess)?.length / validationQueue?.length) * 100)}%
                </p>
                <p className="text-gray-600">Tasa de √âxito</p>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

import React, { useState } from 'react';
import { Users, Edit3, Save, X, AlertTriangle, CheckCircle2, Shield, UserPlus } from 'lucide-react';

export default function RoleManagementTools({
  users,
  roleHierarchy,
  onUpdateUserRole,
  currentUser
}) {
  const [editingUserId, setEditingUserId] = useState(null);
  const [selectedRole, setSelectedRole] = useState('');
  const [bulkSelectedUsers, setBulkSelectedUsers] = useState([]);
  const [bulkRole, setBulkRole] = useState('');
  const [showBulkEdit, setShowBulkEdit] = useState(false);

  // Only allow role changes if current user is superadmin
  const canModifyRoles = currentUser?.role === 'superadmin';

  const handleStartEdit = (userId, currentRole) => {
    if (!canModifyRoles) return;
    setEditingUserId(userId);
    setSelectedRole(currentRole);
  };

  const handleSaveRole = async () => {
    if (editingUserId && selectedRole) {
      await onUpdateUserRole(editingUserId, selectedRole);
      setEditingUserId(null);
      setSelectedRole('');
    }
  };

  const handleCancelEdit = () => {
    setEditingUserId(null);
    setSelectedRole('');
  };

  const handleBulkUserSelection = (userId) => {
    setBulkSelectedUsers(prev => 
      prev?.includes(userId)
        ? prev?.filter(id => id !== userId)
        : [...prev, userId]
    );
  };

  const handleBulkRoleUpdate = async () => {
    if (bulkSelectedUsers?.length === 0 || !bulkRole) return;

    for (const userId of bulkSelectedUsers) {
      await onUpdateUserRole(userId, bulkRole);
    }

    setBulkSelectedUsers([]);
    setBulkRole('');
    setShowBulkEdit(false);
  };

  const getRoleStats = () => {
    const stats = {};
    Object.keys(roleHierarchy)?.forEach(role => {
      stats[role] = users?.filter(user => user?.role === role)?.length || 0;
    });
    return stats;
  };

  const roleStats = getRoleStats();
  const roles = Object.keys(roleHierarchy)?.sort((a, b) => 
    roleHierarchy?.[b]?.level - roleHierarchy?.[a]?.level
  );

  return (
    <div className="space-y-6">
      {/* Role Statistics */}
      <div className="bg-white rounded-lg shadow">
        <div className="p-6">
          <h2 className="text-xl font-bold text-gray-900 mb-4">
            <Users className="h-6 w-6 inline mr-2 text-blue-600" />
            Gesti√≥n de Roles de Usuario
          </h2>

          <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
            {roles?.map(role => (
              <div key={role} className="p-4 border border-gray-200 rounded-lg">
                <div className="flex items-center justify-between mb-2">
                  <span className={`px-2 py-1 rounded-full text-xs font-medium ${roleHierarchy?.[role]?.color}`}>
                    {roleHierarchy?.[role]?.label}
                  </span>
                  <Shield className="h-4 w-4 text-gray-400" />
                </div>

                <div className="text-center">
                  <div className="text-2xl font-bold text-gray-900">
                    {roleStats?.[role]}
                  </div>
                  <div className="text-sm text-gray-600">usuarios</div>
                </div>
              </div>
            ))}
          </div>

          {!canModifyRoles && (
            <div className="mb-4 p-3 bg-yellow-50 border border-yellow-200 rounded">
              <div className="flex items-center">
                <AlertTriangle className="h-4 w-4 text-yellow-600 mr-2" />
                <span className="text-sm text-yellow-800">
                  Solo los Super Administradores pueden modificar roles de usuario.
                </span>
              </div>
            </div>
          )}
        </div>
      </div>
      {/* Bulk Role Management */}
      {canModifyRoles && (
        <div className="bg-white rounded-lg shadow">
          <div className="p-6">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold text-gray-900">
                <UserPlus className="h-5 w-5 inline mr-2" />
                Gesti√≥n Masiva de Roles
              </h3>

              <button
                onClick={() => setShowBulkEdit(!showBulkEdit)}
                className="px-4 py-2 bg-purple-600 text-white rounded-lg hover:bg-purple-700"
              >
                {showBulkEdit ? 'Cancelar' : 'Editar Masivo'}
              </button>
            </div>

            {showBulkEdit && (
              <div className="bg-purple-50 border border-purple-200 rounded-lg p-4">
                <div className="flex items-center space-x-4 mb-4">
                  <div>
                    <label className="block text-sm font-medium text-purple-700 mb-1">
                      Nuevo Rol
                    </label>
                    <select
                      value={bulkRole}
                      onChange={(e) => setBulkRole(e?.target?.value)}
                      className="px-3 py-2 border border-purple-300 rounded-md focus:ring-purple-500 focus:border-purple-500"
                    >
                      <option value="">Seleccionar rol...</option>
                      {roles?.map(role => (
                        <option key={role} value={role}>
                          {roleHierarchy?.[role]?.label}
                        </option>
                      ))}
                    </select>
                  </div>

                  <div className="flex items-end">
                    <button
                      onClick={handleBulkRoleUpdate}
                      disabled={bulkSelectedUsers?.length === 0 || !bulkRole}
                      className="px-4 py-2 bg-purple-600 text-white rounded-lg hover:bg-purple-700 disabled:opacity-50"
                    >
                      Aplicar a {bulkSelectedUsers?.length} usuarios
                    </button>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      )}
      {/* User Role Management Table */}
      <div className="bg-white rounded-lg shadow">
        <div className="p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Lista de Usuarios</h3>

          <div className="overflow-x-auto">
            <table className="w-full table-auto">
              <thead>
                <tr className="bg-gray-50">
                  {showBulkEdit && (
                    <th className="px-4 py-3 text-left">
                      <input
                        type="checkbox"
                        checked={bulkSelectedUsers?.length === users?.length}
                        onChange={(e) => {
                          if (e?.target?.checked) {
                            setBulkSelectedUsers(users?.map(u => u?.id) || []);
                          } else {
                            setBulkSelectedUsers([]);
                          }
                        }}
                        className="rounded border-gray-300 text-purple-600 focus:ring-purple-500"
                      />
                    </th>
                  )}

                  <th className="px-4 py-3 text-left text-sm font-medium text-gray-700">
                    Usuario
                  </th>

                  <th className="px-4 py-3 text-left text-sm font-medium text-gray-700">
                    Rol Actual
                  </th>

                  <th className="px-4 py-3 text-left text-sm font-medium text-gray-700">
                    Estado
                  </th>

                  <th className="px-4 py-3 text-left text-sm font-medium text-gray-700">
                    √öltima Actividad
                  </th>

                  <th className="px-4 py-3 text-left text-sm font-medium text-gray-700">
                    Acciones
                  </th>
                </tr>
              </thead>

              <tbody className="divide-y divide-gray-200">
                {users?.map((user) => (
                  <tr key={user?.id} className="hover:bg-gray-50">
                    {showBulkEdit && (
                      <td className="px-4 py-3">
                        <input
                          type="checkbox"
                          checked={bulkSelectedUsers?.includes(user?.id)}
                          onChange={() => handleBulkUserSelection(user?.id)}
                          className="rounded border-gray-300 text-purple-600 focus:ring-purple-500"
                        />
                      </td>
                    )}

                    <td className="px-4 py-3">
                      <div className="flex items-center">
                        <div className="w-10 h-10 bg-blue-100 rounded-full flex items-center justify-center mr-3">
                          <Users className="h-5 w-5 text-blue-600" />
                        </div>
                        <div>
                          <p className="font-medium text-gray-900">{user?.full_name || 'Sin nombre'}</p>
                          <p className="text-sm text-gray-500">{user?.email || 'Sin email'}</p>
                        </div>
                      </div>
                    </td>

                    <td className="px-4 py-3">
                      {editingUserId === user?.id ? (
                        <select
                          value={selectedRole}
                          onChange={(e) => setSelectedRole(e?.target?.value)}
                          className="px-3 py-1 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
                        >
                          {roles?.map(role => (
                            <option key={role} value={role}>
                              {roleHierarchy?.[role]?.label}
                            </option>
                          ))}
                        </select>
                      ) : (
                        <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                          roleHierarchy?.[user?.role]?.color || 'bg-gray-100 text-gray-800'
                        }`}>
                          {roleHierarchy?.[user?.role]?.label || user?.role}
                        </span>
                      )}
                    </td>

                    <td className="px-4 py-3">
                      <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                        user?.status === 'active' ?'bg-green-100 text-green-800' :'bg-red-100 text-red-800'
                      }`}>
                        {user?.status === 'active' ? 'Activo' : 'Inactivo'}
                      </span>
                    </td>

                    <td className="px-4 py-3 text-sm text-gray-600">
                      {user?.last_attendance_date 
                        ? new Date(user.last_attendance_date)?.toLocaleDateString()
                        : 'Nunca'
                      }
                    </td>

                    <td className="px-4 py-3">
                      {canModifyRoles && (
                        <div className="flex items-center space-x-2">
                          {editingUserId === user?.id ? (
                            <>
                              <button
                                onClick={handleSaveRole}
                                className="p-2 text-green-600 hover:bg-green-100 rounded-full"
                                title="Guardar"
                              >
                                <Save className="h-4 w-4" />
                              </button>
                              <button
                                onClick={handleCancelEdit}
                                className="p-2 text-gray-600 hover:bg-gray-100 rounded-full"
                                title="Cancelar"
                              >
                                <X className="h-4 w-4" />
                              </button>
                            </>
                          ) : (
                            <button
                              onClick={() => handleStartEdit(user?.id, user?.role)}
                              className="p-2 text-blue-600 hover:bg-blue-100 rounded-full"
                              title="Editar rol"
                            >
                              <Edit3 className="h-4 w-4" />
                            </button>
                          )}
                        </div>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

